import React, { useState } from 'react';
import { 
  ArrowLeft, PlusCircle, X, Bell, 
  Eye, Clock, Pill
} from "lucide-react";

// Component for adding a new medication
const AddMedicationForm = ({ setView, addMedication, colors }) => {
  // Form state
  const [formData, setFormData] = useState({
    name: '',
    dosage: '',
    form: '',
    purpose: '',
    instructions: '',
    schedule: [
      { time: "08:00", status: "upcoming", day: "today" }
    ],
    reminders: true,
    sideEffects: [],
    refillRemaining: 30,
    adherenceRate: 100,
    missedDoses: 0,
    refillDate: "May 10, 2025",
    prescribedBy: "",
    startDate: new Date().toLocaleDateString('en-US', { month: 'long', day: 'numeric', year: 'numeric' }),
    interactions: [],
    history: []
  });

  // Update form fields
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  // Add a new time to the schedule
  const addScheduleTime = () => {
    setFormData(prev => ({
      ...prev,
      schedule: [...prev.schedule, { time: "12:00", status: "upcoming", day: "today" }]
    }));
  };

  // Remove a time from the schedule
  const removeScheduleTime = (index) => {
    setFormData(prev => ({
      ...prev,
      schedule: prev.schedule.filter((_, idx) => idx !== index)
    }));
  };

  // Update a specific schedule time
  const updateScheduleTime = (index, time) => {
    setFormData(prev => {
      const newSchedule = [...prev.schedule];
      newSchedule[index] = { ...newSchedule[index], time };
      return { ...prev, schedule: newSchedule };
    });
  };

  // Toggle reminders on/off
  const toggleReminders = () => {
    setFormData(prev => ({ ...prev, reminders: !prev.reminders }));
  };

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();

    // Format the times in AM/PM format for consistency
    const formattedSchedule = formData.schedule.map(item => {
      const [hours, minutes] = item.time.split(':');
      const hour = parseInt(hours);
      const ampm = hour >= 12 ? 'PM' : 'AM';
      const formattedHour = hour % 12 || 12;
      return {
        ...item,
        time: `${formattedHour}:${minutes} ${ampm}`
      };
    });

    // Add the new medication with formatted schedule
    addMedication({
      ...formData,
      schedule: formattedSchedule
    });
  };

  return (
    <>
      <header className="flex items-center mb-8">
        <button 
          className="w-10 h-10 rounded-full flex items-center justify-center mr-4 hover:bg-blue-50 transition-colors duration-300"
          onClick={() => setView('main')}
        >
          <ArrowLeft size={24} style={{ color: colors.textPrimary }} />
        </button>
        <div>
          <h1 className="text-2xl font-bold" style={{ color: colors.textPrimary }}>Add Medication</h1>
          <p className="text-sm" style={{ color: colors.textSecondary }}>Because who can remember everything they take?</p>
        </div>
      </header>

      <div className="mb-8 p-6 rounded-2xl bg-white shadow-lg border border-blue-50"
           style={{ boxShadow: `0 10px 15px -3px ${colors.primary}20` }}>
        <form className="space-y-6" onSubmit={handleSubmit}>
          {/* Medication name */}
          <div>
            <label className="block text-sm font-medium mb-2" style={{ color: colors.textPrimary }}>
              Medication Name
            </label>
            <input 
              type="text"
              name="name"
              value={formData.name}
              onChange={handleChange}
              className="w-full p-3 rounded-xl border border-blue-100 focus:outline-none focus:ring-2 focus:ring-blue-400 transition-all duration-300"
              placeholder="That pill whose name you always forget"
              required
            />
          </div>

          {/* Dosage and Form */}
          <div className="grid grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium mb-2" style={{ color: colors.textPrimary }}>
                Dosage
              </label>
              <input 
                type="text"
                name="dosage"
                value={formData.dosage}
                onChange={handleChange}
                className="w-full p-3 rounded-xl border border-blue-100 focus:outline-none focus:ring-2 focus:ring-blue-400 transition-all duration-300"
                placeholder="e.g., 10mg"
                required
              />
            </div>
            <div>
              <label className="block text-sm font-medium mb-2" style={{ color: colors.textPrimary }}>
                Form
              </label>
              <select 
                name="form"
                value={formData.form}
                onChange={handleChange}
                className="w-full p-3 rounded-xl border border-blue-100 focus:outline-none focus:ring-2 focus:ring-blue-400 transition-all duration-300"
                required
              >
                <option value="">Select form</option>
                <option value="tablet">Tablet</option>
                <option value="capsule">Capsule</option>
                <option value="liquid">Liquid</option>
                <option value="injection">Injection</option>
                <option value="patch">Patch</option>
                <option value="inhaler">Inhaler</option>
              </select>
            </div>
          </div>

          {/* Purpose */}
          <div>
            <label className="block text-sm font-medium mb-2" style={{ color: colors.textPrimary }}>
              Purpose
            </label>
            <input 
              type="text"
              name="purpose"
              value={formData.purpose}
              onChange={handleChange}
              className="w-full p-3 rounded-xl border border-blue-100 focus:outline-none focus:ring-2 focus:ring-blue-400 transition-all duration-300"
              placeholder="What it's used for (besides emptying your wallet)"
              required
            />
          </div>

          {/* Schedule */}
          <div>
            <label className="block text-sm font-medium mb-2" style={{ color: colors.textPrimary }}>
              Schedule
            </label>
            <div className="space-y-3">
              {formData.schedule.map((scheduleItem, index) => (
                <div 
                  key={index} 
                  className="flex items-center justify-between p-3 rounded-xl border border-blue-100"
                >
                  <div className="flex items-center">
                    <div className="w-8 h-8 rounded-full flex items-center justify-center mr-2" 
                         style={{ backgroundColor: `${colors.primary}15`, color: colors.primary }}>
                      <Clock size={16} />
                    </div>
                    <input 
                      type="time"
                      className="p-1 border-0 focus:outline-none bg-transparent"
                      value={scheduleItem.time}
                      onChange={(e) => updateScheduleTime(index, e.target.value)}
                      required
                    />
                  </div>
                  <button 
                    type="button" 
                    className="w-6 h-6 rounded-full flex items-center justify-center"
                    style={{ backgroundColor: `${colors.danger}15`, color: colors.danger }}
                    onClick={() => formData.schedule.length > 1 && removeScheduleTime(index)}
                  >
                    <X size={14} />
                  </button>
                </div>
              ))}
              <button 
                type="button"
                className="flex items-center justify-center w-full p-3 rounded-xl border border-dashed border-blue-200 hover:border-blue-400 transition-all duration-300 text-sm"
                style={{ color: colors.primary }}
                onClick={addScheduleTime}
              >
                <PlusCircle size={16} className="mr-2" />
                Add another time (for those overachievers)
              </button>
            </div>
          </div>

          {/* Instructions */}
          <div>
            <label className="block text-sm font-medium mb-2" style={{ color: colors.textPrimary }}>
              Instructions
            </label>
            <textarea 
              name="instructions"
              value={formData.instructions}
              onChange={handleChange}
              className="w-full p-3 rounded-xl border border-blue-100 focus:outline-none focus:ring-2 focus:ring-blue-400 transition-all duration-300"
              placeholder="Special instructions (e.g., take with food, avoid with alcohol, don't lick)"
              rows="3"
              required
            ></textarea>
          </div>

          {/* Prescriber */}
          <div>
            <label className="block text-sm font-medium mb-2" style={{ color: colors.textPrimary }}>
              Prescribed By
            </label>
            <input 
              type="text"
              name="prescribedBy"
              value={formData.prescribedBy}
              onChange={handleChange}
              className="w-full p-3 rounded-xl border border-blue-100 focus:outline-none focus:ring-2 focus:ring-blue-400 transition-all duration-300"
              placeholder="Your favorite doctor"
            />
          </div>

          {/* Reminders */}
          <div className="flex items-center justify-between p-3 rounded-xl hover:bg-blue-50 transition-colors duration-300">
            <div className="flex items-center">
              <div className="w-8 h-8 rounded-full flex items-center justify-center mr-3" 
                   style={{ 
                     backgroundColor: `${colors.primary}20`,
                     color: colors.primary
                   }}>
                <Bell size={18} />
              </div>
              <span className="text-sm font-medium" style={{ color: colors.textPrimary }}>Reminders</span>
            </div>
            <button 
              type="button"
              className="w-10 h-5 rounded-full relative transition-all duration-300"
              style={{ 
                backgroundColor: formData.reminders ? colors.primary : '#E5E7EB',
                boxShadow: formData.reminders ? `0 0 10px ${colors.primary}40` : 'none'
              }}
              onClick={toggleReminders}
            >
              <span 
                className="absolute top-0.5 w-4 h-4 rounded-full bg-white transform transition-transform duration-300"
                style={{ 
                  left: formData.reminders ? 'auto' : '0.125rem',
                  right: formData.reminders ? '0.125rem' : 'auto'
                }}
              ></span>
            </button>
          </div>

          {/* Photo option */}
          <div className="flex items-center justify-between p-3 rounded-xl hover:bg-blue-50 transition-colors duration-300">
            <div className="flex items-center">
              <div className="w-8 h-8 rounded-full flex items-center justify-center mr-3" 
                   style={{ 
                     backgroundColor: `${colors.accent}20`,
                     color: colors.accent
                   }}>
                <Eye size={18} />
              </div>
              <div>
                <span className="text-sm font-medium" style={{ color: colors.textPrimary }}>Add pill photo</span>
                <p className="text-xs" style={{ color: colors.textSecondary }}>
                  For when all those white pills start to look the same
                </p>
              </div>
            </div>
            <button 
              type="button"
              className="px-3 py-1 rounded-lg text-xs font-medium"
              style={{ 
                backgroundColor: `${colors.accent}15`,
                color: colors.accent
              }}
            >
              Add photo
            </button>
          </div>

          {/* Submit button */}
          <button 
            type="submit"
            className="w-full py-4 rounded-xl text-white font-medium transition-all duration-500 hover:shadow-xl transform hover:-translate-y-1"
            style={{ 
              background: `linear-gradient(to right, ${colors.primary}, ${colors.accent})`,
              boxShadow: `0 10px 15px -3px ${colors.primary}40`
            }}
          >
            Save Medication
          </button>
        </form>
      </div>
    </>
  );
};

export default AddMedicationForm;
