import React from 'react';
import { 
  ArrowLeft, Sun, Clock, Zap, 
  Moon, Pill, CheckCircle, X 
} from "lucide-react";

// Helper component for left chevron icon
const ChevronLeft = ({ size, style }) => {
  return (
    <svg width={size} height={size} viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" style={style}>
      <polyline points="15 18 9 12 15 6"></polyline>
    </svg>
  );
};

// Helper component for right chevron icon
const ChevronRight = ({ size, style }) => {
  return (
    <svg width={size} height={size} viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" style={style}>
      <polyline points="9 18 15 12 9 6"></polyline>
    </svg>
  );
};

// Component for displaying medication schedule
const MedicationSchedule = ({ medication, medications, setView, colors }) => {
  // Days of the week for display in the calendar
  const daysOfWeek = ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sunday'];

  // Time slots for organization
  const timeSlots = ['Morning', 'Afternoon', 'Evening', 'Night'];

  // Generate schedule data from medications - filter for selected medication if provided
  const medsToDisplay = medication ? [medication] : medications;

  // Helper function to determine which time slot a schedule falls into
  const getTimeSlot = (timeString) => {
    const hour = parseInt(timeString.split(':')[0]);
    if (hour >= 5 && hour < 12) return 'Morning';
    if (hour >= 12 && hour < 17) return 'Afternoon';
    if (hour >= 17 && hour < 21) return 'Evening';
    return 'Night';
  };

  // Initialize schedule data
  const scheduleData = {};
  timeSlots.forEach(slot => {
    scheduleData[slot] = [];

    medsToDisplay.forEach(med => {
      med.schedule.forEach(scheduleItem => {
        const time = scheduleItem.time;
        const hour = parseInt(time.split(':')[0]);

        let timeSlot;
        if (time.includes('AM') || time.includes('PM')) {
          // Handle "8:00 AM" format
          const hour = parseInt(time.split(':')[0]);
          const isPM = time.includes('PM');
          const hour24 = isPM ? (hour === 12 ? 12 : hour + 12) : (hour === 12 ? 0 : hour);

          timeSlot = getTimeSlot(`${hour24}:00`);
        } else {
          // Handle 24-hour format
          timeSlot = getTimeSlot(time);
        }

        if (timeSlot === slot) {
          scheduleData[slot].push({
            ...med,
            scheduleItem
          });
        }
      });
    });
  });

  return (
    <>
      <header className="flex items-center mb-8">
        <button 
          className="w-10 h-10 rounded-full flex items-center justify-center mr-4 hover:bg-blue-50 transition-colors duration-300"
          onClick={() => medication ? setView('detail') : setView('main')}
        >
          <ArrowLeft size={24} style={{ color: colors.textPrimary }} />
        </button>
        <div>
          <h1 className="text-2xl font-bold" style={{ color: colors.textPrimary }}>Medication Schedule</h1>
          <p className="text-sm" style={{ color: colors.textSecondary }}>
            {medication ? medication.name : 'All medications'}
          </p>
        </div>
      </header>

      {/* Week selector */}
      <div className="mb-6 p-4 rounded-2xl bg-white shadow-lg flex flex-col"
           style={{ boxShadow: `0 10px 15px -3px ${colors.primary}20` }}>
        <div className="flex justify-between items-center mb-4">
          <button className="w-10 h-10 rounded-full flex items-center justify-center hover:bg-blue-50 transition-colors duration-300">
            <ChevronLeft size={20} style={{ color: colors.textPrimary }} />
          </button>
          <h3 className="font-medium" style={{ color: colors.textPrimary }}>March 22 - 28, 2025</h3>
          <button className="w-10 h-10 rounded-full flex items-center justify-center hover:bg-blue-50 transition-colors duration-300">
            <ChevronRight size={20} style={{ color: colors.textPrimary }} />
          </button>
        </div>

        <div className="flex justify-between">
          {daysOfWeek.map((day, idx) => {
            const isToday = idx === 3; // Assuming Thursday is today
            return (
              <div key={day} className="flex flex-col items-center">
                <span className="text-xs mb-1" style={{ color: colors.textSecondary }}>{day.slice(0, 3)}</span>
                <div 
                  className={`w-8 h-8 rounded-full flex items-center justify-center ${isToday ? 'text-white' : ''}`}
                  style={{ 
                    background: isToday ? `linear-gradient(135deg, ${colors.primary}, ${colors.accent})` : 'transparent',
                    boxShadow: isToday ? `0 0 10px ${colors.primary}40` : 'none'
                  }}
                >
                  <span className="text-sm">{idx + 22}</span>
                </div>
              </div>
            );
          })}
        </div>
      </div>

      {/* Daily schedule */}
      <div className="mb-6">
        <h3 className="font-bold text-lg mb-4" style={{ color: colors.textPrimary }}>Today's Schedule</h3>

        <div className="space-y-5">
          {timeSlots.map((slot, idx) => (
            <div key={slot} className="relative">
              {/* Timeline bar */}
              {idx < timeSlots.length - 1 && (
                <div className="absolute left-5 top-10 w-0.5 h-6 bg-gray-200 z-0"></div>
              )}

              <div className="flex items-start mb-2">
                <div 
                  className="w-10 h-10 rounded-full flex items-center justify-center mr-3 z-10"
                  style={{ 
                    background: `linear-gradient(135deg, ${colors.primary}80, ${colors.accent}60)`,
                    color: 'white',
                    boxShadow: `0 5px 10px -3px ${colors.primary}40`
                  }}
                >
                  {slot === 'Morning' ? (
                    <Sun size={20} />
                  ) : slot === 'Afternoon' ? (
                    <Clock size={20} />
                  ) : slot === 'Evening' ? (
                    <Zap size={20} />
                  ) : (
                    <Moon size={20} />
                  )}
                </div>
                <div>
                  <h4 className="font-medium" style={{ color: colors.textPrimary }}>{slot}</h4>
                  <p className="text-xs" style={{ color: colors.textSecondary }}>
                    {slot === 'Morning' ? '5:00 AM - 11:59 AM' : 
                     slot === 'Afternoon' ? '12:00 PM - 4:59 PM' : 
                     slot === 'Evening' ? '5:00 PM - 8:59 PM' : 
                     '9:00 PM - 4:59 AM'}
                  </p>
                </div>
              </div>

              <div className="ml-13 space-y-2">
                {scheduleData[slot].length > 0 ? (
                  scheduleData[slot].map((medData, medIdx) => (
                    <div 
                      key={`${medData.id}-${slot}-${medIdx}`} 
                      className="p-3 rounded-xl bg-white shadow-md flex items-center justify-between hover:shadow-lg transition-all duration-300"
                      style={{ boxShadow: `0 5px 10px -3px ${colors.primary}10` }}
                    >
                      <div className="flex items-center">
                        <div 
                          className="w-8 h-8 rounded-full flex items-center justify-center mr-2"
                          style={{ 
                            backgroundColor: `${colors.primary}15`,
                            color: colors.primary
                          }}
                        >
                          <Pill size={16} />
                        </div>
                        <div>
                          <p className="font-medium text-sm" style={{ color: colors.textPrimary }}>
                            {medData.name} {medData.dosage}
                          </p>
                          <p className="text-xs" style={{ color: colors.textSecondary }}>
                            {medData.scheduleItem.time}
                          </p>
                        </div>
                      </div>
                      <div className="flex items-center">
                        {medData.scheduleItem.status === 'taken' ? (
                          <div className="w-6 h-6 rounded-full flex items-center justify-center" 
                              style={{ backgroundColor: `${colors.success}20`, color: colors.success }}>
                            <CheckCircle size={14} />
                          </div>
                        ) : medData.scheduleItem.status === 'missed' ? (
                          <div className="w-6 h-6 rounded-full flex items-center justify-center" 
                              style={{ backgroundColor: `${colors.danger}20`, color: colors.danger }}>
                            <X size={14} />
                          </div>
                        ) : (
                          <button 
                            className="px-3 py-1 rounded-lg text-xs font-medium shadow-sm transition-all duration-300 hover:shadow-md transform hover:scale-105" 
                            style={{ 
                              background: `linear-gradient(to right, ${colors.primary}, ${colors.accent})`,
                              color: 'white',
                              boxShadow: `0 2px 4px -1px ${colors.primary}30`
                            }}
                          >
                            Take
                          </button>
                        )}
                      </div>
                    </div>
                  ))
                ) : (
                  <div className="p-3 rounded-xl bg-blue-50 flex items-center">
                    <p className="text-xs italic" style={{ color: colors.textSecondary }}>
                      No medications scheduled. Time for a break!
                    </p>
                  </div>
                )}
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Tips section */}
      <div className="p-5 rounded-2xl bg-white shadow-lg border border-blue-50 mb-6"
           style={{ boxShadow: `0 10px 15px -3px ${colors.primary}20` }}>
        <h3 className="font-bold text-lg mb-3" style={{ color: colors.textPrimary }}>Schedule Tips</h3>
        <ul className="space-y-2">
          <li className="flex items-center text-sm" style={{ color: colors.textSecondary }}>
            <div className="w-5 h-5 rounded-full flex items-center justify-center mr-2" 
                 style={{ backgroundColor: `${colors.success}15`, color: colors.success }}>
              <CheckCircle size={12} />
            </div>
            Take medications at the same time each day to build a routine
          </li>
          <li className="flex items-center text-sm" style={{ color: colors.textSecondary }}>
            <div className="w-5 h-5 rounded-full flex items-center justify-center mr-2" 
                 style={{ backgroundColor: `${colors.success}15`, color: colors.success }}>
              <CheckCircle size={12} />
            </div>
            Use meal times as reminders for medications that need to be taken with food
          </li>
          <li className="flex items-center text-sm" style={{ color: colors.textSecondary }}>
            <div className="w-5 h-5 rounded-full flex items-center justify-center mr-2" 
                 style={{ backgroundColor: `${colors.success}15`, color: colors.success }}>
              <CheckCircle size={12} />
            </div>
            Set reminders on your phone (or use our app, obviously) to help you remember
          </li>
        </ul>
      </div>
    </>
  );
};

export default MedicationSchedule;
