import io from 'socket.io-client';

/**
 * Service that handles WebRTC connections for video calling
 * Enhanced with better state management and error handling
 */
class WebRTCService {
  constructor() {
    // Remove these redundant properties
    // this.isCallActive = false; 
    // this.isCallConnected = false;

    // Use only callState for state management
    this.callState = 'idle'; // idle, connecting, active, ended

    // Add helper methods
    this.isCallActive = () => ['connecting', 'active'].includes(this.callState);
    this.isCallConnected = () => this.callState === 'active';
  }

  /**
   * Log messages with timestamp and store in localStorage
   */
  log(message, data = null) {
    const timestamp = new Date().toISOString().split('T')[1].substring(0, 8);
    const prefix = `[WebRTC ${timestamp}]`;
    if (data) {
      console.log(prefix, message, data);
    } else {
      console.log(prefix, message);
    }

    // Store logs in localStorage for debugging across page refreshes
    const logs = JSON.parse(localStorage.getItem('webrtc_logs') || '[]');
    logs.push({
      timestamp: new Date().toISOString(),
      message,
      data: data ? JSON.stringify(data) : null
    });

    // Keep only the last 100 logs
    if (logs.length > 100) {
      logs.shift();
    }
    localStorage.setItem('webrtc_logs', JSON.stringify(logs));
  }

  /**
   * ENHANCED: Unified error handling method
   */
  handleError(context, error, fatal = false) {
    this.log(`Error in ${context}:`, error.message);
    console.error(`Error in ${context}:`, error);

    // Emit error event for UI components to handle
    this.emit('error', {
      context,
      message: error.message,
      fatal
    });

    // If it's a fatal error, end the call
    if (fatal) {
      this.updateCallState('ended');
      this.endCall(true);
    }
  }

  /**
   * ENHANCED: Event emission system
   */
  emit(event, data) {
    if (this.eventListeners[event]) {
      this.eventListeners[event].forEach(callback => callback(data));
    }
  }

  /**
   * ENHANCED: Event subscription system
   * Returns a function to unsubscribe the listener
   */
  on(event, callback) {
    if (!this.eventListeners[event]) {
      this.eventListeners[event] = [];
    }
    this.eventListeners[event].push(callback);

    // Return a function to remove the listener
    return () => {
      this.eventListeners[event] = this.eventListeners[event].filter(cb => cb !== callback);
    };
  }

  /**
   * ENHANCED: Centralized call state management
   */
  updateCallState(newState) {
    if (this.callState !== newState) {
      this.log(`Call state changing from ${this.callState} to ${newState}`);
      this.callState = newState;

      // Emit event for UI components
      this.emit('callStateChanged', newState);

      // Trigger appropriate callbacks
      if (newState === 'active' && this.onCallStarted) this.onCallStarted();
      if (newState === 'ended' && this.onCallEnded) this.onCallEnded();
      if (newState === 'connecting' && this.onCallConnecting) this.onCallConnecting();
    }
  }

  /**
   * Initialize the WebRTC service with user information and callbacks
   * @param {string} userId - The current user's ID
   * @param {object} callbacks - Callback functions to handle events
   * @param {string} serverUrl - The URL of the signaling server (optional)
   */
  initialize(userId, callbacks = {}, serverUrl = null) {
    // If already initialized with the same ID, just update callbacks
    if (this.isInitialized && this.userId === userId) {
      this.log('WebRTC service already initialized for user:', userId, '- updating callbacks only');
      this.updateCallbacks(callbacks);
      return;
    }

    // If initialized with a different ID, disconnect first
    if (this.isInitialized && this.userId !== userId) {
      this.log('Reinitializing with new user ID - disconnecting first');
      this.disconnect();
    }
    this.log('Initializing WebRTC service for user:', userId);
    this.userId = userId;

    // Set up callbacks
    this.updateCallbacks(callbacks);

    // Connect to the signaling server if not already connected
    if (!this.socket || !this.socket.connected) {
      // Use provided URL, fallback to production URL, then localhost as last resort
      const signalingServer = serverUrl || process.env.REACT_APP_SIGNALING_SERVER || 'http://localhost:3001';
      this.log('Connecting to signaling server:', signalingServer);
      this.socket = io(signalingServer);

      // Set up event listeners for signaling messages
      this.setupSocketListeners();
    }

    // Register user ID with the signaling server
    if (this.socket) {
      this.socket.emit('register', userId);
    }
    this.isInitialized = true;
  }

  /**
   * Update callback functions
   * @param {object} callbacks - Callback functions to update
   */
  updateCallbacks(callbacks = {}) {
    // Only update provided callbacks, keep existing ones otherwise
    if (callbacks.onLocalStream !== undefined) this.onLocalStream = callbacks.onLocalStream;
    if (callbacks.onRemoteStream !== undefined) this.onRemoteStream = callbacks.onRemoteStream;
    if (callbacks.onCallStarted !== undefined) this.onCallStarted = callbacks.onCallStarted;
    if (callbacks.onCallEnded !== undefined) this.onCallEnded = callbacks.onCallEnded;
    if (callbacks.onCallConnecting !== undefined) this.onCallConnecting = callbacks.onCallConnecting;
    if (callbacks.onIncomingCall !== undefined) this.onIncomingCall = callbacks.onIncomingCall;
    if (callbacks.onICEConnectionStateChange !== undefined) this.onICEConnectionStateChange = callbacks.onICEConnectionStateChange;
    if (callbacks.onUserNotOnline !== undefined) this.onUserNotOnline = callbacks.onUserNotOnline;

    // Initialize default callback functions if not already set
    this.onLocalStream = this.onLocalStream || (() => {});
    this.onRemoteStream = this.onRemoteStream || (() => {});
    this.onCallStarted = this.onCallStarted || (() => {});
    this.onCallEnded = this.onCallEnded || (() => {});
    this.onCallConnecting = this.onCallConnecting || (() => {});
    this.onIncomingCall = this.onIncomingCall || (() => {});
    this.onICEConnectionStateChange = this.onICEConnectionStateChange || (() => {});
    this.onUserNotOnline = this.onUserNotOnline || (() => {});
  }

  /**
   * Set up all socket event listeners for signaling
   */
  setupSocketListeners() {
    // Handle incoming calls
    this.socket.on('incoming-call', async ({
      callerUserId,
      offerSDP
    }) => {
      this.log('Incoming call from:', callerUserId);

      // If there's already an active call, ignore this one
      if (this.isCallActive || this.callState === 'active' || this.callState === 'connecting') {
        this.log('Already in a call, ignoring incoming call');
        // Explicitly decline the call so the caller knows
        this.socket.emit('call-declined', {
          targetUserId: callerUserId
        });
        return;
      }
      this.targetUserId = callerUserId;
      this.isCallInitiator = false;

      // Store the offer to use when call is accepted
      this.pendingOffer = offerSDP;

      // Update call state
      this.updateCallState('connecting');

      // Notify the application of the incoming call
      this.onIncomingCall(callerUserId);
    });

    // Handle when the other person answers our call
    this.socket.on('call-answered', async ({
      answerSDP
    }) => {
      this.log('Call was answered, setting remote description');
      try {
        if (this.peerConnection) {
          await this.peerConnection.setRemoteDescription(new RTCSessionDescription(answerSDP));
        }
      } catch (error) {
        this.handleError('setting remote description', error);
      }
    });
    this.socket.on('call-in-progress', ({
      callerUserId
    }) => {
      this.log('Call in progress on another device with:', callerUserId);
      // You can notify the UI if desired
      if (typeof this.onCallInProgress === 'function') {
        this.onCallInProgress(callerUserId);
      }
    });

    // Add handler for declined calls
    this.socket.on('call-declined', () => {
      this.log('Call was declined by the recipient');
      this.updateCallState('ended');
      this.endCall(false); // Don't notify the other person since they declined it

      // Notify the application
      if (typeof this.onCallDeclined === 'function') {
        this.onCallDeclined();
      }
    });

    // Handle ICE candidates from the other peer
    this.socket.on('ice-candidate', async ({
      candidate
    }) => {
      try {
        if (this.peerConnection) {
          await this.peerConnection.addIceCandidate(new RTCIceCandidate(candidate));
          this.log('Added ICE candidate');
        }
      } catch (error) {
        this.handleError('adding ICE candidate', error);
      }
    });

    // Handle the other person ending the call
    this.socket.on('call-ended', () => {
      this.log('Call ended remotely');
      this.updateCallState('ended');
      this.endCall(false); // Don't notify the other person since they ended it
    });

    // Handle call failure
    this.socket.on('call-failed', ({
      message,
      targetUserId
    }) => {
      this.log('Call failed:', message);

      // Handle user not online case
      if (message === 'User is not online') {
        if (this.onUserNotOnline) {
          this.onUserNotOnline(targetUserId);
        } else {
          this.updateCallState('ended');
        }
      } else {
        this.updateCallState('ended');
      }
      this.resetCallState();
    });

    // Handle user online status
    this.socket.on('user-online-status', ({
      userId,
      isOnline,
      responseId
    }) => {
      this.log('User online status received:', {
        userId,
        isOnline,
        responseId
      });
      // The responseId is now used to match requests with responses
      this.emit(`online-status-${responseId}`, {
        userId,
        isOnline
      });
    });
  }
  declineCall() {
    if (this.targetUserId) {
      this.log('Declining call from:', this.targetUserId);
      this.socket.emit('call-declined', {
        targetUserId: this.targetUserId
      });
      this.resetCallState();
    }
  }

  /**
   * ENHANCED: Check if a user is online with request ID for reliable responses
   * @param {string} userId - ID of the user to check
   * @returns {Promise<boolean>} - Whether the user is online
   */
  checkUserOnline(userId) {
    return new Promise(resolve => {
      if (!this.socket || !this.socket.connected) {
        this.log('Socket not connected, user is offline');
        resolve(false);
        return;
