import React, { useState, useEffect } from 'react';
import { 
  Clock, Moon, Sun, Activity, Coffee, 
  Utensils, Zap, ChevronRight, Calendar, ArrowUpRight,
  ArrowLeft, PlusCircle, Bell, CheckCircle, Heart, 
  HelpCircle, ChevronDown, ChevronUp, Info
} from 'lucide-react';

// Default colors in case they're not provided in props
const defaultColors = {
  primary: '#1E40AF',
  primaryDark: '#1E3A8A',
  primaryLight: '#60A5FA',
  primaryGlow: '#93C5FD',
  accent: '#0891B2',
  accentAlt: '#7C3AED',
  success: '#059669',
  warning: '#D97706',
  danger: '#DC2626',
  background: '#EFF6FF',
  darkBg: '#0F172A',
  cardBg: '#FFFFFF',
  textPrimary: '#1E293B',
  textSecondary: '#64748B',
  gradientStart: '#1E40AF',
  gradientEnd: '#0891B2',
  gradientAlt1: '#7C3AED',
  gradientAlt2: '#EC4899',
  neonAccent: '#06B6D4',
  glass: 'rgba(255, 255, 255, 0.25)'
};

// Biorhythm Screen with upgraded UI & sassy commentary - Mobile optimized
const BiorhythmScreen = ({ colors = {}, setActiveScreen }) => {
  // Merge provided colors with defaults
  const themeColors = { ...defaultColors, ...colors };
  const [activeTab, setActiveTab] = useState('today');
  const [currentTime, setCurrentTime] = useState(new Date());
  const [biorhythmData, setBiorhythmData] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [isExpanded, setIsExpanded] = useState({});
  const [userChronotype, setUserChronotype] = useState(0); // 0 = neutral, negative = early bird, positive = night owl
  const [activityReminders, setActivityReminders] = useState({
    exercise: true,
    medication: true, 
    focus: false,
    meal: true,
    sleep: false
  });
  const [showTooltip, setShowTooltip] = useState(null);

  // Mouse position for parallax effects
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });

  useEffect(() => {
    const handleMouseMove = (e) => {
      const { clientX, clientY } = e;
      const x = (clientX / window.innerWidth) - 0.5;
      const y = (clientY / window.innerHeight) - 0.5;
      setMousePosition({ x, y });
    };

    window.addEventListener('mousemove', handleMouseMove);

    // Update current time every minute
    const timer = setInterval(() => {
      setCurrentTime(new Date());
    }, 60000);

    // Fake loading data
    setTimeout(() => {
      generateBiorhythmData();
      setIsLoading(false);
    }, 1000);

    return () => {
      window.removeEventListener('mousemove', handleMouseMove);
      clearInterval(timer);
    };
  }, [userChronotype]); // Re-run when user chronotype changes

  // Generate fake biorhythm data
  const generateBiorhythmData = () => {
    const now = new Date();
    const hours = now.getHours();

    // Physical rhythm - peaks mid-morning to early afternoon
    const physical = calculateRhythmValue(hours, 11, 0.8);

    // Emotional rhythm - peaks late afternoon
    const emotional = calculateRhythmValue(hours, 16, 0.7);

    // Intellectual rhythm - peaks mid-morning
    const intellectual = calculateRhythmValue(hours, 10, 0.9);

    // Metabolic rhythm - peaks around noon
    const metabolic = calculateRhythmValue(hours, 12, 0.75);

    // Calculate the overall score (weighted average)
    const overall = (physical * 0.3) + (emotional * 0.2) + (intellectual * 0.2) + (metabolic * 0.3);

    // Generate hourly data for graph
    const hourlyData = [];
    for (let i = 0; i < 24; i++) {
      hourlyData.push({
        hour: i,
        physical: calculateRhythmValue(i, 11, 0.8),
        emotional: calculateRhythmValue(i, 16, 0.7),
        intellectual: calculateRhythmValue(i, 10, 0.9),
        metabolic: calculateRhythmValue(i, 12, 0.75)
      });
    }

    // Generate recommendations based on biorhythm
    const currentHour = hours;

    let medicationTiming, exerciseTiming, mealTiming, focusTiming, sleepTiming;

    // Medication timing - best when metabolism is active but not peak
    medicationTiming = calculateBestTimes(hourlyData, 'metabolic', 0.6, 0.8);

    // Exercise timing - best during physical peaks
    exerciseTiming = calculateBestTimes(hourlyData, 'physical', 0.7, 1.0);

    // Meal timing - best during metabolic peaks
    mealTiming = calculateBestTimes(hourlyData, 'metabolic', 0.7, 1.0);

    // Focus timing - best during intellectual peaks
    focusTiming = calculateBestTimes(hourlyData, 'intellectual', 0.8, 1.0);

    // Sleep timing - best when physical and emotional are low
    sleepTiming = hourlyData
      .filter(item => item.physical < 0.4 && item.emotional < 0.5)
      .map(item => item.hour);

    setBiorhythmData({
      currentScore: {
        overall: parseFloat((overall * 100).toFixed(1)),
        physical: parseFloat((physical * 100).toFixed(1)),
        emotional: parseFloat((emotional * 100).toFixed(1)),
        intellectual: parseFloat((intellectual * 100).toFixed(1)),
        metabolic: parseFloat((metabolic * 100).toFixed(1))
      },
      hourlyData,
      nextTransition: {
        from: "metabolic",
        to: "emotional",
        hoursFromNow: 3,
        time: "15:00"
      },
      recommendations: {
        medicationTiming,
        exerciseTiming,
        mealTiming,
        focusTiming,
        sleepTiming
      }
    });
  };

  // Calculate rhythm value based on hour of day and peak time
  const calculateRhythmValue = (hour, peakHour, amplitude) => {
    // Adjust for chronotype
    const adjustedHour = (hour + userChronotype + 24) % 24;
    const adjustedPeakHour = (peakHour + userChronotype + 24) % 24;

    // Convert to radians (full cycle over 24 hours)
    const radians = ((adjustedHour - adjustedPeakHour) / 24) * 2 * Math.PI;
    // Cosine function gives oscillation, amplitude controls height, add offset to make positive
    return ((Math.cos(radians) * amplitude) + 1) / 2;
  };

  // Find best time windows based on threshold values
  const calculateBestTimes = (hourlyData, rhythmType, minThreshold, maxThreshold) => {
    return hourlyData
      .filter(item => item[rhythmType] >= minThreshold && item[rhythmType] <= maxThreshold)
      .map(item => item.hour);
  };

  // Format hour for display (e.g., 14 -> "2 PM")
  const formatHour = (hour) => {
    if (hour === 0) return "12 AM";
    if (hour === 12) return "12 PM";
    return hour < 12 ? `${hour} AM` : `${hour - 12} PM`;
  };

  // Format time windows for display (e.g., [8, 9, 10] -> "8 AM - 10 AM")
  const formatTimeWindows = (hours) => {
    if (!hours || hours.length === 0) return "No optimal times found";

    // Sort hours
    hours.sort((a, b) => a - b);

    // Group consecutive hours
    const windows = [];
    let start = hours[0];
    let end = hours[0];

    for (let i = 1; i < hours.length; i++) {
      if (hours[i] === end + 1) {
        end = hours[i];
      } else {
        windows.push({ start, end });
        start = hours[i];
        end = hours[i];
      }
    }
    windows.push({ start, end });

    // Format windows as strings
    return windows.map(window => {
      if (window.start === window.end) {
        return formatHour(window.start);
      } else {
        return `${formatHour(window.start)} - ${formatHour(window.end)}`;
      }
    }).join(", ");
  };

  // Get current biorhythm state based on scores
  const getCurrentState = () => {
    if (!biorhythmData) return null;

    const { physical, emotional, intellectual, metabolic } = biorhythmData.currentScore;

    // Find the highest score
    const scores = [
      { type: 'physical', value: physical },
      { type: 'emotional', value: emotional },
      { type: 'intellectual', value: intellectual },
      { type: 'metabolic', value: metabolic }
    ];

    scores.sort((a, b) => b.value - a.value);
    const highestScore = scores[0];

    // Generate state description
    let state, recommendation, icon;

    switch(highestScore.type) {
      case 'physical':
        state = "Physical Peak";
        recommendation = "Your body's ready to crush a workout! Time to make gym-bros jealous.";
        icon = <Activity size={22} />;
        break;
      case 'emotional':
        state = "Emotional Peak";
        recommendation = "Perfect time for social activities or that heart-to-heart you've been avoiding.";
        icon = <Zap size={22} />;
        break;
      case 'intellectual':
        state = "Intellectual Peak";
        recommendation = "Your brain's firing on all cylinders! Tackle that project you've been procrastinating on.";
        icon = <Coffee size={22} />;
        break;
      case 'metabolic':
        state = "Metabolic Peak";
        recommendation = "Your metabolism is working overtime. Perfect moment for a balanced meal.";
        icon = <Utensils size={22} />;
        break;
      default:
        state = "Balanced State";
        recommendation = "All systems steady. A good time for routine activities.";
        icon = <Activity size={22} />;
    }

    return { state, recommendation, icon, score: highestScore.value };
  };

  // Get sassy recommendation based on current hour
  const getSassyRecommendation = () => {
    if (!biorhythmData) return null;

    const hour = currentTime.getHours();

    if (hour >= 5 && hour < 8) {
      return "Morning person or still awake? Either way, your metabolism is barely online. Coffee first, decisions later.";
    } else if (hour >= 8 && hour < 11) {
      return "Your brain's finally booting up! Perfect time to solve problems before your coworkers ruin your flow.";
    } else if (hour >= 11 && hour < 14) {
      return "Physical energy peaking! Perfect for a workout, unless lunch food coma claims you first.";
    } else if (hour >= 14 && hour < 17) {
      return "Afternoon slump who? Your emotional intelligence is peaking. Time for that difficult conversation you've been avoiding.";
    } else if (hour >= 17 && hour < 20) {
      return "Metabolism slowing down. Maybe don't inhale that entire pizza? Just a biorhythmic suggestion.";
    } else if (hour >= 20 && hour < 23) {
      return "Your brain's winding down. Netflix algorithms make more sense than your decision-making right now.";
    } else {
      return "It's literally the middle of the night. Even your biorhythms are judging your life choices right now.";
    }
  };

  // Get medication-specific timing advice
  const getMedicationAdvice = () => {
    if (!biorhythmData) return null;

    const currentHour = currentTime.getHours();
    const { medicationTiming } = biorhythmData.recommendations;

    if (medicationTiming.includes(currentHour)) {
      return "Now is an optimal time to take medications! Your metabolism is primed for absorption.";
    }

    // Find the next optimal time
    const nextOptimalHour = medicationTiming.find(hour => hour > currentHour);
    if (nextOptimalHour) {
      return `For best absorption, consider taking medications at ${formatHour(nextOptimalHour)}.`;
    } else if (medicationTiming.length > 0) {
      return `Tomorrow's optimal medication time starts at ${formatHour(medicationTiming[0])}.`;
    }

    return "Take medications according to your doctor's instructions, regardless of biorhythm.";
  };

  const currentState = getCurrentState();
  const sassyRecommendation = getSassyRecommendation();
  const medicationAdvice = getMedicationAdvice();

  // Toggle activity reminders
  const toggleActivityReminder = (activity) => {
    setActivityReminders(prev => ({
      ...prev,
      [activity]: !prev[activity]
    }));
  };

  // Toggle expanded state for recommendations
  const toggleExpanded = (id) => {
    setIsExpanded(prev => ({
      ...prev,
      [id]: !prev[id]
    }));
  };

  return (
    <div className="p-4 pb-20 overflow-y-auto bg-blue-50/30">
      <header className="flex justify-between items-center mb-5">
        <div className="flex items-center">
          <button 
            className="mr-2 w-8 h-8 rounded-full flex items-center justify-center hover:bg-blue-50 transition-colors duration-300"
            onClick={() => setActiveScreen('dashboard')}
          >
            <ArrowLeft size={18} style={{ color: themeColors.textPrimary }} />
          </button>
          <div>
            <h1 className="text-2xl font-bold bg-gradient-to-r from-blue-700 to-cyan-500 bg-clip-text text-transparent">Biorhythms</h1>
            <p className="text-xs" style={{ color: themeColors.textSecondary }}>Your body's natural cycles</p>
          </div>
        </div>

        <div className="relative">
          <div className="absolute -inset-1 rounded-full animate-pulse opacity-40" 
               style={{ background: `radial-gradient(circle, ${themeColors.neonAccent}, transparent 70%)` }}></div>
          <button className="relative w-10 h-10 rounded-full bg-white shadow-lg flex items-center justify-center transition-transform active:scale-95 duration-300"
                  style={{ boxShadow: `0 0 15px ${themeColors.primary}30` }}>
            <Bell size={20} color={themeColors.primary} />
          </button>
        </div>
      </header>

      {/* Current time indicator */}
      <div className="flex items-center justify-between mb-4 bg-white p-2 rounded-lg shadow-sm">
        <div className="flex items-center">
          <Clock size={15} className="mr-2" style={{ color: themeColors.primary }} />
          <span className="text-xs font-medium" style={{ color: themeColors.textPrimary }}>
            {currentTime.toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'})}
          </span>
        </div>

        {biorhythmData?.nextTransition && (
          <div className="flex items-center px-2 py-1 rounded-full text-xs animate-pulse" 
               style={{ backgroundColor: `${themeColors.accent}15`, color: themeColors.accent }}>
            <Activity size={12} className="mr-1" />
            {biorhythmData.nextTransition.to} peak in {biorhythmData.nextTransition.hoursFromNow}h
          </div>
        )}
      </div>

      {/* Chronotype selector - Mobile optimized */}
      <div className="mb-4 p-3 rounded-xl bg-white shadow-sm border border-blue-50 transform transition-all duration-300 active:scale-98"
           style={{ boxShadow: `0 5px 10px -3px ${themeColors.primary}20` }}>
        <div className="flex justify-between items-center mb-2">
          <div className="flex items-center">
            <div className="w-8 h-8 rounded-lg mr-2 flex items-center justify-center" 
                 style={{ backgroundColor: `${themeColors.primary}15`, color: themeColors.primary }}>
              <Sun size={16} />
            </div>
            <div>
              <h3 className="text-sm font-bold" style={{ color: themeColors.textPrimary }}>Your Chronotype</h3>
              <p className="text-xs" style={{ color: themeColors.textSecondary }}>
                {userChronotype === 0 ? "Balanced" : 
                userChronotype < 0 ? `Early Bird (${Math.abs(userChronotype)})` : 
                `Night Owl (+${userChronotype})`}
              </p>
            </div>
          </div>
          <button 
            className="flex items-center justify-center h-6 w-6 rounded-full text-xs"
            style={{ backgroundColor: `${themeColors.primary}15`, color: themeColors.primary }}
            onClick={() => setShowTooltip(showTooltip === 'chronotype' ? null : 'chronotype')}
          >
            <Info size={14} />
          </button>
        </div>

        {/* Chronotype tooltip */}
        {showTooltip === 'chronotype' && (
          <div className="p-2 mt-2 rounded-lg bg-blue-50 text-xs border border-blue-100" 
               style={{ color: themeColors.textSecondary }}>
            Your chronotype determines if you're naturally more alert in the morning (early bird) or evening (night owl). 
            Adjusting this helps personalize your biorhythm predictions.
          </div>
        )}

        <div className="pt-2 px-2">
          <div className="flex items-center justify-between mb-1">
            <span className="text-xs" style={{ color: themeColors.textSecondary }}>Early Bird</span>
            <span className="text-xs" style={{ color: themeColors.textSecondary }}>Night Owl</span>
          </div>
          <input 
            type="range" 
            min="-6" 
            max="6" 
            step="1" 
            value={userChronotype} 
            onChange={(e) => setUserChronotype(parseInt(e.target.value))}
            className="w-full h-1.5 rounded-full appearance-none cursor-pointer accent-blue-600 bg-blue-100"
          />
        </div>
      </div>

      {/* Tab navigation */}
      <div className="flex p-1 mb-4 rounded-lg bg-blue-50 shadow-sm">
        {['today', 'week', 'month'].map((tab) => (
          <button
            key={tab}
            onClick={() => setActiveTab(tab)}
            className={`flex-1 py-2 rounded-lg text-xs font-medium transition-all duration-300 ${
              activeTab === tab 
                ? 'bg-white shadow-md' 
                : 'hover:bg-blue-100'
            }`}
            style={{
              color: activeTab === tab ? themeColors.primary : themeColors.textSecondary,
              transform: activeTab === tab ? 'translateY(-1px)' : 'none',
              boxShadow: activeTab === tab ? `0 2px 4px -1px ${themeColors.primary}20` : 'none'
            }}
          >
            {tab.charAt(0).toUpperCase() + tab.slice(1)}
          </button>
        ))}
      </div>

      {isLoading ? (
        // Loading state with shimmer effect
        <div className="space-y-4">
          <div className="h-32 rounded-xl animate-pulse" style={{ backgroundColor: `${themeColors.primary}20` }}></div>
          <div className="h-20 rounded-xl animate-pulse" style={{ backgroundColor: `${themeColors.primary}15` }}></div>
          <div className="h-40 rounded-xl animate-pulse" style={{ backgroundColor: `${themeColors.primary}10` }}></div>
        </div>
      ) : (
        <>
          {/* Current biorhythm state card - Mobile optimized */}
          <div className="mb-4 p-4 rounded-xl relative overflow-hidden transform active:scale-98 transition-all duration-300" 
               style={{ 
                 background: `linear-gradient(135deg, ${themeColors.primary}, ${themeColors.accent})`,
                 boxShadow: `0 10px 15px -5px ${themeColors.primary}30`
               }}>
            {/* Simplified background elements for mobile */}
            <div className="absolute top-0 right-0 w-24 h-24 rounded-full opacity-30" 
                 style={{ 
                   background: `radial-gradient(circle, ${themeColors.primaryLight || '#60A5FA'}, transparent 70%)`,
                   transform: 'translate(30%, -30%)'
                 }}></div>
            <div className="absolute bottom-0 left-0 w-20 h-20 rounded-full opacity-20" 
                 style={{ 
                   background: `radial-gradient(circle, ${themeColors.primaryLight || '#60A5FA'}, transparent 70%)`,
                   transform: 'translate(-30%, 30%)'
                 }}></div>

            <div className="relative flex justify-between items-start">
              <div className="max-w-xs">
                <h2 className="text-xl font-bold text-white mb-2">Current Biorhythm</h2>
                <h3 className="text-2xl font-bold text-white mb-3 flex items-center">
                  {currentState.state}
                  <span className="ml-2 text-base text-white text-opacity-80">({currentState.score}%)</span>
                </h3>
                <p className="text-white text-opacity-90 text-sm mb-3">
                  {currentState.recommendation}
                </p>

                <div className="mt-4 py-2 px-3 rounded-xl bg-white bg-opacity-20 backdrop-blur-sm">
                  <p className="text-white text-opacity-90 text-xs font-medium flex items-center">
                    <Calendar size={14} className="mr-2 flex-shrink-0" />
                    {medicationAdvice}
                  </p>
                </div>
              </div>
              <div className="flex flex-col items-center">
                <div className="w-16 h-16 rounded-full bg-white bg-opacity-20 backdrop-blur-sm flex items-center justify-center mb-2 shadow-lg shimmer transform hover:rotate-12 hover:scale-110 transition-all duration-300" 
                     style={{ boxShadow: `0 0 15px rgba(255, 255, 255, 0.2)` }}>
                  <div className="w-12 h-12 rounded-full bg-white flex items-center justify-center">
                    <div style={{ color: colors.primary }}>{currentState.icon}</div>
                  </div>
                </div>
              </div>
            </div>
            {/* Medication advice - Simplified */}
            <div className="mt-3 py-2 px-3 rounded-lg bg-white bg-opacity-20 backdrop-blur-sm">
              <p className="text-white text-opacity-90 text-xs font-medium flex items-center">
                <Calendar size={12} className="mr-1.5 flex-shrink-0" />
                {medicationAdvice}
              </p>
            </div>

            {/* Simplified biorhythm graph for mobile */}
            <div className="mt-3 pt-2 border-t border-white border-opacity-20">
              <div className="h-8 relative">
                {biorhythmData.hourlyData.map((data, index) => {
                  const overall = (data.physical * 0.3) + (data.emotional * 0.2) + (data.intellectual * 0.2) + (data.metabolic * 0.3);
                  const height = `${overall * 100}%`;
                  const isCurrent = index === currentTime.getHours();

                  return (
                    <div 
                      key={index}
                      className="absolute bottom-0"
                      style={{ 
                        height, 
                        width: '2px',
                        left: `${(index / 24) * 100}%`,
                        backgroundColor: isCurrent ? '#FFFFFF' : 'rgba(255, 255, 255, 0.6)',
                        opacity: isCurrent ? 1 : 0.7,
                        transform: isCurrent ? 'scaleY(1.1)' : 'scaleY(1)',
                        borderRadius: '1px 1px 0 0',
                        zIndex: isCurrent ? 10 : 1
                      }}
                    >
                      {isCurrent && (
                        <div className="absolute -top-4 left-1/2 transform -translate-x-1/2 whitespace-nowrap">
                          <div className="px-1 py-0.5 rounded-sm text-xs bg-white text-blue-600 font-medium">
                            Now
                          </div>
                        </div>
                      )}
                    </div>
                  );
                })}
              </div>
            </div>
          </div>

          {/* Sassy recommendation card with glow effect */}
          <div className="mb-8 p-5 rounded-2xl bg-white shadow-lg border border-blue-50 transform hover:scale-105 transition-all duration-500 relative overflow-hidden"
               style={{ boxShadow: `0 15px 25px -5px ${colors.primary}20` }}>
            <div className="absolute -bottom-8 -right-8 w-32 h-32 rounded-full opacity-10"
                 style={{ 
                   background: `radial-gradient(circle, ${colors.accentAlt}, transparent 70%)`,
                 }}></div>
            <div className="absolute -top-10 -left-10 w-40 h-40 rounded-full opacity-10"
                 style={{ 
                   background: `radial-gradient(circle, ${colors.primary}, transparent 70%)`
                 }}></div>

            <div className="flex items-start relative z-10">
              <div className="w-12 h-12 rounded-xl mr-4 flex items-center justify-center shimmer transform hover:rotate-12 transition-all duration-300" 
                   style={{ 
                     background: `linear-gradient(135deg, ${colors.accentAlt}, ${colors.gradientAlt2})`
                   }}>
                <Zap size={22} color="white" />
              </div>
              <div>
                <h4 className="font-bold text-base mb-2" style={{ color: colors.textPrimary }}>Biorhythm Insight</h4>
                <p className="text-sm" style={{ color: colors.textSecondary }}>
                  {sassyRecommendation}
                </p>
              </div>
            </div>
          </div>

          {/* Detailed rhythm breakdown - Mobile optimized */}
          <div className="mb-6">
            <div className="flex items-center justify-between">
              <h3 className="text-sm font-bold mb-2 flex items-center" style={{ color: themeColors.textPrimary }}>
                <Activity size={15} className="mr-1" style={{ color: themeColors.primary }} />
                Your Rhythm Breakdown
              </h3>
            </div>

            <div className="grid grid-cols-2 gap-2">
              {[
                { 
                  title: 'Physical', 
                  score: biorhythmData.currentScore.physical, 
                  icon: <Activity size={16} />,
                  color: themeColors.success,
                  description: 'Strength & endurance'
                },
                { 
                  title: 'Emotional', 
                  score: biorhythmData.currentScore.emotional, 
                  icon: <Zap size={16} />,
                  color: themeColors.accentAlt,
                  description: 'Mood & stability'
                },
                { 
                  title: 'Mental', 
                  score: biorhythmData.currentScore.intellectual, 
                  icon: <Coffee size={16} />,
                  color: themeColors.primary,
                  description: 'Focus & clarity'
                },
                { 
                  title: 'Metabolic', 
                  score: biorhythmData.currentScore.metabolic, 
                  icon: <Utensils size={16} />,
                  color: themeColors.warning,
                  description: 'Energy & processing'
                }
              ].map((rhythm, index) => (
                <div 
                  key={index}
                  className="p-4 rounded-2xl bg-white shadow-md border transition-all duration-300 hover:shadow-lg transform hover:scale-105 relative overflow-hidden"
                  style={{ 
                    borderLeftColor: rhythm.color, 
                    borderLeftWidth: '4px',
                    boxShadow: `0 10px 15px -3px ${rhythm.color}10` 
                  }}
                >
                  <div className="absolute -top-10 -right-10 w-32 h-32 rounded-full opacity-5"
                       style={{ background: `radial-gradient(circle, ${rhythm.color}, transparent 70%)` }}></div>

                  <div className="flex items-center justify-between mb-2 relative">
                    <div className="flex items-center">
                      <div className="w-9 h-9 rounded-lg flex items-center justify-center mr-3 shimmer" 
                           style={{ backgroundColor: `${rhythm.color}20`, color: rhythm.color }}>
                        {rhythm.icon}
                      </div>
                      <h4 className="font-medium" style={{ color: themeColors.textPrimary }}>{rhythm.title}</h4>
                    </div>
                    <div className="flex items-center">
                      <span className="text-lg font-bold mr-1" style={{ color: rhythm.color }}>{rhythm.score}%</span>
                      <ArrowUpRight size={16} style={{ color: rhythm.color }} />
                    </div>
                  </div>
                  <p className="text-xs relative" style={{ color: themeColors.textSecondary }}>{rhythm.description}</p>

                  {/* Progress bar */}
                  <div className="mt-3 h-1.5 rounded-full bg-gray-100 relative overflow-hidden">
                    <div 
                      className="absolute top-0 left-0 h-full rounded-full shimmer"
                      style={{ 
                        width: `${rhythm.score}%`,
                        background: `linear-gradient(to right, ${rhythm.color}90, ${rhythm.color})` 
                      }}
                    ></div>
                  </div>
                </div>
              ))}
            </div>
          </div>

          {/* Optimal timing recommendations with enhanced cards and animations */}
          <div className="mb-10">
            <h3 className="font-bold text-lg mb-4 flex items-center" style={{ color: themeColors.textPrimary }}>
              <Calendar size={18} className="mr-2" style={{ color: themeColors.primary }} />
              Optimal Timing Recommendations
            </h3>
            <div className="space-y-4">
              {[
                {
                  id: 'medication',
                  title: 'Medication Timing',
                  times: formatTimeWindows(biorhythmData.recommendations.medicationTiming),
                  description: 'Take medications during these windows for optimal absorption and effect',
                  icon: <Calendar size={20} />,
                  gradient: `linear-gradient(135deg, ${themeColors.primary}, ${themeColors.accent})`,
                  reminderEnabled: activityReminders.medication,
                  tip: "Take meds with water, not coffee or alcohol for best absorption",
                  iconColor: themeColors.primary
                },
                {
                  id: 'exercise',
                  title: 'Exercise Windows',
                  times: formatTimeWindows(biorhythmData.recommendations.exerciseTiming),
                  description: 'Work out during these hours for maximum performance and results',
                  icon: <Activity size={20} />,
                  gradient: `linear-gradient(135deg, ${themeColors.success}, ${themeColors.accent})`,
                  reminderEnabled: activityReminders.exercise,
                  tip: "Hydrate before and after workouts; your future self will thank you",
                  iconColor: themeColors.success
                },
                {
                  id: 'meal',
                  title: 'Optimal Meal Times',
                  times: formatTimeWindows(biorhythmData.recommendations.mealTiming),
                  description: 'Eat during these periods when your metabolism is most efficient',
                  icon: <Utensils size={20} />,
                  gradient: `linear-gradient(135deg, ${themeColors.warning}, ${themeColors.success})`,
                  reminderEnabled: activityReminders.meal,
                  tip: "Smaller portions more often boost metabolism better than large meals",
                  iconColor: themeColors.warning
                },
                {
                  id: 'focus',
                  title: 'Focus Sessions',
                  times: formatTimeWindows(biorhythmData.recommendations.focusTiming),
                  description: 'Schedule important work/study during these cognitive peak hours',
                  icon: <Coffee size={20} />,
                  gradient: `linear-gradient(135deg, ${themeColors.accentAlt}, ${themeColors.primary})`,
                  reminderEnabled: activityReminders.focus,
                  tip: "Silence notifications during focus time or you'll hate yourself later",
                  iconColor: themeColors.accentAlt
                },
                {
                  id: 'sleep',
                  title: 'Ideal Sleep Window',
                  times: formatTimeWindows(biorhythmData.recommendations.sleepTiming),
                  description: 'Your body is naturally primed for rest during these hours',
                  icon: <Moon size={20} />,
                  gradient: `linear-gradient(135deg, ${themeColors.primary}, ${themeColors.accentAlt})`,
                  reminderEnabled: activityReminders.sleep,
                  tip: "Use a sleep mask to block out light. Your melatonin production will love you",
                  iconColor: themeColors.primary
                }
              ].map((recommendation, index) => (
                <div key={index} className="relative">
                  <div 
                    className={`p-5 rounded-2xl bg-white shadow-lg border border-blue-50 transition-all duration-300 hover:shadow-xl cursor-pointer ${isExpanded[recommendation.id] ? 'pb-16 scale-103' : 'hover:scale-102'}`}
                    style={{ boxShadow: `0 10px 15px -3px ${recommendation.iconColor}20` }}
                    onClick={() => toggleExpanded(recommendation.id)}
                  >
                    <div className="flex items-start justify-between">
                      <div className="flex items-start flex-1">
                        <div className="w-12 h-12 rounded-xl mr-4 flex items-center justify-center shimmer transform hover:rotate-12 transition-all duration-300" 
                             style={{ background: recommendation.gradient }}>
                          <div className="text-white">{recommendation.icon}</div>
                        </div>
                        <div className="flex-1">
                          <div className="flex items-center justify-between">
                            <h4 className="font-medium" style={{ color: themeColors.textPrimary }}>{recommendation.title}</h4>
                            {isExpanded[recommendation.id] ? (
                              <ChevronUp size={16} style={{ color: themeColors.textSecondary }} />
                            ) : (
                              <ChevronDown size={16} style={{ color: themeColors.textSecondary }} />
                            )}
                          </div>
                          <p className="text-xs mb-2" style={{ color: themeColors.textSecondary }}>{recommendation.description}</p>

                          <div className="bg-gray-50 py-1.5 px-3 rounded-xl inline-block" 
                               style={{ 
                                 background: `${recommendation.iconColor}10`,
                                 color: recommendation.iconColor
                               }}>
                            <span className="text-sm font-medium flex items-center">
                              <Clock size={14} className="mr-1.5" />
                              {recommendation.times}
                            </span>
                          </div>
                        </div>
                      </div>

                      <button 
                        className={`w-8 h-8 ml-3 rounded-full flex items-center justify-center transition-transform hover:scale-110 duration-300 flex-shrink-0 ${recommendation.reminderEnabled ? 'shadow-md' : ''}`}
                        onClick={(e) => {
                          e.stopPropagation();
                          toggleActivityReminder(recommendation.id);
                        }}
                        style={{ 
                          background: recommendation.reminderEnabled ? recommendation.iconColor : '#E5E7EB',
                          color: recommendation.reminderEnabled ? 'white' : '#9CA3AF',
                          boxShadow: recommendation.reminderEnabled ? `0 4px 6px -1px ${recommendation.iconColor}30` : 'none'
                        }}
                      >
                        <Bell size={16} />
                      </button>
                    </div>

                    {isExpanded[recommendation.id] && (
                      <div className="absolute bottom-0 left-0 right-0 p-3 bg-gray-50 rounded-b-2xl" 
                           style={{ 
                             backgroundColor: `${recommendation.iconColor}10`,
                             borderTop: `1px solid ${recommendation.iconColor}20`
                           }}>
                        <p className="text-sm text-center font-medium flex items-center justify-center" 
                           style={{ color: recommendation.iconColor }}>
                          <HelpCircle size={14} className="mr-1.5" />
                          {recommendation.tip}
                        </p>
                      </div>
                    )}
                  </div>
                </div>
              ))}
            </div>
          </div>

          {/* Add to calendar button */}
          <div className="mb-8 flex justify-center">
            <button className="px-6 py-3 rounded-xl bg-white shadow-lg border border-blue-100 flex items-center justify-center transition-all duration-300 hover:shadow-xl hover:scale-105 group"
                    style={{ boxShadow: `0 10px 15px -3px ${themeColors.primary}20` }}>
              <div className="w-8 h-8 rounded-full mr-2 flex items-center justify-center" 
                   style={{ 
                     background: `linear-gradient(135deg, ${themeColors.primary}, ${themeColors.accent})`,
                     transform: 'translateZ(0)'
                   }}>
                <PlusCircle size={16} color="white" />
              </div>
              <span className="font-medium transform group-hover:translate-x-1 transition-transform duration-300" 
                    style={{ color: themeColors.textPrimary }}>
                Sync with Calendar
              </span>
            </button>
          </div>

          {/* Sassy footer note with enhanced styling */}
          <div className="mb-6 p-4 rounded-xl bg-blue-50 text-center relative overflow-hidden">
            <div className="absolute -top-8 -right-8 w-16 h-16 rounded-full opacity-20"
                 style={{ background: `radial-gradient(circle, ${themeColors.accent}, transparent 70%)` }}></div>
            <div className="absolute -bottom-8 -left-8 w-16 h-16 rounded-full opacity-20"
                 style={{ background: `radial-gradient(circle, ${themeColors.primary}, transparent 70%)` }}></div>

            <p className="text-xs italic relative" style={{ color: themeColors.textSecondary }}>
              Note: Biorhythm science is about as reliable as a weather forecast. 
              But it's more fun than just saying "good luck with your day!"
            </p>
          </div>
        </>
      )}

      {/* Custom CSS for animations */}
      <style jsx>{`
        @keyframes float {
          0%, 100% { transform: translateY(0); }
          50% { transform: translateY(-10px); }
        }
        @keyframes pulse {
          0%, 100% { opacity: 1; }
          50% { opacity: 0.6; }
        }
        @keyframes breathe {
          0%, 100% { transform: scale(1); }
          50% { transform: scale(1.05); }
        }
        @keyframes shimmer {
          0% { background-position: -100% 0; }
          100% { background-position: 200% 0; }
        }
        .animate-float {
          animation: float 3s ease-in-out infinite;
        }
        .animate-breathe {
          animation: breathe 4s ease-in-out infinite;
        }
        .shimmer {
          background: linear-gradient(90deg, 
            rgba(255,255,255,0) 0%, 
            rgba(255,255,255,0.2) 50%, 
            rgba(255,255,255,0) 100%);
          background-size: 200% 100%;
          animation: shimmer 2s infinite;
        }
      `}</style>
    </div>
  );
};

export default BiorhythmScreen;
