import React, { useState, useEffect } from 'react';
import { Clock, ArrowRight, Activity, Zap, Coffee, Utensils } from 'lucide-react';

// Import biorhythm engine
import { biorhythmEngine, getSassyRecommendation } from '../utils/BiorhythmEngine';

// Enhanced Biorhythm preview component for Dashboard with better animations and visual appeal
const BiorhythmPreview = ({ colors, setActiveScreen }) => {
  // State for biorhythm data
  const [bioData, setBioData] = useState(null);
  const [isLoading, setIsLoading] = useState(true);

  // Get current hour
  const currentHour = new Date().getHours();

  // Load biorhythm data on component mount
  useEffect(() => {
    // Load data with a slight delay to create smoother transition
    const timer = setTimeout(() => {
      const data = biorhythmEngine.getRecommendations();
      setBioData(data);
      setIsLoading(false);
    }, 200);

    return () => clearTimeout(timer);
  }, []);

  // Get relevant biorhythm data from the engine
  const getBioScores = () => {
    if (!bioData) return {
      physical: 60,
      emotional: 70,
      intellectual: 50,
      metabolic: 65
    };

    return {
      physical: bioData.currentScore.physical,
      emotional: bioData.currentScore.emotional,
      intellectual: bioData.currentScore.intellectual,
      metabolic: bioData.currentScore.metabolic
    };
  };

  // Get the dominant rhythm with icon and name
  const getDominantRhythm = () => {
    if (!bioData) return { name: 'Physical', icon: <Activity size={16} color={colors.success} /> };

    const { type } = bioData.dominantRhythm || { type: 'physical' };

    switch(type) {
      case 'physical':
        return { 
          name: 'Physical', 
          icon: <Activity size={16} color={colors.success} />,
          color: colors.success
        };
      case 'emotional':
        return { 
          name: 'Emotional', 
          icon: <Zap size={16} color={colors.accentAlt} />,
          color: colors.accentAlt
        };
      case 'intellectual':
        return { 
          name: 'Mental', 
          icon: <Coffee size={16} color={colors.primary} />,
          color: colors.primary
        };
      case 'metabolic':
        return { 
          name: 'Metabolic', 
          icon: <Utensils size={16} color={colors.warning} />,
          color: colors.warning
        };
      default:
        return { 
          name: 'Physical', 
          icon: <Activity size={16} color={colors.success} />,
          color: colors.success
        };
    }
  };

  // Get optimal activity recommendation
  const getOptimalActivity = () => {
    if (!bioData) return "Loading your personalized recommendations...";

    const { type } = bioData.dominantRhythm || { type: 'physical' };

    switch(type) {
      case 'physical':
        return "Perfect time for a workout or physical activity";
      case 'emotional':
        return "Great moment for social interaction or creative expression";
      case 'intellectual':
        return "Ideal for focused work, studying, or problem-solving";
      case 'metabolic':
        return "Optimal time for a balanced meal - your metabolism is peaking";
      default:
        return "Balanced state - good for routine activities";
    }
  };

  // Get a sassy comment about current biorhythm
  const sassyComment = getSassyRecommendation();
  const bioScores = getBioScores();
  const dominantRhythm = getDominantRhythm();

  return (
    <div className="mb-8 p-5 rounded-2xl bg-white shadow-lg border border-blue-50 hover:shadow-xl transition-all duration-500 transform hover:scale-102 overflow-hidden"
         style={{ boxShadow: `0 15px 25px -5px ${colors.primary}20` }}>
      <div className="flex items-center justify-between mb-3">
        <h3 className="font-bold text-lg" style={{ color: colors.textPrimary }}>Biorhythm Now</h3>
        <button 
          onClick={() => setActiveScreen('biorhythm')}
          className="text-sm font-medium px-3 py-1 rounded-full transition-all duration-300 hover:shadow-md flex items-center group" 
          style={{ color: colors.primary, backgroundColor: `${colors.primary}10` }}>
          View Details
          <ArrowRight size={16} className="ml-1 group-hover:translate-x-1 transition-transform duration-300" />
        </button>
      </div>

      <div className="flex items-center mb-4">
        <Clock size={16} className="mr-2" style={{ color: colors.textSecondary }} />
        <span className="text-xs" style={{ color: colors.textSecondary }}>
          Current Time: {new Date().toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'})}
        </span>
      </div>

      {isLoading ? (
        // Skeleton loading state
        <div className="animate-pulse space-y-3">
          <div className="grid grid-cols-4 gap-2 mb-4">
            {[...Array(4)].map((_, i) => (
              <div key={i} className="flex flex-col items-center">
                <div className="w-12 h-12 rounded-full bg-blue-100 mb-1"></div>
                <div className="w-10 h-2 bg-blue-100 rounded"></div>
              </div>
            ))}
          </div>
          <div className="h-12 bg-blue-100 rounded-xl mb-3"></div>
          <div className="h-4 bg-blue-100 rounded w-3/4"></div>
        </div>
      ) : (
        <>
          <div className="grid grid-cols-4 gap-2 mb-4">
            {[
              { name: 'Physical', score: bioScores.physical, color: colors.success },
              { name: 'Emotional', score: bioScores.emotional, color: colors.accentAlt },
              { name: 'Mental', score: bioScores.intellectual, color: colors.primary },
              { name: 'Metabolic', score: bioScores.metabolic, color: colors.warning }
            ].map((rhythm, index) => (
              <div key={index} className="flex flex-col items-center">
                <div className="w-12 h-12 rounded-full flex items-center justify-center mb-1 shadow-sm transform hover:scale-110 transition-transform duration-300"
                    style={{ 
                      background: `conic-gradient(${rhythm.color} ${rhythm.score}%, #f1f5f9 0)`,
                      boxShadow: `0 0 10px ${rhythm.color}20`
                    }}>
                  <div className="w-8 h-8 rounded-full bg-white flex items-center justify-center">
                    <span className="text-xs font-bold" style={{ color: rhythm.color }}>{rhythm.score}%</span>
                  </div>
                </div>
                <span className="text-xs text-center" style={{ color: colors.textSecondary }}>{rhythm.name}</span>
              </div>
            ))}
          </div>

          <div className="flex justify-between items-center mb-3">
            <div className="flex items-center">
              <div className="w-5 h-5 rounded-full flex items-center justify-center mr-2" 
                  style={{ backgroundColor: `${dominantRhythm.color}20` }}>
                {dominantRhythm.icon}
              </div>
              <span className="text-xs font-medium" style={{ color: dominantRhythm.color }}>
                {dominantRhythm.name} Peak
              </span>
            </div>
            <div className="px-2 py-0.5 text-xs rounded-full" 
                style={{ backgroundColor: `${colors.primary}10`, color: colors.primary }}>
              Next change in 3h
            </div>
          </div>

          <div className="p-3 rounded-xl animate-breathe mb-3" 
              style={{ 
                background: `linear-gradient(to right, ${colors.primary}10, ${colors.accent}10)`
              }}>
            <p className="text-sm font-medium" style={{ color: colors.primary }}>
              {getOptimalActivity()}
            </p>
          </div>

          <p className="text-xs italic" style={{ color: colors.textSecondary }}>{sassyComment}</p>
        </>
      )}

      <style jsx>{`
        @keyframes breathe {
          0%, 100% { transform: scale(1); }
          50% { transform: scale(1.01); }
        }
        .animate-breathe {
          animation: breathe 4s ease-in-out infinite;
        }
      `}</style>
    </div>
  );
};

export default BiorhythmPreview;
