import React, { useState, useEffect } from 'react';
import { 
  Activity, Calendar, MessageCircle, Heart, AlertTriangle, 
  Clock, Video, ChevronRight, CheckCircle, Trophy, ArrowUpRight, Zap, Bell
} from 'lucide-react';

// Enhanced Dashboard Screen with improved biorhythm integration and animations
const MobileDashboard = () => {
  // Mock colors object to make the component work
  const colors = {
    primary: '#1E40AF',
    primaryDark: '#1E3A8A',
    primaryLight: '#60A5FA',
    primaryGlow: '#93C5FD',
    accent: '#0891B2',
    accentAlt: '#7C3AED',
    success: '#059669',
    warning: '#D97706',
    danger: '#DC2626',
    background: '#EFF6FF',
    textPrimary: '#1E293B',
    textSecondary: '#64748B',
    gradientAlt2: '#EC4899',
    neonAccent: '#06B6D4'
  };

  // State for tracking if biorhythm data is loaded
  const [biorhythmLoaded, setBiorhythmLoaded] = useState(false);

  // Load biorhythm data when component mounts
  useEffect(() => {
    // Simulate loading biorhythm data
    setTimeout(() => {
      setBiorhythmLoaded(true);
    }, 500);
  }, []);

  return (
    <div className="p-4 max-w-md mx-auto bg-gray-50 min-h-screen">
      <header className="flex justify-between items-center mb-6">
        <div>
          <h1 className="text-2xl sm:text-3xl font-bold bg-gradient-to-r from-blue-700 to-cyan-500 bg-clip-text text-transparent">Hi, Sarah!</h1>
          <p className="text-xs sm:text-sm" style={{ color: colors.textSecondary }}>Thursday, March 22</p>
        </div>
        <div className="relative">
          <div className="absolute -inset-1 rounded-full animate-pulse" 
               style={{ background: `radial-gradient(circle, ${colors.neonAccent}50, transparent 70%)` }}></div>
          <button className="relative w-10 h-10 sm:w-12 sm:h-12 rounded-full bg-white shadow-lg flex items-center justify-center transition-transform hover:scale-110 duration-300"
                  style={{ boxShadow: `0 0 20px ${colors.primary}30` }}>
            <Bell size={20} color={colors.primary} className="transition-transform hover:scale-110 duration-300" />
            <span className="absolute -top-1 -right-1 w-4 h-4 sm:w-5 sm:h-5 bg-red-500 rounded-full flex items-center justify-center text-white text-xs font-bold animate-pulse">3</span>
          </button>
        </div>
      </header>

      {/* Health Score Card - Enhanced with 3D hover effect */}
      <div className="mb-6 p-4 sm:p-6 rounded-2xl relative overflow-hidden transition-all duration-500 hover:scale-105 hover:shadow-2xl" 
           style={{ 
             background: `linear-gradient(135deg, ${colors.primary}, ${colors.accent})`,
             boxShadow: `0 20px 25px -5px ${colors.primary}50`
           }}>
        <div className="absolute top-0 right-0 w-24 h-24 sm:w-32 sm:h-32 rounded-full animate-float" 
             style={{ 
               background: `radial-gradient(circle, ${colors.primaryLight}50, transparent 70%)`,
               transform: 'translate(30%, -30%)'
             }}></div>
        <div className="absolute bottom-0 left-0 w-20 h-20 sm:w-24 sm:h-24 rounded-full animate-float" 
             style={{ 
               background: `radial-gradient(circle, ${colors.primaryLight}30, transparent 70%)`,
               transform: 'translate(-30%, 30%)',
               animationDelay: '1s'
             }}></div>
        <div className="relative flex items-center justify-between">
          <div>
            <p className="text-white text-opacity-90 text-xs sm:text-sm font-medium mb-1 sm:mb-2">Today's Health Score</p>
            <h2 className="text-2xl sm:text-4xl font-bold text-white flex items-baseline">
              87<span className="text-lg sm:text-xl ml-1">/100</span>
              <span className="ml-2 sm:ml-3 flex items-center text-xs sm:text-sm font-medium bg-white bg-opacity-20 py-1 px-2 rounded-full backdrop-blur-sm">
                <ArrowUpRight size={12} className="mr-1" /> 5%
              </span>
            </h2>
            <p className="text-white text-opacity-80 text-xs mt-1">You're doing better than yesterday!</p>
          </div>
          <div className="w-16 h-16 sm:w-20 sm:h-20 rounded-full bg-white bg-opacity-20 backdrop-blur-sm flex items-center justify-center p-1 transition-transform hover:scale-110 duration-300">
            <div className="w-full h-full rounded-full bg-white flex items-center justify-center shimmer">
              <Activity size={24} color={colors.primary} />
            </div>
          </div>
        </div>
      </div>

      {/* Biorhythm Preview Card */}
      <div className={`mb-6 transition-opacity duration-500 bg-white rounded-2xl shadow-md p-3 ${biorhythmLoaded ? 'opacity-100' : 'opacity-0'}`}>
        <div className="flex justify-between items-center mb-1">
          <h3 className="text-gray-800 font-bold text-base">Biorhythm Now</h3>
          <div className="flex items-center">
            <button 
              className="text-blue-600 text-xs sm:text-sm flex items-center"
            >
              Details
              <ChevronRight size={16} className="ml-1" />
            </button>
          </div>
        </div>

        <div className="flex items-center text-gray-400 text-xs mb-2">
          <Clock size={12} className="mr-1" />
          <span>10:41 AM</span>
        </div>

        {/* Simple biorhythm graph */}
        <div className="h-12 sm:h-16 relative mb-2 bg-blue-50 rounded-lg overflow-hidden">
          {Array(24).fill().map((_, i) => {
            const height = 20 + Math.sin(i/3) * 50;
            const isCurrent = i === 10;

            return (
              <div 
                key={i} 
                className={`absolute bottom-0 rounded-t-sm transition-all duration-300 ${isCurrent ? 'bg-blue-500' : 'bg-blue-200'}`}
                style={{ 
                  height: `${height}%`,
                  width: '3px',
                  left: `${(i / 24) * 100}%`,
                  opacity: isCurrent ? 1 : 0.7,
                  transform: isCurrent ? 'scaleY(1.1)' : 'scaleY(1)'
                }}
              >
                {isCurrent && (
                  <div className="absolute -top-6 left-1/2 transform -translate-x-1/2">
                    <div className="px-2 py-0.5 rounded-full text-xs bg-blue-500 text-white font-medium">
                      Now
                    </div>
                  </div>
                )}
              </div>
            );
          })}
        </div>

        {/* Current state card */}
        <div className="rounded-xl p-3 relative overflow-hidden bg-purple-50 border-l-4 border-purple-500">
          <div className="flex items-center">
            <div className="w-12 h-12 rounded-full flex items-center justify-center mr-3 shimmer bg-purple-500">
              <Zap size={18} color="white" />
            </div>

            <div>
              <div className="flex items-center">
                <h4 className="text-base font-bold mr-2 text-purple-500">
                  Emotional Peak
                </h4>
                <div className="text-xs font-bold text-purple-500">
                  82%
                </div>
              </div>

              <p className="text-xs text-gray-600 mt-1">
                Perfect time for social activities or that heart-to-heart
              </p>
            </div>
          </div>
        </div>
      </div>

      {/* Progress rings */}
      <div className="mb-6 grid grid-cols-3 gap-3">
        {['Activity', 'Sleep', 'Nutrition'].map((label, index) => {
          const value = index === 0 ? 92 : index === 1 ? 78 : 85;
          const color = index === 0 ? colors.accent : index === 1 ? colors.accentAlt : colors.success;
          const Icon = index === 0 ? Activity : index === 1 ? Clock : Trophy;

          return (
            <div key={label} className="flex flex-col items-center transform hover:scale-110 transition-all duration-500">
              <div className="relative mb-1">
                <svg className="transform -rotate-90 w-14 h-14 sm:w-16 sm:h-16">
                  <circle
                    cx="28"
                    cy="28"
                    r="14"
                    stroke="#E5E7EB"
                    strokeWidth="3"
                    fill="transparent"
                  />
                  <circle
                    cx="28"
                    cy="28"
                    r="14"
                    stroke={color}
                    strokeWidth="3"
                    fill="transparent"
                    strokeDasharray={88}
                    strokeDashoffset={88 - (value / 100) * 88}
                    strokeLinecap="round"
                  />
                </svg>
                <div className="absolute inset-0 flex items-center justify-center">
                  <div className="w-6 h-6 sm:w-8 sm:h-8 rounded-full bg-white shadow-sm flex items-center justify-center" 
                       style={{ 
                         color,
                         boxShadow: `0 0 10px ${color}30`
                       }}>
                    <Icon size={14} />
                  </div>
                </div>
              </div>
              <span className="text-xs font-medium" style={{ color }}>{value}%</span>
              <span className="text-xs" style={{ color: colors.textSecondary }}>{label}</span>
            </div>
          );
        })}
      </div>

      {/* Upcoming events */}
      <div className="mb-6">
        <div className="flex justify-between items-center mb-3">
          <h3 className="font-bold text-base" style={{ color: colors.textPrimary }}>Upcoming</h3>
          <button className="text-xs font-medium px-2 py-1 rounded-full transition-all duration-300 hover:shadow-md" 
                  style={{ color: colors.primary, backgroundColor: `${colors.primary}10` }}>
            View all
          </button>
        </div>

        <div className="space-y-3">
          <div className="p-3 rounded-2xl bg-white shadow-md border border-blue-50 flex items-center transform hover:scale-102 transition-all duration-500 hover:shadow-lg active:scale-98"
               style={{ boxShadow: `0 8px 15px -3px ${colors.primary}20` }}>
            <div className="w-10 h-10 rounded-xl mr-3 flex items-center justify-center shimmer" 
                 style={{ background: `linear-gradient(135deg, ${colors.primary}90, ${colors.primary})` }}>
              <Calendar size={18} color="white" />
            </div>
            <div className="flex-1">
              <h4 className="font-medium text-sm" style={{ color: colors.textPrimary }}>Metformin 500mg</h4>
              <p className="text-xs" style={{ color: colors.textSecondary }}>In 30 minutes</p>
            </div>
            <div className="w-8 h-8 rounded-full flex items-center justify-center shadow-sm transition-transform hover:scale-110 duration-300" 
                 style={{ backgroundColor: `${colors.success}20`, color: colors.success }}>
              <CheckCircle size={16} />
            </div>
          </div>

          <div className="p-3 rounded-2xl bg-white shadow-md border border-blue-50 flex items-center transform hover:scale-102 transition-all duration-500 hover:shadow-lg active:scale-98"
               style={{ boxShadow: `0 8px 15px -3px ${colors.primary}20` }}>
            <div className="w-10 h-10 rounded-xl mr-3 flex items-center justify-center shimmer" 
                 style={{ background: `linear-gradient(135deg, ${colors.accent}90, ${colors.accent})` }}>
              <Video size={18} color="white" />
            </div>
            <div className="flex-1">
              <h4 className="font-medium text-sm" style={{ color: colors.textPrimary }}>Dr. Johnson Appointment</h4>
              <p className="text-xs" style={{ color: colors.textSecondary }}>Today, 3:00 PM</p>
            </div>
            <button className="w-8 h-8 rounded-full flex items-center justify-center shadow-sm bg-blue-50 transition-transform hover:scale-110 duration-300 hover:bg-blue-100 active:scale-95">
              <ChevronRight size={16} color={colors.primary} />
            </button>
          </div>
        </div>
      </div>

      {/* Quick actions */}
      <div className="mb-6">
        <h3 className="font-bold text-base mb-3" style={{ color: colors.textPrimary }}>Quick Actions</h3>
        <div className="grid grid-cols-3 gap-3">
          {[
            { label: 'Symptoms', icon: <Heart size={18} />, gradient: `linear-gradient(135deg, ${colors.danger}, ${colors.gradientAlt2})` },
            { label: 'Meds', icon: <Calendar size={18} />, gradient: `linear-gradient(135deg, ${colors.primary}, ${colors.accent})` },
            { label: 'Biorhythm', icon: <Clock size={18} />, gradient: `linear-gradient(135deg, ${colors.accentAlt}, ${colors.accent})` },
            { label: 'Health', icon: <Zap size={18} />, gradient: `linear-gradient(135deg, ${colors.success}, ${colors.accent})` },
            { label: 'Emergency', icon: <AlertTriangle size={18} />, gradient: `linear-gradient(135deg, ${colors.danger}, ${colors.warning})` },
            { label: 'Connect', icon: <MessageCircle size={18} />, gradient: `linear-gradient(135deg, ${colors.primary}, ${colors.success})` }
          ].map((action, index) => (
            <div key={action.label} className="flex flex-col items-center">
              <button className="w-14 h-14 rounded-2xl bg-white shadow-md flex items-center justify-center mb-1 transform hover:scale-110 transition-all duration-500 hover:shadow-lg active:scale-95"
                style={{ boxShadow: `0 10px 15px -3px rgba(0,0,0,0.1)` }}>
                <div className="w-10 h-10 rounded-xl flex items-center justify-center shimmer" 
                    style={{ 
                      background: action.gradient,
                      transition: 'transform 0.3s ease-in-out, box-shadow 0.3s ease-in-out',
                      boxShadow: '0 8px 15px -3px rgba(0,0,0,0.2)'
                    }}>
                  <div style={{ color: 'white', transition: 'transform 0.3s ease-in-out' }}>{action.icon}</div>
                </div>
              </button>
              <span className="text-xs font-medium text-center">{action.label}</span>
            </div>
          ))}
        </div>
      </div>

      {/* Health insight */}
      <div>
        <div className="flex justify-between items-center mb-3">
          <h3 className="font-bold text-base" style={{ color: colors.textPrimary }}>Health Insights</h3>
          <button className="text-xs font-medium px-2 py-1 rounded-full transition-all duration-300 hover:shadow-md" 
                  style={{ color: colors.primary, backgroundColor: `${colors.primary}10` }}>
            View all
          </button>
        </div>

        <div className="p-4 rounded-2xl bg-white shadow-md border border-blue-50 transform hover:scale-102 transition-all duration-500 hover:shadow-lg active:scale-98"
             style={{ boxShadow: `0 15px 25px -5px ${colors.primary}20` }}>
          <div className="flex items-start">
            <div className="w-10 h-10 rounded-xl mr-3 flex items-center justify-center shadow-sm shimmer" 
                 style={{ background: `linear-gradient(135deg, ${colors.warning}, ${colors.gradientAlt2})` }}>
              <Zap size={18} color="white" />
            </div>
            <div>
              <h4 className="font-bold text-sm mb-1" style={{ color: colors.textPrimary }}>Biorhythm Insight</h4>
              <p className="text-xs" style={{ color: colors.textSecondary }}>
                Your energy levels peak at 2pm today. Schedule your workout then for maximum results.
              </p>
              <button className="mt-2 text-xs font-medium px-3 py-1.5 rounded-lg transition-all duration-300 hover:shadow-md transform hover:-translate-y-1 active:translate-y-0" 
                      style={{ 
                        background: `linear-gradient(to right, ${colors.primary}, ${colors.accent})`,
                        color: 'white',
                        boxShadow: `0 4px 6px -1px ${colors.primary}30`
                      }}>
                Plan my day
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Custom CSS for enhanced animations */}
      <style jsx>{`
        @keyframes float {
          0%, 100% { transform: translateY(0); }
          50% { transform: translateY(-7px); }
        }
        @keyframes pulse {
