import React, { useState, useEffect } from 'react';
import { 
  PlusCircle, Lightbulb, ChevronRight, Activity, Brain, 
  TrendingUp, BarChart2, Clock, Calendar, Heart, RefreshCw 
} from "lucide-react"; 
import SymptomCard from "../shared/SymptomCard"; 
import AddSymptomForm from "../shared/AddSymptomForm";
import GoogleFitIntegration from "../GoogleFitIntegration";
import HealthMetricsPanel from "../HealthMetricsPanel";
import SymptomAIInsights from "../SymptomAIInsights";
import { useTheme } from '../ThemeContext';
import { useHealthData } from '../HealthDataContext';
import { useApiMiddleware } from '../ApiMiddleware';

const SymptomsScreen = ({ colors }) => {
    const { isDarkMode } = useTheme();
    const { isLoading } = useApiMiddleware();
    const { 
      isGoogleFitConnected, 
      symptoms, 
      addSymptom, 
      healthMetrics, 
      aiInsights,
      loading: healthDataLoading,
      fetchHealthData,
      prediction
    } = useHealthData();

    // View state (main, add, insights)
    const [view, setView] = useState('main');
    const [showSuccessMessage, setShowSuccessMessage] = useState(false);
    const [isRefreshing, setIsRefreshing] = useState(false);
    const [lastUpdated, setLastUpdated] = useState(null);

    // Fetch latest health data on component mount
    useEffect(() => {
      if (isGoogleFitConnected) {
        refreshHealthData();
      }
    }, [isGoogleFitConnected]);

    // Refresh health data function
    const refreshHealthData = async () => {
      if (!isGoogleFitConnected) return;

      try {
        setIsRefreshing(true);
        await fetchHealthData({ days: 7 }); // Get most recent week of data
        setLastUpdated(new Date());
      } catch (error) {
        console.error("Failed to refresh health data:", error);
      } finally {
        setIsRefreshing(false);
      }
    };

    // Format the display text for symptom date and time
    const formatTimeDisplay = (symptom) => {
      return `${symptom.date}, ${symptom.time}`;
    };

    // Format last updated time
    const formatLastUpdated = () => {
      if (!lastUpdated) return "Never";

      const now = new Date();
      const diff = Math.floor((now - lastUpdated) / 1000); // seconds

      if (diff < 60) return "Just now";
      if (diff < 3600) return `${Math.floor(diff / 60)} minutes ago`;
      if (diff < 86400) return `${Math.floor(diff / 3600)} hours ago`;
      return lastUpdated.toLocaleDateString();
    };

    // Dark mode specific styles
    const cardBg = isDarkMode ? colors.cardBg : 'white';
    const borderColor = isDarkMode ? `${colors.primary}30` : 'rgba(219, 234, 254, 1)';
    const gradientOverlayBg = isDarkMode 
      ? `radial-gradient(circle, ${colors.gradientAlt1}20, transparent 70%)`
      : `radial-gradient(circle, ${colors.primaryLight}30, transparent 70%)`;

    const insightBg = isDarkMode 
      ? colors.cardBg 
      : 'white';

    // Handle adding a new symptom
    const handleAddSymptom = async (symptomData) => {
      try {
        // Add health metrics from Google Fit if available
        const enhancedSymptomData = {
          ...symptomData,
          healthContext: isGoogleFitConnected ? {
            bmi: healthMetrics.BMI,
            bloodPressure: {
              systolic: healthMetrics.pressure.systolic,
              diastolic: healthMetrics.pressure.diastolic
            },
            glucose: healthMetrics.glucose,
            sleep: healthMetrics.sleep
          } : null
        };

        // Add the symptom through the health data context
        await addSymptom(enhancedSymptomData);

        // Show success message briefly
        setShowSuccessMessage(true);
        setTimeout(() => setShowSuccessMessage(false), 3000);

        // Return to main view
        setView('main');
      } catch (error) {
        console.error('Failed to add symptom:', error);
        // Error is handled by ApiMiddleware
      }
    };

    // Determine frequently occurring symptoms
    const getFrequentSymptoms = () => {
      if (!symptoms || symptoms.length === 0) {
        return [
          { name: "Headaches", count: 8 },
          { name: "Fatigue", count: 5 }
        ];
      }

      // Count occurrences of each symptom
      const symptomCounts = {};
      symptoms.forEach(s => {
        const name = s.symptom;
        symptomCounts[name] = (symptomCounts[name] || 0) + 1;
      });

      // Convert to array and sort by count
      return Object.entries(symptomCounts)
        .map(([name, count]) => ({ name, count }))
        .sort((a, b) => b.count - a.count)
        .slice(0, 3);
    };

    // Render the appropriate view based on state
    if (view === 'add') {
      return <AddSymptomForm 
        setView={setView} 
        addSymptom={handleAddSymptom} 
        colors={colors}
        prefillData={isGoogleFitConnected ? healthMetrics : null}
      />;
    }

    if (view === 'insights') {
      return (
        <div className="p-6 pb-8">
          <header className="flex items-center mb-8">
            <button 
              className="w-10 h-10 rounded-full flex items-center justify-center mr-4 hover:bg-opacity-10 transition-colors duration-300 dark-mode-transition"
              onClick={() => setView('main')}
              style={{ 
                backgroundColor: isDarkMode ? `${colors.primary}10` : 'transparent',
                '&:hover': { backgroundColor: isDarkMode ? `${colors.primary}20` : '#EFF6FF' }
              }}
            >
              <ChevronRight size={24} style={{ transform: 'rotate(180deg)', color: colors.textPrimary }} />
            </button>
            <div>
              <h1 className="text-2xl font-bold" style={{ 
                background: `linear-gradient(135deg, ${colors.primary}, ${colors.accent})`,
                WebkitBackgroundClip: 'text',
                WebkitTextFillColor: 'transparent'
              }}>AI Health Insights</h1>
              <p className="text-sm dark-mode-transition" style={{ color: colors.textSecondary }}>Personalized analysis based on your data</p>
            </div>
          </header>

          <div className="space-y-8">
            <SymptomAIInsights colors={colors} />
          </div>
        </div>
      );
    }

    return (
      <div className="p-6 pb-20">
        <header className="flex justify-between items-center mb-8">
          <div>
            <h1 className="text-3xl font-bold" style={{ 
              background: `linear-gradient(135deg, ${colors.primary}, ${colors.accent})`,
              WebkitBackgroundClip: 'text',
              WebkitTextFillColor: 'transparent'
            }}>Symptoms</h1>
            <p className="text-sm dark-mode-transition" style={{ color: colors.textSecondary }}>Track your health patterns</p>
          </div>
          <button 
            className="w-12 h-12 rounded-full flex items-center justify-center shadow-lg transition-transform hover:scale-110 duration-300" 
            style={{ 
              background: `linear-gradient(135deg, ${colors.primary}, ${colors.accent})`,
              boxShadow: `0 0 20px ${colors.primary}60`
            }}
            onClick={() => setView('add')}
            disabled={isLoading || healthDataLoading}
          >
            <PlusCircle size={24} color="white" className={isLoading || healthDataLoading ? "animate-pulse" : ""} />
          </button>
        </header>

        {/* Success message with animation */}
        {showSuccessMessage && (
          <div className="fixed top-5 left-0 right-0 mx-auto w-max z-50 animate-bounce-in">
            <div className="p-4 rounded-xl shadow-lg flex items-center max-w-md"
                 style={{ 
                   backgroundColor: isDarkMode ? colors.cardBg : 'white',
                   borderLeft: `4px solid ${colors.success}`,
                   boxShadow: `0 10px 15px -3px ${colors.success}30`
                 }}>
              <div className="w-8 h-8 rounded-full mr-3 flex items-center justify-center" 
                   style={{ backgroundColor: `${colors.success}20` }}>
                <TrendingUp size={18} style={{ color: colors.success }} />
              </div>
              <div>
                <h4 className="font-medium text-sm dark-mode-transition" style={{ color: colors.textPrimary }}>
                  Symptom Added
                </h4>
                <p className="text-xs dark-mode-transition" style={{ color: colors.textSecondary }}>
                  AI is analyzing your symptom data
                </p>
              </div>
            </div>
          </div>
        )}

        {/* Google Fit Integration Section */}
        <div className="mb-6">
          <GoogleFitIntegration colors={colors} />
        </div>

        {/* Google Fit Health Overview - Only shown when connected to Google Fit */}
        {isGoogleFitConnected && (
          <div className="mb-8">
            <div className="flex justify-between items-center mb-4">
              <h3 className="font-bold text-lg dark-mode-transition" style={{ color: colors.textPrimary }}>
                Health Overview
              </h3>
              <div className="flex items-center">
                <span className="text-xs mr-3 dark-mode-transition" style={{ color: colors.textSecondary }}>
                  Updated {formatLastUpdated()}
                </span>
                <button
                  onClick={refreshHealthData}
                  disabled={isRefreshing}
                  className="p-2 rounded-full transition-all duration-300 hover:shadow-md"
                  style={{ 
                    backgroundColor: `${colors.primary}15`,
                    color: colors.primary
                  }}
                >
                  <RefreshCw size={16} className={isRefreshing ? "animate-spin" : ""} />
                </button>
              </div>
            </div>

            {/* Health Metrics Cards in a Grid */}
            <div className="grid grid-cols-2 sm:grid-cols-4 gap-4 mb-4">
              {/* Blood Pressure Card */}
              <div className="p-4 rounded-xl border shadow-sm hover:shadow-md transition-all duration-300 dark-mode-transition" 
                   style={{ backgroundColor: cardBg, borderColor: borderColor }}>
                <div className="flex items-center mb-2">
                  <div className="w-8 h-8 rounded-lg mr-2 flex items-center justify-center" style={{ backgroundColor: `${colors.danger}20` }}>
                    <Heart size={16} style={{ color: colors.danger }} />
                  </div>
                  <span className="text-xs font-medium dark-mode-transition" style={{ color: colors.textSecondary }}>
                    Blood Pressure
                  </span>
                </div>
                <p className="text-lg font-bold dark-mode-transition" style={{ color: colors.textPrimary }}>
                  {healthMetrics.pressure.systolic ? 
                    `${Math.round(healthMetrics.pressure.systolic)}/${Math.round(healthMetrics.pressure.diastolic)}` : 
                    'N/A'}
                </p>
                <p className="text-xs dark-mode-transition" style={{ color: colors.textSecondary }}>mmHg</p>
              </div>

              {/* BMI Card */}
              <div className="p-4 rounded-xl border shadow-sm hover:shadow-md transition-all duration-300 dark-mode-transition" 
                   style={{ backgroundColor: cardBg, borderColor: borderColor }}>
                <div className="flex items-center mb-2">
                  <div className="w-8 h-8 rounded-lg mr-2 flex items-center justify-center" style={{ backgroundColor: `${colors.primary}20` }}>
                    <Activity size={16} style={{ color: colors.primary }} />
                  </div>
                  <span className="text-xs font-medium dark-mode-transition" style={{ color: colors.textSecondary }}>
                    BMI
                  </span>
                </div>
                <p className="text-lg font-bold dark-mode-transition" style={{ color: colors.textPrimary }}>
                  {healthMetrics.BMI ? healthMetrics.BMI.toFixed(1) : 'N/A'}
                </p>
                <p className="text-xs dark-mode-transition" style={{ color: colors.textSecondary }}>
                  {healthMetrics.BMI ? (
                    healthMetrics.BMI < 18.5 ? 'Underweight' :
                    healthMetrics.BMI < 25 ? 'Normal' :
                    healthMetrics.BMI < 30 ? 'Overweight' : 'Obese'
                  ) : ''}
                </p>
              </div>

              {/* Glucose Card */}
              <div className="p-4 rounded-xl border shadow-sm hover:shadow-md transition-all duration-300 dark-mode-transition" 
                   style={{ backgroundColor: cardBg, borderColor: borderColor }}>
                <div className="flex items-center mb-2">
                  <div className="w-8 h-8 rounded-lg mr-2 flex items-center justify-center" style={{ backgroundColor: `${colors.accent}20` }}>
                    <BarChart2 size={16} style={{ color: colors.accent }} />
                  </div>
                  <span className="text-xs font-medium dark-mode-transition" style={{ color: colors.textSecondary }}>
                    Glucose
                  </span>
                </div>
                <p className="text-lg font-bold dark-mode-transition" style={{ color: colors.textPrimary }}>
                  {healthMetrics.glucose ? healthMetrics.glucose.toFixed(0) : 'N/A'}
                </p>
                <p className="text-xs dark-mode-transition" style={{ color: colors.textSecondary }}>mg/dL</p>
              </div>

              {/* Sleep Card */}
              <div className="p-4 rounded-xl border shadow-sm hover:shadow-md transition-all duration-300 dark-mode-transition" 
                   style={{ backgroundColor: cardBg, borderColor: borderColor }}>
                <div className="flex items-center mb-2">
                  <div className="w-8 h-8 rounded-lg mr-2 flex items-center justify-center" style={{ backgroundColor: `${colors.gradientAlt1}20` }}>
                    <Clock size={16} style={{ color: colors.gradientAlt1 }} />
                  </div>
                  <span className="text-xs font-medium dark-mode-transition" style={{ color: colors.textSecondary }}>
                    Sleep
                  </span>
                </div>
                <p className="text-lg font-bold dark-mode-transition" style={{ color: colors.textPrimary }}>
                  {healthMetrics.sleep.sleep_time ? `${healthMetrics.sleep.sleep_time} - ${healthMetrics.sleep.wake_time}` : 'N/A'}
                </p>
                <p className="text-xs dark-mode-transition" style={{ color: colors.textSecondary }}>Hours</p>
              </div>
            </div>

            {/* Health Score Panel - If prediction is available */}
            {prediction && (
              <div className="p-4 rounded-xl border shadow-sm hover:shadow-md transition-all duration-300 dark-mode-transition mb-6" 
                 style={{ backgroundColor: cardBg, borderColor: borderColor }}>
                <div className="flex items-center mb-3">
                  <div className="w-10 h-10 rounded-lg mr-3 flex items-center justify-center" 
                       style={{ backgroundColor: `${colors.success}20` }}>
                    <Activity size={20} style={{ color: colors.success }} />
                  </div>
                  <div>
                    <h4 className="font-medium dark-mode-transition" style={{ color: colors.textPrimary }}>Health Score</h4>
                    <p className="text-xs dark-mode-transition" style={{ color: colors.textSecondary }}>Based on your metrics</p>
                  </div>
                  <div className="ml-auto">
                    <div className="px-3 py-1 rounded-full text-xs font-medium" 
                         style={{ 
                           backgroundColor: prediction.HealthScore >= 80 
                             ? `${colors.success}20` 
                             : prediction.HealthScore >= 50 
                               ? `${colors.warning}20` 
                               : `${colors.danger}20`,
                           color: prediction.HealthScore >= 80 
                             ? colors.success 
                             : prediction.HealthScore >= 50 
                               ? colors.warning 
                               : colors.danger,
                         }}>
                      {prediction.HealthScore >= 80 
                        ? 'Excellent' 
                        : prediction.HealthScore >= 50 
                          ? 'Good' 
                          : 'Needs Attention'}
                    </div>
                  </div>
                </div>

                {/* Health Score Progress Bar */}
                <div className="w-full bg-gray-200 rounded-full h-3 mb-2 overflow-hidden dark-mode-transition" style={{
                  backgroundColor: isDarkMode ? 'rgba(255,255,255,0.1)' : 'rgba(0,0,0,0.1)'
                }}>
                  <div 
                    className="h-3 rounded-full shimmer-gradient" 
                    style={{ 
                      width: `${prediction.HealthScore || 0}%`,
                      background: `linear-gradient(to right, ${colors.danger}, ${colors.warning}, ${colors.success})`,
                      transition: 'width 1.5s ease-in-out'
                    }}>
                  </div>
                </div>
                <div className="flex justify-between text-xs dark-mode-transition" style={{ color: colors.textSecondary }}>
                  <span>0</span>
                  <span>50</span>
                  <span>100</span>
                </div>
              </div>
            )}
          </div>
        )}

        {/* Symptom summary with enhanced gradients and animations */}
        <div className="mb-8 p-5 rounded-2xl relative overflow-hidden transform hover:scale-105 transition-all duration-500 animate-breathe"
             style={{ 
               background: `linear-gradient(135deg, ${colors.gradientAlt1}, ${colors.gradientAlt2})`,
               boxShadow: `0 20px 25px -5px ${colors.gradientAlt1}40`
             }}>
          <div className="absolute top-0 right-0 w-32 h-32 rounded-full animate-float" 
               style={{ 
                 background: gradientOverlayBg,
                 transform: 'translate(30%, -30%)'
               }}></div>
          <div className="absolute bottom-0 left-0 w-24 h-24 rounded-full animate-float" 
               style={{ 
                 background: gradientOverlayBg,
                 transform: 'translate(-30%, 30%)',
                 animationDelay: '1s'
               }}></div>

          <div className="relative z-10">
            <div className="flex items-center mb-3">
              <Calendar size={20} className="mr-2 text-white" />
              <h3 className="text-white font-bold text-xl">Monthly Summary</h3>
            </div>
            <p className="text-white text-opacity-90 text-sm mb-5">Your most frequent symptoms this month:</p>

            {getFrequentSymptoms().map((symptom, index) => (
              <div 
                key={index}
                className="bg-white bg-opacity-20 backdrop-blur-sm rounded-xl p-4 mb-4 transition-transform hover:scale-105 duration-300"
              >
                <div className="flex justify-between items-center mb-2">
                  <span className="text-white text-sm font-medium">{symptom.name}</span>
                  <span className="text-white text-xs bg-white bg-opacity-20 px-2 py-0.5 rounded-full">
                    {symptom.count} occurrences
                  </span>
                </div>
                <div className="w-full bg-white bg-opacity-30 h-3 rounded-full overflow-hidden">
                  <div className="bg-white h-3 rounded-full shimmer" 
                       style={{ width: `${Math.min(100, symptom.count * 10)}%` }}></div>
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* Recent symptoms with enhanced card designs */}
        <div className="mb-8">
          <div className="flex justify-between items-center mb-4">
            <h3 className="font-bold text-lg dark-mode-transition" style={{ color: colors.textPrimary }}>Recent Symptoms</h3>
            <button className="text-sm font-medium px-3 py-1 rounded-full transition-all duration-300 hover:shadow-md dark-mode-transition" 
                    style={{ color: colors.primary, backgroundColor: `${colors.primary}10` }}>
              View all
            </button>
          </div>

          <div className="space-y-4">
            {symptoms.length > 0 ? (
              symptoms.map(symptom => (
                <SymptomCard
                  key={symptom.id}
                  symptom={symptom.symptom}
                  severity={symptom.severity}
                  time={formatTimeDisplay(symptom)}
                  note={symptom.notes}
                  colors={colors}
                  triggers={symptom.triggers}
                  associatedSymptoms={symptom.associatedSymptoms}
                  healthContext={symptom.healthContext}
                />
              ))
            ) : (
              <div className="p-5 border rounded-2xl text-center dark-mode-transition" style={{ 
                borderColor: isDarkMode ? `${colors.primary}20` : 'rgba(219, 234, 254, 0.6)',
                backgroundColor: isDarkMode ? `${colors.primary}10` : 'rgba(239, 246, 255, 0.6)'
              }}>
                <Activity size={24} className="mx-auto mb-2 opacity-50" style={{ color: colors.textSecondary }} />
                <p className="text-sm dark-mode-transition" style={{ color: colors.textSecondary }}>
                  No symptoms logged yet. Start tracking to get personalized insights.
                </p>
                <button 
                  className="mt-3 px-4 py-2 rounded-lg text-xs font-medium transition-all duration-300" 
                  style={{ 
                    color: colors.primary,
                    backgroundColor: `${colors.primary}15`
                  }}
                  onClick={() => setView('add')}
                >
                  Log your first symptom
                </button>
              </div>
            )}
          </div>
        </div>

        {/* AI Insights with enhanced gradient and animations */}
        <div className="mb-12">
          <h3 className="font-bold text-lg mb-4 dark-mode-transition" style={{ color: colors.textPrimary }}>AI Insights</h3>
          <div className="p-5 rounded-2xl shadow-lg border transform hover:scale-105 transition-all duration-500 dark-mode-transition"
               style={{ 
                 backgroundColor: insightBg,
                 borderColor: borderColor,
                 boxShadow: `0 15px 25px -5px ${colors.primary}30`
               }}>
            <div className="flex items-start">
              <div className="w-12 h-12 rounded-xl mr-4 flex items-center justify-center shimmer" 
                   style={{ 
                     background: `linear-gradient(135deg, ${colors.accent}, ${colors.primary})`
                   }}>
                <Brain size={24} color="white" />
              </div>
              <div>
                <h4 className="font-bold text-base mb-2 dark-mode-transition" style={{ color: colors.textPrimary }}>
                  {aiInsights ? 'New Pattern Detected' : 'AI Health Assistant'}
                </h4>
                <p className="text-sm dark-mode-transition" style={{ color: colors.textSecondary }}>
                  {aiInsights 
                    ? aiInsights.pattern || 'Your headaches tend to occur after extended screen time. Consider taking breaks using the 20-20-20 rule.'
                    : isGoogleFitConnected 
                      ? 'Your Google Fit data is connected. Log symptoms to get personalized AI insights.'
                      : 'Connect your Google Fit data and log symptoms to get personalized AI insights.'}
                </p>
                <div className="flex mt-4">
                  <button 
                    className="flex-1 py-2.5 px-4 rounded-lg transition-all duration-300 hover:shadow-lg transform hover:-translate-y-1 flex items-center justify-center" 
                    style={{ 
                      background: `linear-gradient(to right, ${colors.primary}, ${colors.accent})`,
                      color: 'white',
                      boxShadow: `0 4px 6px -1px ${colors.primary}30`
                    }}
                    onClick={() => setView('insights')}
                  >
                    View all insights
                    <ChevronRight size={18} className="ml-1" />
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Custom CSS for animations */}
        <style jsx>{`
          @keyframes breathe {
            0%, 100% { transform: scale(1); }
            50% { transform: scale(1.03); }
          }
          @keyframes float {
            0%, 100% { transform: translateY(0); }
            50% { transform: translateY(-10px); }
          }
          @keyframes shimmer {
            0% { background-position: -100% 0; }
            100% { background-position: 200% 0; }
          }
          @keyframes bounce-in {
            0% { transform: translateY(-20px); opacity: 0; }
            50% { transform: translateY(10px); opacity: 0.8; }
            100% { transform: translateY(0); opacity: 1; }
          }
          .animate-breathe {
            animation: breathe 6s ease-in-out infinite;
          }
          .animate-float {
            animation: float 4s ease-in-out infinite;
          }
          .animate-bounce-in {
            animation: bounce-in 0.5s ease-out forwards;
          }
          .shimmer {
            background: linear-gradient(90deg, 
              rgba(255,255,255,0) 0%, 
              rgba(255,255,255,0.3) 50%, 
              rgba(255,255,255,0) 100%);
            background-size: 200% 100%;
            animation: shimmer 2s infinite;
          }
          .shimmer-gradient::after {
            content: '';
            position: absolute;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            background: linear-gradient(90deg, 
              rgba(255,255,255,0) 0%, 
              rgba(255,255,255,0.2) 50%, 
              rgba(255,255,255,0) 100%);
            background-size: 200% 100%;
            animation: shimmer 2s infinite;
          }
        `}</style>
      </div>
    );
  };

export default SymptomsScreen;
