                  bgColor = 'transparent';
                  icon = null;
                } else if (todayTaken.length === todayMeds.length) {
                  bgColor = `linear-gradient(135deg, ${colors.success}, ${colors.success}90)`;
                  icon = <CheckCircle size={18} color="white" />;
                } else if (todayTaken.length > 0) {
