import React, { useState, useEffect } from 'react';
import { PlusCircle, Search, Filter, Calendar, ArrowLeft } from "lucide-react";

// MedicationsScreen component - Main screen for medication management
const MedicationsScreen = ({ colors, setActiveScreen }) => {
  // Core states for managing screen flow and data
  const [view, setView] = useState('main'); // main, detail, add, history, schedule, interactions
  const [selectedMedication, setSelectedMedication] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [showAddOptions, setShowAddOptions] = useState(false);
  const [showFilterOptions, setShowFilterOptions] = useState(false);

  // Mock medication data - would come from API in real app
  const [medications, setMedications] = useState([
    {
      id: 1,
      name: "Metformin",
      dosage: "500mg",
      schedule: [
        { time: "8:00 AM", status: "taken", day: "today" },
        { time: "9:00 PM", status: "upcoming", day: "today" }
      ],
      instructions: "Take with food to reduce stomach upset",
      purpose: "Controls blood sugar levels",
      refillDate: "April 15, 2025",
      refillRemaining: 8,
      prescribedBy: "Dr. Johnson",
      startDate: "January 15, 2025",
      interactions: [
        { medication: "Ibuprofen", severity: "moderate", description: "May reduce effectiveness and increase risk of low blood sugar" },
        { medication: "Alcohol", severity: "high", description: "Can cause dangerously low blood sugar and lactic acidosis" }
      ],
      sideEffects: ["Nausea", "Diarrhea", "Stomach upset", "Metallic taste"],
      adherenceRate: 92,
      missedDoses: 2,
      history: [
        { date: "Mar 22, 2025", time: "8:05 AM", status: "taken", delay: "5 min late" },
        { date: "Mar 21, 2025", time: "8:12 AM", status: "taken", delay: "12 min late" },
        { date: "Mar 21, 2025", time: "9:00 PM", status: "taken", delay: "on time" },
        { date: "Mar 20, 2025", time: "8:00 AM", status: "taken", delay: "on time" },
        { date: "Mar 20, 2025", time: "9:00 PM", status: "taken", delay: "on time" },
        { date: "Mar 19, 2025", time: "8:00 AM", status: "missed", delay: "missed" },
        { date: "Mar 19, 2025", time: "9:00 PM", status: "taken", delay: "on time" }
      ]
    },
    {
      id: 2,
      name: "Lisinopril",
      dosage: "10mg",
      schedule: [
        { time: "8:00 AM", status: "taken", day: "today" }
      ],
      instructions: "Take on an empty stomach",
      purpose: "Controls blood pressure",
      refillDate: "April 8, 2025",
      refillRemaining: 3,
      prescribedBy: "Dr. Smith",
      startDate: "December 10, 2024",
      interactions: [
        { medication: "Potassium supplements", severity: "high", description: "Can cause dangerously high potassium levels" },
        { medication: "NSAIDs", severity: "moderate", description: "May reduce blood pressure lowering effects" }
      ],
      sideEffects: ["Dry cough", "Dizziness", "Headache"],
      adherenceRate: 95,
      missedDoses: 1,
      history: [
        { date: "Mar 22, 2025", time: "8:05 AM", status: "taken", delay: "5 min late" },
        { date: "Mar 21, 2025", time: "8:10 AM", status: "taken", delay: "10 min late" },
        { date: "Mar 20, 2025", time: "8:00 AM", status: "taken", delay: "on time" },
        { date: "Mar 19, 2025", time: "8:00 AM", status: "taken", delay: "on time" },
        { date: "Mar 18, 2025", time: "missed", status: "missed", delay: "missed" }
      ]
    },
    {
      id: 3,
      name: "Atorvastatin",
      dosage: "20mg",
      schedule: [
        { time: "9:00 PM", status: "upcoming", day: "today" }
      ],
      instructions: "Take in the evening",
      purpose: "Lowers cholesterol",
      refillDate: "May 2, 2025",
      refillRemaining: 15,
      prescribedBy: "Dr. Smith",
      startDate: "November 5, 2024",
      interactions: [
        { medication: "Grapefruit juice", severity: "high", description: "Can increase risk of muscle pain and damage" },
        { medication: "Antifungals", severity: "high", description: "Can increase risk of side effects" }
      ],
      sideEffects: ["Muscle pain", "Joint pain", "Digestive issues"],
      adherenceRate: 88,
      missedDoses: 3,
      history: [
        { date: "Mar 21, 2025", time: "9:15 PM", status: "taken", delay: "15 min late" },
        { date: "Mar 20, 2025", time: "9:00 PM", status: "taken", delay: "on time" },
        { date: "Mar 19, 2025", time: "9:00 PM", status: "taken", delay: "on time" },
        { date: "Mar 18, 2025", time: "9:00 PM", status: "missed", delay: "missed" },
        { date: "Mar 17, 2025", time: "9:20 PM", status: "taken", delay: "20 min late" }
      ]
    },
    {
      id: 4,
      name: "Aspirin",
      dosage: "81mg",
      schedule: [
        { time: "8:00 AM", status: "taken", day: "today" }
      ],
      instructions: "Take with food to reduce stomach irritation",
      purpose: "Blood thinner",
      refillDate: "June 10, 2025",
      refillRemaining: 25,
      prescribedBy: "Dr. Johnson",
      startDate: "January 5, 2025",
      interactions: [
        { medication: "Ibuprofen", severity: "moderate", description: "May reduce heart benefits of aspirin" },
        { medication: "Blood thinners", severity: "high", description: "Increased risk of bleeding" }
      ],
      sideEffects: ["Upset stomach", "Heartburn", "Easy bruising"],
      adherenceRate: 98,
      missedDoses: 0,
      history: [
        { date: "Mar 22, 2025", time: "8:00 AM", status: "taken", delay: "on time" },
        { date: "Mar 21, 2025", time: "8:00 AM", status: "taken", delay: "on time" },
        { date: "Mar 20, 2025", time: "8:00 AM", status: "taken", delay: "on time" },
        { date: "Mar 19, 2025", time: "8:00 AM", status: "taken", delay: "on time" },
        { date: "Mar 18, 2025", time: "8:00 AM", status: "taken", delay: "on time" }
      ]
    }
  ]);

  // Filter medications based on search query
  const filteredMedications = medications.filter(med => 
    med.name.toLowerCase().includes(searchQuery.toLowerCase())
  );

  // Add a new medication
  const addMedication = (newMedication) => {
    setMedications([...medications, {
      id: medications.length + 1,
      ...newMedication
    }]);
    setView('main');
  };

  // Cleanup any popups when clicking outside
  useEffect(() => {
    const handleOutsideClick = () => {
      setShowAddOptions(false);
      setShowFilterOptions(false);
    };

    document.addEventListener('click', handleOutsideClick);
    return () => document.removeEventListener('click', handleOutsideClick);
  }, []);

  // Main component renderer
  const renderView = () => {
    switch(view) {
      case 'detail':
        return <MedicationDetail medication={selectedMedication} setView={setView} colors={colors} />;
      case 'history':
        return <MedicationHistory medication={selectedMedication} medications={medications} setView={setView} colors={colors} />;
      case 'schedule':
        return <MedicationSchedule medication={selectedMedication} medications={medications} setView={setView} colors={colors} />;
      case 'interactions':
        return <MedicationInteractions medication={selectedMedication} setView={setView} colors={colors} />;
      case 'add':
        return <AddMedicationForm setView={setView} addMedication={addMedication} colors={colors} />;
      default:
        return <MedicationList medications={filteredMedications} setSelectedMedication={setSelectedMedication} setView={setView} searchQuery={searchQuery} setSearchQuery={setSearchQuery} showAddOptions={showAddOptions} setShowAddOptions={setShowAddOptions} showFilterOptions={showFilterOptions} setShowFilterOptions={setShowFilterOptions} colors={colors} />;
    }
  };

  return (
    <div className="p-6 pb-6 overflow-y-auto">
      {renderView()}

      {/* Global animations */}
      <style jsx>{`
        @keyframes float {
          0%, 100% { transform: translateY(0); }
          50% { transform: translateY(-8px); }
        }

        @keyframes pulse {
          0%, 100% { opacity: 1; }
          50% { opacity: 0.7; }
        }

        @keyframes breathe {
          0%, 100% { transform: scale(1); }
          50% { transform: scale(1.03); }
        }

        @keyframes shimmer {
          0% { background-position: -100% 0; }
          100% { background-position: 200% 0; }
        }

        .animate-float {
          animation: float 3s ease-in-out infinite;
        }

        .animate-breathe {
          animation: breathe 4s ease-in-out infinite;
        }

        .shimmer {
          background: linear-gradient(90deg, 
            rgba(255,255,255,0) 0%, 
            rgba(255,255,255,0.2) 50%, 
            rgba(255,255,255,0) 100%);
          background-size: 200% 100%;
          animation: shimmer 2s infinite;
        }
      `}</style>
    </div>
  );
};

