import React, { useState, useEffect } from 'react';
import { 
  Activity, Heart, BarChart2, Droplet, Moon, 
  TrendingUp, AlertTriangle, Calendar, 
  RefreshCw, ArrowUpRight, Zap, BatteryCharging,
  CheckCircle, Clock, BookOpen, Shield, 
  ArrowRight, PlusCircle, Pill
} from 'lucide-react';
import { useHealthData } from '../HealthDataContext';
import { useApiMiddleware } from '../ApiMiddleware';
import { useTheme } from '../ThemeContext';

// Component that will use real data from the API
const DashBoardScreen = () => {
  const { isDarkMode } = useTheme();
  const { isLoading } = useApiMiddleware();
  const { 
    healthMetrics, 
    activityMetrics,
    lastRefreshTime,
    fetchHealthData,
    isGoogleFitConnected,
    prediction
  } = useHealthData();

  const [currentTime, setCurrentTime] = useState(new Date());
  const [refreshing, setRefreshing] = useState(false);
  const [slideIn, setSlideIn] = useState(false);

  // Define color theme based on dark mode
  const colors = isDarkMode ? {
    primary: '#3B82F6',       // Vivid blue
    accent: '#0EA5E9',        // Bright cyan
    success: '#10B981',       // Green
    warning: '#F59E0B',       // Amber
    danger: '#EF4444',        // Red
    info: '#818CF8',          // Lighter indigo
    gradientAlt1: '#8B5CF6',  // Brighter purple
    gradientAlt2: '#3B82F6',  // Blue
    accentAlt: '#EC4899',     // Pink
    primaryLight: '#1E3A8A',  // Deep blue
    textPrimary: '#F9FAFB',
    textSecondary: '#9CA3AF',
    cardBg: '#1F2937',        // Darker card background 
    darkBg: '#111827',        // Very dark background
    neonAccent: '#38BDF8'     // Neon blue for highlights
  } : {
    primary: '#2563EB',      // Stronger blue
    accent: '#0EA5E9',       // Cyan accent
    success: '#10B981',      // Green
    warning: '#F59E0B',      // Amber
    danger: '#EF4444',       // Red
    info: '#6366F1',         // Indigo
    gradientAlt1: '#7C3AED', // Purple
    gradientAlt2: '#3B82F6',  // Lighter blue
    accentAlt: '#EC4899',     // Pink
    primaryLight: '#DBEAFE',  // Very light blue
    textPrimary: '#1F2937',
    textSecondary: '#6B7280',
    cardBg: 'white',
    darkBg: 'white',
    neonAccent: '#38BDF8'     // Bright blue for highlights
  };

  // Format today's date
  const today = new Date().toLocaleDateString('en-US', { 
    weekday: 'long', 
    month: 'long', 
    day: 'numeric' 
  });

  useEffect(() => {
    // Set slide-in animation on mount
    setTimeout(() => setSlideIn(true), 100);

    // Update time every minute
    const timer = setInterval(() => {
      setCurrentTime(new Date());
    }, 60000);

    // Get real data on component mount if connected
    if (isGoogleFitConnected) {
      fetchHealthData();
    }

    return () => clearInterval(timer);
  }, [isGoogleFitConnected, fetchHealthData]);

  const handleRefresh = async () => {
    setRefreshing(true);
    try {
      // Get real data from API
      await fetchHealthData();
    } catch (error) {
      console.error('Failed to refresh data:', error);
    } finally {
      setRefreshing(false);
    }
  };

  // Format blood pressure for display
  const formatBloodPressure = () => {
    if (!healthMetrics?.pressure?.systolic || !healthMetrics?.pressure?.diastolic) {
      return '120/80';
    }
    return `${Math.round(healthMetrics.pressure.systolic)}/${Math.round(healthMetrics.pressure.diastolic)}`;
  };

  // Get health status color based on score
  const getHealthScoreColor = (score) => {
    if (score >= 80) return colors.success;
    if (score >= 60) return colors.info;
    if (score >= 40) return colors.warning;
    return colors.danger;
  };

  // Get color for a specific metric
  const getMetricColor = (type, value) => {
    switch(type) {
      case 'bp':
        const systolic = value?.systolic;
        const diastolic = value?.diastolic;
        if (!systolic || !diastolic) return colors.textSecondary;
        if (systolic >= 140 || diastolic >= 90) return colors.danger;
        if (systolic >= 130 || diastolic >= 80) return colors.warning;
        return colors.success;
      case 'glucose':
        if (!value) return colors.textSecondary;
        if (value >= 126) return colors.danger;
        if (value >= 100) return colors.warning;
        if (value < 70) return colors.warning;
        return colors.success;
      case 'bmi':
        if (!value) return colors.textSecondary;
        if (value < 18.5 || value >= 30) return colors.danger;
        if (value >= 25) return colors.warning;
        return colors.success;
      default:
        return colors.primary;
    }
  };

  // Format steps for display (e.g., 8423 -> "8.4k")
  const formatSteps = (steps) => {
    if (!steps) return "0";
    return steps >= 1000 ? `${(steps / 1000).toFixed(1)}k` : steps.toString();
  };

  // Get health score from prediction or use default
  const healthScore = prediction?.HealthScore || 85;

  return (
    <div className={`w-full max-w-md mx-auto p-4 space-y-5 transform transition-all duration-700 ${slideIn ? 'translate-x-0 opacity-100' : 'translate-x-8 opacity-0'}`}
         style={{ color: colors.textPrimary }}>
      {/* Header */}
      <header className="flex justify-between items-center">
        <div>
          <h1 className="text-xl font-bold mb-0.5 bg-gradient-to-r from-blue-600 to-cyan-500 bg-clip-text text-transparent">
            Hi, Samantha! 
          </h1>
          <div className="flex items-center">
            <Calendar size={14} className="mr-1" style={{ color: colors.textSecondary }} />
            <p className="text-xs" style={{ color: colors.textSecondary }}>{today}</p>
          </div>
        </div>
        <div className="w-10 h-10 rounded-full bg-gradient-to-br from-blue-500 to-blue-700 flex items-center justify-center shadow-lg">
          <span className="text-white font-medium">S</span>
        </div>
      </header>

      {/* Health Score Card - Using real data from prediction */}
      <div className="p-5 rounded-xl relative overflow-hidden transform transition-all duration-500 hover:scale-105"
           style={{ 
             background: `linear-gradient(135deg, ${colors.primary}, ${colors.accent})`,
             boxShadow: `0 10px 20px -5px ${colors.primary}60`
           }}>
        <div className="absolute top-0 right-0 w-32 h-32 rounded-full" 
             style={{ 
               background: `radial-gradient(circle, rgba(255,255,255,0.15), transparent 70%)`,
               transform: 'translate(30%, -30%)'
             }}></div>
        <div className="absolute bottom-0 left-0 w-24 h-24 rounded-full" 
             style={{ 
               background: `radial-gradient(circle, rgba(255,255,255,0.1), transparent 70%)`,
               transform: 'translate(-30%, 30%)'
             }}></div>

        <div className="relative z-10">
          <div className="flex items-center justify-between mb-2">
            <p className="text-white text-opacity-90 text-sm font-medium">Today's Health Score</p>
            <div className="flex items-center text-xs font-medium bg-white bg-opacity-20 py-1 px-2 rounded-full backdrop-blur-sm">
              <TrendingUp size={12} className="mr-1" /> Daily Trend
            </div>
          </div>

          <div className="flex items-center justify-between">
            <h2 className="text-3xl font-bold text-white flex items-baseline">
              {healthScore}<span className="text-sm ml-1">/100</span>
            </h2>
            <div className="flex items-center text-xs font-medium bg-white bg-opacity-20 py-0.5 px-2 rounded-full backdrop-blur-sm">
              <ArrowUpRight size={10} className="mr-0.5" /> 5%
            </div>
          </div>

          {/* Progress bar */}
          <div className="mt-3 mb-2 w-full bg-white bg-opacity-20 rounded-full h-2 overflow-hidden">
            <div 
              className="h-2 rounded-full shimmer-gradient" 
              style={{ 
                width: `${healthScore}%`,
                background: 'rgba(255, 255, 255, 0.4)',
                transition: 'width 1.5s ease-in-out'
              }}>
            </div>
          </div>

          <div className="flex justify-between text-xs text-white text-opacity-80">
            <span>Critical</span>
            <span>Moderate</span>
            <span>Excellent</span>
          </div>
        </div>
      </div>

      {/* Energy & Activity Card - Using real activity data */}
      <div className="p-4 rounded-xl border relative overflow-hidden transform transition-all duration-500 hover:scale-105 dark-mode-transition"
           style={{ 
             backgroundColor: colors.cardBg,
             borderColor: isDarkMode ? `${colors.primary}30` : 'rgba(219, 234, 254, 1)',
             boxShadow: `0 10px 20px -5px ${colors.primary}40`
           }}>
        <div className="absolute top-0 right-0 w-32 h-32 rounded-full" 
             style={{ 
               background: `radial-gradient(circle, ${colors.gradientAlt1}10, transparent 70%)`,
               transform: 'translate(30%, -30%)'
             }}></div>

        <div className="flex items-center justify-between mb-3 relative z-10">
          <div>
            <h3 className="font-bold text-base dark-mode-transition" style={{ color: colors.textPrimary }}>
              Energy & Activity
            </h3>
            <p className="text-xs dark-mode-transition" style={{ color: colors.textSecondary }}>
              Your energy levels today
            </p>
          </div>
          <div className="flex space-x-1">
            <div className="flex items-center text-xs font-medium px-2 py-1 rounded-full"
                 style={{ 
                   backgroundColor: isDarkMode ? `${colors.gradientAlt1}20` : `${colors.gradientAlt1}10`,
                   color: colors.gradientAlt1
                 }}>
              <BatteryCharging size={12} className="mr-1" /> {activityMetrics.energyLevel || 85}%
            </div>
          </div>
        </div>

        {/* Energy wave visualization */}
        <div className="h-16 mb-2 relative overflow-hidden rounded-xl dark-mode-transition"
             style={{ 
               backgroundColor: isDarkMode ? `${colors.primary}10` : 'rgba(239, 246, 255, 0.6)',
               boxShadow: `inset 0 2px 6px ${colors.primary}10`
             }}>
          <div className="absolute inset-0 flex items-end energy-wave">
            <svg viewBox="0 0 400 100" preserveAspectRatio="none" className="w-full h-full">
              <path
                fill={`${colors.primary}30`}
                d="M0,50 C30,40 70,60 100,50 C130,40 170,60 200,50 C230,40 270,60 300,50 C330,40 370,60 400,50 V100 H0 Z"
              />
              <path
                fill={`${colors.primary}20`}
                d="M0,60 C30,50 70,70 100,60 C130,50 170,70 200,60 C230,50 270,70 300,60 C330,50 370,70 400,60 V100 H0 Z"
              />
            </svg>
          </div>

          {/* Time markers */}
          <div className="absolute inset-x-0 bottom-2 flex justify-between px-3 text-xxs dark-mode-transition" style={{ color: colors.textSecondary }}>
            <span>6AM</span>
            <span>12PM</span>
            <span>6PM</span>
            <span>Now</span>
          </div>

          {/* Current time indicator */}
          <div className="absolute right-3 h-full w-px bg-white dark-mode-transition" style={{ backgroundColor: colors.primary }}>
            <div className="absolute bottom-6 transform -translate-x-1/2 w-3 h-3 rounded-full dark-mode-transition"
                 style={{ backgroundColor: colors.primary }}></div>
          </div>
        </div>

        {/* Activity status - Using real data */}
        <div className="flex justify-between">
          <div className="flex space-x-3">
            <div className="text-center">
              <div className="text-xl font-bold dark-mode-transition" style={{ color: colors.primary }}>
                {formatSteps(activityMetrics.steps)}
              </div>
              <div className="text-xs dark-mode-transition" style={{ color: colors.textSecondary }}>Steps</div>
            </div>
            <div className="text-center">
              <div className="text-xl font-bold dark-mode-transition" style={{ color: colors.accent }}>
                {activityMetrics.heartRate || '68'}<span className="text-sm">bpm</span>
              </div>
              <div className="text-xs dark-mode-transition" style={{ color: colors.textSecondary }}>Heart Rate</div>
            </div>
            <div className="text-center">
              <div className="text-xl font-bold dark-mode-transition" style={{ color: colors.gradientAlt1 }}>
                {activityMetrics.distance || '0'}<span className="text-sm">mi</span>
              </div>
              <div className="text-xs dark-mode-transition" style={{ color: colors.textSecondary }}>Distance</div>
            </div>
          </div>
          <button className="flex items-center text-xs font-medium dark-mode-transition" style={{ color: colors.primary }}>
            Details
            <ArrowRight size={14} className="ml-0.5" />
          </button>
        </div>
      </div>

      {/* Health Metrics - Using real data */}
      <div>
        <div className="flex justify-between items-center mb-3">
          <h3 className="text-base font-bold dark-mode-transition" style={{ color: colors.textPrimary }}>
            Health Metrics
          </h3>

          <button 
            onClick={handleRefresh}
            className="flex items-center text-xs font-medium px-2 py-1 rounded-full transition-all duration-300 dark-mode-transition"
            style={{ 
              color: colors.primary, 
              backgroundColor: isDarkMode ? `${colors.primary}20` : `${colors.primary}15`
            }}
          >
            <RefreshCw size={12} className={`mr-1 ${refreshing ? 'animate-spin' : ''}`} />
            {refreshing ? 'Refreshing...' : 'Refresh'}
          </button>
        </div>

        {/* Health Cards Grid with real metrics */}
        <div className="grid grid-cols-2 gap-2">
          {/* Blood Pressure Card */}
          <div className="p-3 rounded-xl border transition-all duration-300 transform hover:scale-102 dark-mode-transition"
               style={{ 
                 backgroundColor: isDarkMode ? `${colors.primary}10` : 'rgba(239, 246, 255, 0.6)',
                 borderColor: isDarkMode ? `${colors.primary}30` : 'rgba(219, 234, 254, 1)',
                 boxShadow: `0 4px 6px -1px ${colors.primary}20`
               }}>
            <div className="flex items-center mb-1">
              <div className="w-7 h-7 rounded-lg mr-2 flex items-center justify-center dark-mode-transition" 
                   style={{ backgroundColor: `${colors.accent}20` }}>
                <Heart size={16} style={{ color: colors.accent }} />
              </div>
              <span className="text-xs font-medium dark-mode-transition" style={{ color: colors.textSecondary }}>
                Blood Pressure
              </span>
            </div>
            <div className="flex items-center justify-between">
              <p className="text-xl font-bold" style={{ 
                color: getMetricColor('bp', healthMetrics?.pressure)
              }}>
                {formatBloodPressure()}
                <span className="text-xs font-normal ml-1 dark-mode-transition" style={{ color: colors.textSecondary }}>mmHg</span>
              </p>
              <TrendingUp size={16} style={{ color: getMetricColor('bp', healthMetrics?.pressure) }} />
            </div>
          </div>

          {/* BMI Card */}
          <div className="p-3 rounded-xl border transition-all duration-300 transform hover:scale-102 dark-mode-transition"
               style={{ 
                 backgroundColor: isDarkMode ? `${colors.primary}10` : 'rgba(239, 246, 255, 0.6)',
                 borderColor: isDarkMode ? `${colors.primary}30` : 'rgba(219, 234, 254, 1)',
                 boxShadow: `0 4px 6px -1px ${colors.primary}20`
               }}>
            <div className="flex items-center mb-1">
              <div className="w-7 h-7 rounded-lg mr-2 flex items-center justify-center dark-mode-transition" 
                   style={{ backgroundColor: `${colors.primary}20` }}>
                <BarChart2 size={16} style={{ color: colors.primary }} />
              </div>
              <span className="text-xs font-medium dark-mode-transition" style={{ color: colors.textSecondary }}>
                BMI
              </span>
            </div>
            <p className="text-xl font-bold" style={{ 
              color: getMetricColor('bmi', healthMetrics?.BMI)
            }}>
              {healthMetrics?.BMI ? healthMetrics.BMI.toFixed(1) : '23.0'}
            </p>
          </div>

          {/* Glucose Card */}
          <div className="p-3 rounded-xl border transition-all duration-300 transform hover:scale-102 dark-mode-transition"
               style={{ 
                 backgroundColor: isDarkMode ? `${colors.primary}10` : 'rgba(239, 246, 255, 0.6)',
                 borderColor: isDarkMode ? `${colors.primary}30` : 'rgba(219, 234, 254, 1)',
                 boxShadow: `0 4px 6px -1px ${colors.primary}20`
               }}>
            <div className="flex items-center mb-1">
              <div className="w-7 h-7 rounded-lg mr-2 flex items-center justify-center dark-mode-transition" 
                   style={{ backgroundColor: `${colors.gradientAlt1}20` }}>
                <Droplet size={16} style={{ color: colors.gradientAlt1 }} />
              </div>
              <span className="text-xs font-medium dark-mode-transition" style={{ color: colors.textSecondary }}>
                Glucose
              </span>
            </div>
            <p className="text-xl font-bold" style={{ 
              color: getMetricColor('glucose', healthMetrics?.glucose)
            }}>
              {healthMetrics?.glucose ? healthMetrics.glucose.toFixed(0) : '95'}
              <span className="text-xs ml-1 font-normal dark-mode-transition" style={{ color: colors.textSecondary }}>mg/dL</span>
            </p>
          </div>

          {/* Sleep Card */}
          <div className="p-3 rounded-xl border transition-all duration-300 transform hover:scale-102 dark-mode-transition"
               style={{ 
                 backgroundColor: isDarkMode ? `${colors.primary}10` : 'rgba(239, 246, 255, 0.6)',
                 borderColor: isDarkMode ? `${colors.primary}30` : 'rgba(219, 234, 254, 1)',
                 boxShadow: `0 4px 6px -1px ${colors.primary}20`
               }}>
            <div className="flex items-center mb-1">
              <div className="w-7 h-7 rounded-lg mr-2 flex items-center justify-center dark-mode-transition" 
                   style={{ backgroundColor: `${colors.gradientAlt2}20` }}>
                <Moon size={16} style={{ color: colors.gradientAlt2 }} />
              </div>
              <span className="text-xs font-medium dark-mode-transition" style={{ color: colors.textSecondary }}>
                Sleep
              </span>
            </div>
            <p className="text-xl font-bold dark-mode-transition" style={{ color: colors.textPrimary }}>
              {healthMetrics?.sleep?.sleep_time || '23:30'}<span className="text-xs font-normal dark-mode-transition" style={{ color: colors.textSecondary }}> - {healthMetrics?.sleep?.wake_time || '07:15'}</span>
            </p>
          </div>
        </div>
      </div>

      {/* Quick actions section - IMPORTANT: MUST KEEP THIS SECTION */}
      <div className="space-y-3">
        <h3 className="text-base font-bold dark-mode-transition" style={{ color: colors.textPrimary }}>
          Quick Actions
        </h3>

        <div className="grid grid-cols-2 gap-2">
          {/* Log Symptoms Button */}
          <button className="p-3 rounded-xl border flex flex-col items-center justify-center transition-all duration-300 transform hover:scale-102 dark-mode-transition"
                  style={{ 
                    backgroundColor: isDarkMode ? `${colors.danger}10` : `${colors.danger}05`,
                    borderColor: isDarkMode ? `${colors.danger}30` : `${colors.danger}20`,
                    color: colors.danger,
                    boxShadow: `0 4px 6px ${colors.danger}10`
                  }}>
            <Heart size={20} className="mb-1" />
            <span className="text-xs font-medium">Log Symptoms</span>
          </button>

          {/* Biorhythms Button */}
          <button className="p-3 rounded-xl border flex flex-col items-center justify-center transition-all duration-300 transform hover:scale-102 dark-mode-transition"
                  style={{ 
                    backgroundColor: isDarkMode ? `${colors.accent}10` : `${colors.accent}05`,
                    borderColor: isDarkMode ? `${colors.accent}30` : `${colors.accent}20`,
                    color: colors.accent,
                    boxShadow: `0 4px 6px ${colors.accent}10`
                  }}>
            <Clock size={20} className="mb-1" />
            <span className="text-xs font-medium">Biorhythms</span>
          </button>

          {/* Education Button */}
          <button className="p-3 rounded-xl border flex flex-col items-center justify-center transition-all duration-300 transform hover:scale-102 dark-mode-transition"
                  style={{ 
                    backgroundColor: isDarkMode ? `${colors.gradientAlt1}10` : `${colors.gradientAlt1}05`,
                    borderColor: isDarkMode ? `${colors.gradientAlt1}30` : `${colors.gradientAlt1}20`,
                    color: colors.gradientAlt1,
                    boxShadow: `0 4px 6px ${colors.gradientAlt1}10`
                  }}>
            <BookOpen size={20} className="mb-1" />
            <span className="text-xs font-medium">Education</span>
          </button>

          {/* Emergency Button */}
          <button className="p-3 rounded-xl border flex flex-col items-center justify-center transition-all duration-300 transform hover:scale-102 dark-mode-transition"
                  style={{ 
                    backgroundColor: isDarkMode ? `${colors.warning}10` : `${colors.warning}05`,
                    borderColor: isDarkMode ? `${colors.warning}30` : `${colors.warning}20`,
                    color: colors.warning,
                    boxShadow: `0 4px 6px ${colors.warning}10`
                  }}>
            <Shield size={20} className="mb-1" />
            <span className="text-xs font-medium">Emergency</span>
          </button>
        </div>
      </div>

      {/* Medication Reminder Card */}
      <div className="p-4 rounded-xl border transition-all duration-300 transform hover:scale-105 dark-mode-transition" 
           style={{ 
             borderColor: isDarkMode ? `${colors.primary}30` : 'rgba(219, 234, 254, 1)',
             backgroundColor: colors.cardBg,
             boxShadow: `0 10px 20px -5px ${colors.primary}30`
           }}>
        <div className="flex items-center mb-3">
          <div className="w-10 h-10 rounded-xl mr-3 flex items-center justify-center shimmer" 
               style={{ 
                 background: `linear-gradient(135deg, ${colors.primary}, ${colors.accent})`
               }}>
            <Pill size={20} color="white" />
          </div>
          <div className="flex-1">
            <h4 className="text-sm font-bold dark-mode-transition" style={{ color: colors.textPrimary }}>
              Next Medication
            </h4>
            <p className="text-xs dark-mode-transition" style={{ color: colors.textSecondary }}>
              In 45 minutes (08:30 AM)
            </p>
          </div>
          <div className="flex items-center px-2 py-1 rounded-full text-xs font-medium" 
               style={{ backgroundColor: `${colors.primary}20`, color: colors.primary }}>
            <Clock size={12} className="mr-1" />
            Metformin
          </div>
        </div>

        <button className="w-full flex items-center justify-center py-2 rounded-lg text-xs font-medium transition-all duration-300 transform hover:scale-102 dark-mode-transition"
                style={{ 
                  backgroundColor: isDarkMode ? `${colors.primary}15` : `${colors.primary}10`,
                  color: colors.primary
                }}>
          <PlusCircle size={14} className="mr-2" />
          View all medications
        </button>
      </div>

      {/* Biorhythm Card */}
      <div className="p-4 rounded-xl border transition-all duration-500 transform hover:scale-105 dark-mode-transition"
           style={{ 
             backgroundColor: colors.cardBg,
             borderColor: isDarkMode ? `${colors.primary}30` : 'rgba(219, 234, 254, 1)',
             boxShadow: `0 8px 16px -4px ${colors.primary}20`
           }}>
        <div className="flex items-center mb-3">
          <div className="w-10 h-10 rounded-lg mr-3 flex items-center justify-center shimmer" 
               style={{ 
                 background: `linear-gradient(135deg, ${colors.gradientAlt1}, ${colors.gradientAlt2})`
               }}>
            <Zap size={20} color="white" />
          </div>
          <div>
            <h4 className="text-sm font-bold dark-mode-transition" style={{ color: colors.textPrimary }}>
              Today's Peak Time
            </h4>
            <p className="text-xs dark-mode-transition" style={{ color: colors.textSecondary }}>
              2-4 PM (Exercise)
            </p>
          </div>
        </div>

        {/* Biorhythm visualization */}
        <div className="mb-3 h-16 relative">
          <div className="absolute inset-x-0 bottom-0 flex items-end">
            <div className="flex-1 h-6 rounded-l-lg dark-mode-transition" style={{ backgroundColor: `${colors.primary}30` }}></div>
            <div className="flex-1 h-10 bg-gradient-to-t transition-all dark-mode-transition" 
                 style={{ 
                   backgroundImage: `linear-gradient(to top, ${colors.primary}50, ${colors.primary}20)`,
                   boxShadow: `0 -4px 6px ${colors.primary}20`
                 }}></div>
            <div className="flex-1 h-14 bg-gradient-to-t transition-all dark-mode-transition" 
                 style={{ 
                   backgroundImage: `linear-gradient(to top, ${colors.accent}50, ${colors.accent}20)`,
                   boxShadow: `0 -4px 6px ${colors.accent}20`
                 }}></div>
            <div className="flex-1 h-10 bg-gradient-to-t transition-all dark-mode-transition" 
                 style={{ 
                   backgroundImage: `linear-gradient(to top, ${colors.primary}50, ${colors.primary}20)`,
                   boxShadow: `0 -4px 6px ${colors.primary}20`
                 }}></div>
            <div className="flex-1 h-6 rounded-r-lg dark-mode-transition" style={{ backgroundColor: `${colors.primary}30` }}></div>
          </div>
          <div className="absolute inset-x-0 bottom-0 flex justify-between text-xxs px-2 dark-mode-transition" style={{ color: colors.textSecondary }}>
            <span>8AM</span>
            <span>12PM</span>
            <span>3PM</span>
            <span>6PM</span>
            <span>9PM</span>
          </div>
          <div className="absolute left-1/2 bottom-0 w-0.5 h-16 transform -translate-x-1/2"
               style={{ backgroundColor: `${colors.success}60` }}>
            <div className="absolute -top-1 left-1/2 transform -translate-x-1/2 w-3 h-3 rounded-full animate-pulse"
                 style={{ backgroundColor: colors.success }}></div>
            <div className="absolute -top-1 left-1/2 transform -translate-x-1/2 w-5 h-5 rounded-full animate-ping opacity-30"
                 style={{ backgroundColor: colors.success }}></div>
          </div>
        </div>

        <button 
          className="w-full py-2 rounded-lg text-white text-xs font-medium transition-all duration-300 transform hover:scale-102 flex items-center justify-center dark-mode-transition"
          style={{ 
            background: `linear-gradient(to right, ${colors.gradientAlt1}, ${colors.gradientAlt2})`,
            boxShadow: `0 4px 8px ${colors.gradientAlt1}40`
          }}
        >
          <Clock size={14} className="mr-2" />
          See optimal times
        </button>
      </div>

      {/* Style definitions */}
      <style jsx>{`
        .shimmer {
          position: relative;
          overflow: hidden;
        }
        .shimmer::after {
          content: '';
          position: absolute;
          top: 0;
          left: 0;
          width: 100%;
          height: 100%;
          background: linear-gradient(
            90deg,
            rgba(255, 255, 255, 0) 0%,
            rgba(255, 255, 255, 0.3) 50%,
            rgba(255, 255, 255, 0) 100%
          );
          background-size: 200% 100%;
          animation: shimmerEffect 2s infinite;
        }
        .shimmer-gradient::after {
          content: '';
          position: absolute;
          top: 0;
          left: 0;
          width: 100%;
          height: 100%;
          background: linear-gradient(
            90deg,
            rgba(255, 255, 255, 0) 0%,
            rgba(255, 255, 255, 0.3) 50%,
            rgba(255, 255, 255, 0) 100%
          );
          background-size: 200% 100%;
          animation: shimmerEffect 2s infinite;
        }
        .energy-wave {
          animation: waveAnimation 15s linear infinite;
        }
        @keyframes shimmerEffect {
          0% { background-position: -100% 0; }
          100% { background-position: 200% 0; }
        }
        @keyframes waveAnimation {
          0% { transform: translateX(0); }
          100% { transform: translateX(-100%); }
        }
        @keyframes pulse {
          0%, 100% { opacity: 1; }
          50% { opacity: 0.6; }
        }
        .animate-pulse {
          animation: pulse 2s infinite;
        }
        @keyframes ping {
          0% { transform: scale(1); opacity: 1; }
          75%, 100% { transform: scale(2); opacity: 0; }
        }
        .animate-ping {
          animation: ping 1.5s cubic-bezier(0, 0, 0.2, 1) infinite;
        }
        @keyframes spin {
          0% { transform: rotate(0deg); }
          100% { transform: rotate(360deg); }
        }
        .animate-spin {
          animation: spin 1s linear infinite;
        }
        .text-xxs {
          font-size: 0.625rem;
        }
        .dark-mode-transition {
          transition: background-color 0.3s ease, color 0.3s ease, border-color 0.3s ease, box-shadow 0.3s ease;
        }
      `}</style>
    </div>
  );
};

export default DashBoardScreen;
