import React, { useState, useEffect } from 'react';
import { useTheme } from '../ThemeContext';
import { useAuth } from '../AuthProvider';
import supabase from '../supabaseClient';
import ProfileSummaryCard from '../components/ProfileSummaryCard';
import ProfileTabs from '../components/ProfileTabs';
import ProfileNotification from '../components/ProfileNotification';
import { AccountTab, HealthTab, PreferencesTab, PrivacyTab } from '../tabs';

const ProfileScreen = () => {
  // Theme and auth contexts
  const { isDarkMode, toggleDarkMode } = useTheme();
  const { user, updateUserProfile } = useAuth();

  // State for mouse position (for parallax effects)
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });

  // State for profile editing and data
  const [editMode, setEditMode] = useState(false);
  const [activeTab, setActiveTab] = useState('account');
  const [hoveredTab, setHoveredTab] = useState(null);
  const [notification, setNotification] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [originalUserData, setOriginalUserData] = useState(null);

  // User data state with default values
  const [userData, setUserData] = useState({
    name: '',
    email: '',
    avatar: '',
    dob: '',
    gender: '',
    bloodType: '',
    height: '',
    weight: '',
    conditions: [],
    allergies: [],
    isPremium: false,
    goalSteps: 8000,
    goalSleep: 8,
    goalWater: 2500
  });

  // Preferences state
  const [preferences, setPreferences] = useState({
    notifications: true,
    reminders: true,
    biometrics: true,
    darkMode: isDarkMode,
    dataSharing: false
  });

  // Theme colors
  const colors = {
    primary: '#3B82F6',
    primaryLight: '#60A5FA',
    accent: '#0EA5E9',
    accentAlt: '#06B6D4',
    neonAccent: '#22D3EE',
    textPrimary: isDarkMode ? '#F1F5F9' : '#1E293B',
    textSecondary: isDarkMode ? '#94A3B8' : '#64748B',
    warning: '#F59E0B',
    danger: '#EF4444',
    success: '#10B981'
  };

  // Load user data on component mount
  useEffect(() => {
    if (user) {
      loadUserData();
    }
  }, [user]);

  // Set up mouse position tracking for parallax effects
  useEffect(() => {
    const handleMouseMove = (e) => {
      const { clientX, clientY } = e;
      const x = (clientX / window.innerWidth) - 0.5;
      const y = (clientY / window.innerHeight) - 0.5;
      setMousePosition({ x, y });
    };

    window.addEventListener('mousemove', handleMouseMove);
    return () => window.removeEventListener('mousemove', handleMouseMove);
  }, []);

  // Load user data from database
  const loadUserData = async () => {
    setIsLoading(true);

    try {
      // In a real app, fetch from database. For demo, use dummy data if no user
      if (!user || user.email === 'demo@healthsync.com') {
        const demoData = {
          name: 'Demo User',
          email: 'demo@healthsync.com',
          avatar: '/avatar-placeholder.jpg',
          dob: '1990-05-15',
          gender: 'Not specified',
          bloodType: 'O+',
          height: '175',
          weight: '70',
          conditions: ['Seasonal allergies', 'Mild asthma'],
          allergies: ['Pollen', 'Dust mites'],
          isPremium: true,
          goalSteps: 8000,
          goalSleep: 8,
          goalWater: 2500
        };

        setUserData(demoData);
        setOriginalUserData(JSON.parse(JSON.stringify(demoData)));
        setIsLoading(false);
        return;
      }

      // Fetch real user data
      const { data, error } = await supabase
        .from('users')
        .select(`
          *,
          user_health_data(*),
          user_preferences(*)
        `)
        .eq('user_id', user.id)
        .single();

      if (error) throw error;

      // If we have data from the database
      if (data) {
        const healthData = data.user_health_data?.[0] || {};
        const userPrefs = data.user_preferences?.[0] || {};

        // Populate user data state
        const loadedUserData = {
          name: data.name || '',
          email: data.email || '',
          avatar: data.avatar_url || '',
          dob: healthData.date_of_birth || '',
          gender: healthData.gender || '',
          bloodType: healthData.blood_type || '',
          height: healthData.height || '',
          weight: healthData.weight || '',
          conditions: healthData.medical_conditions || [],
          allergies: healthData.allergies || [],
          isPremium: data.is_premium || false,
          goalSteps: healthData.goal_steps || 8000,
          goalSleep: healthData.goal_sleep || 8,
          goalWater: healthData.goal_water || 2500
        };

        // Populate preferences state
        setPreferences({
          notifications: userPrefs.notifications_enabled || true,
          reminders: userPrefs.reminders_enabled || true,
          biometrics: userPrefs.biometrics_enabled || true,
          darkMode: userPrefs.enable_dark_mode || isDarkMode,
          dataSharing: userPrefs.data_sharing_enabled || false
        });

        setUserData(loadedUserData);
        setOriginalUserData(JSON.parse(JSON.stringify(loadedUserData)));
      }
    } catch (error) {
      console.error('Error loading user data:', error);
      setNotification({
        type: 'error',
        message: 'Failed to load profile data. Are you even real?'
      });
    } finally {
      setIsLoading(false);
    }
  };

  // Handle saving user data
  const handleSave = async () => {
    setIsLoading(true);

    try {
      // In a real app, save to database. For demo, just show success
      if (!user || user.email === 'demo@healthsync.com') {
        // Simulate save delay
        await new Promise(resolve => setTimeout(resolve, 800));

        setNotification({
          type: 'success',
          message: 'Profile updated successfully! If this were real, your data would be saved.'
        });

        setOriginalUserData(JSON.parse(JSON.stringify(userData)));
        setEditMode(false);
        return;
      }

      // Update user profile in the database
      const { error: userError } = await supabase
        .from('users')
        .update({
          name: userData.name,
          avatar_url: userData.avatar,
          updated_at: new Date()
        })
        .eq('user_id', user.id);

      if (userError) throw userError;

      // Update health data in the database
      const healthData = {
        user_id: user.id,
        height: userData.height,
        weight: userData.weight,
        date_of_birth: userData.dob,
        gender: userData.gender,
        blood_type: userData.bloodType,
        medical_conditions: userData.conditions,
        allergies: userData.allergies,
        goal_steps: userData.goalSteps,
        goal_sleep: userData.goalSleep,
        goal_water: userData.goalWater,
        updated_at: new Date()
      };

      // Upsert health data
      const { error: healthError } = await supabase
        .from('user_health_data')
        .upsert(healthData);

      if (healthError) throw healthError;

      // Update preferences in the database
      const prefsData = {
        user_id: user.id,
        notifications_enabled: preferences.notifications,
        reminders_enabled: preferences.reminders,
        biometrics_enabled: preferences.biometrics,
        enable_dark_mode: preferences.darkMode,
        data_sharing_enabled: preferences.dataSharing,
        updated_at: new Date()
      };

      // Upsert preferences
      const { error: prefsError } = await supabase
        .from('user_preferences')
        .upsert(prefsData);

      if (prefsError) throw prefsError;

      // Update local storage for dark mode
      localStorage.setItem('healthsync_dark_mode', preferences.darkMode);

      // Update user data in auth context
      if (updateUserProfile) {
        await updateUserProfile({
          name: userData.name,
          avatar: userData.avatar
        });
      }

      // Update original data and exit edit mode
      setOriginalUserData(JSON.parse(JSON.stringify(userData)));
      setNotification({
        type: 'success',
        message: 'Profile updated successfully! Looking fabulous as always.'
      });

      setEditMode(false);
    } catch (error) {
      console.error('Error saving profile:', error);
      setNotification({
        type: 'error',
        message: 'Failed to save profile. The Internet must be having a bad hair day.'
      });
    } finally {
      setIsLoading(false);
    }
  };

  // Handle preference toggles
  const handleTogglePreference = (key) => {
    const newPreferences = { ...preferences };
    newPreferences[key] = !newPreferences[key];

    // Special handling for dark mode
    if (key === 'darkMode' && toggleDarkMode) {
      toggleDarkMode();
    }

    setPreferences(newPreferences);
  };

  // Handle adding/removing conditions
  const addCondition = (condition) => {
    if (!userData.conditions.includes(condition)) {
      setUserData({
        ...userData,
        conditions: [...userData.conditions, condition]
      });
    }
  };

  const removeCondition = (condition) => {
    setUserData({
      ...userData,
      conditions: userData.conditions.filter(c => c !== condition)
    });
  };

  // Handle adding/removing allergies
  const addAllergy = (allergy) => {
    if (!userData.allergies.includes(allergy)) {
      setUserData({
        ...userData,
        allergies: [...userData.allergies, allergy]
      });
    }
  };

  const removeAllergy = (allergy) => {
    setUserData({
      ...userData,
      allergies: userData.allergies.filter(a => a !== allergy)
    });
  };

  // Handle canceling edit mode
  const handleCancelEdit = () => {
    setUserData(JSON.parse(JSON.stringify(originalUserData)));
    setEditMode(false);
    setNotification({
      type: 'info',
      message: 'Changes discarded. Your profile remains gloriously unchanged.'
    });
  };

  // Render the active tab content
  const renderTabContent = () => {
    switch (activeTab) {
      case 'account':
        return (
          <AccountTab 
            userData={userData} 
            setUserData={setUserData} 
            editMode={editMode} 
            colors={colors} 
          />
        );
      case 'health':
        return (
          <HealthTab 
            userData={userData} 
            setUserData={setUserData} 
            editMode={editMode} 
            colors={colors}
            addCondition={addCondition}
            removeCondition={removeCondition}
            addAllergy={addAllergy}
            removeAllergy={removeAllergy}
            isLoading={isLoading}
          />
        );
      case 'preferences':
        return (
          <PreferencesTab 
            preferences={preferences} 
            handleToggle={handleTogglePreference} 
            colors={colors} 
          />
        );
      case 'privacy':
        return (
          <PrivacyTab 
            preferences={preferences} 
            handleToggle={handleTogglePreference} 
            colors={colors} 
          />
        );
      default:
        return (
          <div className="text-center p-12">
            <p className="text-blue-200">Something went wrong. The developers probably went to lunch.</p>
          </div>
        );
    }
  };

  return (
    <div className="pt-6 px-6 pb-12 h-full overflow-auto relative dark-mode-transition">
      {/* Background elements - orbs with parallax effect */}
      <div className="fixed inset-0 overflow-hidden pointer-events-none">
        <div className="absolute top-20 right-10 w-96 h-96 rounded-full opacity-10 dark-mode-transition" 
             style={{ 
               background: `radial-gradient(circle, ${colors.primary}90, transparent 70%)`,
               transform: `translate3d(${mousePosition.x * 20}px, ${mousePosition.y * 20}px, 0)`,
               filter: 'blur(50px)'
             }}></div>
        <div className="absolute bottom-40 left-20 w-80 h-80 rounded-full opacity-10 dark-mode-transition" 
             style={{ 
               background: `radial-gradient(circle, ${colors.accent}90, transparent 70%)`,
               transform: `translate3d(${mousePosition.x * -20}px, ${mousePosition.y * -20}px, 0)`,
               filter: 'blur(40px)'
             }}></div>
        <div className="absolute top-1/3 left-1/4 w-64 h-64 rounded-full opacity-10 dark-mode-transition" 
             style={{ 
               background: `radial-gradient(circle, ${colors.accentAlt}90, transparent 70%)`,
               transform: `translate3d(${mousePosition.x * 10}px, ${mousePosition.y * 10}px, 0)`,
               filter: 'blur(30px)'
             }}></div>
      </div>

      <div className="max-w-3xl mx-auto relative z-10">
        {/* Header with page title */}
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-white mb-2 flex items-center">
            <svg className="w-8 h-8 mr-3 text-blue-400" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M5.121 17.804A13.937 13.937 0 0112 16c2.5 0 4.847.655 6.879 1.804M15 10a3 3 0 11-6 0 3 3 0 016 0zm6 2a9 9 0 11-18 0 9 9 0 0118 0z"></path>
            </svg>
            <span className="bg-clip-text text-transparent bg-gradient-to-r from-white to-blue-200">Your Profile</span>
          </h1>
          <p className="text-blue-300 text-opacity-80">
            {editMode 
              ? "Edit mode engaged! Make yourself look good (or at least better than your ID photo)." 
              : "All your vital stats and preferences. Try not to be shocked by how awesome you are."}
          </p>
        </div>

        {/* Notification component */}
        {notification && (
          <ProfileNotification 
            message={notification.message}
            type={notification.type}
            onClose={() => setNotification(null)}
            colors={colors}
          />
        )}

        {/* Profile summary card */}
        <ProfileSummaryCard 
          userData={userData}
          editMode={editMode}
          setEditMode={setEditMode}
          handleSave={handleSave}
          mousePosition={mousePosition}
          colors={colors}
        />

        {/* Edit mode actions */}
        {editMode && (
          <div className="mb-6 p-4 rounded-xl bg-gradient-to-br from-blue-900/20 to-blue-800/20 backdrop-blur-sm flex justify-between items-center">
            <div className="flex items-center">
              <svg className="w-5 h-5 mr-2 text-blue-400" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M13 16h-1v-4h-1m1-4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z"></path>
              </svg>
              <span className="text-sm text-blue-200">
                Edit mode active. Feel free to express your true self (or your fantasy self).
              </span>
            </div>
            <div className="flex space-x-3">
              <button
                onClick={handleCancelEdit}
                className="px-3 py-1.5 rounded-lg text-xs font-medium bg-blue-900/50 text-blue-200 hover:bg-blue-900/70 transition-colors duration-300 flex items-center"
              >
                <svg className="w-3.5 h-3.5 mr-1.5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12"></path>
                </svg>
                Cancel
              </button>
              <button
                onClick={handleSave}
                className="px-3 py-1.5 rounded-lg text-xs font-medium bg-emerald-600/70 text-white hover:bg-emerald-600/90 transition-colors duration-300 flex items-center"
                disabled={isLoading}
              >
                {isLoading ? (
                  <>
                    <svg className="animate-spin w-3.5 h-3.5 mr-1.5" fill="none" viewBox="0 0 24 24">
                      <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                      <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                    </svg>
                    Saving...
                  </>
                ) : (
                  <>
                    <svg className="w-3.5 h-3.5 mr-1.5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M5 13l4 4L19 7"></path>
                    </svg>
                    Save Changes
                  </>
                )}
              </button>
            </div>
          </div>
        )}

        {/* Tab navigation */}
        <ProfileTabs 
          activeTab={activeTab}
          setActiveTab={setActiveTab}
          hoveredTab={hoveredTab}
          setHoveredTab={setHoveredTab}
          colors={colors}
        />

        {/* Tab content with glass card effect */}
        <div className="p-6 rounded-2xl backdrop-blur-md border relative z-10 min-h-[400px] dark-mode-transition"
             style={{ 
               background: isDarkMode ? 'rgba(30, 41, 59, 0.7)' : 'rgba(255, 255, 255, 0.07)', 
               borderColor: isDarkMode ? 'rgba(255, 255, 255, 0.05)' : 'rgba(255, 255, 255, 0.2)',
               boxShadow: `0 10px 25px -5px ${colors.primary}20, 0 0 15px ${colors.accent}10`
             }}>
          {renderTabContent()}
        </div>

        {/* Sassy footer note */}
        <div className="mt-8 text-center">
          <p className="text-xs text-blue-300 opacity-70 dark-mode-transition">
            Last updated: 10 minutes ago (while you were thinking about that snack in the kitchen)
          </p>
        </div>
      </div>

      {/* Floating help button */}
      <button 
        className="fixed bottom-6 right-6 w-12 h-12 rounded-full bg-gradient-to-r from-blue-500 to-cyan-400 flex items-center justify-center text-white shadow-lg hover:shadow-xl transition-all duration-300 transform hover:scale-110 z-50"
        style={{ boxShadow: `0 10px 25px -5px ${colors.primary}80` }}
      >
        <svg className="w-6 h-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M8.228 9c.549-1.165 2.03-2 3.772-2 2.21 0 4 1.343 4 3 0 1.4-1.278 2.575-3.006 2.907-.542.104-.994.54-.994 1.093m0 3h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z"></path>
        </svg>
      </button>

      {/* Global CSS for animations and effects */}
      <style jsx global>{`
        .tooltip-container {
          position: relative;
        }

        .tooltip-container .tooltip {
          visibility: hidden;
          position: absolute;
          bottom: 100%;
          left: 50%;
          transform: translateX(-50%);
          background: rgba(15, 23, 42, 0.8);
          color: white;
          padding: 4px 8px;
          border-radius: 4px;
          font-size: 12px;
          white-space: nowrap;
          opacity: 0;
          transition: opacity 0.2s, visibility 0.2s;
          z-index: 100;
          pointer-events: none;
          margin-bottom: 5px;
        }

        .tooltip-container:hover .tooltip {
          visibility: visible;
          opacity: 1;
        }

        @keyframes shimmer {
          0% { background-position: -100% 0; }
          100% { background-position: 200% 0; }
        }

        .shimmer {
          background: linear-gradient(90deg, 
            rgba(255,255,255,0) 0%, 
            rgba(255,255,255,0.2) 50%, 
            rgba(255,255,255,0) 100%);
          background-size: 200% 100%;
          animation: shimmer 2s infinite;
        }

        @keyframes fadein {
          from { opacity: 0; transform: translateY(10px); }
          to { opacity: 1; transform: translateY(0); }
        }

        .animate-fadein {
          animation: fadein 0.5s ease-out forwards;
        }

        .dark-mode-transition {
          transition: background-color 0.5s, border-color 0.5s, color 0.5s, box-shadow 0.5s;
        }

        .transform-gpu {
          transform: translate3d(0, 0, 0);
        }

        .hover\:scale-102:hover {
          transform: scale(1.02);
        }

        /* Scrollbar styling */
        ::-webkit-scrollbar {
          width: 8px;
          height: 8px;
        }

        ::-webkit-scrollbar-track {
          background: rgba(30, 41, 59, 0.2);
          border-radius: 10px;
        }

        ::-webkit-scrollbar-thumb {
          background: rgba(96, 165, 250, 0.5);
          border-radius: 10px;
        }

        ::-webkit-scrollbar-thumb:hover {
          background: rgba(96, 165, 250, 0.7);
        }
      `}</style>
    </div>
  );
};

export default ProfileScreen;
