import React, { useState, useEffect } from 'react';
import { PlusCircle, Search, Filter, Calendar, Zap, CheckCircle, Clock, X, Pill, AlertTriangle, ChevronRight } from "lucide-react";

// Import sub-components
import MedicationList from '../shared/MedicationList';
import MedicationDetail from '../shared/MedicationDetail';
import MedicationHistory from '../shared/MedicationHistory';
import MedicationSchedule from '../shared/MedicationSchedule';
import MedicationInteractions from '../shared/MedicationInteractions';
import AddMedicationForm from '../shared/AddMedicationForm';

// Import biorhythm engine
import { biorhythmEngine, formatTimeWindows } from '../services/BiorhythmEngine';

// Main component for medication management with biorhythm integration
const MedicationsScreen = ({ colors, setActiveScreen }) => {
  // View state (main, detail, add, history, schedule, interactions)
  const [view, setView] = useState('main');
  const [selectedMedication, setSelectedMedication] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [showAddOptions, setShowAddOptions] = useState(false);
  const [showFilterOptions, setShowFilterOptions] = useState(false);
  const [biorhythmOptimized, setBiorhythmOptimized] = useState(false);
  const [biorhythmData, setBiorhythmData] = useState(null);
  const [optimizedMeds, setOptimizedMeds] = useState([]);

  // Mock medication data - in a real app, this would come from API/database
  const [medications, setMedications] = useState([
    {
      id: 1,
      name: "Metformin",
      dosage: "500mg",
      schedule: [
        { time: "8:00 AM", status: "taken", day: "today" },
        { time: "9:00 PM", status: "upcoming", day: "today" }
      ],
      instructions: "Take with food to reduce stomach upset",
      purpose: "Controls blood sugar levels",
      refillDate: "April 15, 2025",
      refillRemaining: 8,
      prescribedBy: "Dr. Johnson",
      startDate: "January 15, 2025",
      category: "metabolic",
      interactions: [
        { medication: "Ibuprofen", severity: "moderate", description: "May reduce effectiveness and increase risk of low blood sugar" },
        { medication: "Alcohol", severity: "high", description: "Can cause dangerously low blood sugar and lactic acidosis" }
      ],
      sideEffects: ["Nausea", "Diarrhea", "Stomach upset", "Metallic taste"],
      adherenceRate: 92,
      missedDoses: 2,
      history: [
        { date: "Mar 22, 2025", time: "8:05 AM", status: "taken", delay: "5 min late" },
        { date: "Mar 21, 2025", time: "8:12 AM", status: "taken", delay: "12 min late" },
        { date: "Mar 21, 2025", time: "9:00 PM", status: "taken", delay: "on time" },
        { date: "Mar 20, 2025", time: "8:00 AM", status: "taken", delay: "on time" },
        { date: "Mar 20, 2025", time: "9:00 PM", status: "taken", delay: "on time" },
        { date: "Mar 19, 2025", time: "8:00 AM", status: "missed", delay: "missed" },
        { date: "Mar 19, 2025", time: "9:00 PM", status: "taken", delay: "on time" }
      ]
    },
    {
      id: 2,
      name: "Lisinopril",
      dosage: "10mg",
      schedule: [
        { time: "8:00 AM", status: "taken", day: "today" }
      ],
      instructions: "Take on an empty stomach",
      purpose: "Controls blood pressure",
      refillDate: "April 8, 2025",
      refillRemaining: 3,
      prescribedBy: "Dr. Smith",
      startDate: "December 10, 2024",
      category: "cardiovascular",
      interactions: [
        { medication: "Potassium supplements", severity: "high", description: "Can cause dangerously high potassium levels" },
        { medication: "NSAIDs", severity: "moderate", description: "May reduce blood pressure lowering effects" }
      ],
      sideEffects: ["Dry cough", "Dizziness", "Headache"],
      adherenceRate: 95,
      missedDoses: 1,
      history: [
        { date: "Mar 22, 2025", time: "8:05 AM", status: "taken", delay: "5 min late" },
        { date: "Mar 21, 2025", time: "8:10 AM", status: "taken", delay: "10 min late" },
        { date: "Mar 20, 2025", time: "8:00 AM", status: "taken", delay: "on time" },
        { date: "Mar 19, 2025", time: "8:00 AM", status: "taken", delay: "on time" },
        { date: "Mar 18, 2025", time: "missed", status: "missed", delay: "missed" }
      ]
    },
    {
      id: 3,
      name: "Atorvastatin",
      dosage: "20mg",
      schedule: [
        { time: "9:00 PM", status: "upcoming", day: "today" }
      ],
      instructions: "Take in the evening",
      purpose: "Lowers cholesterol",
      refillDate: "May 2, 2025",
      refillRemaining: 15,
      prescribedBy: "Dr. Smith",
      startDate: "November 5, 2024",
      category: "cardiovascular",
      interactions: [
        { medication: "Grapefruit juice", severity: "high", description: "Can increase risk of muscle pain and damage" },
        { medication: "Antifungals", severity: "high", description: "Can increase risk of side effects" }
      ],
      sideEffects: ["Muscle pain", "Joint pain", "Digestive issues"],
      adherenceRate: 88,
      missedDoses: 3,
      history: [
        { date: "Mar 21, 2025", time: "9:15 PM", status: "taken", delay: "15 min late" },
        { date: "Mar 20, 2025", time: "9:00 PM", status: "taken", delay: "on time" },
        { date: "Mar 19, 2025", time: "9:00 PM", status: "taken", delay: "on time" },
        { date: "Mar 18, 2025", time: "9:00 PM", status: "missed", delay: "missed" },
        { date: "Mar 17, 2025", time: "9:20 PM", status: "taken", delay: "20 min late" }
      ]
    },
    {
      id: 4,
      name: "Aspirin",
      dosage: "81mg",
      schedule: [
        { time: "8:00 AM", status: "taken", day: "today" }
      ],
      instructions: "Take with food to reduce stomach irritation",
      purpose: "Blood thinner",
      refillDate: "June 10, 2025",
      refillRemaining: 25,
      prescribedBy: "Dr. Johnson",
      startDate: "January 5, 2025",
      category: "cardiovascular",
      interactions: [
        { medication: "Ibuprofen", severity: "moderate", description: "May reduce heart benefits of aspirin" },
        { medication: "Blood thinners", severity: "high", description: "Increased risk of bleeding" }
      ],
      sideEffects: ["Upset stomach", "Heartburn", "Easy bruising"],
      adherenceRate: 98,
      missedDoses: 0,
      history: [
        { date: "Mar 22, 2025", time: "8:00 AM", status: "taken", delay: "on time" },
        { date: "Mar 21, 2025", time: "8:00 AM", status: "taken", delay: "on time" },
        { date: "Mar 20, 2025", time: "8:00 AM", status: "taken", delay: "on time" },
        { date: "Mar 19, 2025", time: "8:00 AM", status: "taken", delay: "on time" },
        { date: "Mar 18, 2025", time: "8:00 AM", status: "taken", delay: "on time" }
      ]
    }
  ]);

  // Load biorhythm data
  useEffect(() => {
    try {
      const data = biorhythmEngine.getRecommendations();
      setBiorhythmData(data);

      // Create optimized medication schedule based on biorhythm
      if (data) {
        optimizeMedicationTiming(data);
      }
    } catch (error) {
      console.error("Failed to load biorhythm data:", error);
    }
  }, []);

  // Optimize medication timing based on biorhythm data
  const optimizeMedicationTiming = (bioData) => {
    if (!bioData) return;

    // Get optimal times for medication from biorhythm engine
    const optimalMedicationTimes = bioData.recommendations.medicationTiming;

    // Create new array of medications with optimized timing
    const optimized = medications.map(med => {
      // Deep clone the medication object
      const optimizedMed = JSON.parse(JSON.stringify(med));

      // Calculate biorhythm alignment score for each medication based on its category
      let alignmentScore = 0;
      let optimizedTimes = [];
      let suggestedChanges = [];

      optimizedMed.schedule.forEach(scheduleItem => {
        // Convert time string to hour (24-hour format)
        let hour = 0;
        if (scheduleItem.time.includes('AM') || scheduleItem.time.includes('PM')) {
          const timeParts = scheduleItem.time.split(':');
          let timeHour = parseInt(timeParts[0]);
          const isPM = scheduleItem.time.includes('PM');

          hour = isPM && timeHour !== 12 ? timeHour + 12 : 
                !isPM && timeHour === 12 ? 0 : timeHour;
        }

        // Check if current time is optimal according to biorhythm
        const isOptimalTime = optimalMedicationTimes.includes(hour);

        // If not optimal, find the nearest optimal time
        if (!isOptimalTime && scheduleItem.status === 'upcoming') {
          let nearestOptimalHour = optimalMedicationTimes.reduce((nearest, current) => {
            return Math.abs(current - hour) < Math.abs(nearest - hour) ? current : nearest;
          }, optimalMedicationTimes[0]);

          // Format the optimal time in AM/PM format
          const optimalTimeFormatted = `${nearestOptimalHour > 12 ? nearestOptimalHour - 12 : nearestOptimalHour === 0 ? 12 : nearestOptimalHour}:00 ${nearestOptimalHour >= 12 ? 'PM' : 'AM'}`;

          optimizedTimes.push({
            currentTime: scheduleItem.time,
            optimalTime: optimalTimeFormatted
          });

          suggestedChanges.push({
            from: scheduleItem.time,
            to: optimalTimeFormatted,
            difference: Math.abs(nearestOptimalHour - hour)
          });
        }

        // Calculate alignment score
        if (isOptimalTime) {
          alignmentScore += 100;
        } else {
          // Find how far the current time is from optimal
          const closestOptimalHour = optimalMedicationTimes.reduce((nearest, current) => {
            return Math.abs(current - hour) < Math.abs(nearest - hour) ? current : nearest;
          }, optimalMedicationTimes[0]);

          const hourDifference = Math.abs(closestOptimalHour - hour);

          // Calculate score based on proximity to optimal time (100 - distance * 10)
          alignmentScore += Math.max(0, 100 - (hourDifference * 10));
        }
      });

      // Calculate average alignment score
      alignmentScore = Math.round(alignmentScore / optimizedMed.schedule.length);

      // Add biorhythm data to medication
      return {
        ...optimizedMed,
        biorhythmData: {
          alignmentScore,
          optimizedTimes,
          suggestedChanges
        }
      };
    });

    setOptimizedMeds(optimized);
  };

  // Filter medications based on search query
  const filteredMedications = (biorhythmOptimized ? optimizedMeds : medications).filter(med => 
    med.name.toLowerCase().includes(searchQuery.toLowerCase())
  );

  // Add a new medication
  const addMedication = (newMedication) => {
    const updatedMedications = [...medications, {
      id: medications.length + 1,
      ...newMedication
    }];

    setMedications(updatedMedications);

    // Re-optimize if biorhythm is enabled
    if (biorhythmData) {
      const optimized = updatedMedications.map(med => {
        // If this is not the new medication, use existing optimized data if available
        const existingOptimized = optimizedMeds.find(m => m.id === med.id);
        if (existingOptimized && med.id !== updatedMedications.length) {
          return existingOptimized;
        }

        // Calculate new optimization data for new medication
        // (simplified version - in a real app you'd use the full logic from optimizeMedicationTiming)
        return {
          ...med,
          biorhythmData: {
            alignmentScore: 70, // Default score
            optimizedTimes: [],
            suggestedChanges: []
          }
        };
      });

      setOptimizedMeds(optimized);
    }

    setView('main');
  };

  // Toggle biorhythm optimization
  const toggleBiorhythmOptimization = () => {
    setBiorhythmOptimized(!biorhythmOptimized);
  };

  // Cleanup any popups when clicking outside
  useEffect(() => {
    const handleOutsideClick = () => {
      setShowAddOptions(false);
      setShowFilterOptions(false);
    };

    document.addEventListener('click', handleOutsideClick);
    return () => document.removeEventListener('click', handleOutsideClick);
  }, []);

  // Render the appropriate view based on state
  const renderView = () => {
    switch(view) {
      case 'detail':
        return (
          <MedicationDetail 
            medication={selectedMedication} 
            setView={setView} 
            colors={colors} 
          />
        );
      case 'history':
        return (
          <MedicationHistory 
            medication={selectedMedication} 
            medications={biorhythmOptimized ? optimizedMeds : medications} 
            setView={setView} 
            colors={colors} 
          />
        );
      case 'schedule':
        return (
          <MedicationSchedule 
            medication={selectedMedication} 
            medications={biorhythmOptimized ? optimizedMeds : medications} 
            setView={setView} 
            colors={colors} 
          />
        );
      case 'interactions':
        return (
          <MedicationInteractions 
            medication={selectedMedication} 
            setView={setView} 
            colors={colors} 
          />
        );
      case 'add':
        return (
          <AddMedicationForm 
            setView={setView} 
            addMedication={addMedication}
            colors={colors} 
          />
        );
      default:
        return (
          <>
            {/* Title section extracted from MedicationList */}
            <header className="flex justify-between items-center mb-4">
              <div>
                <h1 className="text-3xl font-bold bg-gradient-to-r from-blue-700 to-cyan-500 bg-clip-text text-transparent">Medications</h1>
                <p className="text-sm" style={{ color: colors.textSecondary }}>
                  Track your pills, because even I can't remember all this stuff
                </p>
              </div>
              <div className="relative">
                <button 
                  className="w-12 h-12 rounded-full flex items-center justify-center shadow-lg transition-transform hover:scale-110 duration-300 z-10" 
                  style={{ 
                    background: `linear-gradient(135deg, ${colors.primary}, ${colors.accent})`,
                    boxShadow: `0 0 15px ${colors.primary}40`
                  }}
                  onClick={(e) => {
                    e.stopPropagation();
                    setShowAddOptions(!showAddOptions);
                  }}
                >
                  <PlusCircle size={24} color="white" />
                </button>

                {/* Add medication options popup */}
                {showAddOptions && (
                  <div 
                    className="absolute right-0 top-14 w-56 p-2 rounded-xl shadow-xl z-20 border border-blue-100 bg-white"
                    style={{ boxShadow: `0 10px 25px -5px ${colors.primary}30` }}
                    onClick={e => e.stopPropagation()}
                  >
                    <div className="p-2 rounded-lg hover:bg-blue-50 cursor-pointer transition-colors duration-200" 
                        onClick={() => {setView('add'); setShowAddOptions(false);}}>
                      <div className="flex items-center">
                        <div className="w-8 h-8 rounded-full mr-3 flex items-center justify-center" 
                            style={{ backgroundColor: `${colors.primary}20`, color: colors.primary }}>
                          <Pill size={16} />
                        </div>
                        <span className="text-sm font-medium" style={{ color: colors.textPrimary }}>
                          Add medication manually
                        </span>
                      </div>
                    </div>

                    <div className="p-2 rounded-lg hover:bg-blue-50 cursor-pointer transition-colors duration-200">
                      <div className="flex items-center">
                        <div className="w-8 h-8 rounded-full mr-3 flex items-center justify-center" 
                            style={{ backgroundColor: `${colors.accent}20`, color: colors.accent }}>
                          <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                            <rect x="3" y="3" width="18" height="18" rx="2" ry="2"></rect>
                            <circle cx="8.5" cy="8.5" r="1.5"></circle>
                            <polyline points="21 15 16 10 5 21"></polyline>
                          </svg>
                        </div>
                        <span className="text-sm font-medium" style={{ color: colors.textPrimary }}>
                          Scan prescription
                        </span>
                      </div>
                    </div>

                    <div className="p-2 rounded-lg hover:bg-blue-50 cursor-pointer transition-colors duration-200">
                      <div className="flex items-center">
                        <div className="w-8 h-8 rounded-full mr-3 flex items-center justify-center" 
                            style={{ backgroundColor: `${colors.success}20`, color: colors.success }}>
                          <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                            <path d="M21 12.79A9 9 0 1 1 11.21 3 7 7 0 0 0 21 12.79z"></path>
                          </svg>
                        </div>
                        <span className="text-sm font-medium" style={{ color: colors.textPrimary }}>
                          Import from pharmacy
                        </span>
                      </div>
                    </div>
                  </div>
                )}
              </div>
            </header>

            {/* Biorhythm Optimization Toggle - NOW BELOW THE TITLE */}
            <div className="mb-6 p-4 rounded-2xl bg-white shadow-lg border border-blue-50 flex items-center justify-between"
                 style={{ boxShadow: `0 10px 15px -3px ${colors.primary}20` }}>
              <div className="flex items-center">
                <div className="w-12 h-12 rounded-full flex items-center justify-center mr-4 shimmer" 
                     style={{ 
                       background: `linear-gradient(135deg, ${colors.accent}, ${colors.primary})`,
                     }}>
                  <Zap size={24} color="white" />
                </div>
                <div>
                  <h3 className="font-bold text-lg" style={{ color: colors.textPrimary }}>Biorhythm Optimization</h3>
                  <p className="text-sm" style={{ color: colors.textSecondary }}>
                    {biorhythmOptimized 
                      ? "Your meds are now aligned with your body's rhythm. Science or magic? You decide." 
                      : "Optimize your medication schedule based on your biorhythm"}
                  </p>
                </div>
              </div>
              <div>
                <button 
                  className="w-14 h-7 rounded-full relative transition-all duration-300 focus:outline-none"
                  style={{ 
                    backgroundColor: biorhythmOptimized ? colors.primary : '#E5E7EB',
                    boxShadow: biorhythmOptimized ? `0 0 10px ${colors.primary}40` : 'none'
                  }}
                  onClick={toggleBiorhythmOptimization}
                >
                  <span 
                    className="absolute top-1 w-5 h-5 rounded-full bg-white transform transition-transform duration-300"
                    style={{ 
                      left: biorhythmOptimized ? 'auto' : '0.25rem',
                      right: biorhythmOptimized ? '0.25rem' : 'auto'
                    }}
                  ></span>
                </button>
              </div>
            </div>

            {/* Use MedicationList without the header/title since we're rendering it separately */}
            <div className="mb-8">
              {/* Weekly medication adherence overview */}
              <div className="mb-8 p-5 rounded-2xl bg-white shadow-lg border border-blue-50 transform hover:scale-102 transition-all duration-500"
                   style={{ boxShadow: `0 15px 25px -5px ${colors.primary}20` }}>
                <div className="flex justify-between items-center mb-4">
                  <h3 className="font-bold text-lg" style={{ color: colors.textPrimary }}>Weekly Adherence</h3>
                  <button 
                    className="flex items-center text-sm font-medium px-3 py-1 rounded-full transition-all duration-300 hover:shadow-md" 
                    style={{ color: colors.primary, backgroundColor: `${colors.primary}10` }}
                    onClick={() => setView('history')}
                  >
                    <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="mr-1">
                      <line x1="18" y1="20" x2="18" y2="10"></line>
                      <line x1="12" y1="20" x2="12" y2="4"></line>
                      <line x1="6" y1="20" x2="6" y2="14"></line>
                    </svg>
                    History
                  </button>
                </div>
                <div className="flex justify-between items-center">
                  {['Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat', 'Sun'].map((day, index) => {
                    let bgColor, icon;
                    if (index <= 1 || index === 3) {
                      bgColor = `linear-gradient(135deg, ${colors.success}, ${colors.success}90)`;
                      icon = <CheckCircle size={18} color="white" />;
                    } else if (index === 2) {
                      bgColor = `linear-gradient(135deg, ${colors.warning}, ${colors.warning}90)`;
                      icon = <Clock size={18} color="white" />;
                    } else {
                      bgColor = 'transparent';
                      icon = null;
                    }

                    return (
                      <div key={day} className="flex flex-col items-center">
                        <div className="w-10 h-10 rounded-full flex items-center justify-center shadow-md mb-2 transition-transform hover:scale-110 duration-300" 
                             style={{ 
                               background: bgColor,
                               border: index > 3 ? '2px dashed #E5E7EB' : 'none',
                               boxShadow: index <= 3 ? `0 0 10px ${colors.primary}20` : 'none'
                             }}>
                          {icon || <span className="text-xs font-medium text-gray-400">{day[0]}</span>}
                        </div>
                        <span className="text-xs">{day}</span>
                      </div>
                    );
                  })}
                </div>
                <div className="mt-4 p-3 rounded-xl shadow-sm flex items-center animate-breathe" 
                     style={{ 
                       background: `linear-gradient(to right, ${colors.primary}20, ${colors.accent}10)`
                     }}>
                  <div className="w-10 h-10 rounded-full flex items-center justify-center mr-3 shadow-sm shimmer" 
                       style={{ 
                         background: `linear-gradient(135deg, ${colors.primary}40, ${colors.primary}20)`
                       }}>
                    <Zap size={18} color={colors.primary} />
                  </div>
                  <p className="text-sm font-medium" style={{ color: colors.primary }}>
                    You've taken 92% of your medications on time this week! That's more consistent than my sleep schedule.
                  </p>
                </div>
              </div>

              {/* Search and filter */}
              <div className="mb-6 flex items-center gap-3">
                <div className="flex-1 relative">
                  <div className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400">
                    <Search size={18} />
                  </div>
                  <input
                    type="text"
                    className="w-full pl-10 pr-4 py-3 rounded-xl border border-blue-100 focus:outline-none focus:ring-2 focus:ring-blue-400 transition-all duration-300"
                    placeholder="Search for your pills... if you remember what they're called"
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                  />
                </div>
                <div className="relative">
                  <button 
                    className="p-3 rounded-xl bg-white border border-blue-100 hover:bg-blue-50 transition-colors duration-300 flex items-center justify-center"
                    onClick={(e) => {
                      e.stopPropagation();
                      setShowFilterOptions(!showFilterOptions);
                    }}
                  >
                    <Filter size={18} style={{ color: colors.textPrimary }} />
                  </button>

                  {/* Filter options popup */}
                  {showFilterOptions && (
                    <div 
                      className="absolute right-0 top-12 w-44 p-2 rounded-xl shadow-xl z-20 border border-blue-100 bg-white"
                      style={{ boxShadow: `0 10px 25px -5px ${colors.primary}30` }}
                      onClick={e => e.stopPropagation()}
                    >
                      <div className="text-xs font-medium mb-1 px-2" style={{ color: colors.textSecondary }}>
                        Filter by
                      </div>
                      <div className="p-2 rounded-lg hover:bg-blue-50 cursor-pointer transition-colors duration-200">
                        <span className="text-sm" style={{ color: colors.textPrimary }}>All medications</span>
                      </div>
                      <div className="p-2 rounded-lg hover:bg-blue-50 cursor-pointer transition-colors duration-200">
                        <span className="text-sm" style={{ color: colors.textPrimary }}>Morning pills</span>
                      </div>
                      <div className="p-2 rounded-lg hover:bg-blue-50 cursor-pointer transition-colors duration-200">
                        <span className="text-sm" style={{ color: colors.textPrimary }}>Evening pills</span>
                      </div>
                      <div className="p-2 rounded-lg hover:bg-blue-50 cursor-pointer transition-colors duration-200">
                        <span className="text-sm" style={{ color: colors.textPrimary }}>Needs refill soon</span>
                      </div>
                    </div>
                  )}
                </div>
                <button 
                  className="p-3 rounded-xl bg-white border border-blue-100 hover:bg-blue-50 transition-colors duration-300 flex items-center justify-center"
                  onClick={() => setView('schedule')}
                >
                  <Calendar size={18} style={{ color: colors.textPrimary }} />
                </button>
              </div>

              {/* Medications section with sticky header */}
              <div className="mb-6">
                <div className="mb-4 sticky top-0 bg-blue-50 z-10 flex justify-between items-center px-4 py-2 rounded-lg">
                  <h3 className="font-bold" style={{ color: colors.textPrimary }}>Today's Medications</h3>
                  <button
                    className="text-xs px-2 py-1 rounded-full transition-colors duration-300"
                    style={{ color: colors.primary, backgroundColor: colors.primary + '20' }}
                    onClick={() => setView('schedule')}
                  >
                    View schedule
                  </button>
                </div>

                {/* Medication list */}
                <div className="space-y-4">
                  {filteredMedications.filter(med => 
                    med.schedule.some(s => s.day === 'today')
                  ).map(med => (
                    <div
                      key={med.id}
                      className="p-4 rounded-2xl bg-white shadow-lg border border-blue-50 flex items-center transform hover:scale-105 transition-all duration-500 hover:shadow-xl cursor-pointer"
                      style={{ boxShadow: `0 10px 15px -3px ${colors.primary}20` }}
                      onClick={() => {
                        setSelectedMedication(med);
                        setView('detail');
                      }}
                    >
                      <div className="w-12 h-12 rounded-xl mr-4 flex items-center justify-center shimmer" 
                          style={{ 
                            background: `linear-gradient(135deg, ${colors.primary}90, ${colors.primary})`
                          }}>
                        <Pill size={22} color="white" />
                      </div>
                      <div className="flex-1">
                        <div className="flex items-center">
                          <h4 className="font-medium" style={{ color: colors.textPrimary }}>{med.name}</h4>
                          <span className="ml-2 px-2 py-0.5 rounded-full text-xs font-medium transition-all duration-300 hover:shadow-sm" 
                                style={{ 
                                  background: `linear-gradient(to right, ${colors.primary}20, ${colors.accent}10)`,
                                  color: colors.primary
                                }}>
                            {med.dosage}
                          </span>
                        </div>
                        <div className="flex mt-1">
                          {med.schedule.map((time, idx) => (
                            <div key={idx} className="flex items-center mr-3">
                              <div 
                                className={`w-2 h-2 rounded-full mr-1`}
                                style={{ 
                                  backgroundColor: time.status === 'taken' 
                                    ? colors.success 
                                    : time.status === 'missed' 
                                      ? colors.danger 
                                      : colors.warning 
                                }}
                              ></div>
                              <p className="text-xs" style={{ color: colors.textSecondary }}>{time.time}</p>
                            </div>
                          ))}
                        </div>
                      </div>
                      <ChevronRight size={20} style={{ color: colors.textSecondary }} />
                    </div>
                  ))}
                </div>
              </div>

              {/* All medications section */}
              <div>
                <div className="mb-4 sticky top-12 bg-blue-50 z-10 flex justify-between items-center px-4 py-2 rounded-lg">
                  <h3 className="font-bold" style={{ color: colors.textPrimary }}>All Medications</h3>
                  <span className="text-xs px-2 py-1 rounded-full" style={{ backgroundColor: colors.primary + '20', color: colors.primary }}>
                    {medications.length} total
                  </span>
                </div>

                <div className="space-y-4">
                  {filteredMedications.map(med => (
                    <div
                      key={med.id}
                      className="p-4 rounded-2xl bg-white shadow-lg border border-blue-50 flex items-center transform hover:scale-105 transition-all duration-300 hover:shadow-xl cursor-pointer"
                      style={{ boxShadow: `0 10px 15px -3px ${colors.primary}20` }}
                      onClick={() => {
                        setSelectedMedication(med);
                        setView('detail');
                      }}
                    >
                      <div className="w-10 h-10 rounded-xl mr-3 flex items-center justify-center" 
                          style={{ 
                            background: `${colors.primary}20`
                          }}>
                        <Pill size={20} style={{ color: colors.primary }} />
                      </div>
                      <div className="flex-1">
                        <div className="flex items-center">
                          <h4 className="font-medium text-sm" style={{ color: colors.textPrimary }}>{med.name}</h4>
                          <span className="ml-2 px-2 py-0.5 rounded-full text-xs font-medium" 
                                style={{ 
                                  background: `${colors.primary}10`,
                                  color: colors.primary
                                }}>
                            {med.dosage}
                          </span>
                        </div>
                        <p className="text-xs mt-0.5" style={{ color: colors.textSecondary }}>{med.purpose}</p>
                      </div>
                      <div className="text-xs px-2 py-1 rounded-full flex items-center" 
                          style={{ 
                            backgroundColor: med.adherenceRate > 90 
                              ? colors.success + '20' 
                              : med.adherenceRate > 80 
                                ? colors.warning + '20' 
                                : colors.danger + '20',
                            color: med.adherenceRate > 90 
                              ? colors.success 
                              : med.adherenceRate > 80 
                                ? colors.warning 
                                : colors.danger
                          }}>
                        <span>{med.adherenceRate}%</span>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            </div>
          </>
        );
    }
  };

  return (
    <div className="p-6 pb-8 overflow-y-auto">
      {renderView()}

      {/* Custom CSS for animations */}
      <style jsx>{`
        @keyframes float {
          0%, 100% { transform: translateY(0); }
          50% { transform: translateY(-10px); }
        }

        @keyframes pulse {
          0%, 100% { opacity: 1; }
          50% { opacity: 0.6; }
        }

        @keyframes breathe {
          0%, 100% { transform: scale(1); }
          50% { transform: scale(1.05); }
        }

        @keyframes shimmer {
          0% { background-position: -100% 0; }
          100% { background-position: 200% 0; }
        }

        .animate-float {
          animation: float 3s ease-in-out infinite;
        }

        .animate-breathe {
          animation: breathe 4s ease-in-out infinite;
        }

        .shimmer {
          background: linear-gradient(90deg, 
            rgba(255,255,255,0) 0%, 
            rgba(255,255,255,0.2) 50%, 
            rgba(255,255,255,0) 100%);
          background-size: 200% 100%;
          animation: shimmer 2s infinite;
        }
      `}</style>
    </div>
  );
};

export default MedicationsScreen;
