// In VideoCallScreen.jsx, replace the existing component with this improved version
// This directly forces the UI to active state when the remote stream exists

import React, { useState, useEffect, useRef } from 'react';
import { useTheme } from '../../ThemeContext';
import webRTCService from '../../services/WebRTCService';
import VideoDisplay from './VideoDisplay';
import CallControls from './CallControls';
import ConnectionStatus from './ConnectionStatus'; 
import CallHeader from './CallHeader';
import ConnectingState from './ConnectingState';
import EndedState from './EndedState';

const VideoCallScreen = ({ isOpen, onClose, colors, doctor }) => {
  const { isDarkMode } = useTheme();

  // State variables
  const [callStatus, setCallStatus] = useState('connecting'); // connecting, active, ended
  const [isMuted, setIsMuted] = useState(false);
  const [isVideoOff, setIsVideoOff] = useState(false);
  const [isSpeakerOff, setIsSpeakerOff] = useState(false);
  const [callDuration, setCallDuration] = useState(0);
  const [showControls, setShowControls] = useState(true);
  const [connectionQuality, setConnectionQuality] = useState('good'); // good, poor, unstable
  const [reconnectAttempt, setReconnectAttempt] = useState(0);
  const [errorMessage, setErrorMessage] = useState('');
  const [audioMonitorCleanup, setAudioMonitorCleanup] = useState(null);
  const [forceDisplay, setForceDisplay] = useState(false); // NEW: Force display flag

  // New state to track queued video changes
  const [videoToggleQueue, setVideoToggleQueue] = useState(null);

  // Refs for video elements
  const localVideoRef = useRef(null);
  const remoteVideoRef = useRef(null);
  const lastMoveTimeRef = useRef(0);
  const callDurationTimer = useRef(null);
  const peerConnectionRef = useRef(null);
  const hasMedia = useRef(false); // NEW: Track if we've received media


  // Log when callStatus changes for debugging
  useEffect(() => {
    console.log("ðŸ”„ CALLSCREEN: Call status changed to:", callStatus);
  }, [callStatus]);

  // Function to safely toggle video state
  const toggleVideo = (videoOff) => {
    console.log('Toggle video called with videoOff:', videoOff);

    if (webRTCService && webRTCService.mediaManager.hasLocalStream()) {
      console.log('Toggling video directly through webRTCService');
      webRTCService.toggleVideo(videoOff);
      setIsVideoOff(videoOff);
    } else {
      console.log('Queueing video toggle for when stream is available');
      setVideoToggleQueue(videoOff);
      setIsVideoOff(videoOff); // Update UI immediately
    }
  };

  // CRITICAL NEW EFFECT: Check remote stream and force active state
  useEffect(() => {
    if (!isOpen || !doctor) return;

    console.log("ðŸ” Checking remote stream and call status...");

    // Define a function to check for remote media
    const checkForRemoteMedia = () => {
      if (remoteVideoRef.current && remoteVideoRef.current.srcObject) {
        const tracks = remoteVideoRef.current.srcObject.getTracks();
        console.log("ðŸŽ¥ Remote video has tracks:", tracks.length);

        if (tracks.length > 0 && callStatus !== 'active') {
          console.log("ðŸš¨ FORCE ACTIVATING CALL - Media detected but UI shows:", callStatus);
          setCallStatus('active');
          hasMedia.current = true;
          setForceDisplay(true);

          // Start call timer if not already started
          if (callDurationTimer.current) {
            clearInterval(callDurationTimer.current);
          }

          callDurationTimer.current = setInterval(() => {
            setCallDuration(prev => prev + 1);
          }, 1000);

          return true;
        }
      }
      return false;
    };

    // Immediate check
    const hasMedia = checkForRemoteMedia();

    if (!hasMedia) {
      // Set up a polling interval to keep checking
      const intervalCheck = setInterval(() => {
        const found = checkForRemoteMedia();
        if (found) {
          clearInterval(intervalCheck);
        }
      }, 500);

      // Also set a timeout to force active state after 5 seconds if we have a connection
      const forceActiveTimeout = setTimeout(() => {
        if (callStatus === 'connecting' && webRTCService && webRTCService.isCallActive()) {
          console.log("â±ï¸ TIMEOUT FORCE: Connection active but UI still connecting after 5s");
          setCallStatus('active');
          setForceDisplay(true);
        }
      }, 5000);

      return () => {
        clearInterval(intervalCheck);
        clearTimeout(forceActiveTimeout);
      };
    }
  }, [isOpen, doctor, callStatus, remoteVideoRef.current]);

  // Initialize or clean up WebRTC when component mounts/unmounts
  useEffect(() => {
    if (isOpen && doctor) {
      console.log('VideoCallScreen opened for doctor:', doctor.name);

      // Set up WebRTC callbacks
      const callbacks = {
        onLocalStream: (stream) => {
          console.log('Got local stream with tracks:', stream.getTracks().map(t => t.kind).join(','));
          if (localVideoRef.current) {
            localVideoRef.current.srcObject = stream;
            console.log('Local video stream set to element');
          } else {
            console.warn('localVideoRef is not available to set stream');
          }

          // Apply any queued video toggle
          if (videoToggleQueue !== null) {
            webRTCService.toggleVideo(videoToggleQueue);
            setVideoToggleQueue(null);
          }
        },

        onRemoteStream: (stream) => {
          console.log('Got remote stream with tracks:', stream.getTracks().map(t => t.kind).join(','));
          if (remoteVideoRef.current) {
            remoteVideoRef.current.srcObject = stream;
            console.log('Remote video stream set to element');

            // Ensure volume is correct based on speaker state
            remoteVideoRef.current.muted = isSpeakerOff;

            // IMPORTANT: Force call to active state when we have remote media
            if (stream.getTracks().length > 0) {
              console.log('ðŸ”´ Remote media detected, forcing call to active state');
              hasMedia.current = true;
              setCallStatus('active');
              setForceDisplay(true);

              // Start call timer
              if (callDurationTimer.current) {
                clearInterval(callDurationTimer.current);
              }

              callDurationTimer.current = setInterval(() => {
                setCallDuration(prev => prev + 1);
              }, 1000);
            }
          } else {
            console.warn('remoteVideoRef is not available to set stream');
          }

          const cleanup = monitorAudioLevel(stream);
          if (cleanup) setAudioMonitorCleanup(cleanup);
        },

        onCallStarted: () => {
          console.log('Call is now active');
          setCallStatus('active');
          setReconnectAttempt(0);
          setErrorMessage('');

          // Store peer connection reference for stats
          if (webRTCService.connectionManager && webRTCService.connectionManager.peerConnection) {
            peerConnectionRef.current = webRTCService.connectionManager.peerConnection;
          }

          // Start call timer
          if (callDurationTimer.current) {
            clearInterval(callDurationTimer.current);
          }

          callDurationTimer.current = setInterval(() => {
            setCallDuration(prev => prev + 1);
          }, 1000);
        },

        onCallEnded: () => {
          console.log('Call has ended');
          setCallStatus('ended');

          if (callDurationTimer.current) {
            clearInterval(callDurationTimer.current);
          }

          setTimeout(() => {
            onClose();
          }, 2000);
        },

        onCallConnecting: () => {
          console.log('Call is connecting...');
          setCallStatus('connecting');
        },

        onUserNotOnline: (userId) => {
          console.log('User not online:', userId);
          setErrorMessage('Doctor is not available right now');
          setCallStatus('ended');

          setTimeout(() => {
            onClose();
          }, 3000);
        },

        onICEConnectionStateChange: (state) => {
          console.log('Connection state changed:', state);

          if (state === 'checking') {
            setConnectionQuality('unstable');
          } else if (state === 'connected' || state === 'completed') {
            setConnectionQuality('good');
            // FORCE ACTIVATE when ICE is connected
            setTimeout(() => {
              setCallStatus('active');
              setForceDisplay(true);
            }, 500);
          } else if (state === 'disconnected') {
            setConnectionQuality('poor');
            setReconnectAttempt(prev => prev + 1);
          } else if (state === 'failed') {
            setConnectionQuality('unstable');
            setReconnectAttempt(prev => prev + 1);

            // Attempt reconnection after ice connection failure
            if (webRTCService && typeof webRTCService.attemptReconnection === 'function') {
              try {
                webRTCService.attemptReconnection(true);
              } catch (error) {
                console.error('Failed to attempt reconnection:', error);
              }
            }
          }
        }
      };

      // Subscribe to call state changes
      const unsubscribeCallState = webRTCService.on('callStateChanged', (state) => {
        console.log('Call state changed to:', state);
        setCallStatus(state);

        if (state === 'active') {
          setForceDisplay(true);
        }
      });

      // Subscribe to errors
      const unsubscribeError = webRTCService.on('error', (error) => {
        console.log('WebRTC error received:', error);
        setErrorMessage(error.message || 'Something went wrong with the call');

        if (error.fatal) {
          setCallStatus('ended');
        }
      });

      webRTCService.updateCallbacks(callbacks);

      // Try to get local media access early if it hasn't been obtained yet
      if (!webRTCService.mediaManager.hasLocalStream()) {
        console.log('Requesting camera and microphone access');
        webRTCService.getLocalMedia(true, true).catch(error => {
          console.error('Failed to get media access:', error);
          setErrorMessage(`Camera/microphone access failed: ${error.message}`);
        });
      }

      return () => {
        console.log('Cleaning up video call resources');

        unsubscribeCallState();
        unsubscribeError();

        if (callDurationTimer.current) {
          clearInterval(callDurationTimer.current);
          callDurationTimer.current = null;
        }

        if (audioMonitorCleanup) {
          audioMonitorCleanup();
        }

        if (localVideoRef.current && localVideoRef.current.srcObject) {
          const stream = localVideoRef.current.srcObject;
          stream.getTracks().forEach(track => {
            track.stop();
          });
          localVideoRef.current.srcObject = null;
        }

        if (remoteVideoRef.current && remoteVideoRef.current.srcObject) {
          remoteVideoRef.current.srcObject = null;
        }

        webRTCService.endCall(true);
      };
    }
  }, [isOpen, doctor, onClose, videoToggleQueue]);

  // Monitor WebRTC stats for connection quality
  useEffect(() => {
    if (peerConnectionRef.current && callStatus === 'active') {
      // Monitor network stats instead of just audio levels
      const monitorConnectionQuality = () => {
        const statsInterval = setInterval(async () => {
          try {
            const stats = await peerConnectionRef.current.getStats();
            let packetLoss = 0;
            let jitter = 0;
            let roundTripTime = 0;
            let statsCount = 0;

            stats.forEach(report => {
              if (report.type === 'inbound-rtp' && report.kind === 'video') {
                if (report.packetsLost && report.packetsReceived) {
                  packetLoss = report.packetsLost / (report.packetsLost + report.packetsReceived);
                }
                if (report.jitter) {
                  jitter = report.jitter;
                }
                statsCount++;
              }
              if (report.type === 'remote-inbound-rtp') {
                if (report.roundTripTime) {
                  roundTripTime = report.roundTripTime;
                }
                statsCount++;
              }
            });

            if (statsCount > 0) {
              // Calculate connection quality based on multiple factors
              if (packetLoss > 0.1 || jitter > 50 || roundTripTime > 500) {
                setConnectionQuality('poor');
              } else if (packetLoss > 0.05 || jitter > 30 || roundTripTime > 300) {
                setConnectionQuality('unstable');
              } else {
                setConnectionQuality('good');
              }
            }
          } catch (error) {
            console.error('Error getting connection stats:', error);
          }
        }, 2000);

        return () => clearInterval(statsInterval);
      };

      const cleanup = monitorConnectionQuality();
      return cleanup;
    }
  }, [peerConnectionRef.current, callStatus]);

  // Monitor remote stream for audio level changes
  useEffect(() => {
    if (remoteVideoRef.current && remoteVideoRef.current.srcObject) {
      const cleanup = monitorAudioLevel(remoteVideoRef.current.srcObject);
      if (cleanup) setAudioMonitorCleanup(cleanup);
    }
  }, [remoteVideoRef.current?.srcObject]);

  // Update remote video muted state when isSpeakerOff changes
  useEffect(() => {
    if (remoteVideoRef.current) {
      remoteVideoRef.current.muted = isSpeakerOff;
    }
  }, [isSpeakerOff]);

  // Function to monitor audio levels for connection quality checks
  const monitorAudioLevel = (stream) => {
    if (!window.AudioContext && !window.webkitAudioContext) {
      return null;
    }

    try {
      const AudioContext = window.AudioContext || window.webkitAudioContext;
      const audioContext = new AudioContext();
      const analyser = audioContext.createAnalyser();
      const microphone = audioContext.createMediaStreamSource(stream);
      const javascriptNode = audioContext.createScriptProcessor(2048, 1, 1);

      microphone.connect(analyser);
      analyser.connect(javascriptNode);
      javascriptNode.connect(audioContext.destination);

      let lowAudioCounter = 0;
      const LOW_AUDIO_THRESHOLD = 10;

      javascriptNode.onaudioprocess = () => {
        const array = new Uint8Array(analyser.frequencyBinCount);
        analyser.getByteFrequencyData(array);
        const values = array.reduce((a, b) => a + b, 0) / array.length;

        if (values < 5 && callStatus === 'active' && connectionQuality !== 'poor') {
          lowAudioCounter++;
          if (lowAudioCounter >= LOW_AUDIO_THRESHOLD && callDuration > 5) {
            // Only downgrade to poor if not already poor from network stats
            setConnectionQuality(prevQuality => 
              prevQuality === 'unstable' ? 'poor' : prevQuality);
          }
        } else {
          lowAudioCounter = 0;
          // Don't automatically set to good here, defer to the stats-based quality
        }
      };

      return () => {
        try {
          javascriptNode.disconnect();
          analyser.disconnect();
          microphone.disconnect();
          if (audioContext.state !== 'closed') {
            audioContext.close();
          }
        } catch (error) {
          console.error('Error cleaning up audio monitoring:', error);
        }
      };
    } catch (error) {
      console.error('Error setting up audio monitoring:', error);
      return null;
    }
  };

  // Update WebRTC when audio mute state changes
  useEffect(() => {
    if (webRTCService) {
      webRTCService.toggleAudio(isMuted);
    }
  }, [isMuted]);

  // Update WebRTC when video state changes
  useEffect(() => {
    if (webRTCService && webRTCService.mediaManager.hasLocalStream()) {
      webRTCService.toggleVideo(isVideoOff);
    }
  }, [isVideoOff]);

  // Handle key press to end call
  useEffect(() => {
    const handleKeyDown = (e) => {
      if (e.key === 'Escape' && isOpen) {
        handleEndCall();
      }
    };

    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, [isOpen]);

  // Mouse movement handler to show/hide controls
  useEffect(() => {
    let timer;

    const handleMouseMove = () => {
      const now = Date.now();
      if (callStatus === 'active' && now - lastMoveTimeRef.current > 150) {
        lastMoveTimeRef.current = now;

        if (!showControls) {
          setShowControls(true);
        }

        clearTimeout(timer);
        timer = setTimeout(() => {
          setShowControls(false);
        }, 5000);
      }
    };

    window.addEventListener('mousemove', handleMouseMove);
    return () => {
      window.removeEventListener('mousemove', handleMouseMove);
      clearTimeout(timer);
    };
  }, [callStatus, showControls]);

  // Format seconds to MM:SS
  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  // Handle ending call
  const handleEndCall = () => {
    console.log('User clicked end call button');
    webRTCService.endCall(true);
  };

  // CRITICAL: Regularly check if there's a remote stream and force active state
  useEffect(() => {
    if (!isOpen) return;

    const forceCheckInterval = setInterval(() => {
      // First check if we have remote media directly
      const hasRemoteTracks = remoteVideoRef.current?.srcObject?.getTracks().length > 0;

      // Then check if WebRTC service thinks we're in a call
      const serviceIsActive = webRTCService?.isCallActive();

      console.log(`Force check - Remote tracks: ${hasRemoteTracks}, Service active: ${serviceIsActive}, UI state: ${callStatus}`);

      if ((hasRemoteTracks || serviceIsActive) && callStatus !== 'active') {
        console.log('âš¡ FORCE CHECK: WebRTC active but UI not showing active state');
        setCallStatus('active');
        setForceDisplay(true);
      }
    }, 1000);

    return () => clearInterval(forceCheckInterval);
  }, [isOpen, callStatus]);

  if (!isOpen) return null;

  // CRITICAL: Modified rendering logic - prioritize showing VideoDisplay when we have media
  const shouldShowVideo = callStatus === 'active' || hasMedia.current || forceDisplay || 
    (remoteVideoRef.current && remoteVideoRef.current.srcObject && 
     remoteVideoRef.current.srcObject.getTracks().length > 0);

  console.log(`Rendering decision - callStatus: ${callStatus}, hasMedia: ${hasMedia.current}, forceDisplay: ${forceDisplay}`);

  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center"
         style={{ backgroundColor: 'rgba(0,0,0,0.85)', backdropFilter: 'blur(10px)' }}>
      <div className="w-full h-full max-w-md relative overflow-hidden">

        {shouldShowVideo ? (
          // Always show video display when we have media, regardless of call status
          <VideoDisplay
            remoteVideoRef={remoteVideoRef}
            localVideoRef={localVideoRef}
            isSpeakerOff={isSpeakerOff}
            isVideoOff={isVideoOff}
            doctor={doctor}
            connectionQuality={connectionQuality}
            reconnectAttempt={reconnectAttempt}
            showControls={showControls}
            isDarkMode={isDarkMode}
            colors={colors}
          >
            <CallHeader 
              doctor={doctor}
              callDuration={callDuration}
              formatTime={formatTime}
              showControls={showControls}
            />

            <ConnectionStatus
              connectionQuality={connectionQuality}
              showControls={showControls}
            />
          </VideoDisplay>
        ) : callStatus === 'ended' ? (
          <EndedState 
            doctor={doctor}
            errorMessage={errorMessage} 
            callDuration={callDuration} 
            formatTime={formatTime}
            colors={colors}
            isDarkMode={isDarkMode}
          />
        ) : (
          <ConnectingState doctor={doctor} colors={colors} />
        )}

        <CallControls
          isMuted={isMuted}
          setIsMuted={setIsMuted}
          isVideoOff={isVideoOff}
          setIsVideoOff={toggleVideo} 
          isSpeakerOff={isSpeakerOff}
          setIsSpeakerOff={setIsSpeakerOff}
          handleEndCall={handleEndCall}
          showControls={showControls}
          colors={colors}
          callStatus={callStatus}
        />
      </div>

      <style jsx>{`
        @keyframes pulse {
          0%, 100% { opacity: 0.5; transform: scale(1); }
          50% { opacity: 1; transform: scale(1.2); }
        }
      `}</style>
    </div>
  );
};

export default VideoCallScreen;
