import React, { useState, useEffect } from 'react';
import { Menu, MessageCircle, Video, Phone, Search, Bell, ChevronRight, Clock, Shield } from "lucide-react";

// Create enhanced DoctorCard component directly in this file
const DoctorCard = ({ name, specialty, image, availability, colors, rating }) => {
  const isAvailableNow = availability.includes('Available') && !availability.includes('in');
  const [isHovered, setIsHovered] = useState(false);

  return (
    <div 
      className="p-4 rounded-2xl bg-white shadow-lg border border-blue-50 min-w-[180px] transform transition-all duration-500 hover:shadow-xl"
      style={{ 
        boxShadow: isHovered 
          ? `0 20px 25px -5px ${colors.primary}30, 0 10px 10px -5px ${colors.accent}20` 
          : `0 10px 15px -3px ${colors.primary}20`,
        transform: isHovered ? 'translateY(-8px) scale(1.02)' : 'translateY(0) scale(1)'
      }}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      <div className="flex items-center mb-3">
        <div className="w-14 h-14 rounded-full shadow-md mr-3 relative overflow-hidden transition-all duration-300"
            style={{ 
              boxShadow: `0 0 15px ${colors.primary}30`,
              transform: isHovered ? 'scale(1.05)' : 'scale(1)'
            }}>
          <img src={image} alt={name} className="w-full h-full rounded-full object-cover" />
          <div className="absolute bottom-0 right-0 w-4 h-4 rounded-full border-2 border-white animate-pulse" 
              style={{ backgroundColor: isAvailableNow ? colors.success : colors.warning }}></div>
        </div>
        <div>
          <h4 className="font-semibold text-sm" style={{ color: colors.textPrimary }}>{name}</h4>
          <p className="text-xs" style={{ color: colors.textSecondary }}>{specialty}</p>
          {rating && (
            <div className="flex items-center mt-1">
              {[...Array(5)].map((_, i) => (
                <div key={i} className="w-2 h-2 rounded-full mr-1" 
                    style={{ backgroundColor: i < rating ? colors.primary : colors.background }}></div>
              ))}
            </div>
          )}
        </div>
      </div>
      <div className="flex items-center mb-3">
        <div className="w-3 h-3 mr-2 rounded-full animate-pulse" 
            style={{ backgroundColor: isAvailableNow ? colors.success : colors.warning }}></div>
        <span className="text-xs font-medium" 
            style={{ color: isAvailableNow ? colors.success : colors.warning }}>
          {availability}
        </span>
      </div>
      <button 
        className="w-full p-2 rounded-lg text-xs font-medium shadow-md transition-all duration-300 hover:shadow-lg transform hover:-translate-y-1" 
        style={{ 
          background: isAvailableNow 
            ? `linear-gradient(135deg, ${colors.primary}, ${colors.accent})`
            : `linear-gradient(135deg, ${colors.background}, white)`,
          color: isAvailableNow ? 'white' : colors.primary,
          border: isAvailableNow ? 'none' : `1px solid ${colors.primary}30`,
          boxShadow: isAvailableNow 
            ? `0 8px 15px -3px ${colors.primary}40` 
            : `0 4px 6px -1px ${colors.primary}20`
        }}
      >
        {isAvailableNow ? 'Connect Now' : 'Schedule'}
      </button>
    </div>
  );
};

// Enhanced Message Card component
const MessageCard = ({ doctor, message, time, unread, colors }) => {
  const [isHovered, setIsHovered] = useState(false);

  return (
    <div 
      className="p-4 rounded-2xl bg-white shadow-lg border border-blue-50 transition-all duration-500 relative overflow-hidden"
      style={{ 
        boxShadow: isHovered 
          ? `0 15px 25px -5px ${colors.primary}30` 
          : `0 10px 15px -3px ${colors.primary}20`,
        transform: isHovered ? 'translateX(5px)' : 'translateX(0)',
        borderLeft: unread ? `4px solid ${colors.primary}` : '4px solid transparent'
      }}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      {unread && (
        <div className="absolute top-0 right-0">
          <div className="w-3 h-3 m-2 rounded-full animate-pulse" 
              style={{ backgroundColor: colors.primary }}></div>
        </div>
      )}
      <div className="flex items-center mb-2">
        <div className="w-10 h-10 rounded-full shadow-md mr-3 relative overflow-hidden flex items-center justify-center"
             style={{ 
               background: `linear-gradient(135deg, ${colors.primary}40, ${colors.accent}30)`,
               boxShadow: `0 0 15px ${colors.primary}30` 
             }}>
          <span className="text-sm font-bold" style={{ color: colors.primary }}>{doctor.split(' ')[1][0]}</span>
        </div>
        <div className="flex-1">
          <h4 className="font-semibold text-sm" style={{ color: colors.textPrimary }}>{doctor}</h4>
          <p className="text-xs" style={{ color: colors.textSecondary }}>{time}</p>
        </div>
        <ChevronRight size={18} style={{ color: colors.textSecondary, opacity: isHovered ? 1 : 0.5 }} />
      </div>
      <p className="text-sm ml-13 pl-13" style={{ 
        color: unread ? colors.textPrimary : colors.textSecondary,
        fontWeight: unread ? '500' : '400',
        marginLeft: '3.25rem' 
      }}>{message}</p>
    </div>
  );
};

// Quick Actions component
const QuickAction = ({ icon, label, colors, gradient }) => {
  const [isHovered, setIsHovered] = useState(false);

  const renderIcon = () => {
    switch(icon) {
      case 'message': return <MessageCircle size={28} color="white" />;
      case 'video': return <Video size={28} color="white" />;
      case 'phone': return <Phone size={28} color="white" />;
      case 'shield': return <Shield size={28} color="white" />;
      default: return <MessageCircle size={28} color="white" />;
    }
  };

  return (
    <button 
      className="p-5 rounded-2xl bg-white shadow-lg flex flex-col items-center transition-all duration-500"
      style={{ 
        boxShadow: isHovered 
          ? `0 20px 25px -5px ${colors.primary}30, 0 10px 10px -5px ${colors.accent}20` 
          : `0 15px 25px -5px ${colors.primary}20`,
        transform: isHovered ? 'translateY(-5px)' : 'translateY(0)'
      }}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      <div className="w-16 h-16 rounded-full mb-3 flex items-center justify-center shadow-md transition-all duration-500 overflow-hidden relative" 
           style={{ 
             background: gradient,
             boxShadow: isHovered 
              ? `0 0 20px ${colors.primary}50` 
              : `0 0 15px ${colors.primary}30`
           }}>
        {renderIcon()}

        {/* Animated gradient overlay */}
        <div className="absolute inset-0 opacity-0 transition-opacity duration-500"
             style={{ 
               opacity: isHovered ? 0.3 : 0, 
               background: 'linear-gradient(45deg, transparent 25%, rgba(255,255,255,0.4) 50%, transparent 75%)',
               backgroundSize: '200% 200%',
               animation: 'shimmer 2s infinite linear'
             }}></div>
      </div>
      <span className="text-sm font-medium transition-all duration-500" 
            style={{ 
              color: isHovered ? colors.primary : colors.textPrimary,
              transform: isHovered ? 'scale(1.05)' : 'scale(1)'
            }}>{label}</span>
    </button>
  );
};

// Main ConnectScreen component
const ConnectScreen = ({ colors, setActiveScreen }) => {
  const [searchFocused, setSearchFocused] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const [notifications, setNotifications] = useState(3);
  const [filterActive, setFilterActive] = useState(false);

  // Animation for header
  const [headerVisible, setHeaderVisible] = useState(false);
  useEffect(() => {
    const timer = setTimeout(() => setHeaderVisible(true), 100);
    return () => clearTimeout(timer);
  }, []);

  return (
    <div className="p-6 pb-24">
      {/* Enhanced header with animations */}
      <header className={`flex justify-between items-center mb-8 transition-all duration-700 ${headerVisible ? 'opacity-100 translate-y-0' : 'opacity-0 -translate-y-4'}`}>
        <div>
          <h1 className="text-3xl font-bold" 
              style={{ 
                background: `linear-gradient(to right, ${colors.primary}, ${colors.accent})`,
                WebkitBackgroundClip: 'text',
                WebkitTextFillColor: 'transparent',
                backgroundClip: 'text',
                textFillColor: 'transparent'
              }}>Connect</h1>
          <p className="text-sm" style={{ color: colors.textSecondary }}>Your health team at your fingertips</p>
        </div>
        <div className="flex items-center space-x-3">
          <button 
            className="w-10 h-10 rounded-full flex items-center justify-center shadow-lg transition-transform hover:scale-110 duration-300 relative"
            style={{ 
              background: notifications > 0
                ? `linear-gradient(135deg, ${colors.accent}, ${colors.accentAlt})`
                : `linear-gradient(135deg, ${colors.primaryLight}40, ${colors.primaryLight}20)`,
              boxShadow: notifications > 0
                ? `0 0 15px ${colors.accent}40`
                : `0 0 10px ${colors.primary}20`
            }}
            onClick={() => setNotifications(0)}
          >
            <Bell size={20} color={notifications > 0 ? "white" : colors.textSecondary} />
            {notifications > 0 && (
              <div className="absolute -top-1 -right-1 w-5 h-5 rounded-full bg-red-500 flex items-center justify-center">
                <span className="text-xs text-white font-bold">{notifications}</span>
              </div>
            )}
          </button>
          <button className="w-10 h-10 rounded-full flex items-center justify-center shadow-lg transition-transform hover:scale-110 duration-300" 
            style={{ 
              background: `linear-gradient(135deg, ${colors.primary}, ${colors.accent})`,
              boxShadow: `0 0 15px ${colors.primary}40`
            }}>
            <Menu size={20} color="white" />
          </button>
        </div>
      </header>

      {/* Search bar with animations */}
      <div className={`mb-6 transition-all duration-500 transform ${headerVisible ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-4'}`} style={{ transitionDelay: '100ms' }}>
        <div className="relative">
          <div className={`absolute left-4 top-1/2 transform -translate-y-1/2 transition-all duration-300`}
               style={{ color: searchFocused ? colors.primary : colors.textSecondary }}>
            <Search size={18} />
          </div>
          <input
            type="text"
            placeholder="Search doctors, messages..."
            className="w-full py-3 pl-12 pr-4 rounded-xl transition-all duration-300"
            style={{
              backgroundColor: searchFocused ? 'white' : `${colors.primary}05`,
              border: searchFocused ? `2px solid ${colors.primary}30` : `1px solid ${colors.primary}10`,
              boxShadow: searchFocused ? `0 10px 15px -3px ${colors.primary}20` : 'none',
              color: colors.textPrimary,
              outline: 'none',
            }}
            onFocus={() => setSearchFocused(true)}
            onBlur={() => setSearchFocused(false)}
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
          {searchQuery && (
            <button 
              className="absolute right-4 top-1/2 transform -translate-y-1/2"
              onClick={() => setSearchQuery('')}
              style={{ color: colors.textSecondary }}
            >
              Ã—
            </button>
          )}
        </div>
      </div>

      {/* Filter chips */}
      <div className={`flex space-x-2 mb-6 transition-all duration-500 ${headerVisible ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-4'}`} style={{ transitionDelay: '200ms' }}>
        <button 
          className="px-4 py-2 rounded-full text-xs font-medium transition-all duration-300 flex items-center"
          style={{
            backgroundColor: filterActive ? colors.primary : `${colors.primary}10`,
            color: filterActive ? 'white' : colors.primary,
            boxShadow: filterActive ? `0 8px 15px -3px ${colors.primary}40` : 'none'
          }}
          onClick={() => setFilterActive(!filterActive)}
        >
          <Clock size={14} className="mr-1" />
          Available now
        </button>
        <button className="px-4 py-2 rounded-full text-xs font-medium" 
          style={{ backgroundColor: `${colors.primary}10`, color: colors.primary }}>
          Primary Care
        </button>
        <button className="px-4 py-2 rounded-full text-xs font-medium" 
          style={{ backgroundColor: `${colors.primary}10`, color: colors.primary }}>
          Specialists
        </button>
      </div>

      {/* Care team with enhanced card animations */}
      <div className={`mb-8 transition-all duration-500 ${headerVisible ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-4'}`} style={{ transitionDelay: '300ms' }}>
        <div className="flex justify-between items-center mb-4">
          <h3 className="font-bold text-lg" style={{ color: colors.textPrimary }}>Your Care Team</h3>
          <button className="text-sm font-medium px-3 py-1 rounded-full transition-all duration-300 hover:shadow-md" 
                  style={{ color: colors.primary, backgroundColor: `${colors.primary}10` }}>
            View all
          </button>
        </div>

        <div className="flex overflow-x-auto pb-4 -mx-2 px-2 space-x-4 hide-scrollbar">
          <DoctorCard
            name="Dr. Johnson"
            specialty="Cardiologist"
            image="/User_1.png"
            availability="Available now"
            rating={5}
            colors={colors}
          />
          <DoctorCard
            name="Dr. Smith"
            specialty="Primary Care"
            image="/User_2.jpg"
            availability="Available in 15m"
            rating={4}
            colors={colors}
          />
          <DoctorCard
            name="Dr. Garcia"
            specialty="Endocrinologist"
            image="/User_3.png"
            availability="Available tomorrow"
            rating={5}
            colors={colors}
          />
          <DoctorCard
            name="Dr. Chen"
            specialty="Neurologist"
            image="/User_4.png"
            availability="Available in 2h"
            rating={4}
            colors={colors}
          />
        </div>
      </div>

      {/* Communication options with enhanced gradient backgrounds and hover effects */}
      <div className={`mb-8 transition-all duration-500 ${headerVisible ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-4'}`} style={{ transitionDelay: '400ms' }}>
        <h3 className="font-bold text-lg mb-4" style={{ color: colors.textPrimary }}>Quick Connect</h3>
        <div className="grid grid-cols-2 gap-4">
          <QuickAction 
            icon="message" 
            label="Message" 
            colors={colors} 
            gradient={`linear-gradient(135deg, ${colors.primary}, ${colors.accent})`}
          />
          <QuickAction 
            icon="video" 
            label="Video Call" 
            colors={colors} 
            gradient={`linear-gradient(135deg, ${colors.accentAlt}, ${colors.accent})`}
          />
          <QuickAction 
            icon="phone" 
            label="Phone Call" 
            colors={colors} 
            gradient={`linear-gradient(135deg, ${colors.success}, ${colors.accent})`}
          />
          <QuickAction 
            icon="shield" 
            label="Emergency" 
            colors={colors} 
            gradient={`linear-gradient(135deg, ${colors.danger}, ${colors.accentAlt})`}
          />
        </div>
      </div>

      {/* Recent messages with enhanced card design and animations */}
      <div className={`transition-all duration-500 ${headerVisible ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-4'}`} style={{ transitionDelay: '500ms' }}>
        <div className="flex justify-between items-center mb-4">
          <h3 className="font-bold text-lg" style={{ color: colors.textPrimary }}>Recent Messages</h3>
          <button className="text-sm font-medium px-3 py-1 rounded-full transition-all duration-300 hover:shadow-md" 
                  style={{ color: colors.primary, backgroundColor: `${colors.primary}10` }}>
            View all
          </button>
        </div>

        <div className="space-y-4">
          <MessageCard
            doctor="Dr. Johnson"
            message="Your latest blood pressure readings look good. Keep up the good work with the diet modifications we discussed!"
            time="Today, 10:23 AM"
            unread={true}
            colors={colors}
          />
          <MessageCard
            doctor="Dr. Smith"
            message="I've adjusted your medication schedule based on your recent feedback. Please review the changes in your medication section."
            time="Yesterday, 4:15 PM"
            unread={false}
            colors={colors}
          />
          <MessageCard
            doctor="Nurse Williams"
            message="How are you feeling after the new medication? Any side effects to report?"
            time="Mar 20, 9:30 AM"
            unread={false}
            colors={colors}
          />
        </div>
      </div>

      {/* Custom CSS for animations */}
      <style jsx>{`
        @keyframes shimmer {
          0% { background-position: -100% 0; }
          100% { background-position: 200% 0; }
        }

        .hide-scrollbar::-webkit-scrollbar {
          display: none;
        }

        .hide-scrollbar {
          -ms-overflow-style: none;
          scrollbar-width: none;
        }
      `}</style>
    </div>
  );
};

export default ConnectScreen;
