import React, { useState } from 'react';
import { 
  ArrowLeft, PlusCircle, X, Heart, 
  Clock, AlertTriangle, Sun, Moon
} from "lucide-react";
import { useTheme } from '../ThemeContext';

// Component for adding a new symptom with dark mode support
const AddSymptomForm = ({ setView, addSymptom, colors }) => {
  const { isDarkMode } = useTheme();

  // Form state
  const [formData, setFormData] = useState({
    symptom: '',
    severity: 'Mild',
    time: new Date().toLocaleTimeString('en-US', { hour: '2-digit', minute: '2-digit' }),
    date: new Date().toLocaleDateString('en-US', { month: 'long', day: 'numeric', year: 'numeric' }),
    duration: '',
    notes: '',
    triggers: [],
    associatedSymptoms: [],
    timeOfDay: 'Morning'
  });

  // Update form fields
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  // Add a new trigger
  const [newTrigger, setNewTrigger] = useState('');

  const addTrigger = () => {
    if (newTrigger.trim()) {
      setFormData(prev => ({
        ...prev,
        triggers: [...prev.triggers, newTrigger]
      }));
      setNewTrigger('');
    }
  };

  // Remove a trigger
  const removeTrigger = (index) => {
    setFormData(prev => ({
      ...prev,
      triggers: prev.triggers.filter((_, idx) => idx !== index)
    }));
  };

  // Add a new associated symptom
  const [newAssociatedSymptom, setNewAssociatedSymptom] = useState('');

  const addAssociatedSymptom = () => {
    if (newAssociatedSymptom.trim()) {
      setFormData(prev => ({
        ...prev,
        associatedSymptoms: [...prev.associatedSymptoms, newAssociatedSymptom]
      }));
      setNewAssociatedSymptom('');
    }
  };

  // Remove an associated symptom
  const removeAssociatedSymptom = (index) => {
    setFormData(prev => ({
      ...prev,
      associatedSymptoms: prev.associatedSymptoms.filter((_, idx) => idx !== index)
    }));
  };

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();

    // Format the time for display consistency
    const formattedData = {
      ...formData,
      // Add any additional formatting here
    };

    // Add the new symptom
    addSymptom(formattedData);
  };

  // Dark mode specific styles
  const cardBg = isDarkMode ? colors.cardBg : 'white';
  const inputBg = isDarkMode ? colors.darkBg : 'white';
  const inputBorder = isDarkMode ? `${colors.primary}30` : 'rgba(219, 234, 254, 1)';
  const inputText = isDarkMode ? colors.textPrimary : 'inherit';
  const hoverBgColor = isDarkMode ? colors.primary + '20' : 'rgba(239, 246, 255, 1)';
  const buttonHoverBg = isDarkMode ? colors.primary + '30' : 'rgba(219, 234, 254, 0.7)';

  // Map for severity colors
  const severityColors = {
    'Mild': colors.success,
    'Moderate': colors.warning,
    'Severe': colors.danger
  };

  return (
    <>
      <header className="flex items-center mb-8">
        <button 
          className="w-10 h-10 rounded-full flex items-center justify-center mr-4 hover:bg-blue-50 transition-colors duration-300 dark-mode-transition"
          onClick={() => setView('main')}
          style={{ '&:hover': { backgroundColor: hoverBgColor } }}
        >
          <ArrowLeft size={24} style={{ color: colors.textPrimary }} />
        </button>
        <div>
          <h1 className="text-2xl font-bold dark-mode-transition" style={{ color: colors.textPrimary }}>Log Symptom</h1>
          <p className="text-sm dark-mode-transition" style={{ color: colors.textSecondary }}>Track what your body's telling you</p>
        </div>
      </header>

      <div className="mb-8 p-6 rounded-2xl shadow-lg border dark-mode-transition"
           style={{ 
             backgroundColor: cardBg, 
             borderColor: inputBorder,
             boxShadow: `0 10px 15px -3px ${colors.gradientAlt1}20` 
           }}>
        <form className="space-y-6" onSubmit={handleSubmit}>
          {/* Symptom name */}
          <div>
            <label className="block text-sm font-medium mb-2 dark-mode-transition" style={{ color: colors.textPrimary }}>
              Symptom
            </label>
            <input 
              type="text"
              name="symptom"
              value={formData.symptom}
              onChange={handleChange}
              className="w-full p-3 rounded-xl border focus:outline-none focus:ring-2 focus:ring-blue-400 transition-all duration-300 dark-mode-transition"
              style={{ 
                backgroundColor: inputBg, 
                borderColor: inputBorder,
                color: inputText
              }}
              placeholder="What's bothering you? (e.g., Headache, Nausea)"
              required
            />
          </div>

          {/* Severity and Time */}
          <div className="grid grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium mb-2 dark-mode-transition" style={{ color: colors.textPrimary }}>
                Severity
              </label>
              <select 
                name="severity"
                value={formData.severity}
                onChange={handleChange}
                className="w-full p-3 rounded-xl border focus:outline-none focus:ring-2 focus:ring-blue-400 transition-all duration-300 dark-mode-transition"
                style={{ 
                  backgroundColor: inputBg, 
                  borderColor: inputBorder,
                  color: inputText
                }}
                required
              >
                <option value="Mild">Mild</option>
                <option value="Moderate">Moderate</option>
                <option value="Severe">Severe</option>
              </select>
            </div>
            <div>
              <label className="block text-sm font-medium mb-2 dark-mode-transition" style={{ color: colors.textPrimary }}>
                Time
              </label>
              <input 
                type="time"
                name="time"
                value={formData.time}
                onChange={handleChange}
                className="w-full p-3 rounded-xl border focus:outline-none focus:ring-2 focus:ring-blue-400 transition-all duration-300 dark-mode-transition"
                style={{ 
                  backgroundColor: inputBg, 
                  borderColor: inputBorder,
                  color: inputText
                }}
                required
              />
            </div>
          </div>

          {/* Duration & Time of Day */}
          <div className="grid grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium mb-2 dark-mode-transition" style={{ color: colors.textPrimary }}>
                Duration
              </label>
              <input 
                type="text"
                name="duration"
                value={formData.duration}
                onChange={handleChange}
                className="w-full p-3 rounded-xl border focus:outline-none focus:ring-2 focus:ring-blue-400 transition-all duration-300 dark-mode-transition"
                style={{ 
                  backgroundColor: inputBg, 
                  borderColor: inputBorder,
                  color: inputText
                }}
                placeholder="e.g., 2 hours, 30 minutes"
              />
            </div>
            <div>
              <label className="block text-sm font-medium mb-2 dark-mode-transition" style={{ color: colors.textPrimary }}>
                Time of Day
              </label>
              <select 
                name="timeOfDay"
                value={formData.timeOfDay}
                onChange={handleChange}
                className="w-full p-3 rounded-xl border focus:outline-none focus:ring-2 focus:ring-blue-400 transition-all duration-300 dark-mode-transition"
                style={{ 
                  backgroundColor: inputBg, 
                  borderColor: inputBorder,
                  color: inputText
                }}
              >
                <option value="Morning">Morning</option>
                <option value="Afternoon">Afternoon</option>
                <option value="Evening">Evening</option>
                <option value="Night">Night</option>
              </select>
            </div>
          </div>

          {/* Notes */}
          <div>
            <label className="block text-sm font-medium mb-2 dark-mode-transition" style={{ color: colors.textPrimary }}>
              Notes
            </label>
            <textarea 
              name="notes"
              value={formData.notes}
              onChange={handleChange}
              className="w-full p-3 rounded-xl border focus:outline-none focus:ring-2 focus:ring-blue-400 transition-all duration-300 dark-mode-transition"
              style={{ 
                backgroundColor: inputBg, 
                borderColor: inputBorder,
                color: inputText
              }}
              placeholder="Describe your symptom (e.g., throbbing pain, location, what makes it better/worse)"
              rows="3"
            ></textarea>
          </div>

          {/* Triggers */}
          <div>
            <label className="block text-sm font-medium mb-2 dark-mode-transition" style={{ color: colors.textPrimary }}>
              Possible Triggers
            </label>
            <div className="flex items-center mb-2">
              <input 
                type="text"
                value={newTrigger}
                onChange={(e) => setNewTrigger(e.target.value)}
                className="flex-1 p-3 rounded-l-xl border focus:outline-none focus:ring-2 focus:ring-blue-400 transition-all duration-300 dark-mode-transition"
                style={{ 
                  backgroundColor: inputBg, 
                  borderColor: inputBorder,
                  color: inputText
                }}
                placeholder="Add potential triggers (e.g., stress, food)"
              />
              <button 
                type="button"
                className="p-3 rounded-r-xl border-t border-r border-b transition-colors duration-300 dark-mode-transition"
                style={{ 
                  backgroundColor: `${colors.primary}10`,
                  borderColor: inputBorder,
                  color: colors.primary,
                  '&:hover': { backgroundColor: `${colors.primary}20` }
                }}
                onClick={addTrigger}
              >
                <PlusCircle size={20} />
              </button>
            </div>

            <div className="flex flex-wrap gap-2">
              {formData.triggers.map((trigger, idx) => (
                <div 
                  key={idx} 
                  className="px-3 py-1.5 rounded-full flex items-center text-sm dark-mode-transition"
                  style={{ 
                    backgroundColor: `${colors.primary}15`,
                    color: colors.primary
                  }}
                >
                  <span className="mr-1">{trigger}</span>
                  <button 
                    type="button" 
                    className="w-4 h-4 rounded-full flex items-center justify-center hover:bg-red-500 transition-colors duration-300"
                    onClick={() => removeTrigger(idx)}
                  >
                    <X size={12} />
                  </button>
                </div>
              ))}
            </div>
          </div>

          {/* Associated Symptoms */}
          <div>
            <label className="block text-sm font-medium mb-2 dark-mode-transition" style={{ color: colors.textPrimary }}>
              Associated Symptoms
            </label>
            <div className="flex items-center mb-2">
              <input 
                type="text"
                value={newAssociatedSymptom}
                onChange={(e) => setNewAssociatedSymptom(e.target.value)}
                className="flex-1 p-3 rounded-l-xl border focus:outline-none focus:ring-2 focus:ring-blue-400 transition-all duration-300 dark-mode-transition"
                style={{ 
                  backgroundColor: inputBg, 
                  borderColor: inputBorder,
                  color: inputText
                }}
                placeholder="Add other symptoms (e.g., nausea with headache)"
              />
              <button 
                type="button"
                className="p-3 rounded-r-xl border-t border-r border-b transition-colors duration-300 dark-mode-transition"
                style={{ 
                  backgroundColor: `${colors.primary}10`,
                  borderColor: inputBorder,
                  color: colors.primary,
                  '&:hover': { backgroundColor: `${colors.primary}20` }
                }}
                onClick={addAssociatedSymptom}
              >
                <PlusCircle size={20} />
              </button>
            </div>

            <div className="flex flex-wrap gap-2">
              {formData.associatedSymptoms.map((symptom, idx) => (
                <div 
                  key={idx} 
                  className="px-3 py-1.5 rounded-full flex items-center text-sm dark-mode-transition"
                  style={{ 
                    backgroundColor: `${colors.accent}15`,
                    color: colors.accent
                  }}
                >
                  <span className="mr-1">{symptom}</span>
                  <button 
                    type="button" 
                    className="w-4 h-4 rounded-full flex items-center justify-center hover:bg-red-500 transition-colors duration-300"
                    onClick={() => removeAssociatedSymptom(idx)}
                  >
                    <X size={12} />
                  </button>
                </div>
              ))}
            </div>
          </div>

          {/* Submit button */}
          <button 
            type="submit"
            className="w-full py-4 rounded-xl text-white font-medium transition-all duration-500 hover:shadow-xl transform hover:-translate-y-1"
            style={{ 
              background: `linear-gradient(to right, ${colors.gradientAlt1}, ${colors.gradientAlt2})`,
              boxShadow: `0 10px 15px -3px ${colors.gradientAlt1}40`
            }}
          >
            Save Symptom
          </button>
        </form>
      </div>
    </>
  );
};

export default AddSymptomForm;
