import React, { useState, useEffect } from 'react';
import { 
  Activity, Calendar, MessageCircle, Heart, AlertTriangle, 
  Clock, Video, ChevronRight, CheckCircle, Trophy, ArrowUpRight, Zap, Bell
} from 'lucide-react';

// Import shared components
import ProgressRing from '../shared/ProgressRing';
import ActionButton from '../shared/ActionButton';
import BiorhythmPreview from '../shared/BiorhythmPreview';

// Import biorhythm engine for direct access if needed
import { biorhythmEngine } from '../services/BiorhythmEngine';

// Enhanced Dashboard Screen with improved biorhythm integration and animations
const DashboardScreen = ({ colors, setActiveScreen }) => {
  // State for tracking if biorhythm data is loaded
  const [biorhythmLoaded, setBiorhythmLoaded] = useState(false);

  // Load biorhythm data when component mounts
  useEffect(() => {
    // Pre-cache biorhythm calculations for faster rendering
    biorhythmEngine.getRecommendations();
    setBiorhythmLoaded(true);
  }, []);

  return (
    <div className="p-6 pb-8 dark-mode-transition">
      <header className="flex justify-between items-center mb-8">
        <div>
          <h1 className="text-3xl font-bold bg-gradient-to-r from-blue-700 to-cyan-500 bg-clip-text text-transparent dark-mode-transition">Hi, Sarah!</h1>
          <p className="text-sm dark-mode-transition" style={{ color: colors.textSecondary }}>Thursday, March 22</p>
        </div>
        <div className="relative">
          <div className="absolute -inset-1 rounded-full animate-pulse" 
               style={{ background: `radial-gradient(circle, ${colors.neonAccent}50, transparent 70%)` }}></div>
          <button className="relative w-12 h-12 rounded-full bg-white shadow-lg flex items-center justify-center transition-transform hover:scale-110 duration-300 dark-mode-transition"
                  style={{ 
                    boxShadow: `0 0 20px ${colors.primary}30`,
                    backgroundColor: colors.cardBg 
                  }}>
            <Bell size={24} style={{ color: colors.primary }} className="transition-transform hover:scale-110 duration-300" />
            <span className="absolute -top-1 -right-1 w-5 h-5 bg-red-500 rounded-full flex items-center justify-center text-white text-xs font-bold animate-pulse">3</span>
          </button>
        </div>
      </header>

      {/* Health Score Card - Enhanced with 3D hover effect */}
      <div className="mb-8 p-6 rounded-2xl relative overflow-hidden transform transition-all duration-500 hover:scale-105 hover:shadow-2xl group dark-mode-transition" 
           style={{ 
             background: `linear-gradient(135deg, ${colors.primary}, ${colors.accent})`,
             boxShadow: `0 20px 25px -5px ${colors.primary}50`
           }}>
        <div className="absolute top-0 right-0 w-32 h-32 rounded-full animate-float group-hover:animate-pulse" 
             style={{ 
               background: `radial-gradient(circle, ${colors.primaryLight}50, transparent 70%)`,
               transform: 'translate(30%, -30%)'
             }}></div>
        <div className="absolute bottom-0 left-0 w-24 h-24 rounded-full animate-float" 
             style={{ 
               background: `radial-gradient(circle, ${colors.primaryLight}30, transparent 70%)`,
               transform: 'translate(-30%, 30%)',
               animationDelay: '1s'
             }}></div>
        <div className="absolute top-1/2 left-1/3 w-16 h-16 rounded-full animate-float" 
             style={{ 
               background: `radial-gradient(circle, ${colors.primaryLight}20, transparent 70%)`,
               transform: 'translate(-50%, -50%)',
               animationDelay: '1.5s'
             }}></div>
        <div className="relative flex items-center justify-between">
          <div>
            <p className="text-white text-opacity-90 text-sm font-medium mb-2">Today's Health Score</p>
            <h2 className="text-4xl font-bold text-white flex items-baseline">
              87<span className="text-xl ml-1">/100</span>
              <span className="ml-3 flex items-center text-sm font-medium bg-white bg-opacity-20 py-1 px-2 rounded-full backdrop-blur-sm">
                <ArrowUpRight size={14} className="mr-1" /> 5%
              </span>
            </h2>
            <p className="text-white text-opacity-80 text-xs mt-1">You're doing better than yesterday!</p>
          </div>
          <div className="w-20 h-20 rounded-full bg-white bg-opacity-20 backdrop-blur-sm flex items-center justify-center p-1 transform transition-transform hover:scale-110 duration-300 group-hover:rotate-12">
            <div className="w-full h-full rounded-full bg-white flex items-center justify-center shimmer">
              <Activity size={32} color={colors.primary} />
            </div>
          </div>
        </div>
      </div>

      {/* Enhanced Biorhythm Preview with loading transition */}
      <div className={`transition-opacity duration-500 ${biorhythmLoaded ? 'opacity-100' : 'opacity-0'}`}>
        <BiorhythmPreview colors={colors} setActiveScreen={setActiveScreen} />
      </div>
      <br></br>

      {/* Upcoming events with enhanced floating cards and hover effects */}
      <div className="mb-8">
        <div className="flex justify-between items-center mb-4">
          <h3 className="font-bold text-lg mb-2 dark-mode-transition" style={{ color: colors.textPrimary }}>Upcoming</h3>
          <button className="text-sm font-medium px-3 py-1 rounded-full transition-all duration-300 hover:shadow-md dark-mode-transition" 
                  style={{ 
                    color: colors.primary, 
                    backgroundColor: `${colors.primary}10`,
                    transform: 'translateZ(0)'
                  }}>
            View all
          </button>
        </div>

        <div className="space-y-4">
          <div className="p-4 rounded-2xl bg-white shadow-lg border border-blue-50 flex items-center transform hover:scale-105 transition-all duration-500 hover:shadow-xl dark-mode-transition"
               style={{ 
                 boxShadow: `0 10px 15px -3px ${colors.primary}20`,
                 backgroundColor: colors.cardBg,
                 borderColor: `${colors.primary}20`
               }}>
            <div className="w-12 h-12 rounded-xl mr-4 flex items-center justify-center shimmer" 
                 style={{ 
                   background: `linear-gradient(135deg, ${colors.primary}90, ${colors.primary})` 
                 }}>
              <Calendar size={22} color="white" />
            </div>
            <div className="flex-1">
              <h4 className="font-medium text-base dark-mode-transition" style={{ color: colors.textPrimary }}>Metformin 500mg</h4>
              <p className="text-xs dark-mode-transition" style={{ color: colors.textSecondary }}>In 30 minutes</p>
            </div>
            <div className="w-10 h-10 rounded-full flex items-center justify-center shadow-md transition-transform hover:scale-110 duration-300" 
                 style={{ backgroundColor: `${colors.success}20`, color: colors.success }}>
              <CheckCircle size={18} />
            </div>
          </div>

          <div className="p-4 rounded-2xl bg-white shadow-lg border border-blue-50 flex items-center transform hover:scale-105 transition-all duration-500 hover:shadow-xl dark-mode-transition"
               style={{ 
                 boxShadow: `0 10px 15px -3px ${colors.primary}20`,
                 backgroundColor: colors.cardBg,
                 borderColor: `${colors.primary}20`
               }}>
            <div className="w-12 h-12 rounded-xl mr-4 flex items-center justify-center shimmer" 
                 style={{ 
                   background: `linear-gradient(135deg, ${colors.accent}90, ${colors.accent})` 
                 }}>
              <Video size={22} color="white" />
            </div>
            <div className="flex-1">
              <h4 className="font-medium text-base dark-mode-transition" style={{ color: colors.textPrimary }}>Dr. Johnson Appointment</h4>
              <p className="text-xs dark-mode-transition" style={{ color: colors.textSecondary }}>Today, 3:00 PM</p>
            </div>
            <button className="w-10 h-10 rounded-full flex items-center justify-center shadow-md transition-transform hover:scale-110 duration-300 dark-mode-transition" 
                    style={{ 
                      backgroundColor: `${colors.primary}15`,
                      color: colors.primary
                    }}>
              <ChevronRight size={18} />
            </button>
          </div>
        </div>
      </div>

      {/* Quick actions with enhanced gradient and hover effects */}
      <div className="mb-8">
        <h3 className="font-bold text-lg mb-4 dark-mode-transition" style={{ color: colors.textPrimary }}>Quick Actions</h3>
        <div className="grid grid-cols-3 gap-4">
          <ActionButton 
            label="Log Symptoms" 
            icon={<Heart size={22} />} 
            gradient={`linear-gradient(135deg, ${colors.danger}, ${colors.gradientAlt2})`}
            onClick={() => setActiveScreen('symptoms')} 
          />
          <ActionButton 
            label="Medications" 
            icon={<Calendar size={22} />} 
            gradient={`linear-gradient(135deg, ${colors.primary}, ${colors.accent})`}
            onClick={() => setActiveScreen('medications')} 
          />
          <ActionButton 
            label="Biorhythms" 
            icon={<Clock size={22} />} 
            gradient={`linear-gradient(135deg, ${colors.accentAlt}, ${colors.accent})`}
            onClick={() => setActiveScreen('biorhythm')} 
          />
          <ActionButton 
            label="Health Info" 
            icon={<Zap size={22} />} 
            gradient={`linear-gradient(135deg, ${colors.success}, ${colors.accent})`}
            onClick={() => setActiveScreen('education')} 
          />
          <ActionButton 
            label="Emergency" 
            icon={<AlertTriangle size={22} />} 
            gradient={`linear-gradient(135deg, ${colors.danger}, ${colors.warning})`}
            onClick={() => setActiveScreen('emergency')} 
          />
          <ActionButton 
            label="Connect" 
            icon={<MessageCircle size={22} />} 
            gradient={`linear-gradient(135deg, ${colors.primary}, ${colors.success})`}
            onClick={() => setActiveScreen('connect')} 
          />
        </div>
      </div>

      {/* Health insight with enhanced card design and animations */}
      <div>
        <div className="flex justify-between items-center mb-4">
          <h3 className="font-bold text-lg dark-mode-transition" style={{ color: colors.textPrimary }}>Health Insights</h3>
          <button className="text-sm font-medium px-3 py-1 rounded-full transition-all duration-300 hover:shadow-md dark-mode-transition" 
                  style={{ color: colors.primary, backgroundColor: `${colors.primary}10` }}>
            View all
          </button>
        </div>

        <div className="p-5 rounded-2xl bg-white shadow-lg border border-blue-50 transform hover:scale-105 transition-all duration-500 hover:shadow-xl dark-mode-transition"
             style={{ 
               boxShadow: `0 15px 25px -5px ${colors.primary}20`,
               backgroundColor: colors.cardBg,
               borderColor: `${colors.primary}20`
             }}>
          <div className="flex items-start">
            <div className="w-12 h-12 rounded-xl mr-4 flex items-center justify-center shadow-md shimmer" 
                 style={{ 
                   background: `linear-gradient(135deg, ${colors.warning}, ${colors.gradientAlt2})`
                 }}>
              <Zap size={22} color="white" />
            </div>
            <div>
              <h4 className="font-bold text-base mb-1 dark-mode-transition" style={{ color: colors.textPrimary }}>Biorhythm Insight</h4>
              <p className="text-sm dark-mode-transition" style={{ color: colors.textSecondary }}>
                Your energy levels peak at 2pm today. Schedule your workout then for maximum results.
              </p>
              <button className="mt-3 text-sm font-medium px-4 py-1.5 rounded-lg transition-all duration-300 hover:shadow-lg transform hover:-translate-y-1" 
                      style={{ 
                        background: `linear-gradient(to right, ${colors.primary}, ${colors.accent})`,
                        color: 'white',
                        boxShadow: `0 4px 6px -1px ${colors.primary}30`
                      }}>
                Plan my day
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Custom CSS for enhanced animations */}
      <style jsx>{`
        @keyframes float {
          0%, 100% { transform: translateY(0); }
          50% { transform: translateY(-10px); }
        }
        @keyframes pulse {
          0%, 100% { opacity: 1; }
          50% { opacity: 0.6; }
        }
        @keyframes breathe {
          0%, 100% { transform: scale(1); }
          50% { transform: scale(1.05); }
        }
        @keyframes shimmer {
          0% { background-position: -100% 0; }
          100% { background-position: 200% 0; }
        }
        .animate-float {
          animation: float 3s ease-in-out infinite;
        }
        .animate-breathe {
          animation: breathe 4s ease-in-out infinite;
        }
        .shimmer {
          background: linear-gradient(90deg, 
            rgba(255,255,255,0) 0%, 
            rgba(255,255,255,0.2) 50%, 
            rgba(255,255,255,0) 100%);
          background-size: 200% 100%;
          animation: shimmer 2s infinite;
        }
      `}</style>
    </div>
  );
};

export default DashboardScreen;
