    this.callbacks = {
      ...this.callbacks,
      ...callbacks
    };
  }

  /**
   * Set pending offer for incoming call
   * @param {RTCSessionDescriptionInit} offer - SDP offer
   */
  setPendingOffer(offer) {
    this.pendingOffer = offer;
  }

