import React, { useState, useEffect } from 'react';
import { 
  Clock, 
  ArrowRight, 
  Activity, 
  Zap, 
  Coffee, 
  Utensils, 
  Moon, 
  Sun,
  ChevronUp,
  ChevronDown
} from 'lucide-react';

// Import biorhythm engine (simulated here - use your actual import)
import { biorhythmEngine, getSassyRecommendation } from './BiorhythmEngine';

const EnhancedBiorhythmPreview = ({ colors, setActiveScreen }) => {
  // State for biorhythm data and UI
  const [bioData, setBioData] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [isExpanded, setIsExpanded] = useState(false);
  const [sassyComment, setSassyComment] = useState("");

  // Get current hour and date
  const now = new Date();
  const currentHour = now.getHours();

  // Load biorhythm data on component mount
  useEffect(() => {
    // Generate a random sassy comment in case the real one fails
    const defaultSassyComments = [
      "Your biorhythm today looks better than your dating history.",
      "Your mental energy is peaking. Too bad it's just for scrolling social media.",
      "Physical energy is low. Perfect excuse to skip that workout again.",
      "Emotional peak at 8pm - ideal for both deep conversations or ugly crying to reality TV."
    ];
    setSassyComment(defaultSassyComments[Math.floor(Math.random() * defaultSassyComments.length)]);

    // Load data with a slight delay to create smoother transition
    const timer = setTimeout(() => {
      try {
        const data = biorhythmEngine.getRecommendations();
        setBioData(data);
        // Update with real sassy comment from engine
        setSassyComment(getSassyRecommendation(now));
      } catch (error) {
        console.error("Error loading biorhythm data:", error);
        // Keep using the random sassy comment we already set
      } finally {
        setIsLoading(false);
      }
    }, 600);

    return () => clearTimeout(timer);
  }, []);

  // Get relevant biorhythm data from the engine
  const getBioScores = () => {
    if (!bioData) return {
      physical: 65, 
      emotional: 78,
      intellectual: 52,
      metabolic: 70
    };

    return {
      physical: bioData.currentScore.physical,
      emotional: bioData.currentScore.emotional,
      intellectual: bioData.currentScore.intellectual,
      metabolic: bioData.currentScore.metabolic
    };
  };

  // Get the dominant rhythm with icon and name
  const getDominantRhythm = () => {
    if (!bioData) return { 
      name: 'Physical', 
      icon: <Activity size={16} color={colors.success} />,
      description: "Your body's ready to crush a workout! Time to make gym-bros jealous." 
    };

    const { type } = bioData?.dominantRhythm || { type: 'physical' };

    switch(type) {
      case 'physical':
        return { 
          name: 'Physical', 
          icon: <Activity size={16} color={colors.success} />,
          color: colors.success,
          description: "Your body is at peak performance. Perfect for workouts or, you know, walking to the fridge."
        };
      case 'emotional':
        return { 
          name: 'Emotional', 
          icon: <Zap size={16} color={colors.accentAlt} />,
          color: colors.accentAlt,
          description: "Peak emotional intelligence activated. Ideal for deep talks or dramatically overreacting to minor inconveniences."
        };
      case 'intellectual':
        return { 
          name: 'Mental', 
          icon: <Coffee size={16} color={colors.primary} />,
          color: colors.primary,
          description: "Your brain is firing on all cylinders! Perfect time to solve complex problems or finally understand TikTok trends."
        };
      case 'metabolic':
        return { 
          name: 'Metabolic', 
          icon: <Utensils size={16} color={colors.warning} />,
          color: colors.warning,
          description: "Metabolism on overdrive. Ideal time to eat that meal you've been thinking about all day."
        };
      default:
        return { 
          name: 'Physical', 
          icon: <Activity size={16} color={colors.success} />,
          color: colors.success,
          description: "Your body is at peak performance. Perfect for workouts or, you know, walking to the fridge."
        };
    }
  };

  // Get the next biorhythm transition info
  const getNextTransition = () => {
    if (!bioData || !bioData.nextTransition) return { 
      hoursFromNow: 3, 
      to: 'Emotional' 
    };

    return bioData.nextTransition;
  };

  // Generate time blocks for visualization
  const generateTimeBlocks = () => {
    if (!bioData) return Array(24).fill(0.5);

    return bioData.hourlyData.map(hour => hour.overall);
  };

  // Format activity time windows for display
  const getFormattedActivities = () => {
    if (!bioData) return [];

    const formatTime = (hour) => {
      return hour === 0 ? "12 AM" : 
             hour === 12 ? "12 PM" : 
             hour < 12 ? `${hour} AM` : `${hour-12} PM`;
    };

    const getNextWindow = (hours) => {
      if (!hours || hours.length === 0) return "None today";

      const nextHours = hours.filter(h => h > currentHour);
      if (nextHours.length === 0) return "Tomorrow";

      return formatTime(nextHours[0]);
    };

    return [
      {
        name: "Exercise",
        next: getNextWindow(bioData.recommendations.exerciseTiming),
        icon: <Activity size={14} />,
        color: colors.success
      },
      {
        name: "Focus Work",
        next: getNextWindow(bioData.recommendations.focusTiming),
        icon: <Coffee size={14} />,
        color: colors.primary
      },
      {
        name: "Social Time", 
        next: getNextWindow(bioData.recommendations.exerciseTiming),
        icon: <Zap size={14} />,
        color: colors.accentAlt
      }
    ];
  };

  const bioScores = getBioScores();
  const dominantRhythm = getDominantRhythm();
  const nextTransition = getNextTransition();
  const timeBlocks = generateTimeBlocks();
  const activities = getFormattedActivities();

  return (
    <div 
      className={`mb-8 p-5 rounded-2xl bg-white shadow-lg border border-blue-50 hover:shadow-xl transition-all duration-500 transform ${isExpanded ? 'scale-102' : 'hover:scale-102'} overflow-hidden relative`}
      style={{ boxShadow: `0 15px 25px -5px ${colors.primary}20` }}
    >
      {/* Floating background elements */}
      <div className="absolute inset-0 pointer-events-none overflow-hidden">
        <div className="absolute top-10 right-10 w-40 h-40 rounded-full opacity-5 animate-float"
             style={{ 
               background: `radial-gradient(circle, ${colors.primary}, transparent 70%)`,
               animation: 'float 15s ease-in-out infinite'
             }}></div>
        <div className="absolute -bottom-10 -left-10 w-40 h-40 rounded-full opacity-5 animate-float-delayed"
             style={{ 
               background: `radial-gradient(circle, ${colors.accentAlt}, transparent 70%)`,
               animation: 'float 12s ease-in-out 2s infinite'
             }}></div>
      </div>

      <div className="flex items-center justify-between mb-3 z-10 relative">
        <h3 className="font-bold text-lg" style={{ color: colors.textPrimary }}>Biorhythm Now</h3>
        <div className="flex items-center">
          <button 
            onClick={() => setIsExpanded(!isExpanded)}
            className="text-sm font-medium px-2 py-1 rounded-full transition-all duration-300 hover:bg-blue-50 mr-2 flex items-center" 
            style={{ color: colors.primary }}>
            {isExpanded ? 
              <><ChevronUp size={16} className="mr-1" /> Less</> : 
              <><ChevronDown size={16} className="mr-1" /> More</>
            }
          </button>
          <button 
            onClick={() => setActiveScreen('biorhythm')}
            className="text-sm font-medium px-3 py-1 rounded-full transition-all duration-300 hover:shadow-md flex items-center group" 
            style={{ color: colors.primary, backgroundColor: `${colors.primary}10` }}>
            View Details
            <ArrowRight size={16} className="ml-1 group-hover:translate-x-1 transition-transform duration-300" />
          </button>
        </div>
      </div>

      <div className="flex items-center mb-4">
        <Clock size={16} className="mr-2" style={{ color: colors.textSecondary }} />
        <span className="text-xs" style={{ color: colors.textSecondary }}>
          Current Time: {now.toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'})}
        </span>
      </div>

      {isLoading ? (
        // Skeleton loading state with pulse animation
        <div className="animate-pulse space-y-3">
          <div className="grid grid-cols-4 gap-2 mb-4">
            {[...Array(4)].map((_, i) => (
              <div key={i} className="flex flex-col items-center">
                <div className="w-12 h-12 rounded-full bg-blue-100 mb-1"></div>
                <div className="w-10 h-2 bg-blue-100 rounded"></div>
              </div>
            ))}
          </div>
          <div className="h-12 bg-blue-100 rounded-xl mb-3"></div>
          <div className="h-4 bg-blue-100 rounded w-3/4"></div>
        </div>
      ) : (
        <>
          {/* Biorhythm visualization */}
          <div className="mb-4 h-16 relative">
            <div className="absolute inset-x-0 top-0 h-full flex items-end">
              {timeBlocks.map((value, idx) => {
                const isCurrent = idx === currentHour;
                const height = `${Math.max(15, value * 100)}%`;

                return (
                  <div key={idx} className="flex-1 flex items-end mx-px">
                    <div 
                      className={`w-full rounded-t transition-all duration-300 ${isCurrent ? 'bg-blue-500' : 'bg-blue-300'}`}
                      style={{ 
                        height, 
                        opacity: isCurrent ? 1 : 0.7 * value + 0.3,
                        transform: isCurrent ? 'scaleY(1.1)' : 'scaleY(1)'
                      }}
                    >
                      {/* Show time marker for current hour */}
                      {isCurrent && (
                        <div className="absolute -top-6 left-1/2 transform -translate-x-1/2 whitespace-nowrap">
                          <div className="px-2 py-1 rounded-full text-xs font-medium bg-blue-500 text-white">
                            {idx === 0 ? '12 AM' : 
                             idx === 12 ? '12 PM' : 
                             idx < 12 ? `${idx} AM` : `${idx-12} PM`}
                          </div>
                        </div>
                      )}
                    </div>
                  </div>
                );
              })}
            </div>

            {/* Time markers */}
            <div className="absolute bottom-0 left-0 w-0.5 h-1.5 bg-blue-200"></div>
            <div className="absolute bottom-0 left-1/4 w-0.5 h-1.5 bg-blue-200"></div>
            <div className="absolute bottom-0 left-1/2 w-0.5 h-1.5 bg-blue-200"></div>
            <div className="absolute bottom-0 left-3/4 w-0.5 h-1.5 bg-blue-200"></div>
            <div className="absolute bottom-0 right-0 w-0.5 h-1.5 bg-blue-200"></div>
          </div>

          <div className="flex justify-between text-xs mb-4" style={{ color: colors.textSecondary }}>
            <span>12 AM</span>
            <span>6 AM</span>
            <span>12 PM</span>
            <span>6 PM</span>
            <span>12 AM</span>
          </div>

          {/* Current state and upcoming transitions */}
          <div className="p-4 rounded-xl mb-4 relative overflow-hidden transform hover:scale-102 transition-all duration-300 animate-breathe" 
              style={{ 
                background: `linear-gradient(135deg, ${dominantRhythm.color}40, ${dominantRhythm.color}10)`,
                boxShadow: `0 5px 15px -3px ${dominantRhythm.color}20`
              }}>
            <div className="absolute -right-12 -bottom-12 w-32 h-32 rounded-full opacity-10"
                 style={{ background: `radial-gradient(circle, ${dominantRhythm.color}, transparent 70%)` }}></div>

            <div className="flex items-start">
              <div className="w-10 h-10 rounded-full flex items-center justify-center mr-3 shimmer" 
                  style={{ 
                    background: `linear-gradient(135deg, ${dominantRhythm.color}, ${dominantRhythm.color}70)`,
                    boxShadow: `0 0 15px ${dominantRhythm.color}30`
                  }}>
                {React.cloneElement(dominantRhythm.icon, { size: 20, color: 'white' })}
              </div>
              <div>
                <div className="flex items-center">
                  <h4 className="font-bold text-base mr-2" style={{ color: dominantRhythm.color }}>
                    {dominantRhythm.name} Peak
                  </h4>
                  <span className="text-xs px-2 py-0.5 rounded-full bg-white bg-opacity-20">
                    {bioScores[dominantRhythm.name.toLowerCase()]}%
                  </span>
                </div>
                <p className="text-sm" style={{ color: colors.textPrimary }}>
                  {dominantRhythm.description}
                </p>

                <div className="mt-1 flex items-center">
                  <span className="text-xs" style={{ color: colors.textSecondary }}>
                    Next change: 
                  </span>
                  <span className="ml-1 text-xs font-medium" style={{ color: colors.textPrimary }}>
                    {nextTransition.hoursFromNow}h to {nextTransition.to} peak
                  </span>
                </div>
              </div>
            </div>
          </div>

          {/* Sassy comment */}
          <div className="p-3 rounded-xl bg-gradient-to-r from-blue-50 to-indigo-50 mb-4">
            <p className="text-sm italic" style={{ color: colors.textSecondary }}>{sassyComment}</p>
          </div>

          {/* Optional expanded content */}
          {isExpanded && (
            <div className="pt-2 border-t border-blue-100 mt-4 animate-fade-in">
              <h4 className="font-medium text-sm mb-3" style={{ color: colors.textPrimary }}>Optimal Times Today</h4>
              <div className="grid grid-cols-3 gap-3">
                {activities.map((activity, idx) => (
                  <div key={idx} className="p-3 rounded-lg bg-blue-50 hover:bg-blue-100 transition-colors duration-300">
                    <div className="flex items-center mb-1">
                      <div className="w-5 h-5 rounded-full flex items-center justify-center mr-1.5" 
                           style={{ backgroundColor: `${activity.color}20`, color: activity.color }}>
                        {activity.icon}
                      </div>
                      <span className="text-xs font-medium" style={{ color: colors.textPrimary }}>{activity.name}</span>
                    </div>
                    <div className="text-xs font-bold" style={{ color: activity.color }}>
                      Next: {activity.next}
                    </div>
                  </div>
                ))}
              </div>

              <div className="grid grid-cols-4 gap-2 mt-4">
                {[
                  { name: 'Physical', score: bioScores.physical, color: colors.success, icon: <Activity size={14} /> },
                  { name: 'Emotional', score: bioScores.emotional, color: colors.accentAlt, icon: <Zap size={14} /> },
                  { name: 'Mental', score: bioScores.intellectual, color: colors.primary, icon: <Coffee size={14} /> },
                  { name: 'Metabolic', score: bioScores.metabolic, color: colors.warning, icon: <Utensils size={14} /> }
                ].map((rhythm, index) => (
                  <div key={index} className="flex flex-col items-center bg-blue-50 rounded-lg p-2 hover:bg-blue-100 transition-colors duration-300">
                    <div className="w-10 h-10 rounded-full flex items-center justify-center mb-1 shadow-sm"
                        style={{ 
                          background: `conic-gradient(${rhythm.color} ${rhythm.score}%, #f1f5f9 0)`,
                          boxShadow: `0 0 10px ${rhythm.color}20`
                        }}>
                      <div className="w-6 h-6 rounded-full bg-white flex items-center justify-center">
                        <div style={{ color: rhythm.color }}>{rhythm.icon}</div>
                      </div>
                    </div>
                    <span className="text-xs text-center" style={{ color: colors.textSecondary }}>{rhythm.name}</span>
                    <span className="text-xs font-bold" style={{ color: rhythm.color }}>{rhythm.score}%</span>
                  </div>
                ))}
              </div>
            </div>
          )}
        </>
      )}

      <style jsx>{`
        @keyframes float {
          0%, 100% { transform: translateY(0); }
          50% { transform: translateY(-15px); }
        }
        @keyframes float-delayed {
          0%, 100% { transform: translateY(0); }
          50% { transform: translateY(-10px); }
        }
        @keyframes breathe {
          0%, 100% { transform: scale(1); }
          50% { transform: scale(1.01); }
        }
        @keyframes shimmer {
          0% { background-position: -100% 0; }
          100% { background-position: 200% 0; }
        }
        @keyframes fade-in {
          from { opacity: 0; transform: translateY(10px); }
          to { opacity: 1; transform: translateY(0); }
        }
        .animate-breathe {
          animation: breathe 4s ease-in-out infinite;
        }
        .animate-float {
          animation: float 15s ease-in-out infinite;
        }
        .animate-float-delayed {
          animation: float 12s ease-in-out 2s infinite;
        }
        .animate-fade-in {
          animation: fade-in 0.4s ease-out forwards;
        }
        .shimmer {
          background: linear-gradient(90deg, 
            rgba(255,255,255,0) 0%, 
            rgba(255,255,255,0.3) 50%, 
            rgba(255,255,255,0) 100%);
          background-size: 200% 100%;
          animation: shimmer 2s infinite;
        }
      `}</style>
    </div>
  );
};

export default EnhancedBiorhythmPreview;
