      console.log('Operation in progress, please wait...');
      return;
    }

    if (!twoFactorEnabled) {
      // Initialize 2FA setup
      setIsLoading(true);
      try {
        console.log('Enrolling in 2FA...');

        // Call the enrollment method
        const result = await enrollMFA();
        console.log('Enrollment result:', result);

        if (!result.success) {
          throw new Error(result.error || 'Failed to set up two-factor authentication');
        }

        // Store the data for the setup process
        setFactorId(result.factorId);
        setQrCodeUrl(result.qrCode);
        setSecretKey(result.secret);

        // Open the setup screen
        setTwoFactorSetupOpen(true);
      } catch (error) {
        console.error('Error setting up 2FA:', error);

        // More detailed error message
