        // Verify we have a valid factorId before proceeding
        if (!factorId) {
          throw new Error('Missing factor ID. Cannot disable 2FA without a valid factor ID.');
        }

        // Call the unenrollment method
        const result = await unenrollMFA(factorId);
        console.log('Unenrollment result:', result);

        if (!result.success) {
          throw new Error(result.error || 'Failed to disable two-factor authentication');
        }

        // Update state
        setTwoFactorEnabled(false);
        setFactorId('');

        // Update user profile in the database
        console.log('Updating user profile...');
        const updateResult = await updateUserProfile({
          twoFactorEnabled: false,
          factorId: null
        });

        console.log('Profile update result:', updateResult);

        if (!updateResult.success) {
          console.warn('Profile update failed, but 2FA was disabled:', updateResult.error);
