        console.error('Error disabling 2FA:', error);

        // More helpful error message
        setPasswordError(`Failed to disable 2FA: ${error.message}. Please try again or contact support if the issue persists.`);

        // Reset the toggle to its previous state
        setTwoFactorEnabled(true);
      } finally {
        setIsLoading(false);
      }
