import React from 'react';
import { 
  PlusCircle, Search, Filter, Calendar, ChevronRight, 
  Pill, Zap, CheckCircle, Clock, X
} from "lucide-react";

// Medication list component - displays all medications and handles search/filtering
const MedicationList = ({ 
  medications, 
  setSelectedMedication, 
  setView, 
  searchQuery, 
  setSearchQuery, 
  showAddOptions, 
  setShowAddOptions, 
  showFilterOptions, 
  setShowFilterOptions, 
  colors 
}) => {
  // Handler for add medication button click - prevents propagation to avoid immediate popup close
  const handleAddButtonClick = (e) => {
    e.stopPropagation();
    setShowAddOptions(!showAddOptions);
  };

  // Handler for filter button click - prevents propagation
  const handleFilterButtonClick = (e) => {
    e.stopPropagation();
    setShowFilterOptions(!showFilterOptions);
  };

  return (
    <>
      <header className="flex justify-between items-center mb-8">
        <div>
          <h1 className="text-3xl font-bold bg-gradient-to-r from-blue-700 to-cyan-500 bg-clip-text text-transparent">Medications</h1>
          <p className="text-sm" style={{ color: colors.textSecondary }}>
            Track your pills, because even I can't remember all this stuff
          </p>
        </div>
        <div className="relative">
          <button 
            className="w-12 h-12 rounded-full flex items-center justify-center shadow-lg transition-transform hover:scale-110 duration-300 z-10" 
            style={{ 
              background: `linear-gradient(135deg, ${colors.primary}, ${colors.accent})`,
              boxShadow: `0 0 15px ${colors.primary}40`
            }}
            onClick={handleAddButtonClick}
          >
            <PlusCircle size={24} color="white" />
          </button>

          {/* Add medication options popup */}
          {showAddOptions && (
            <div 
              className="absolute right-0 top-14 w-56 p-2 rounded-xl shadow-xl z-20 border border-blue-100 bg-white"
              style={{ boxShadow: `0 10px 25px -5px ${colors.primary}30` }}
              onClick={e => e.stopPropagation()}
            >
              <div className="p-2 rounded-lg hover:bg-blue-50 cursor-pointer transition-colors duration-200" 
                   onClick={() => {setView('add'); setShowAddOptions(false);}}>
                <div className="flex items-center">
                  <div className="w-8 h-8 rounded-full mr-3 flex items-center justify-center" 
                       style={{ backgroundColor: `${colors.primary}20`, color: colors.primary }}>
                    <Pill size={16} />
                  </div>
                  <span className="text-sm font-medium" style={{ color: colors.textPrimary }}>
                    Add medication manually
                  </span>
                </div>
              </div>

              <div className="p-2 rounded-lg hover:bg-blue-50 cursor-pointer transition-colors duration-200">
                <div className="flex items-center">
                  <div className="w-8 h-8 rounded-full mr-3 flex items-center justify-center" 
                       style={{ backgroundColor: `${colors.accent}20`, color: colors.accent }}>
                    <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                      <rect x="3" y="3" width="18" height="18" rx="2" ry="2"></rect>
                      <circle cx="8.5" cy="8.5" r="1.5"></circle>
                      <polyline points="21 15 16 10 5 21"></polyline>
                    </svg>
                  </div>
                  <span className="text-sm font-medium" style={{ color: colors.textPrimary }}>
                    Scan prescription
                  </span>
                </div>
              </div>

              <div className="p-2 rounded-lg hover:bg-blue-50 cursor-pointer transition-colors duration-200">
                <div className="flex items-center">
                  <div className="w-8 h-8 rounded-full mr-3 flex items-center justify-center" 
                       style={{ backgroundColor: `${colors.success}20`, color: colors.success }}>
                    <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                      <path d="M21 12.79A9 9 0 1 1 11.21 3 7 7 0 0 0 21 12.79z"></path>
                    </svg>
                  </div>
                  <span className="text-sm font-medium" style={{ color: colors.textPrimary }}>
                    Import from pharmacy
                  </span>
                </div>
              </div>
            </div>
          )}
        </div>
      </header>

      {/* Weekly medication adherence overview */}
      <div className="mb-8 p-5 rounded-2xl bg-white shadow-lg border border-blue-50 transform hover:scale-102 transition-all duration-500"
           style={{ boxShadow: `0 15px 25px -5px ${colors.primary}20` }}>
        <div className="flex justify-between items-center mb-4">
          <h3 className="font-bold text-lg" style={{ color: colors.textPrimary }}>Weekly Adherence</h3>
          <button 
            className="flex items-center text-sm font-medium px-3 py-1 rounded-full transition-all duration-300 hover:shadow-md" 
            style={{ color: colors.primary, backgroundColor: `${colors.primary}10` }}
            onClick={() => setView('history')}
          >
            <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="mr-1">
              <line x1="18" y1="20" x2="18" y2="10"></line>
              <line x1="12" y1="20" x2="12" y2="4"></line>
              <line x1="6" y1="20" x2="6" y2="14"></line>
            </svg>
            History
          </button>
        </div>
        <div className="flex justify-between items-center">
          {['Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat', 'Sun'].map((day, index) => {
            let bgColor, icon;
            if (index <= 1 || index === 3) {
              bgColor = `linear-gradient(135deg, ${colors.success}, ${colors.success}90)`;
              icon = <CheckCircle size={18} color="white" />;
            } else if (index === 2) {
              bgColor = `linear-gradient(135deg, ${colors.warning}, ${colors.warning}90)`;
              icon = <Clock size={18} color="white" />;
            } else {
              bgColor = 'transparent';
              icon = null;
            }

            return (
              <div key={day} className="flex flex-col items-center">
                <div className="w-10 h-10 rounded-full flex items-center justify-center shadow-md mb-2 transition-transform hover:scale-110 duration-300" 
                     style={{ 
                       background: bgColor,
                       border: index > 3 ? '2px dashed #E5E7EB' : 'none',
                       boxShadow: index <= 3 ? `0 0 10px ${colors.primary}20` : 'none'
                     }}>
                  {icon || <span className="text-xs font-medium text-gray-400">{day[0]}</span>}
                </div>
                <span className="text-xs">{day}</span>
              </div>
            );
          })}
        </div>
        <div className="mt-4 p-3 rounded-xl shadow-sm flex items-center animate-breathe" 
             style={{ 
               background: `linear-gradient(to right, ${colors.primary}20, ${colors.accent}10)`
             }}>
          <div className="w-10 h-10 rounded-full flex items-center justify-center mr-3 shadow-sm shimmer" 
               style={{ 
                 background: `linear-gradient(135deg, ${colors.primary}40, ${colors.primary}20)`
               }}>
            <Zap size={18} color={colors.primary} />
          </div>
          <p className="text-sm font-medium" style={{ color: colors.primary }}>
            You've taken 92% of your medications on time this week! That's more consistent than my sleep schedule.
          </p>
        </div>
      </div>

      {/* Search and filter */}
      <div className="mb-6 flex items-center gap-3">
        <div className="flex-1 relative">
          <div className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400">
            <Search size={18} />
          </div>
          <input
            type="text"
            className="w-full pl-10 pr-4 py-3 rounded-xl border border-blue-100 focus:outline-none focus:ring-2 focus:ring-blue-400 transition-all duration-300"
            placeholder="Search for your pills... if you remember what they're called"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
        </div>
        <div className="relative">
          <button 
            className="p-3 rounded-xl bg-white border border-blue-100 hover:bg-blue-50 transition-colors duration-300 flex items-center justify-center"
            onClick={handleFilterButtonClick}
          >
            <Filter size={18} style={{ color: colors.textPrimary }} />
          </button>

          {/* Filter options popup */}
          {showFilterOptions && (
            <div 
              className="absolute right-0 top-12 w-44 p-2 rounded-xl shadow-xl z-20 border border-blue-100 bg-white"
              style={{ boxShadow: `0 10px 25px -5px ${colors.primary}30` }}
              onClick={e => e.stopPropagation()}
            >
              <div className="text-xs font-medium mb-1 px-2" style={{ color: colors.textSecondary }}>
                Filter by
              </div>
              <div className="p-2 rounded-lg hover:bg-blue-50 cursor-pointer transition-colors duration-200">
                <span className="text-sm" style={{ color: colors.textPrimary }}>All medications</span>
              </div>
              <div className="p-2 rounded-lg hover:bg-blue-50 cursor-pointer transition-colors duration-200">
                <span className="text-sm" style={{ color: colors.textPrimary }}>Morning pills</span>
              </div>
              <div className="p-2 rounded-lg hover:bg-blue-50 cursor-pointer transition-colors duration-200">
                <span className="text-sm" style={{ color: colors.textPrimary }}>Evening pills</span>
              </div>
              <div className="p-2 rounded-lg hover:bg-blue-50 cursor-pointer transition-colors duration-200">
                <span className="text-sm" style={{ color: colors.textPrimary }}>Needs refill soon</span>
              </div>
            </div>
          )}
        </div>
        <button 
          className="p-3 rounded-xl bg-white border border-blue-100 hover:bg-blue-50 transition-colors duration-300 flex items-center justify-center"
          onClick={() => setView('schedule')}
        >
          <Calendar size={18} style={{ color: colors.textPrimary }} />
        </button>
      </div>

      {/* Medications section with sticky header */}
      <div className="mb-6">
        <div className="mb-4 sticky top-0 bg-blue-50 z-10 flex justify-between items-center px-4 py-2 rounded-lg">
          <h3 className="font-bold" style={{ color: colors.textPrimary }}>Today's Medications</h3>
          <button
            className="text-xs px-2 py-1 rounded-full transition-colors duration-300"
            style={{ color: colors.primary, backgroundColor: colors.primary + '20' }}
            onClick={() => setView('schedule')}
          >
            View schedule
          </button>
        </div>

        {/* Medication list */}
        <div className="space-y-4">
          {medications.filter(med => 
            med.schedule.some(s => s.day === 'today')
          ).map(med => (
            <div
              key={med.id}
              className="p-4 rounded-2xl bg-white shadow-lg border border-blue-50 flex items-center transform hover:scale-105 transition-all duration-500 hover:shadow-xl cursor-pointer"
              style={{ boxShadow: `0 10px 15px -3px ${colors.primary}20` }}
              onClick={() => {
                setSelectedMedication(med);
                setView('detail');
              }}
            >
              <div className="w-12 h-12 rounded-xl mr-4 flex items-center justify-center shimmer" 
                  style={{ 
                    background: `linear-gradient(135deg, ${colors.primary}90, ${colors.primary})`
                  }}>
                <Pill size={22} color="white" />
              </div>
              <div className="flex-1">
                <div className="flex items-center">
                  <h4 className="font-medium" style={{ color: colors.textPrimary }}>{med.name}</h4>
                  <span className="ml-2 px-2 py-0.5 rounded-full text-xs font-medium transition-all duration-300 hover:shadow-sm" 
                        style={{ 
                          background: `linear-gradient(to right, ${colors.primary}20, ${colors.accent}10)`,
                          color: colors.primary
                        }}>
                    {med.dosage}
                  </span>
                </div>
                <div className="flex mt-1">
                  {med.schedule.map((time, idx) => (
                    <div key={idx} className="flex items-center mr-3">
                      <div 
                        className={`w-2 h-2 rounded-full mr-1`}
                        style={{ 
                          backgroundColor: time.status === 'taken' 
                            ? colors.success 
                            : time.status === 'missed' 
                              ? colors.danger 
                              : colors.warning 
                        }}
                      ></div>
                      <p className="text-xs" style={{ color: colors.textSecondary }}>{time.time}</p>
                    </div>
                  ))}
                </div>
              </div>
              <ChevronRight size={20} style={{ color: colors.textSecondary }} />
            </div>
          ))}
        </div>
      </div>

      {/* All medications section */}
      <div>
        <div className="mb-4 sticky top-12 bg-blue-50 z-10 flex justify-between items-center px-4 py-2 rounded-lg">
          <h3 className="font-bold" style={{ color: colors.textPrimary }}>All Medications</h3>
          <span className="text-xs px-2 py-1 rounded-full" style={{ backgroundColor: colors.primary + '20', color: colors.primary }}>
            {medications.length} total
          </span>
        </div>

        <div className="space-y-4">
          {medications.map(med => (
            <div
              key={med.id}
              className="p-4 rounded-2xl bg-white shadow-lg border border-blue-50 flex items-center transform hover:scale-105 transition-all duration-300 hover:shadow-xl cursor-pointer"
              style={{ boxShadow: `0 10px 15px -3px ${colors.primary}20` }}
              onClick={() => {
                setSelectedMedication(med);
                setView('detail');
              }}
            >
              <div className="w-10 h-10 rounded-xl mr-3 flex items-center justify-center" 
                  style={{ 
                    background: `${colors.primary}20`
                  }}>
                <Pill size={20} style={{ color: colors.primary }} />
              </div>
              <div className="flex-1">
                <div className="flex items-center">
                  <h4 className="font-medium text-sm" style={{ color: colors.textPrimary }}>{med.name}</h4>
                  <span className="ml-2 px-2 py-0.5 rounded-full text-xs font-medium" 
                        style={{ 
                          background: `${colors.primary}10`,
                          color: colors.primary
                        }}>
                    {med.dosage}
                  </span>
                </div>
                <p className="text-xs mt-0.5" style={{ color: colors.textSecondary }}>{med.purpose}</p>
              </div>
              <div className="text-xs px-2 py-1 rounded-full flex items-center" 
                  style={{ 
                    backgroundColor: med.adherenceRate > 90 
                      ? colors.success + '20' 
                      : med.adherenceRate > 80 
                        ? colors.warning + '20' 
                        : colors.danger + '20',
                    color: med.adherenceRate > 90 
                      ? colors.success 
                      : med.adherenceRate > 80 
                        ? colors.warning 
                        : colors.danger
                  }}>
                <span>{med.adherenceRate}%</span>
              </div>
            </div>
          ))}
        </div>
      </div>
    </>
  );
};

export default MedicationList;
