import React, { useState, useEffect, useRef } from 'react';
import { Menu, MessageCircle, Video, Search, Bell, ChevronRight, Clock, Phone } from "lucide-react";
import MessageDialog from '../screens/MessageDialog';
import VideoCallScreen from '../screens/VideoCallScreen';
import { useTheme } from '../ThemeContext'; 
import webRTCService from '../services/WebRTCService';

// Create enhanced DoctorCard component directly in this file
const DoctorCard = ({ name, specialty, image, availability, colors, rating, onConnectClick, isConnecting }) => {
  const isAvailableNow = availability.includes('Available') && !availability.includes('in');
  const [isHovered, setIsHovered] = useState(false);
  const { isDarkMode } = useTheme();

  return (
    <div 
      className="p-4 rounded-2xl bg-white shadow-lg border border-blue-50 min-w-[180px] transform transition-all duration-500 hover:shadow-xl dark-mode-transition"
      style={{ 
        backgroundColor: isDarkMode ? colors.cardBg : 'white',
        borderColor: isDarkMode ? `${colors.primary}30` : 'rgba(219, 234, 254, 1)',
        boxShadow: isHovered 
          ? `0 20px 25px -5px ${colors.primary}30, 0 10px 10px -5px ${colors.accent}20` 
          : `0 10px 15px -3px ${colors.primary}20`,
        transform: isHovered ? 'translateY(-8px) scale(1.02)' : 'translateY(0) scale(1)'
      }}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      <div className="flex items-center mb-3">
        <div className="w-14 h-14 rounded-full shadow-md mr-3 relative overflow-hidden transition-all duration-300"
            style={{ 
              boxShadow: `0 0 15px ${colors.primary}30`,
              transform: isHovered ? 'scale(1.05)' : 'scale(1)'
            }}>
          <img src={image} alt={name} className="w-full h-full rounded-full object-cover" />
          <div className="absolute bottom-0 right-0 w-4 h-4 rounded-full border-2 border-white animate-pulse" 
              style={{ 
                backgroundColor: isConnecting ? colors.accent : isAvailableNow ? colors.success : colors.warning,
                borderColor: isDarkMode ? colors.cardBg : 'white'
              }}></div>
        </div>
        <div>
          <h4 className="font-semibold text-sm dark-mode-transition" style={{ color: colors.textPrimary }}>{name}</h4>
          <p className="text-xs dark-mode-transition" style={{ color: colors.textSecondary }}>{specialty}</p>
          {rating && (
            <div className="flex items-center mt-1">
              {[...Array(5)].map((_, i) => (
                <div key={i} className="w-2 h-2 rounded-full mr-1 dark-mode-transition" 
                    style={{ backgroundColor: i < rating ? colors.primary : isDarkMode ? colors.darkBg : colors.background }}></div>
              ))}
            </div>
          )}
        </div>
      </div>
      <div className="flex items-center mb-3">
        <div className="w-3 h-3 mr-2 rounded-full animate-pulse" 
            style={{ backgroundColor: isConnecting ? colors.accent : isAvailableNow ? colors.success : colors.warning }}></div>
        <span className="text-xs font-medium" 
            style={{ color: isConnecting ? colors.accent : isAvailableNow ? colors.success : colors.warning }}>
          {isConnecting ? "Connecting..." : availability}
        </span>
      </div>
      <button 
        className="w-full p-2 rounded-lg text-xs font-medium shadow-md transition-all duration-300 hover:shadow-lg transform hover:-translate-y-1" 
        style={{ 
          background: isConnecting 
            ? `linear-gradient(135deg, ${colors.accent}, ${colors.accentAlt})`
            : isAvailableNow 
              ? `linear-gradient(135deg, ${colors.primary}, ${colors.accent})`
              : isDarkMode
                ? `linear-gradient(135deg, ${colors.darkBg}, ${colors.cardBg})`
                : `linear-gradient(135deg, ${colors.background}, white)`,
          color: (isConnecting || isAvailableNow) ? 'white' : colors.primary,
          border: (isConnecting || isAvailableNow) ? 'none' : `1px solid ${colors.primary}30`,
          boxShadow: (isConnecting || isAvailableNow)
            ? `0 8px 15px -3px ${colors.primary}40` 
            : `0 4px 6px -1px ${colors.primary}20`
        }}
        onClick={onConnectClick}
      >
        {isConnecting ? 'Disconnect' : isAvailableNow ? 'Connect Now' : 'Schedule'}
      </button>
    </div>
  );
};

// Enhanced Message Card component
const MessageCard = ({ doctor, message, time, unread, colors }) => {
  const [isHovered, setIsHovered] = useState(false);
  const { isDarkMode } = useTheme();

  return (
    <div 
      className="p-4 rounded-2xl bg-white shadow-lg border border-blue-50 transition-all duration-500 relative overflow-hidden dark-mode-transition"
      style={{ 
        backgroundColor: isDarkMode ? colors.cardBg : 'white',
        borderColor: isDarkMode ? `${colors.primary}30` : 'rgba(219, 234, 254, 1)',
        boxShadow: isHovered 
          ? `0 15px 25px -5px ${colors.primary}30` 
          : `0 10px 15px -3px ${colors.primary}20`,
        transform: isHovered ? 'translateX(5px)' : 'translateX(0)',
        borderLeft: unread ? `4px solid ${colors.primary}` : '4px solid transparent'
      }}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      {unread && (
        <div className="absolute top-0 right-0">
          <div className="w-3 h-3 m-2 rounded-full animate-pulse" 
              style={{ backgroundColor: colors.primary }}></div>
        </div>
      )}
      <div className="flex items-center mb-2">
        <div className="w-10 h-10 rounded-full shadow-md mr-3 relative overflow-hidden flex items-center justify-center"
             style={{ 
               background: `linear-gradient(135deg, ${colors.primary}40, ${colors.accent}30)`,
               boxShadow: `0 0 15px ${colors.primary}30` 
             }}>
          <span className="text-sm font-bold" style={{ color: colors.primary }}>{doctor.split(' ')[1][0]}</span>
        </div>
        <div className="flex-1">
          <h4 className="font-semibold text-sm dark-mode-transition" style={{ color: colors.textPrimary }}>{doctor}</h4>
          <p className="text-xs dark-mode-transition" style={{ color: colors.textSecondary }}>{time}</p>
        </div>
        <ChevronRight size={18} className="transition-opacity duration-300" style={{ color: colors.textSecondary, opacity: isHovered ? 1 : 0.5 }} />
      </div>
      <p className="text-sm ml-13 pl-13 dark-mode-transition" style={{ 
        color: unread ? colors.textPrimary : colors.textSecondary,
        fontWeight: unread ? '500' : '400',
        marginLeft: '3.25rem' 
      }}>{message}</p>
    </div>
  );
};

// Main ConnectScreen component
const ConnectScreen = ({ colors, setActiveScreen }) => {
  const { isDarkMode } = useTheme();
  const [searchFocused, setSearchFocused] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const [notifications, setNotifications] = useState(3);
  const [filterActive, setFilterActive] = useState(false);

  // Modal states
  const [showMessageDialog, setShowMessageDialog] = useState(false);
  const [showVideoCall, setShowVideoCall] = useState(false);
  const [selectedDoctor, setSelectedDoctor] = useState(null);
  const [connectingDoctor, setConnectingDoctor] = useState(null);
  const [schedulingDoctor, setSchedulingDoctor] = useState(null); // New state for scheduling vs connecting
  const [readyToConnect, setReadyToConnect] = useState(false); // New state to control when connections can be made

  // Doctor mode toggle for testing
  const [isDoctorMode, setIsDoctorMode] = useState(
    localStorage.getItem('is_doctor') === 'true'
  );

  // Enhanced notification system
  const [toastQueue, setToastQueue] = useState([]);
  const [activeToast, setActiveToast] = useState(null);

  // Incoming call notification
  const [incomingCallInfo, setIncomingCallInfo] = useState(null);
  const [ringtoneAudio, setRingtoneAudio] = useState(null);
  const [autoAcceptTimer, setAutoAcceptTimer] = useState(null);

  // Debug counter for incoming calls - new
  const [incomingCallCounter, setIncomingCallCounter] = useState(0);

  // Ref to track previous video call state
  const prevShowVideoCall = useRef(showVideoCall);

  // Sample doctors data
  const doctors = [
    {
      id: 1,
      name: "Dr. Johnson",
      specialty: "Cardiologist",
      image: "/User_1.png",
      availability: "Available now",
      rating: 5
    },
    {
      id: 2,
      name: "Dr. Smith",
      specialty: "Primary Care",
      image: "/User_2.jpg",
      availability: "Available in 15m",
      rating: 4
    },
    {
      id: 3,
      name: "Dr. Garcia",
      specialty: "Endocrinologist",
      image: "/User_3.png",
      availability: "Available tomorrow",
      rating: 5
    },
    {
      id: 4,
      name: "Dr. Chen",
      specialty: "Neurologist",
      image: "/User_4.png",
      availability: "Available in 2h",
      rating: 4
    },
  ];

  // Toggle doctor mode status
  useEffect(() => {
    localStorage.setItem('is_doctor', isDoctorMode ? 'true' : 'false');

    if (isDoctorMode && webRTCService.socket && webRTCService.socket.connected) {
      // Register as doctor-1
      webRTCService.socket.emit('register', 'doctor-1');
      console.log('Registered as doctor-1 via toggle');
    }
  }, [isDoctorMode]);

  // DEBUG: Log incoming call info whenever it changes - new
  useEffect(() => {
    if (incomingCallInfo) {
      console.log('INCOMING CALL INFO UPDATED:', incomingCallInfo);
    }
  }, [incomingCallInfo]);

  // Initialize WebRTC when component mounts
  useEffect(() => {
    // Generate a unique ID for the current user instance
    // This ensures each browser instance has a different ID
    const generateUniqueId = () => {
      // Check if we already have an ID stored
      const storedId = localStorage.getItem('user_instance_id');
      if (storedId) {
        return storedId;
      }

      // Generate a new random ID and store it
      const newId = 'user-' + Math.random().toString(36).substring(2, 15);
      localStorage.setItem('user_instance_id', newId);
      return newId;
    };

    const currentUserId = generateUniqueId();
    console.log('Initializing with user ID:', currentUserId);

    // Set up WebRTC callbacks
    const callbacks = {
      onIncomingCall: (callerId) => {
        // Increment our debug counter - new
        setIncomingCallCounter(prev => prev + 1);

        // Find which doctor is calling
        const callingDoctor = doctors.find(doc => `doctor-${doc.id}` === callerId);

        if (callingDoctor) {
          console.log('Incoming call from doctor detected:', callingDoctor.name);

          // Set the connecting doctor
          setConnectingDoctor(callingDoctor);
          setSelectedDoctor(callingDoctor);

          // Use the enhanced notification system
          displayIncomingCallAlert(callingDoctor);

          // NEW: Force a notification if the previous one didn't show up
          setTimeout(() => {
            console.log('Checking if notification is visible...');
            // If we don't see a notification after 500ms, force it again
            if (!incomingCallInfo) {
              console.log('Forcing notification display...');
              displayIncomingCallAlert(callingDoctor);
            }
          }, 500);
        } else {
          // Handle the case where we can't find the doctor
          console.warn('Received call from unknown doctor ID:', callerId);
          // Create a generic doctor object
          const unknownDoctor = {
            id: 999,
            name: "Unknown Doctor",
            specialty: "Medical Professional",
            image: "/User_1.png",
            availability: "Available now",
            rating: 5
          };
          setConnectingDoctor(unknownDoctor);
          setSelectedDoctor(unknownDoctor);
          displayIncomingCallAlert(unknownDoctor);
        }
      },
      onUserNotOnline: (userId) => {
        // Try to find which doctor this userId belongs to
        const doctorId = userId.replace('doctor-', '');
        const doctor = doctors.find(doc => doc.id.toString() === doctorId);

        if (doctor) {
          displayToast(`${doctor.name} is not available right now. Please try again later.`);
        } else {
          displayToast('The selected doctor is not available right now.');
        }

        // Reset connecting state
        setConnectingDoctor(null);
      }
    };

    // Get the server URL from environment variable if available
    const serverUrl = process.env.REACT_APP_SIGNALING_SERVER || null;

    // Initialize WebRTC service
    webRTCService.initialize(currentUserId, callbacks, serverUrl);

    // If this browser is in doctor mode, register with doctor ID after a short delay
    if (localStorage.getItem('is_doctor') === 'true') {
      setTimeout(() => {
        if (webRTCService.socket && webRTCService.socket.connected) {
          webRTCService.socket.emit('register', 'doctor-1');
          console.log('Registered as doctor-1 on initialization');
        }
      }, 1000);
    }

    // Add delay before allowing connections to ensure proper registration with the server
    setTimeout(() => {
      setReadyToConnect(true);
    }, 2000);

    // Periodically re-register to ensure connection
    const registerInterval = setInterval(() => {
      if (webRTCService.socket && webRTCService.socket.connected) {
        // Always register with user ID
        webRTCService.socket.emit('register', currentUserId);

        // If in doctor mode, also register with doctor ID
        if (localStorage.getItem('is_doctor') === 'true') {
          webRTCService.socket.emit('register', 'doctor-1');
          console.log('Re-registering as doctor-1');
        }
      }
    }, 5000); // Every 5 seconds

    // ADDITIONAL DEBUG: Add direct socket listener for incoming calls - new
    if (webRTCService.socket) {
      webRTCService.socket.on('incoming-call', ({ callerUserId }) => {
        console.log('DIRECT SOCKET: Incoming call detected from:', callerUserId);
      });
    }

    // Clean up on unmount
    return () => {
      clearInterval(registerInterval);
      webRTCService.disconnect();
    };
  }, []); // Only run once when component mounts

  // FIXED: Enhanced incoming call notification display
  const displayIncomingCallAlert = (doctor) => {
    console.log('Displaying incoming call alert for doctor:', doctor.name);

    // Clear existing toasts to prevent overlap
    setActiveToast(null);
    setToastQueue([]);

    // Stop any previous ringtone
    if (ringtoneAudio) {
      try {
        ringtoneAudio.pause();
        ringtoneAudio.currentTime = 0;
      } catch (e) {
        console.error('Error stopping previous ringtone:', e);
      }
      setRingtoneAudio(null);
    }

    // Clear any previous auto-accept timer
    if (autoAcceptTimer) {
      clearTimeout(autoAcceptTimer);
      setAutoAcceptTimer(null);
    }

    // Create a more prominent incoming call notification as a state variable
    setIncomingCallInfo({
      doctor: doctor,
      timestamp: Date.now(),
      id: Math.random().toString(36).substring(2, 9) // Add a unique ID
    });

    // Play sound effect if available (add a ringtone to your public folder)
    try {
      const audio = new Audio('/song1.mp3');
      audio.loop = true;
      audio.volume = 0.7; // Set volume to 70%

      // Use a promise to handle play
      const playPromise = audio.play();

      if (playPromise !== undefined) {
        playPromise
          .then(() => {
            console.log('Ringtone playing successfully');
            setRingtoneAudio(audio);
          })
          .catch(e => {
            console.log('Could not play notification sound:', e);
            // Try alternative method if autoplay is blocked
            document.addEventListener('click', function audioUnlock() {
              audio.play().catch(e => console.log('Still cannot play audio:', e));
              document.removeEventListener('click', audioUnlock);
            });
          });
      }
    } catch (error) {
      console.log('Could not initialize ringtone:', error);
    }

    // Auto-accept call after 15 seconds if not manually handled
    const timer = setTimeout(() => {
      if (incomingCallInfo) {
        console.log('Auto-accepting call after timeout');
        acceptIncomingCall(doctor);
      }
    }, 15000); // Increased to 15 seconds for better user experience

    // Store timer reference to clear it later
    setAutoAcceptTimer(timer);

    // Also show a toast notification as a backup
    displayToast(`Incoming call from ${doctor.name}`);
  };

  // Accept incoming call handler
  const acceptIncomingCall = (doctor) => {
    console.log('Accepting incoming call from:', doctor?.name);

    // Stop ringtone
    if (ringtoneAudio) {
      try {
        ringtoneAudio.pause();
        ringtoneAudio.currentTime = 0;
      } catch (e) {
        console.error('Error stopping ringtone:', e);
      }
      setRingtoneAudio(null);
    }

    // Clear auto-accept timer
    if (autoAcceptTimer) {
      clearTimeout(autoAcceptTimer);
      setAutoAcceptTimer(null);
    }

    // Clear the incoming call notification
    setIncomingCallInfo(null);

    // Show video call screen
    setShowVideoCall(true);

    // Show a confirmation toast
    displayToast(`Connected with ${doctor.name}`);
  };

  // Reject incoming call handler
  const rejectIncomingCall = () => {
    console.log('Rejecting incoming call');

    // Stop ringtone
    if (ringtoneAudio) {
      try {
        ringtoneAudio.pause();
        ringtoneAudio.currentTime = 0;
      } catch (e) {
        console.error('Error stopping ringtone:', e);
      }
      setRingtoneAudio(null);
    }

    // Clear auto-accept timer
    if (autoAcceptTimer) {
      clearTimeout(autoAcceptTimer);
      setAutoAcceptTimer(null);
    }

    // End call in WebRTC service
    webRTCService.endCall(true);

    // Clear states
    setIncomingCallInfo(null);
    setConnectingDoctor(null);
    setSelectedDoctor(null);

    // Show toast to confirm rejection
    displayToast('Call rejected');
  };

  // Check if a doctor is online before connecting
  const checkDoctorOnline = async (doctorId) => {
    const isOnline = await webRTCService.checkUserOnline(`doctor-${doctorId}`);
    return isOnline;
  };

  // Improved toast display function
  const displayToast = (message) => {
    console.log('Displaying toast:', message);
    setToastQueue(prev => [...prev, message]);
  };

  // Process toast queue with increased display time (4 seconds)
  useEffect(() => {
    if (toastQueue.length > 0 && !activeToast) {
      // Set the first toast as active
      setActiveToast(toastQueue[0]);

      // Remove it from the queue
      setToastQueue(prev => prev.slice(1));

      // Set timeout to clear the active toast - changed from 1000ms to 4000ms
      const timer = setTimeout(() => {
        setActiveToast(null);
      }, 4000); // Now toast shows for 4 seconds

      return () => clearTimeout(timer);
    }
  }, [toastQueue, activeToast]);

  // Handle starting/ending a connection with a doctor
  const handleConnectDoctor = async (doctor) => {
    if (!readyToConnect) {
      displayToast('Please wait while connecting to the service...');
      return;
    }

    if (connectingDoctor && connectingDoctor.id === doctor.id) {
      // If already connecting to this doctor, disconnect
      setConnectingDoctor(null);
      setSelectedDoctor(null); // Also clear selected doctor

      // Clear any active toast and toast queue without showing a new toast
      setActiveToast(null); // Immediately clear the current toast
      setToastQueue([]); // Clear any pending toasts in queue
      displayToast(`Disconnected from ${doctor.name}`);
    } else {
      // Check if doctor is online first
      const isOnline = await checkDoctorOnline(doctor.id);

      if (!isOnline) {
        displayToast(`${doctor.name} is not online right now. Please try again later.`);
        return;
      }

      // If connecting to a different doctor, disconnect from current doctor first
      if (connectingDoctor) {
        displayToast(`Disconnected from ${connectingDoctor.name}`);
      }

      // Start connecting to this doctor
      setConnectingDoctor(doctor);
      setSelectedDoctor(doctor);

      // Clear any previous toasts and show connecting toast
      setActiveToast(null);
      setToastQueue([]);
      displayToast(`Connecting to ${doctor.name}...`);
    }
  };

  // Reset connection when video call ends, but only if previous state was true
  useEffect(() => {
    if (prevShowVideoCall.current && !showVideoCall && connectingDoctor) {
      displayToast(`Call with ${connectingDoctor.name} ended`);
      setConnectingDoctor(null);
    }

    // Update previous state
    prevShowVideoCall.current = showVideoCall;
  }, [showVideoCall, connectingDoctor]);

  // Handle search filtering
  const handleSearch = (query) => {
    setSearchQuery(query);
  };

  // Filter doctors based on search query and availability filter
  const filteredDoctors = doctors.filter(doctor => {
    // Filter by search query
    const matchesSearch = searchQuery 
      ? (doctor.name.toLowerCase().includes(searchQuery.toLowerCase()) || 
         doctor.specialty.toLowerCase().includes(searchQuery.toLowerCase()))
      : true;

    // Filter by availability if filter is active
    const matchesAvailability = filterActive 
      ? doctor.availability.includes('Available') && !doctor.availability.includes('in')
      : true;

    return matchesSearch && matchesAvailability;
  });

  // Animation for header
  const [headerVisible, setHeaderVisible] = useState(false);
  useEffect(() => {
    const timer = setTimeout(() => setHeaderVisible(true), 100);
    return () => clearTimeout(timer);
  }, []);

  return (
    <div className="p-6 pb-24 dark-mode-transition" style={{ 
      backgroundColor: isDarkMode ? colors.background : 'transparent',
      backgroundImage: isDarkMode 
        ? `radial-gradient(circle at 70% 30%, ${colors.primary}10, transparent 50%), 
           radial-gradient(circle at 30% 70%, ${colors.accent}10, transparent 50%)`
        : 'none'
    }}>
      {/* Enhanced header with animations */}
      <header className={`flex justify-between items-center mb-8 transition-all duration-700 ${headerVisible ? 'opacity-100 translate-y-0' : 'opacity-0 -translate-y-4'}`}>
        <div>
          <h1 className="text-3xl font-bold" 
              style={{ 
                background: `linear-gradient(to right, ${colors.primary}, ${colors.accent})`,
                WebkitBackgroundClip: 'text',
                WebkitTextFillColor: 'transparent',
                backgroundClip: 'text',
                textFillColor: 'transparent'
              }}>Connect</h1>
          <p className="text-sm dark-mode-transition" style={{ color: colors.textSecondary }}>Your health team at your fingertips</p>
        </div>
        <div className="flex items-center space-x-3">
          {/* Doctor Mode Toggle */}
          <button 
            onClick={() => setIsDoctorMode(!isDoctorMode)}
            className="px-3 py-1 rounded-lg text-xs font-medium"
            style={{ 
              backgroundColor: isDoctorMode ? colors.success : colors.cardBg,
              color: isDoctorMode ? 'white' : colors.textPrimary
            }}
          >
            {isDoctorMode ? 'Doctor Mode: ON' : 'Doctor Mode: OFF'}
          </button>

          <button 
            className="w-10 h-10 rounded-full flex items-center justify-center shadow-lg transition-transform hover:scale-110 duration-300 relative"
            style={{ 
              background: notifications > 0
                ? `linear-gradient(135deg, ${colors.accent}, ${colors.accentAlt})`
                : isDarkMode 
                  ? `linear-gradient(135deg, ${colors.darkBg}, ${colors.cardBg})`
                  : `linear-gradient(135deg, ${colors.primaryLight}40, ${colors.primaryLight}20)`,
              boxShadow: notifications > 0
                ? `0 0 15px ${colors.accent}40`
                : `0 0 10px ${colors.primary}20`
            }}
            onClick={() => setNotifications(0)}
          >
            <Bell size={20} color={notifications > 0 ? "white" : isDarkMode ? colors.textSecondary : colors.textSecondary} />
            {notifications > 0 && (
              <div className="absolute -top-1 -right-1 w-5 h-5 rounded-full bg-red-500 flex items-center justify-center">
                <span className="text-xs text-white font-bold">{notifications}</span>
              </div>
            )}
          </button>
          <button className="w-10 h-10 rounded-full flex items-center justify-center shadow-lg transition-transform hover:scale-110 duration-300" 
            style={{ 
              background: `linear-gradient(135deg, ${colors.primary}, ${colors.accent})`,
              boxShadow: `0 0 15px ${colors.primary}40`
            }}>
            <Menu size={20} color="white" />
          </button>
        </div>
      </header>

      {/* NEW: Incoming Call Counter for debugging */}
      {incomingCallCounter > 0 && (
        <div className="mb-2 p-2 bg-red-100 rounded-md text-xs">
          Incoming call events received: {incomingCallCounter}
        </div>
      )}

      {/* Search bar with animations and dark mode support */}
      <div className={`mb-6 transition-all duration-500 transform ${headerVisible ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-4'}`} style={{ transitionDelay: '100ms' }}>
        <div className="relative">
          <div className={`absolute left-4 top-1/2 transform -translate-y-1/2 transition-all duration-300`}
               style={{ color: searchFocused ? colors.primary : colors.textSecondary }}>
            <Search size={18} />
          </div>
          <input
            type="text"
            placeholder="Search doctors, messages..."
            className="w-full pl-12 pr-4 py-3 rounded-xl transition-all duration-300 dark-mode-transition"
            style={{
              backgroundColor: searchFocused 
                ? isDarkMode ? colors.cardBg : 'white' 
                : isDarkMode ? `${colors.primary}15` : `${colors.primary}05`,
              border: searchFocused 
                ? `2px solid ${colors.primary}30` 
                : isDarkMode ? `1px solid ${colors.primary}20` : `1px solid ${colors.primary}10`,
              boxShadow: searchFocused ? `0 10px 15px -3px ${colors.primary}20` : 'none',
              color: colors.textPrimary,
              outline: 'none',
            }}
            onFocus={() => setSearchFocused(true)}
            onBlur={() => setSearchFocused(false)}
            value={searchQuery}
            onChange={(e) => handleSearch(e.target.value)}
          />
          {searchQuery && (
            <button 
              className="absolute right-4 top-1/2 transform -translate-y-1/2"
              onClick={() => setSearchQuery('')}
              style={{ color: colors.textSecondary }}
            >
              ×
            </button>
          )}
        </div>
      </div>

      {/* Filter chips */}
      <div className={`flex space-x-2 mb-6 transition-all duration-500 ${headerVisible ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-4'}`} style={{ transitionDelay: '200ms' }}>
        <button 
          className="px-4 py-2 rounded-full text-xs font-medium transition-all duration-300 flex items-center dark-mode-transition"
          style={{
            backgroundColor: filterActive 
              ? colors.primary 
              : isDarkMode ? `${colors.primary}20` : `${colors.primary}10`,
            color: filterActive ? 'white' : colors.primary,
            boxShadow: filterActive ? `0 8px 15px -3px ${colors.primary}40` : 'none'
          }}
          onClick={() => setFilterActive(!filterActive)}
        >
          <Clock size={14} className="mr-1" />
          Available now
        </button>
        <button className="px-4 py-2 rounded-full text-xs font-medium dark-mode-transition" 
          style={{ 
            backgroundColor: isDarkMode ? `${colors.primary}20` : `${colors.primary}10`, 
            color: colors.primary 
          }}>
          Primary Care
        </button>
        <button className="px-4 py-2 rounded-full text-xs font-medium dark-mode-transition" 
          style={{ 
            backgroundColor: isDarkMode ? `${colors.primary}20` : `${colors.primary}10`, 
            color: colors.primary 
          }}>
          Specialists
        </button>
      </div>

      {/* Care team with enhanced card animations - now using filtered doctors */}
      <div className={`mb-8 transition-all duration-500 ${headerVisible ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-4'}`} style={{ transitionDelay: '300ms' }}>
        <div className="flex justify-between items-center mb-4">
          <h3 className="font-bold text-lg dark-mode-transition" style={{ color: colors.textPrimary }}>Your Care Team</h3>
          <button className="text-sm font-medium px-3 py-1 rounded-full transition-all duration-300 hover:shadow-md dark-mode-transition" 
                  style={{ 
                    color: colors.primary, 
                    backgroundColor: isDarkMode ? `${colors.primary}20` : `${colors.primary}10` 
                  }}>
            View all
          </button>
        </div>

        {filteredDoctors.length > 0 ? (
          <div className="flex overflow-x-auto pb-4 -mx-2 px-2 space-x-4 hide-scrollbar">
            {filteredDoctors.map(doctor => (
              <div key={doctor.id}>
                <DoctorCard
                  name={doctor.name}
                  specialty={doctor.specialty}
                  image={doctor.image}
                  availability={doctor.availability}
                  rating={doctor.rating}
                  colors={colors}
                  isConnecting={connectingDoctor?.id === doctor.id}
                  onConnectClick={async () => {
                    const isAvailableNow = doctor.availability.includes('Available') && !doctor.availability.includes('in');

                    if (!isAvailableNow) {
                      // If doctor is not available, use scheduling flow
                      setSelectedDoctor(doctor);
                      setSchedulingDoctor(doctor);
                      setShowMessageDialog(true);
                      return;
                    }

                    // Toggle connecting state for available doctors
                    await handleConnectDoctor(doctor);
                  }}
                />
              </div>
            ))}
          </div>
        ) : (
          <div className="p-6 rounded-2xl bg-white shadow-lg text-center dark-mode-transition"
               style={{ 
                 backgroundColor: isDarkMode ? colors.cardBg : 'white',
                 boxShadow: `0 10px 15px -3px ${colors.primary}20`
               }}>
            <p className="dark-mode-transition" style={{ color: colors.textSecondary }}>No doctors match your search criteria</p>
          </div>
        )}
      </div>

      {/* Communication options with enhanced gradient backgrounds and hover effects */}
      <div className={`mb-8 transition-all duration-500 ${headerVisible ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-4'}`} style={{ transitionDelay: '400ms' }}>
        <h3 className="font-bold text-lg mb-4 dark-mode-transition" style={{ color: colors.textPrimary }}>Quick Connect</h3>
        <div className="grid grid-cols-2 gap-6">
          <button 
            className="p-6 rounded-2xl shadow-lg flex flex-col items-center justify-center transition-all duration-500 relative overflow-hidden h-36 dark-mode-transition"
            style={{ 
              backgroundColor: isDarkMode ? colors.cardBg : 'white',
              boxShadow: `0 15px 25px -5px ${colors.primary}30, 0 10px 10px -5px ${colors.accent}20`,
              opacity: connectingDoctor ? 1 : 0.7
            }}
            onClick={() => {
              // Check if a doctor is connecting before allowing messaging
              if (!connectingDoctor) {
                displayToast("Please connect to a doctor first");
                return;
              }

              setShowMessageDialog(true);
            }}
          >
            {/* Background gradient effect */}
            <div className="absolute inset-0 opacity-10" 
                style={{ 
                  background: `linear-gradient(135deg, ${colors.primary}, ${colors.accent})`,
                }}></div>

            {/* Icon container */}
            <div className="w-20 h-20 rounded-full mb-4 flex items-center justify-center shadow-lg transition-all duration-500 relative z-10" 
                style={{ 
                  background: `linear-gradient(135deg, ${colors.primary}, ${colors.accent})`,
                  boxShadow: `0 0 20px ${colors.primary}40`
                }}>
              <MessageCircle size={32} color="white" />

              {/* Shimmer effect */}
              <div className="absolute inset-0 rounded-full overflow-hidden">
                <div className="shimmer-effect absolute inset-0"></div>
              </div>
            </div>

            {/* Label */}
            <span className="text-base font-semibold z-10 dark-mode-transition" style={{ color: colors.primary }}>Message</span>

            {/* Subtle indicator dots */}
            <div className="absolute bottom-4 right-4 flex space-x-1">
              <div className="w-2 h-2 rounded-full dark-mode-transition" style={{ backgroundColor: isDarkMode ? `${colors.primary}60` : `${colors.primary}40` }}></div>
              <div className="w-2 h-2 rounded-full animate-pulse" style={{ backgroundColor: isDarkMode ? `${colors.primary}90` : `${colors.primary}80` }}></div>
              <div className="w-2 h-2 rounded-full dark-mode-transition" style={{ backgroundColor: isDarkMode ? `${colors.primary}60` : `${colors.primary}40` }}></div>
            </div>
          </button>

          <button 
            className="p-6 rounded-2xl shadow-lg flex flex-col items-center justify-center transition-all duration-500 relative overflow-hidden h-36 dark-mode-transition"
            style={{ 
              backgroundColor: isDarkMode ? colors.cardBg : 'white',
              boxShadow: `0 15px 25px -5px ${colors.accentAlt}30, 0 10px 10px -5px ${colors.accent}20`,
              opacity: connectingDoctor && connectingDoctor.availability.includes('Available') && 
                      !connectingDoctor.availability.includes('in') ? 1 : 0.7
            }}
            onClick={async () => {
              // Check if a doctor is connecting before allowing video call
              if (!connectingDoctor) {
                displayToast("Please connect to a doctor first");
                return;
              }

              // Check if the connecting doctor is available now
              const isAvailableNow = connectingDoctor.availability.includes('Available') && 
                                   !connectingDoctor.availability.includes('in');

              if (!isAvailableNow) {
                displayToast(`${connectingDoctor.name} is not available for video call right now`);
                return;
              }

              // Check if the doctor is online
              const isOnline = await checkDoctorOnline(connectingDoctor.id);

              if (!isOnline) {
                displayToast(`${connectingDoctor.name} is not online. Please try again later.`);
                return;
              }

              // Start video call with the connecting doctor
              setShowVideoCall(true);
              // The actual WebRTC call will be initiated by the VideoCallScreen component
            }}
          >
            {/* Background gradient effect */}
            <div className="absolute inset-0 opacity-10" 
                style={{ 
                  background: `linear-gradient(135deg, ${colors.accentAlt}, ${colors.accent})`,
                }}></div>

            {/* Icon container */}
            <div className="w-20 h-20 rounded-full mb-4 flex items-center justify-center shadow-lg transition-all duration-500 relative z-10" 
                style={{ 
                  background: `linear-gradient(135deg, ${colors.accentAlt}, ${colors.accent})`,
                  boxShadow: `0 0 20px ${colors.accentAlt}40`
                }}>
              <Video size={32} color="white" />

              {/* Shimmer effect */}
              <div className="absolute inset-0 rounded-full overflow-hidden">
                <div className="shimmer-effect absolute inset-0"></div>
              </div>
            </div>

            {/* Label */}
            <span className="text-base font-semibold z-10 dark-mode-transition" style={{ color: colors.accentAlt }}>Video Call</span>

            {/* Subtle indicator dots */}
            <div className="absolute bottom-4 right-4 flex space-x-1">
              <div className="w-2 h-2 rounded-full dark-mode-transition" style={{ backgroundColor: isDarkMode ? `${colors.accentAlt}60` : `${colors.accentAlt}40` }}></div>
              <div className="w-2 h-2 rounded-full animate-pulse" style={{ backgroundColor: isDarkMode ? `${colors.accentAlt}90` : `${colors.accentAlt}80` }}></div>
              <div className="w-2 h-2 rounded-full dark-mode-transition" style={{ backgroundColor: isDarkMode ? `${colors.accentAlt}60` : `${colors.accentAlt}40` }}></div>
            </div>
          </button>
        </div>
      </div>

      {/* Recent messages with enhanced card design and animations */}
      <div className={`transition-all duration-500 ${headerVisible ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-4'}`} style={{ transitionDelay: '500ms' }}>
        <div className="flex justify-between items-center mb-4">
          <h3 className="font-bold text-lg dark-mode-transition" style={{ color: colors.textPrimary }}>Recent Messages</h3>
          <button className="text-sm font-medium px-3 py-1 rounded-full transition-all duration-300 hover:shadow-md dark-mode-transition" 
                  style={{ 
                    color: colors.primary, 
                    backgroundColor: isDarkMode ? `${colors.primary}20` : `${colors.primary}10` 
                  }}>
            View all
          </button>
        </div>

        <div className="space-y-4">
          <MessageCard
            doctor="Dr. Johnson"
            message="Your latest blood pressure readings look good. Keep up the good work with the diet modifications we discussed!"
            time="Today, 10:23 AM"
            unread={true}
            colors={colors}
          />
          <MessageCard
            doctor="Dr. Smith"
            message="I've adjusted your medication schedule based on your recent feedback. Please review the changes in your medication section."
            time="Yesterday, 4:15 PM"
            unread={false}
            colors={colors}
          />
          <MessageCard
            doctor="Nurse Williams"
            message="How are you feeling after the new medication? Any side effects to report?"
            time="Mar 20, 9:30 AM"
            unread={false}
            colors={colors}
          />
        </div>
      </div>

      {/* Message Dialog */}
      <MessageDialog 
        isOpen={showMessageDialog}
        onClose={() => {
          setShowMessageDialog(false);
          setSchedulingDoctor(null); // Reset scheduling state on close
        }}
        colors={colors}
        recipient={schedulingDoctor || connectingDoctor} // Use scheduling doctor if set
        doctors={doctors}
      />

      {/* Video Call Screen */}
      <VideoCallScreen 
        isOpen={showVideoCall}
        onClose={() => setShowVideoCall(false)}
        colors={colors}
        doctor={connectingDoctor} // Use connecting doctor for video call
      />

      {/* IMPROVED: Enhanced incoming call UI with more attention-grabbing design and higher z-index */}
      {incomingCallInfo && (
        <div className="fixed inset-0 z-[9999] flex items-center justify-center bg-black bg-opacity-70 backdrop-blur-sm animate-fade-in">
          <div className="bg-white dark:bg-gray-800 rounded-2xl overflow-hidden shadow-2xl max-w-md w-full mx-4 animate-bounce-in">
            <div className="p-6 text-center">
              <div className="w-28 h-28 rounded-full mx-auto mb-4 relative overflow-hidden border-4 animate-pulse" 
                  style={{ borderColor: colors.primary }}>
                <img 
                  src={incomingCallInfo.doctor.image} 
                  alt={incomingCallInfo.doctor.name} 
                  className="w-full h-full object-cover"
                />
                <div className="absolute inset-0 flex items-center justify-center">
                  <div className="w-full h-full bg-black bg-opacity-20 flex items-center justify-center">
                    <div className="w-20 h-20 rounded-full border-4 border-white border-opacity-60 border-t-transparent animate-spin"></div>
                  </div>
                </div>
              </div>

              <h3 className="text-2xl font-bold mb-2 dark:text-white">{incomingCallInfo.doctor.name}</h3>
              <p className="text-md text-gray-500 dark:text-gray-300 mb-6 animate-pulse">Incoming video call...</p>

              <div className="flex justify-center space-x-6">
                <button 
                  className="w-18 h-18 rounded-full flex items-center justify-center shadow-lg transform transition-transform hover:scale-110"
                  style={{ backgroundColor: colors.danger }}
                  onClick={rejectIncomingCall}
                >
                  <div className="w-16 h-16 rounded-full flex items-center justify-center bg-red-600">
                    <Phone size={30} className="text-white transform rotate-135" />
                  </div>
                </button>

                <button 
                  className="w-18 h-18 rounded-full flex items-center justify-center shadow-lg transform transition-transform hover:scale-110"
                  style={{ backgroundColor: colors.success }}
                  onClick={() => acceptIncomingCall(incomingCallInfo.doctor)}
                >
                  <div className="w-16 h-16 rounded-full flex items-center justify-center bg-green-600 animate-pulse">
                    <Phone size={30} className="text-white" />
                  </div>
                </button>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Enhanced toast with longer display time */}
      <div 
        className={`fixed bottom-24 left-1/2 transform -translate-x-1/2 rounded-xl px-4 py-3 shadow-lg transition-all duration-300 z-50 dark-mode-transition ${
          activeToast ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-10 pointer-events-none'
        }`}
        style={{ 
          maxWidth: '90%',
          backgroundColor: isDarkMode ? colors.cardBg : 'white',
          boxShadow: `0 15px 30px -5px ${colors.primary}30`
        }}
      >
        <div className="text-sm font-medium dark-mode-transition" style={{ color: colors.textPrimary }}>
          {activeToast}
        </div>
      </div>

      {/* Custom CSS for animations */}
      <style jsx>{`
        @keyframes shimmer {
          0% { background-position: -100% 0; }
          100% { background-position: 200% 0; }
        }

        .shimmer-effect {
          background: linear-gradient(
            90deg,
            rgba(255, 255, 255, 0) 0%,
            rgba(255, 255, 255, 0.4) 50%,
            rgba(255, 255, 255, 0) 100%
          );
          background-size: 200% 100%;
          animation: shimmer 2s infinite linear;
        }

        .dark .shimmer-effect {
          background: linear-gradient(
            90deg,
            rgba(30, 41, 59, 0) 0%,
            rgba(30, 41, 59, 0.4) 50%,
            rgba(30, 41, 59, 0) 100%
          );
          background-size: 200% 100%;
          animation: shimmer 2s infinite linear;
        }

        .hide-scrollbar::-webkit-scrollbar {
          display: none;
        }

        .hide-scrollbar {
          -ms-overflow-style: none;
          scrollbar-width: none;
        }

        @keyframes bounce-in {
          0% { transform: scale(0.8); opacity: 0; }
          70% { transform: scale(1.05); opacity: 1; }
          100% { transform: scale(1); opacity: 1; }
        }
        .animate-bounce-in {
          animation: bounce-in 0.5s ease-out forwards;
        }

        @keyframes fade-in {
          0% { opacity: 0; }
          100% { opacity: 1; }
        }
        .animate-fade-in {
          animation: fade-in 0.3s ease-out forwards;
        }
      `}</style>
    </div>
  );
};

export default ConnectScreen;
