import React, { useState, useEffect } from 'react';
import { 
  PlusCircle, CheckCircle, Clock, Zap, AlertTriangle, 
  Calendar, ChevronRight, X, ArrowLeft, Search, 
  Edit, MoreVertical, Bell, Eye, EyeOff, BarChart2, 
  Shield, Pill, Info, RefreshCw, ChevronDown, Filter
} from "lucide-react";

// Default colors in case they're not provided or missing properties
const defaultColors = {
  primary: '#1E40AF',
  primaryDark: '#1E3A8A',
  primaryLight: '#60A5FA',
  primaryGlow: '#93C5FD',
  accent: '#0891B2',
  accentAlt: '#7C3AED',
  success: '#059669',
  warning: '#D97706',
  danger: '#DC2626',
  background: '#EFF6FF',
  darkBg: '#0F172A',
  cardBg: '#FFFFFF',
  textPrimary: '#1E293B',
  textSecondary: '#64748B',
  gradientStart: '#1E40AF',
  gradientEnd: '#0891B2',
  gradientAlt1: '#7C3AED',
  gradientAlt2: '#EC4899',
  neonAccent: '#06B6D4',
  glass: 'rgba(255, 255, 255, 0.25)'
};

const MedicationsScreen = ({ colors = {}, setActiveScreen }) => {
  // Ensure colors object is complete by merging with defaults
  const themeColors = { ...defaultColors, ...colors };

  const [view, setView] = useState('main'); // main, detail, add, history, schedule, interactions
  const [selectedMedication, setSelectedMedication] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [showAddOptions, setShowAddOptions] = useState(false);
  const [showFilterOptions, setShowFilterOptions] = useState(false);

  // Medication data - in a real app, this would come from a database or API
  const [medications, setMedications] = useState([
    {
      id: 1,
      name: "Metformin",
      dosage: "500mg",
      schedule: [
        { time: "8:00 AM", status: "taken", day: "today" },
        { time: "9:00 PM", status: "upcoming", day: "today" }
      ],
      instructions: "Take with food to reduce stomach upset",
      purpose: "Controls blood sugar levels",
      refillDate: "April 15, 2025",
      refillRemaining: 8,
      prescribedBy: "Dr. Johnson",
      startDate: "January 15, 2025",
      interactions: [
        { medication: "Ibuprofen", severity: "moderate", description: "May reduce effectiveness and increase risk of low blood sugar" },
        { medication: "Alcohol", severity: "high", description: "Can cause dangerously low blood sugar and lactic acidosis" }
      ],
      sideEffects: ["Nausea", "Diarrhea", "Stomach upset", "Metallic taste"],
      adherenceRate: 92,
      missedDoses: 2,
      history: [
        { date: "Mar 22, 2025", time: "8:05 AM", status: "taken", delay: "5 min late" },
        { date: "Mar 21, 2025", time: "8:12 AM", status: "taken", delay: "12 min late" },
        { date: "Mar 21, 2025", time: "9:00 PM", status: "taken", delay: "on time" },
        { date: "Mar 20, 2025", time: "8:00 AM", status: "taken", delay: "on time" },
        { date: "Mar 20, 2025", time: "9:00 PM", status: "taken", delay: "on time" },
        { date: "Mar 19, 2025", time: "8:00 AM", status: "missed", delay: "missed" },
        { date: "Mar 19, 2025", time: "9:00 PM", status: "taken", delay: "on time" }
      ]
    },
    {
      id: 2,
      name: "Lisinopril",
      dosage: "10mg",
      schedule: [
        { time: "8:00 AM", status: "taken", day: "today" }
      ],
      instructions: "Take on an empty stomach",
      purpose: "Controls blood pressure",
      refillDate: "April 8, 2025",
      refillRemaining: 3,
      prescribedBy: "Dr. Smith",
      startDate: "December 10, 2024",
      interactions: [
        { medication: "Potassium supplements", severity: "high", description: "Can cause dangerously high potassium levels" },
        { medication: "NSAIDs", severity: "moderate", description: "May reduce blood pressure lowering effects" }
      ],
      sideEffects: ["Dry cough", "Dizziness", "Headache"],
      adherenceRate: 95,
      missedDoses: 1,
      history: [
        { date: "Mar 22, 2025", time: "8:05 AM", status: "taken", delay: "5 min late" },
        { date: "Mar 21, 2025", time: "8:10 AM", status: "taken", delay: "10 min late" },
        { date: "Mar 20, 2025", time: "8:00 AM", status: "taken", delay: "on time" },
        { date: "Mar 19, 2025", time: "8:00 AM", status: "taken", delay: "on time" },
        { date: "Mar 18, 2025", time: "missed", status: "missed", delay: "missed" }
      ]
    },
    {
      id: 3,
      name: "Atorvastatin",
      dosage: "20mg",
      schedule: [
        { time: "9:00 PM", status: "upcoming", day: "today" }
      ],
      instructions: "Take in the evening",
      purpose: "Lowers cholesterol",
      refillDate: "May 2, 2025",
      refillRemaining: 15,
      prescribedBy: "Dr. Smith",
      startDate: "November 5, 2024",
      interactions: [
        { medication: "Grapefruit juice", severity: "high", description: "Can increase risk of muscle pain and damage" },
        { medication: "Antifungals", severity: "high", description: "Can increase risk of side effects" }
      ],
      sideEffects: ["Muscle pain", "Joint pain", "Digestive issues"],
      adherenceRate: 88,
      missedDoses: 3,
      history: [
        { date: "Mar 21, 2025", time: "9:15 PM", status: "taken", delay: "15 min late" },
        { date: "Mar 20, 2025", time: "9:00 PM", status: "taken", delay: "on time" },
        { date: "Mar 19, 2025", time: "9:00 PM", status: "taken", delay: "on time" },
        { date: "Mar 18, 2025", time: "9:00 PM", status: "missed", delay: "missed" },
        { date: "Mar 17, 2025", time: "9:20 PM", status: "taken", delay: "20 min late" }
      ]
    },
    {
      id: 4,
      name: "Aspirin",
      dosage: "81mg",
      schedule: [
        { time: "8:00 AM", status: "taken", day: "today" }
      ],
      instructions: "Take with food to reduce stomach irritation",
      purpose: "Blood thinner",
      refillDate: "June 10, 2025",
      refillRemaining: 25,
      prescribedBy: "Dr. Johnson",
      startDate: "January 5, 2025",
      interactions: [
        { medication: "Ibuprofen", severity: "moderate", description: "May reduce heart benefits of aspirin" },
        { medication: "Blood thinners", severity: "high", description: "Increased risk of bleeding" }
      ],
      sideEffects: ["Upset stomach", "Heartburn", "Easy bruising"],
      adherenceRate: 98,
      missedDoses: 0,
      history: [
        { date: "Mar 22, 2025", time: "8:00 AM", status: "taken", delay: "on time" },
        { date: "Mar 21, 2025", time: "8:00 AM", status: "taken", delay: "on time" },
        { date: "Mar 20, 2025", time: "8:00 AM", status: "taken", delay: "on time" },
        { date: "Mar 19, 2025", time: "8:00 AM", status: "taken", delay: "on time" },
        { date: "Mar 18, 2025", time: "8:00 AM", status: "taken", delay: "on time" }
      ]
    }
  ]);

  // Filter medications based on search query
  const filteredMedications = medications.filter(med => 
    med.name.toLowerCase().includes(searchQuery.toLowerCase())
  );

  // Main screen with list of medications
  const renderMainScreen = () => (
    <>
      <header className="flex justify-between items-center mb-8">
        <div>
          <h1 className="text-3xl font-bold bg-gradient-to-r from-blue-700 to-cyan-500 bg-clip-text text-transparent">Medications</h1>
          <p className="text-sm" style={{ color: themeColors.textSecondary }}>Track and manage your prescriptions</p>
        </div>
        <div className="relative">
          <button 
            className="w-12 h-12 rounded-full flex items-center justify-center shadow-lg transition-transform hover:scale-110 duration-300 z-10" 
            style={{ 
              background: `linear-gradient(135deg, ${themeColors.primary}, ${themeColors.accent})`,
              boxShadow: `0 0 15px ${themeColors.primary}40`
            }}
            onClick={() => setShowAddOptions(!showAddOptions)}
          >
            <PlusCircle size={24} color="white" />
          </button>

          {/* Add medication options popup */}
          {showAddOptions && (
            <div 
              className="absolute right-0 top-14 w-56 p-2 rounded-xl shadow-xl z-20 border border-blue-100 bg-white"
              style={{ boxShadow: `0 10px 25px -5px ${themeColors.primary}30` }}
            >
              <div className="p-2 rounded-lg hover:bg-blue-50 cursor-pointer transition-colors duration-200" onClick={() => {setView('add'); setShowAddOptions(false);}}>
                <div className="flex items-center">
                  <div className="w-8 h-8 rounded-full mr-3 flex items-center justify-center" style={{ backgroundColor: `${themeColors.primary}20`, color: themeColors.primary }}>
                    <Pill size={16} />
                  </div>
                  <span className="text-sm font-medium" style={{ color: themeColors.textPrimary }}>Add medication manually</span>
                </div>
              </div>

              <div className="p-2 rounded-lg hover:bg-blue-50 cursor-pointer transition-colors duration-200">
                <div className="flex items-center">
                  <div className="w-8 h-8 rounded-full mr-3 flex items-center justify-center" style={{ backgroundColor: `${themeColors.accent}20`, color: themeColors.accent }}>
                    <Shield size={16} />
                  </div>
                  <span className="text-sm font-medium" style={{ color: themeColors.textPrimary }}>Scan prescription</span>
                </div>
              </div>

              <div className="p-2 rounded-lg hover:bg-blue-50 cursor-pointer transition-colors duration-200">
                <div className="flex items-center">
                  <div className="w-8 h-8 rounded-full mr-3 flex items-center justify-center" style={{ backgroundColor: `${themeColors.success}20`, color: themeColors.success }}>
                    <RefreshCw size={16} />
                  </div>
                  <span className="text-sm font-medium" style={{ color: themeColors.textPrimary }}>Import from pharmacy</span>
                </div>
              </div>
            </div>
          )}
        </div>
      </header>

      {/* Weekly medication adherence overview */}
      <div className="mb-8 p-5 rounded-2xl bg-white shadow-lg border border-blue-50 transform hover:scale-102 transition-all duration-500"
           style={{ boxShadow: `0 15px 25px -5px ${themeColors.primary}20` }}>
        <div className="flex justify-between items-center mb-4">
          <h3 className="font-bold text-lg" style={{ color: themeColors.textPrimary }}>Weekly Adherence</h3>
          <button className="flex items-center text-sm font-medium px-3 py-1 rounded-full transition-all duration-300 hover:shadow-md" 
                  style={{ color: themeColors.primary, backgroundColor: `${themeColors.primary}10` }}
                  onClick={() => setView('history')}>
            <BarChart2 size={16} className="mr-1" />
            History
          </button>
        </div>
        <div className="flex justify-between items-center">
          {['Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat', 'Sun'].map((day, index) => {
            let bgColor, icon;
            if (index <= 1 || index === 3) {
              bgColor = `linear-gradient(135deg, ${themeColors.success}, ${themeColors.success}90)`;
              icon = <CheckCircle size={18} color="white" />;
            } else if (index === 2) {
              bgColor = `linear-gradient(135deg, ${themeColors.warning}, ${themeColors.warning}90)`;
              icon = <Clock size={18} color="white" />;
            } else {
              bgColor = 'transparent';
              icon = null;
            }

            return (
              <div key={day} className="flex flex-col items-center">
                <div className="w-10 h-10 rounded-full flex items-center justify-center shadow-md mb-2 transition-transform hover:scale-110 duration-300" 
                     style={{ 
                       background: bgColor,
                       border: index > 3 ? '2px dashed #E5E7EB' : 'none',
                       boxShadow: index <= 3 ? `0 0 10px ${themeColors.primary}20` : 'none'
                     }}>
                  {icon || <span className="text-xs font-medium text-gray-400">{day[0]}</span>}
                </div>
                <span className="text-xs">{day}</span>
              </div>
            );
          })}
        </div>
        <div className="mt-4 p-3 rounded-xl shadow-sm flex items-center animate-breathe" 
             style={{ 
               background: `linear-gradient(to right, ${themeColors.primary}20, ${themeColors.accent}10)`
             }}>
          <div className="w-10 h-10 rounded-full flex items-center justify-center mr-3 shadow-sm shimmer" 
               style={{ 
                 background: `linear-gradient(135deg, ${themeColors.primary}40, ${themeColors.primary}20)`
               }}>
            <Zap size={18} color={themeColors.primary} />
          </div>
          <p className="text-sm font-medium" style={{ color: themeColors.primary }}>
            You've taken 92% of your medications on time this week! Better than 87% of users.
          </p>
        </div>
      </div>

      {/* Search and filter */}
      <div className="mb-6 flex items-center gap-3">
        <div className="flex-1 relative">
          <div className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400">
            <Search size={18} />
          </div>
          <input
            type="text"
            className="w-full pl-10 pr-4 py-3 rounded-xl border border-blue-100 focus:outline-none focus:ring-2 focus:ring-blue-400 transition-all duration-300"
            placeholder="Search medications..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
        </div>
        <div className="relative">
          <button 
            className="p-3 rounded-xl bg-white border border-blue-100 hover:bg-blue-50 transition-colors duration-300 flex items-center justify-center"
            onClick={() => setShowFilterOptions(!showFilterOptions)}
          >
            <Filter size={18} style={{ color: themeColors.textPrimary }} />
          </button>

          {/* Filter options popup */}
          {showFilterOptions && (
            <div 
              className="absolute right-0 top-12 w-44 p-2 rounded-xl shadow-xl z-20 border border-blue-100 bg-white"
              style={{ boxShadow: `0 10px 25px -5px ${themeColors.primary}30` }}
            >
              <div className="text-xs font-medium mb-1 px-2" style={{ color: themeColors.textSecondary }}>Filter by</div>
              <div className="p-2 rounded-lg hover:bg-blue-50 cursor-pointer transition-colors duration-200">
                <span className="text-sm" style={{ color: themeColors.textPrimary }}>All medications</span>
              </div>
              <div className="p-2 rounded-lg hover:bg-blue-50 cursor-pointer transition-colors duration-200">
                <span className="text-sm" style={{ color: themeColors.textPrimary }}>Morning</span>
              </div>
              <div className="p-2 rounded-lg hover:bg-blue-50 cursor-pointer transition-colors duration-200">
                <span className="text-sm" style={{ color: themeColors.textPrimary }}>Evening</span>
              </div>
              <div className="p-2 rounded-lg hover:bg-blue-50 cursor-pointer transition-colors duration-200">
                <span className="text-sm" style={{ color: themeColors.textPrimary }}>Needs refill</span>
              </div>
            </div>
          )}
        </div>
        <button 
          className="p-3 rounded-xl bg-white border border-blue-100 hover:bg-blue-50 transition-colors duration-300 flex items-center justify-center"
          onClick={() => setView('schedule')}
        >
          <Calendar size={18} style={{ color: themeColors.textPrimary }} />
        </button>
      </div>

      {/* Medications section with sticky header */}
      <div className="mb-6">
        <div className="mb-4 sticky top-0 bg-blue-50 z-10 flex justify-between items-center px-4 py-2 rounded-lg">
          <h3 className="font-bold" style={{ color: themeColors.textPrimary }}>Today's Medications</h3>
          <button
            className="text-xs px-2 py-1 rounded-full transition-colors duration-300"
            style={{ color: themeColors.primary, backgroundColor: themeColors.primary + '20' }}
            onClick={() => setView('schedule')}
          >
            View schedule
          </button>
        </div>

        {/* Medication list */}
        <div className="space-y-4">
          {filteredMedications.filter(med => 
            med.schedule.some(s => s.day === 'today')
          ).map(med => (
            <div
              key={med.id}
              className="p-4 rounded-2xl bg-white shadow-lg border border-blue-50 flex items-center transform hover:scale-105 transition-all duration-500 hover:shadow-xl cursor-pointer"
              style={{ boxShadow: `0 10px 15px -3px ${themeColors.primary}20` }}
              onClick={() => {
                setSelectedMedication(med);
                setView('detail');
              }}
            >
              <div className="w-12 h-12 rounded-xl mr-4 flex items-center justify-center shimmer" 
                  style={{ 
                    background: `linear-gradient(135deg, ${themeColors.primary}90, ${themeColors.primary})`
                  }}>
                <Pill size={22} color="white" />
              </div>
              <div className="flex-1">
                <div className="flex items-center">
                  <h4 className="font-medium" style={{ color: themeColors.textPrimary }}>{med.name}</h4>
                  <span className="ml-2 px-2 py-0.5 rounded-full text-xs font-medium transition-all duration-300 hover:shadow-sm" 
                        style={{ 
                          background: `linear-gradient(to right, ${themeColors.primary}20, ${themeColors.accent}10)`,
                          color: themeColors.primary
                        }}>
                    {med.dosage}
                  </span>
                </div>
                <div className="flex mt-1">
                  {med.schedule.map((time, idx) => (
                    <div key={idx} className="flex items-center mr-3">
                      <div 
                        className={`w-2 h-2 rounded-full mr-1`}
                        style={{ 
                          backgroundColor: time.status === 'taken' 
                            ? themeColors.success 
                            : time.status === 'missed' 
                              ? themeColors.danger 
                              : themeColors.warning 
                        }}
                      ></div>
                      <p className="text-xs" style={{ color: themeColors.textSecondary }}>{time.time}</p>
                    </div>
                  ))}
                </div>
              </div>
              <ChevronRight size={20} style={{ color: themeColors.textSecondary }} />
            </div>
          ))}
        </div>
      </div>

      {/* All medications section */}
      <div>
        <div className="mb-4 sticky top-12 bg-blue-50 z-10 flex justify-between items-center px-4 py-2 rounded-lg">
          <h3 className="font-bold" style={{ color: themeColors.textPrimary }}>All Medications</h3>
          <span className="text-xs px-2 py-1 rounded-full" style={{ backgroundColor: themeColors.primary + '20', color: themeColors.primary }}>
            {filteredMedications.length} total
          </span>
        </div>

        <div className="space-y-4">
          {filteredMedications.map(med => (
            <div
              key={med.id}
              className="p-4 rounded-2xl bg-white shadow-lg border border-blue-50 flex items-center transform hover:scale-105 transition-all duration-300 hover:shadow-xl cursor-pointer"
              style={{ boxShadow: `0 10px 15px -3px ${themeColors.primary}20` }}
              onClick={() => {
                setSelectedMedication(med);
                setView('detail');
              }}
            >
              <div className="w-10 h-10 rounded-xl mr-3 flex items-center justify-center" 
                  style={{ 
                    background: `${themeColors.primary}20`
                  }}>
                <Pill size={20} style={{ color: themeColors.primary }} />
              </div>
              <div className="flex-1">
