                      const timeForSlot = med.schedule.find(s => {
                        const hour = parseInt(s.time.split(':')[0]);
                        if (slot === 'Morning') return hour >= 5 && hour < 12;
                        if (slot === 'Afternoon') return hour >= 12 && hour < 17;
                        if (slot === 'Evening') return hour >= 17 && hour < 21;
                        return hour >= 21 || hour < 5;
                      });

                      return (
                        <div 
                          key={`${med.id}-${slot}`} 
                          className="p-3 rounded-xl bg-white shadow-md flex items-center justify-between hover:shadow-lg transition-all duration-300"
                          style={{ boxShadow: `0 5px 10px -3px ${themeColors.primary}10` }}
                        >
                          <div className="flex items-center">
                            <div 
                              className="w-8 h-8 rounded-full flex items-center justify-center mr-2"
