                style={{ 
                  backgroundColor: themeColors.primary,
                  boxShadow: `0 0 10px ${themeColors.primary}40`
                }}
              >
                <span className="absolute top-0.5 right-0.5 w-4 h-4 rounded-full bg-white transform transition-transform duration-300"></span>
              </button>
            </div>

            {/* Photo option */}
            <div className="flex items-center justify-between p-3 rounded-xl hover:bg-blue-50 transition-colors duration-300">
              <div className="flex items-center">
                <div className="w-8 h-8 rounded-full flex items-center justify-center mr-3" 
                     style={{ 
                       backgroundColor: `${themeColors.accent}20`,
