              }}
            >
              Save Medication
            </button>
          </form>
        </div>
      </>
    );
  };

  // Render the appropriate view
  const renderView = () => {
    switch(view) {
      case 'detail':
        return renderMedicationDetail();
      case 'history':
        return renderMedicationHistory();
      case 'schedule':
        return renderMedicationSchedule();
      case 'interactions':
        return renderMedicationInteractions();
      case 'add':
        return renderAddMedicationForm();
      default:
        return renderMainScreen();
    }
  };

  // Cleanup any popups when clicking outside
  useEffect(() => {
    const handleOutsideClick = () => {
      setShowAddOptions(false);
      setShowFilterOptions(false);
    };

    window.addEventListener('click', handleOutsideClick);
    return () => window.removeEventListener('click', handleOutsideClick);
  }, []);

  return (
    <div className="p-6 pb-8 overflow-y-auto">
      {renderView()}

      {/* Custom CSS for animations */}
      <style jsx>{`
        @keyframes float {
          0%, 100% { transform: translateY(0); }
          50% { transform: translateY(-10px); }
        }

        @keyframes pulse {
          0%, 100% { opacity: 1; }
          50% { opacity: 0.6; }
        }

        @keyframes breathe {
          0%, 100% { transform: scale(1); }
          50% { transform: scale(1.05); }
