import React, { useState, useEffect, useCallback } from 'react';
import { 
  Activity, Calendar, MessageCircle, Heart, AlertTriangle, 
  Clock, ChevronRight, CheckCircle, Trophy, ArrowUpRight, 
  Zap, Bell, BarChart2, Thermometer, Droplet, RefreshCw, Pill,
  TrendingUp, Hexagon, Moon, Users, BatteryCharging, CloudSun, Wind,
  ShieldCheck, Plus, Award
} from 'lucide-react';
import { useHealthData } from '../HealthDataContext';
import { useApiMiddleware } from '../ApiMiddleware';
import { useTheme } from '../ThemeContext';
import { useAuth } from '../AuthProvider';
import useWindowSize from '../hooks/useWindowSize';

// Import subcomponents to improve organization
import HealthMetricsSection from '../components/dashboard/HealthMetricsSection';
import MedicationCard from '../components/dashboard/MedicationCard';
import BiorhythmPreview from '../components/dashboard/BiorhythmPreview';

const DashboardScreen = ({ colors, setActiveScreen }) => {
  const { isDarkMode } = useTheme();
  const { isLoading, callApiWithOptions } = useApiMiddleware();
  const { user } = useAuth();
  const { 
    healthMetrics, 
    isGoogleFitConnected,
    prediction, 
    symptoms,
    lastRefreshTime,
    fetchHealthData,
    connectGoogleFit,
    biorhythmAdvice,
    environmentAdvice,
    getBiorhythmAdvice,
    getEnvironmentAdvice
  } = useHealthData();

  // Get window size for responsive adjustments
  const { width, height } = useWindowSize();

  // Local state
  const [refreshing, setRefreshing] = useState(false);
  const [medications, setMedications] = useState([]);
  const [upcomingMedication, setUpcomingMedication] = useState(null);
  const [loadingMedications, setLoadingMedications] = useState(true);
  const [healthScore, setHealthScore] = useState(prediction?.HealthScore || 85);
  const [refreshAnimation, setRefreshAnimation] = useState(false);
  const [selectedMetric, setSelectedMetric] = useState('all');
  const [energyLevel, setEnergyLevel] = useState(78);
  const [loadingEnvironment, setLoadingEnvironment] = useState(false);
  const [showStats, setShowStats] = useState(false);

  // Format today's date
  const today = new Date().toLocaleDateString('en-US', { 
    weekday: 'long', 
    month: 'long', 
    day: 'numeric' 
  });

  // Memoized fetch medications function to prevent unnecessary re-renders
  const fetchMedications = useCallback(async () => {
    try {
      setLoadingMedications(true);

      // Use API middleware for better error handling
      const medData = await callApiWithOptions(
        async () => {
          try {
            const response = await fetch('/api/medications');
            if (!response.ok) throw new Error('Failed to fetch medications');
            return await response.json();
          } catch (err) {
            console.error('Fetch error:', err);
            throw err;
          }
        },
        [],
        {
          skipCache: false,
          cacheKey: 'user-medications',
          showSuccessMessage: false
        }
      );

      if (medData && medData.length > 0) {
        setMedications(medData);

        // Find nearest upcoming medication
        const now = new Date();
        const nextMeds = medData
          .map(med => {
            const nextSchedule = med.schedule?.find(s => s.status === 'upcoming');
            if (!nextSchedule) return null;

            const [time, period] = nextSchedule.time.split(' ');
            const [hours, minutes] = time.split(':');

            let scheduleHours = parseInt(hours);
            if (period === 'PM' && scheduleHours !== 12) {
              scheduleHours += 12;
            } else if (period === 'AM' && scheduleHours === 12) {
              scheduleHours = 0;
            }

            const scheduleTime = new Date();
            scheduleTime.setHours(scheduleHours, parseInt(minutes), 0, 0);

            if (scheduleTime < now) {
              scheduleTime.setDate(scheduleTime.getDate() + 1);
            }

            return {
              ...med,
              nextDose: scheduleTime,
              nextSchedule
            };
          })
          .filter(Boolean)
          .sort((a, b) => a.nextDose - b.nextDose);

        setUpcomingMedication(nextMeds[0] || null);
      }

      setLoadingMedications(false);
    } catch (error) {
      console.error("Failed to fetch medications:", error);
      setLoadingMedications(false);

      // Fallback to sample data for demo
      const sampleMedications = [
        {
          id: 1,
          name: "Metformin",
          dosage: "500mg",
          form: "Tablet",
          schedule: [
            { time: "08:00 AM", status: "upcoming", day_reference: "daily" },
            { time: "08:00 PM", status: "upcoming", day_reference: "daily" }
          ],
          last_taken: new Date(new Date().setHours(new Date().getHours() - 12)).toISOString()
        }
      ];

      setMedications(sampleMedications);
      setUpcomingMedication(sampleMedications[0]);
    }
  }, [callApiWithOptions]);

  // Load initial data
  useEffect(() => {
    // Fetch medications
    fetchMedications();

    // Fetch or refresh biorhythm advice if needed
    if (!biorhythmAdvice) {
      getBiorhythmAdvice('morning').catch(err => 
        console.error('Error fetching biorhythm advice:', err)
      );
    }

    // Only fetch environment advice if we don't have it
    if (!environmentAdvice) {
      setLoadingEnvironment(true);
      getEnvironmentAdvice()
        .finally(() => setLoadingEnvironment(false));
    }

    // Update health score from prediction when available
    if (prediction && prediction.HealthScore) {
      setHealthScore(prediction.HealthScore);
    }

    // Simulated energy level changes for demo
    const energyInterval = setInterval(() => {
      setEnergyLevel(prev => {
        const change = Math.random() > 0.5 ? 1 : -1;
        return Math.min(Math.max(prev + change, 70), 95);
      });
    }, 10000);

    // Cleanup
    return () => {
      clearInterval(energyInterval);
    };
  }, [
    fetchMedications, 
    getBiorhythmAdvice, 
    getEnvironmentAdvice, 
    biorhythmAdvice, 
    environmentAdvice,
    prediction
  ]);

  // Connect to Google Fit
  const handleConnectGoogleFit = async () => {
    try {
      await connectGoogleFit();
    } catch (error) {
      console.error('Failed to connect to Google Fit:', error);
    }
  };

  // Refresh health data with animation
  const handleRefreshData = async () => {
    try {
      setRefreshing(true);
      setRefreshAnimation(true);
      await fetchHealthData();

      // Also refresh medications
      await fetchMedications();

      setRefreshing(false);

      // Reset animation after a delay
      setTimeout(() => {
        setRefreshAnimation(false);
      }, 2000);
    } catch (error) {
      console.error('Failed to refresh health data:', error);
      setRefreshing(false);
      setRefreshAnimation(false);
    }
  };

  // Format time until next medication
  const formatTimeUntil = (medication) => {
    if (!medication || !medication.nextDose) return "No upcoming medications";

    const now = new Date();
    const diffMs = medication.nextDose - now;
    const diffMinutes = Math.floor(diffMs / 60000);

    if (diffMinutes < 60) {
      return `In ${diffMinutes} minute${diffMinutes !== 1 ? 's' : ''}`;
    } else {
      const hours = Math.floor(diffMinutes / 60);
      const minutes = diffMinutes % 60;
      return `In ${hours} hour${hours !== 1 ? 's' : ''} ${minutes > 0 ? `and ${minutes} min` : ''}`;
    }
  };

  // Format blood pressure for display
  const formatBloodPressure = () => {
    if (!healthMetrics?.pressure?.systolic || !healthMetrics?.pressure?.diastolic) {
      return '120/80';
    }
    return `${Math.round(healthMetrics.pressure.systolic)}/${Math.round(healthMetrics.pressure.diastolic)}`;
  };

  // Get color for metric based on health status
  const getMetricColor = (metricType, value) => {
    if (!value) return colors.textSecondary;

    switch (metricType) {
      case 'BMI':
        if (value < 18.5 || value >= 30) return colors.danger;
        if (value >= 25) return colors.warning;
        return colors.success;
      case 'BP':
        const systolic = value.systolic;
        const diastolic = value.diastolic;
        if (!systolic || !diastolic) return colors.textSecondary;
        if (systolic >= 140 || diastolic >= 90) return colors.danger;
        if (systolic >= 130 || diastolic >= 80) return colors.warning;
        return colors.success;
      case 'GLUCOSE':
        if (value >= 126) return colors.danger;
        if (value >= 100) return colors.warning;
        if (value < 70) return colors.warning;
        return colors.success;
      default:
        return colors.primary;
    }
  };

  // Format time since last refresh
  const formatTimeSince = (date) => {
    if (!date) return 'Not yet';

    const now = new Date();
    const seconds = Math.floor((now - date) / 1000);

    if (seconds < 60) return 'Just now';
    if (seconds < 120) return '1 minute ago';
    if (seconds < 3600) return `${Math.floor(seconds / 60)} minutes ago`;
    if (seconds < 7200) return '1 hour ago';
    if (seconds < 86400) return `${Math.floor(seconds / 3600)} hours ago`;

    return date.toLocaleTimeString();
  };

  // Get color for AQI based on value
  const getAqiColor = (aqi) => {
    if (!aqi) return colors.textSecondary;
    if (aqi <= 1) return colors.success;
    if (aqi <= 3) return colors.warning;
    return colors.danger;
  };

  // Format AQI text
  const formatAqiText = (aqi) => {
    if (!aqi) return 'Unknown';
    if (aqi <= 1) return 'Good';
    if (aqi <= 2) return 'Fair';
    if (aqi <= 3) return 'Moderate';
    if (aqi <= 4) return 'Poor';
    return 'Very Poor';
  };

  return (
    <div className="px-4 pt-4 pb-20 space-y-5">
      {/* Gradient background with animated mesh */}
      <div className="fixed inset-0 -z-10 overflow-hidden pointer-events-none">
        <div className="absolute inset-0 bg-gradient-to-b dark-mode-transition"
             style={{ 
               backgroundImage: isDarkMode 
                 ? `linear-gradient(to bottom, ${colors.darkBg}, ${colors.cardBg})`
                 : `linear-gradient(to bottom, #EFF6FF, white)`
             }}></div>
        <div className="absolute inset-0 opacity-30 dark-mode-transition" 
             style={{ backgroundImage: `radial-gradient(${colors.primary}20 1px, transparent 1px)`,
                      backgroundSize: '20px 20px' }}></div>
        <div className="absolute -top-40 -right-40 w-96 h-96 rounded-full opacity-20 blur-3xl dark-mode-transition"
             style={{ background: `radial-gradient(circle at center, ${colors.primary}, transparent 70%)` }}></div>
        <div className="absolute -bottom-20 -left-20 w-64 h-64 rounded-full opacity-10 blur-3xl dark-mode-transition"
             style={{ background: `radial-gradient(circle at center, ${colors.gradientAlt1}, transparent 70%)` }}></div>
      </div>

      {/* Header with welcome message and notifications */}
      <header className="flex justify-between items-center mb-2">
        <div>
          <h1 className="text-xl font-bold mb-1 transition-all duration-500" style={{ 
            background: `linear-gradient(135deg, ${colors.primary}, ${colors.accent})`,
            WebkitBackgroundClip: 'text',
            WebkitTextFillColor: 'transparent'
          }}>Hi, {user?.name || 'there'}! </h1>
          <div className="flex items-center">
            <Calendar size={14} className="mr-1" style={{ color: colors.textSecondary }} />
            <p className="text-xs dark-mode-transition" style={{ color: colors.textSecondary }}>{today}</p>
          </div>
        </div>
        <div className="relative">
          <div className="absolute -inset-1 rounded-full animate-pulse" 
               style={{ background: `radial-gradient(circle, ${colors.accent}50, transparent 70%)` }}></div>
          <button 
            className="relative w-10 h-10 rounded-full shadow-lg flex items-center justify-center transition-transform hover:scale-110 duration-300"
            style={{ 
              boxShadow: `0 0 15px ${colors.primary}40`,
              backgroundColor: isDarkMode ? colors.cardBg : 'white'
            }}
            onClick={() => alert('Notifications feature coming soon!')}
          >
            <Bell size={20} style={{ color: colors.primary }} />
            <span className="absolute -top-1 -right-1 w-5 h-5 rounded-full flex items-center justify-center text-white text-xs font-bold animate-pulse"
                  style={{ backgroundColor: colors.danger }}>
              3
            </span>
          </button>
        </div>
      </header>

      {/* Health Score Card with 3D effect and particles */}
      <div 
        className="p-5 rounded-2xl relative overflow-hidden transform transition-all duration-500 hover:scale-105 group"
        style={{ 
          background: `linear-gradient(135deg, ${colors.primary}, ${colors.accent})`,
          boxShadow: `0 15px 30px -10px ${colors.primary}70`
        }}
      >
        {/* Animated particles */}
        {[...Array(6)].map((_, i) => (
          <div 
            key={i}
            className="absolute rounded-full opacity-20 particle"
            style={{
              width: Math.random() * 12 + 4 + 'px',
              height: Math.random() * 12 + 4 + 'px',
              left: Math.random() * 100 + '%',
              top: Math.random() * 100 + '%',
              background: 'rgba(255,255,255,0.4)',
              boxShadow: '0 0 10px rgba(255,255,255,0.4)',
              animation: `float ${Math.random() * 10 + 10}s linear infinite`,
              transform: `translateY(${Math.random() * 50}px)`,
              animationDelay: `${Math.random() * 5}s`
            }}
          />
        ))}

        <div className="absolute top-0 right-0 w-32 h-32 rounded-full animate-float group-hover:animate-pulse" 
             style={{ 
               background: `radial-gradient(circle, rgba(255,255,255,0.15), transparent 70%)`,
               transform: 'translate(30%, -30%)'
             }}></div>
        <div className="absolute bottom-0 left-0 w-24 h-24 rounded-full animate-float-delayed" 
             style={{ 
               background: `radial-gradient(circle, rgba(255,255,255,0.1), transparent 70%)`,
               transform: 'translate(-30%, 30%)'
             }}></div>
        <div className="relative z-10">
          <div className="flex items-center justify-between mb-2">
            <p className="text-white text-opacity-90 text-sm font-medium">Today's Health Score</p>
            <div className="flex items-center text-xs font-medium px-2 py-1 bg-white bg-opacity-20 rounded-full backdrop-blur-sm">
              <TrendingUp size={12} className="mr-1" /> Daily Trend
            </div>
          </div>

          <div className="flex items-center justify-between">
            <h2 className="text-3xl font-bold text-white flex items-baseline">
              {healthScore || 90}<span className="text-sm ml-1">/100</span>
            </h2>
            <div className="flex items-center text-xs font-medium px-2 py-0.5 bg-white bg-opacity-20 rounded-full backdrop-blur-sm">
              <ArrowUpRight size={10} className="mr-0.5" /> 5%
            </div>
          </div>

          {/* Health score visualization with glow */}
          <div className="mt-3 mb-2 w-full bg-white bg-opacity-20 rounded-full h-2.5 overflow-hidden">
            <div 
              className="h-full rounded-full shimmer-gradient relative" 
              style={{ 
                width: `${healthScore || 90}%`,
                background: 'rgba(255, 255, 255, 0.4)',
                transition: 'width 1.5s ease-in-out'
              }}
            >
              {/* Glowing dot at the progress end */}
              <div 
                className="absolute right-0 top-1/2 transform -translate-y-1/2 w-4 h-4 rounded-full animate-pulse"
                style={{
                  backgroundColor: 'rgba(255, 255, 255, 0.9)',
                  boxShadow: '0 0 10px 2px rgba(255, 255, 255, 0.8)',
                  filter: 'blur(1px)'
                }}
              ></div>
            </div>
          </div>

          <div className="flex justify-between text-xs text-white text-opacity-80">
            <span>Critical</span>
            <span>Moderate</span>
            <span>Excellent</span>
          </div>
        </div>

        {/* Health score details link */}
        <button 
          className="absolute bottom-2 right-2 text-xs flex items-center text-white text-opacity-80 hover:text-opacity-100 transition-opacity"
          onClick={() => setShowStats(!showStats)}
        >
          {showStats ? 'Hide details' : 'View details'} <ChevronRight size={14} />
        </button>

        {/* Expandable stats (conditionally rendered) */}
        {showStats && (
          <div className="mt-4 pt-4 border-t border-white border-opacity-20 animate-fade-in">
            <div className="grid grid-cols-3 gap-2 text-white">
              <div className="text-center">
                <div className="text-xs text-white text-opacity-70">Heart Rate</div>
                <div className="text-xl font-bold">72<span className="text-xs font-normal">bpm</span></div>
              </div>
              <div className="text-center">
                <div className="text-xs text-white text-opacity-70">Oxygen</div>
                <div className="text-xl font-bold">98<span className="text-xs font-normal">%</span></div>
              </div>
              <div className="text-center">
                <div className="text-xs text-white text-opacity-70">Steps</div>
                <div className="text-xl font-bold">6.2k</div>
              </div>
            </div>
          </div>
        )}
      </div>

      {/* Energy & Activity Card with wave animation */}
      <div className="p-4 rounded-2xl border relative overflow-hidden transform transition-all duration-500 hover:scale-105 group"
           style={{ 
             backgroundColor: isDarkMode ? colors.cardBg : 'white',
             borderColor: isDarkMode ? `${colors.primary}30` : 'rgba(219, 234, 254, 1)',
             boxShadow: `0 10px 20px -5px ${colors.primary}40`
           }}>
        <div className="absolute top-0 right-0 w-32 h-32 rounded-full" 
             style={{ 
               background: `radial-gradient(circle, ${colors.gradientAlt1}10, transparent 70%)`,
               transform: 'translate(30%, -30%)'
             }}></div>

        <div className="flex items-center justify-between mb-3 relative z-10">
          <div>
            <h3 className="font-bold text-base dark-mode-transition" style={{ color: colors.textPrimary }}>
              Energy & Activity
            </h3>
            <p className="text-xs dark-mode-transition" style={{ color: colors.textSecondary }}>
              Your energy levels today
            </p>
          </div>
          <div className="flex space-x-1">
            <div className="flex items-center text-xs font-medium px-2 py-1 rounded-full"
                 style={{ 
                   backgroundColor: isDarkMode ? `${colors.gradientAlt1}20` : `${colors.gradientAlt1}10`,
                   color: colors.gradientAlt1
                 }}>
              <BatteryCharging size={12} className="mr-1" /> {energyLevel}%
            </div>
          </div>
        </div>

        {/* Energy wave visualization with animated waves */}
        <div className="h-16 mb-2 relative overflow-hidden rounded-xl"
             style={{ 
               backgroundColor: isDarkMode ? `${colors.primary}10` : 'rgba(239, 246, 255, 0.6)',
               boxShadow: `inset 0 2px 6px ${colors.primary}10`
             }}>
          <div className="absolute inset-0 flex items-end">
            <svg viewBox="0 0 400 100" preserveAspectRatio="none" className="w-full h-full">
              {/* First wave layer */}
              <path
                className="wave wave-1"
                fill={`${colors.primary}20`}
                d="M0,40 C30,30 70,50 100,40 C130,30 170,50 200,40 C230,30 270,50 300,40 C330,30 370,50 400,40 V100 H0 Z"
              />
              {/* Second wave layer */}
              <path
                className="wave wave-2"
                fill={`${colors.primary}15`}
                d="M0,50 C30,40 70,60 100,50 C130,40 170,60 200,50 C230,40 270,60 300,50 C330,40 370,60 400,50 V100 H0 Z"
              />
              {/* Third wave layer */}
              <path
                className="wave wave-3"
                fill={`${colors.primary}10`}
                d="M0,60 C30,50 70,70 100,60 C130,50 170,70 200,60 C230,50 270,70 300,60 C330,50 370,70 400,60 V100 H0 Z"
              />
            </svg>
          </div>

          {/* Time markers */}
          <div className="absolute inset-x-0 bottom-2 flex justify-between px-3 text-xxs" style={{ color: colors.textSecondary }}>
            <span>6AM</span>
            <span>12PM</span>
            <span>6PM</span>
            <span>Now</span>
          </div>

          {/* Current time indicator with pulse animation */}
          <div className="absolute right-3 h-full w-px" style={{ backgroundColor: colors.primary }}>
            <div className="absolute bottom-6 transform -translate-x-1/2 w-3 h-3 rounded-full animate-pulse"
                 style={{ backgroundColor: colors.primary }}></div>
          </div>
        </div>

        {/* Activity stats */}
        <div className="flex justify-between">
          <div className="flex space-x-3">
            <div className="text-center">
              <div className="text-xl font-bold" style={{ color: colors.primary }}>6.3k</div>
              <div className="text-xs" style={{ color: colors.textSecondary }}>Steps</div>
            </div>
            <div className="text-center">
              <div className="text-xl font-bold" style={{ color: colors.accent }}>73<span className="text-sm">bpm</span></div>
              <div className="text-xs" style={{ color: colors.textSecondary }}>Heart Rate</div>
            </div>
            <div className="text-center">
