              <div className="w-10 h-10 rounded-lg mr-3 flex items-center justify-center shimmer" 
                  style={{ 
                    background: `linear-gradient(135deg, ${colors.gradientAlt1}, ${colors.gradientAlt2})`,
                    boxShadow: `0 4px 8px ${colors.gradientAlt1}30`
                  }}>
                <Zap size={20} color="white" />
              </div>
              <div>
                <h4 className="text-sm font-bold dark-mode-transition" style={{ color: colors.textPrimary }}>
                  {biorhythmAdvice ? "Today's Peak Time" : "Optimize Your Day"}
                </h4>
                <p className="text-xs dark-mode-transition" style={{ color: colors.textSecondary }}>
                  {biorhythmAdvice ? '2-4 PM (Exercise & Creativity)' : 'Connect to optimize your activities'}
                </p>
              </div>
            </div>

            {/* Enhanced biorhythm visualization */}
            {biorhythmAdvice && (
              <div className="mb-4 h-20 relative">
                <div className="absolute inset-x-0 bottom-0 flex items-end">
                  {/* Physical rhythm (green) */}
                  <div className="flex-1 h-8 rounded-l-lg" 
                       style={{ backgroundColor: `${colors.success}30` }}>
                    <div className="w-full h-1 mt-4 bg-white bg-opacity-20"></div>
                  </div>
                  {/* Emotional rhythm (blue) */}
                  <div className="flex-1 h-12 bg-gradient-to-t transition-all" 
                       style={{ 
                         backgroundImage: `linear-gradient(to top, ${colors.primary}50, ${colors.primary}20)`,
                         boxShadow: `0 -4px 6px ${colors.primary}20`
                       }}>
                    <div className="w-full h-1 mt-6 bg-white bg-opacity-20"></div>
                  </div>
                  {/* Intellectual rhythm (purple) - highlighted as current peak */}
                  <div className="flex-1 h-16 bg-gradient-to-t transition-all relative" 
                       style={{ 
                         backgroundImage: `linear-gradient(to top, ${colors.accent}50, ${colors.accent}20)`,
                         boxShadow: `0 -4px 6px ${colors.accent}20`
                       }}>
                    <div className="w-full h-1 mt-8 bg-white bg-opacity-20"></div>
                    {/* Current time indicator */}
                    <div className="absolute top-0 left-1/2 transform -translate-x-1/2 -translate-y-1/2">
                      <div className="w-4 h-4 rounded-full animate-pulse"
                           style={{ backgroundColor: 'white', boxShadow: `0 0 8px ${colors.accent}` }}></div>
                    </div>
                  </div>
                  {/* Emotional rhythm (blue) */}
                  <div className="flex-1 h-12 bg-gradient-to-t transition-all" 
                       style={{ 
                         backgroundImage: `linear-gradient(to top, ${colors.primary}50, ${colors.primary}20)`,
                         boxShadow: `0 -4px 6px ${colors.primary}20`
                       }}>
                    <div className="w-full h-1 mt-6 bg-white bg-opacity-20"></div>
                  </div>
                  {/* Physical rhythm (green) */}
                  <div className="flex-1 h-8 rounded-r-lg" 
                       style={{ backgroundColor: `${colors.success}30` }}>
                    <div className="w-full h-1 mt-4 bg-white bg-opacity-20"></div>
                  </div>
                </div>
                <div className="absolute inset-x-0 bottom-0 flex justify-between text-xxs px-2 pt-1 dark-mode-transition" style={{ color: colors.textSecondary }}>
                  <span>8AM</span>
                  <span>12PM</span>
                  <span>3PM</span>
                  <span>6PM</span>
                  <span>9PM</span>
                </div>
              </div>
            )}

            <div className="flex flex-col space-y-2">
              {/* Optimal time indicators */}
              <div className="flex items-center justify-between text-xs">
                <div className="flex items-center dark-mode-transition" style={{ color: colors.success }}>
                  <Activity size={12} className="mr-1" />
                  <span>Exercise: 2-4 PM</span>
                </div>
                <div className="flex items-center dark-mode-transition" style={{ color: colors.accent }}>
                  <Brain size={12} className="mr-1" />
                  <span>Focus: 10 AM-12 PM</span>
                </div>
              </div>

              <button 
                className="w-full py-2.5 rounded-lg text-white text-xs font-medium transition-all duration-300 hover:shadow-lg flex items-center justify-center"
                style={{ 
                  background: `linear-gradient(to right, ${colors.gradientAlt1}, ${colors.gradientAlt2})`,
                  boxShadow: `0 4px 8px ${colors.gradientAlt1}40`
                }}
              >
                <Award size={14} className="mr-1.5" />
                See optimal times
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Security & community component with interactive elements */}
      <div 
        className="p-4 rounded-xl border transition-all duration-300 mt-6 hover:shadow-lg transform hover:scale-102"
        style={{ 
          backgroundColor: isDarkMode ? colors.cardBg : 'white',
          borderColor: isDarkMode ? `${colors.primary}30` : 'rgba(219, 234, 254, 1)',
          boxShadow: `0 8px 16px -4px ${colors.primary}20`
        }}
      >
        <div className="flex justify-between items-center mb-3">
          <h4 className="text-sm font-bold dark-mode-transition" style={{ color: colors.textPrimary }}>Community Support</h4>
          <div className="flex items-center">
            <div className="flex -space-x-2">
              {[1, 2, 3].map(i => (
                <div 
                  key={i} 
                  className="w-6 h-6 rounded-full border-2 dark-mode-transition"
                  style={{ 
                    backgroundImage: `linear-gradient(135deg, ${colors.primary}${20*i}, ${colors.accent}${20*i})`,
                    borderColor: isDarkMode ? colors.cardBg : 'white'
                  }}
                ></div>
              ))}
            </div>
            <span className="text-xs ml-1 dark-mode-transition" style={{ color: colors.textSecondary }}>+42</span>
          </div>
        </div>

        <div className="flex items-center">
          <div className="w-10 h-10 rounded-lg mr-3 flex items-center justify-center" 
               style={{ 
                 background: `linear-gradient(135deg, ${colors.gradientAlt1}30, ${colors.gradientAlt1}10)`,
                 boxShadow: `0 4px 6px ${colors.gradientAlt1}20`
               }}>
            <Users size={18} style={{ color: colors.gradientAlt1 }} />
          </div>
          <div className="flex-1">
            <p className="text-xs dark-mode-transition" style={{ color: colors.textSecondary }}>
              Join others with similar health goals in our supportive community
            </p>
          </div>
          <button 
            className="ml-2 px-3 py-1.5 rounded-lg text-xs font-medium text-white transition-all duration-300 hover:shadow-md transform hover:scale-105"
            style={{ 
              background: `linear-gradient(to right, ${colors.gradientAlt1}, ${colors.gradientAlt2})`,
              boxShadow: `0 2px 4px ${colors.gradientAlt1}30`
            }}
          >
            Join
          </button>
        </div>

        {/* Enhanced feature with data protection highlight */}
        <div className="mt-3 p-2 rounded-lg flex items-center border dark-mode-transition"
             style={{ 
               borderColor: isDarkMode ? `${colors.primary}20` : 'rgba(219, 234, 254, 0.6)',
               backgroundColor: isDarkMode ? `${colors.primary}10` : 'rgba(239, 246, 255, 0.3)'
             }}>
          <ShieldCheck size={14} className="mx-2" style={{ color: colors.primary }} />
          <p className="text-xxs dark-mode-transition" style={{ color: colors.textSecondary }}>
            Your health data is encrypted and protected
          </p>
        </div>
      </div>

      {/* Custom CSS for animations */}
      <style jsx>{`
        @keyframes float {
          0%, 100% { transform: translateY(0); }
          50% { transform: translateY(-10px); }
        }
        @keyframes float-delayed {
          0%, 100% { transform: translateY(0); }
          50% { transform: translateY(-10px); }
        }
        .animate-float {
          animation: float 6s ease-in-out infinite;
        }
        .animate-float-delayed {
          animation: float-delayed 6s ease-in-out infinite;
          animation-delay: 3s;
        }

        @keyframes shimmer {
          0% { background-position: -100% 0; }
          100% { background-position: 200% 0; }
        }
        .shimmer {
          background-size: 200% 100%;
          animation: shimmer 3s infinite;
        }
        .shimmer-gradient::after {
          content: '';
          position: absolute;
          top: 0;
          left: 0;
          width: 100%;
          height: 100%;
          background: linear-gradient(90deg, 
            rgba(255,255,255,0) 0%, 
            rgba(255,255,255,0.3) 50%, 
            rgba(255,255,255,0) 100%);
          background-size: 200% 100%;
          animation: shimmer 2s infinite;
        }

        @keyframes waveAnimation-1 {
          0% { transform: translateX(0) translateY(0); }
          50% { transform: translateX(-25%) translateY(-10%); }
          100% { transform: translateX(-50%) translateY(0); }
        }
        @keyframes waveAnimation-2 {
          0% { transform: translateX(0) translateY(0); }
          50% { transform: translateX(-30%) translateY(10%); }
          100% { transform: translateX(-60%) translateY(0); }
        }
        @keyframes waveAnimation-3 {
          0% { transform: translateX(0) translateY(0); }
          50% { transform: translateX(-20%) translateY(-15%); }
          100% { transform: translateX(-40%) translateY(0); }
        }
        .wave-1 {
          animation: waveAnimation-1 10s linear infinite;
        }
        .wave-2 {
          animation: waveAnimation-2 12s linear infinite;
        }
        .wave-3 {
          animation: waveAnimation-3 14s linear infinite;
        }

        @keyframes pulse-border {
          0%, 100% { opacity: 0.5; }
          50% { opacity: 1; }
        }
        .animate-pulse-border {
          animation: pulse-border 2s ease-in-out infinite;
        }

        @keyframes fade-in {
          from { opacity: 0; transform: translateY(10px); }
          to { opacity: 1; transform: translateY(0); }
        }
        .animate-fade-in {
          animation: fade-in 0.5s ease-out forwards;
        }

        @keyframes particle-float {
          0% { transform: translateY(0) translateX(0); }
          25% { transform: translateY(-20px) translateX(10px); }
          50% { transform: translateY(-30px) translateX(-5px); }
          75% { transform: translateY(-10px) translateX(-15px); }
          100% { transform: translateY(0) translateX(0); }
        }
        .particle {
          animation: particle-float 15s ease-in-out infinite;
        }

        .hide-scrollbar {
          -ms-overflow-style: none;
          scrollbar-width: none;
        }
        .hide-scrollbar::-webkit-scrollbar {
          display: none;
        }

        .text-xxs {
          font-size: 0.625rem;
        }

        /* Custom animation for text gradients */
        @keyframes gradient-shift {
          0% { background-position: 0% 50%; }
          50% { background-position: 100% 50%; }
          100% { background-position: 0% 50%; }
        }
        .gradient-text {
          animation: gradient-shift 3s ease infinite;
          background-size: 200% auto;
        }
      `}</style>
    </div>
  );
};

export default DashboardScreen;
