import React, { useState, useEffect, useRef } from 'react';
import { X, Mic, MicOff, Video, VideoOff, Phone, Volume2, Volume, MessageSquare, Users, MoreVertical } from 'lucide-react';
import { useTheme } from '../ThemeContext';
import webRTCService from '../services/WebRTCService';

const VideoCallScreen = ({ isOpen, onClose, colors, doctor }) => {
  const { isDarkMode } = useTheme();

  // State variables
  const [callStatus, setCallStatus] = useState('connecting'); // connecting, active, ended
  const [isMuted, setIsMuted] = useState(false);
  const [isVideoOff, setIsVideoOff] = useState(false);
  const [isSpeakerOff, setIsSpeakerOff] = useState(false);
  const [callDuration, setCallDuration] = useState(0);
  const [showControls, setShowControls] = useState(true);
  const [isIncomingCall, setIsIncomingCall] = useState(false);

  // Refs for video elements
  const localVideoRef = useRef(null);
  const remoteVideoRef = useRef(null);
  const lastMoveTimeRef = useRef(0);
  const callDurationTimer = useRef(null);

  // Initialize or clean up WebRTC when component mounts/unmounts
  useEffect(() => {
    if (isOpen && doctor) {
      console.log('VideoCallScreen opened for doctor:', doctor.name);

      // Check if there's a pending call in WebRTC service
      const isPendingCall = webRTCService.pendingOffer !== null;
      if (isPendingCall) {
        console.log('Detected pending call, handling as incoming call');
        setIsIncomingCall(true);
      }

      // Set up WebRTC callbacks
      const callbacks = {
        // Called when we get access to our own camera/mic
        onLocalStream: (stream) => {
          console.log('Got local stream');
          if (localVideoRef.current) {
            localVideoRef.current.srcObject = stream;
          }
        },

        // Called when we receive the other person's video/audio
        onRemoteStream: (stream) => {
          console.log('Got remote stream');
          if (remoteVideoRef.current) {
            remoteVideoRef.current.srcObject = stream;
          }
        },

        // Called when the call is successfully connected
        onCallStarted: () => {
          console.log('Call is now active');
          setCallStatus('active');

          // Start timer for call duration
          callDurationTimer.current = setInterval(() => {
            setCallDuration(prev => prev + 1);
          }, 1000);
        },

        // Called when the call ends
        onCallEnded: () => {
          console.log('Call has ended');
          setCallStatus('ended');

          // Clear the duration timer
          if (callDurationTimer.current) {
            clearInterval(callDurationTimer.current);
          }

          // Close the dialog after showing "Call Ended" for 2 seconds
          setTimeout(() => {
            onClose();
          }, 2000);
        },

        // Called when establishing the connection
        onCallConnecting: () => {
          console.log('Call is connecting...');
          setCallStatus('connecting');
        },

        // Called when we receive an incoming call
        onIncomingCall: (callerId) => {
          console.log('Incoming call from:', callerId);
          setIsIncomingCall(true);
          // No need to auto-accept, we're already showing the video screen
        },

        // Called when connection state changes
        onICEConnectionStateChange: (state) => {
          console.log('Connection state changed:', state);
        }
      };

      // Update the callbacks
      webRTCService.updateCallbacks(callbacks);

      // Handle the call differently depending on whether it's incoming or outgoing
      if (isIncomingCall || isPendingCall) {
        console.log('Accepting incoming call...');
        // If this is an incoming call, accept it
        setTimeout(() => {
          webRTCService.acceptCall(); 
        }, 500);
      } else {
        // Create a unique ID for the doctor
        // In a real app, this would come from your authentication system
        const doctorUserId = `doctor-${doctor.id}`;

        // Start the call
        const initCall = async () => {
          try {
            console.log('Starting outgoing call process...');
            // Get access to camera and microphone
            await webRTCService.getLocalMedia();
            // Call the doctor
            webRTCService.makeCall(doctorUserId);
          } catch (error) {
            console.error('Failed to initialize call:', error);
            setCallStatus('ended');
          }
        };

        // Start the call process
        initCall();
      }
    }

    // Clean up on unmount
    return () => {
      console.log('Cleaning up video call resources');
      if (callDurationTimer.current) {
        clearInterval(callDurationTimer.current);
      }

      // Only end the call, don't disconnect from the service
      // as ConnectScreen might still be using it
      webRTCService.endCall(true);
    };
  }, [isOpen, doctor, isIncomingCall]);

  // Update WebRTC when audio mute state changes
  useEffect(() => {
    if (webRTCService) {
      webRTCService.toggleAudio(isMuted);
    }
  }, [isMuted]);

  // Update WebRTC when video state changes
  useEffect(() => {
    if (webRTCService) {
      webRTCService.toggleVideo(isVideoOff);
    }
  }, [isVideoOff]);

  // Handle key press to end call
  useEffect(() => {
    const handleKeyDown = (e) => {
      if (e.key === 'Escape' && isOpen) {
        handleEndCall();
      }
    };

    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, [isOpen]);

  // Mouse movement handler to show/hide controls
  useEffect(() => {
    let timer;

    const handleMouseMove = () => {
      const now = Date.now();
      // Only update if 150ms has passed since last update (debounce)
      if (callStatus === 'active' && now - lastMoveTimeRef.current > 150) {
        lastMoveTimeRef.current = now;

        if (!showControls) {
          setShowControls(true);
        }

        clearTimeout(timer);
        timer = setTimeout(() => {
          setShowControls(false);
        }, 5000);
      }
    };

    window.addEventListener('mousemove', handleMouseMove);
    return () => {
      window.removeEventListener('mousemove', handleMouseMove);
      clearTimeout(timer);
    };
  }, [callStatus, showControls]);

  // Format seconds to MM:SS
  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  // Handle ending call
  const handleEndCall = () => {
    console.log('User clicked end call button');
    webRTCService.endCall(true);
  };

  // Handle accepting call
  const handleAcceptCall = () => {
    console.log('User accepted incoming call');
    webRTCService.acceptCall();
  };

  // Dark mode specific colors
  const videoOffBg = isDarkMode 
    ? `linear-gradient(135deg, ${colors.darkBg}, #031838)` 
    : `linear-gradient(135deg, ${colors.primaryDark}, #031838)`;

  const videoOnBg = isDarkMode
    ? `linear-gradient(45deg, #0a0a0a, #1e1e1e)`
    : `linear-gradient(45deg, #1e1e1e, #0a0a0a)`;

  const endedBgColor = isDarkMode ? colors.darkBg : 'black';

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center"
         style={{ backgroundColor: 'rgba(0,0,0,0.85)', backdropFilter: 'blur(10px)' }}>
      <div className="w-full h-full max-w-md relative overflow-hidden">

        {/* Doctor video (remote) */}
        <div className="absolute inset-0 bg-black dark-mode-transition">
          {callStatus === 'connecting' ? (
            <div className="h-full flex flex-col items-center justify-center">
              <div className="w-24 h-24 rounded-full mb-6 relative overflow-hidden">
                <img src={doctor?.image || '/User_1.png'} alt={doctor?.name || 'Doctor'} className="w-full h-full object-cover" />
                <div className="absolute inset-0 flex items-center justify-center bg-black bg-opacity-40">
                  <div className="w-16 h-16 rounded-full border-4 border-white border-opacity-20 border-t-white animate-spin"></div>
                </div>
              </div>
              <h3 className="text-xl font-semibold text-white mb-2">
                {doctor?.name || 'Dr. Johnson'}
              </h3>
              <p className="text-white text-opacity-70 mb-6">
                {isIncomingCall ? 'Incoming call...' : 'Connecting...'}
              </p>

              {/* Show accept button for incoming calls in connecting state */}
              {isIncomingCall && callStatus === 'connecting' && (
                <button 
                  className="px-6 py-2 rounded-full bg-green-500 text-white font-medium mb-4 hover:bg-green-600 transition-colors"
                  onClick={handleAcceptCall}
                >
                  Accept Call
                </button>
              )}

              <div className="flex space-x-1">
                {[1, 2, 3].map(dot => (
                  <div
                    key={dot}
                    className="w-2 h-2 rounded-full bg-white"
                    style={{
                      animation: `pulse 1.5s ease-in-out ${(dot - 1) * 0.3}s infinite`
                    }}
                  ></div>
                ))}
              </div>
            </div>
          ) : callStatus === 'ended' ? (
            <div className="h-full flex flex-col items-center justify-center dark-mode-transition"
                 style={{ backgroundColor: endedBgColor }}>
              <div className="w-20 h-20 rounded-full bg-red-500 bg-opacity-20 flex items-center justify-center mb-6">
                <Phone size={32} className="text-red-500 transform rotate-135" />
              </div>
              <h3 className="text-xl font-semibold text-white mb-2">Call Ended</h3>
              <p className="text-white text-opacity-70">Duration: {formatTime(callDuration)}</p>
            </div>
          ) : (
            <div className="h-full relative">
              {/* Remote video element */}
              <video
                ref={remoteVideoRef}
                autoPlay
                playsInline
                muted={isSpeakerOff}
                className="w-full h-full object-cover"
              />

              {/* Fallback when remote video is off or not available yet */}
              {isVideoOff && (
                <div className="absolute inset-0 flex items-center justify-center dark-mode-transition"
                     style={{ background: isDarkMode ? colors.darkBg : '#000811' }}>
                  <div className="w-32 h-32 rounded-full overflow-hidden border-4 border-white border-opacity-20">
                    <img src={doctor?.image || '/User_1.png'} alt={doctor?.name || 'Doctor'} className="w-full h-full object-cover" />
                  </div>
                </div>
              )}

              {/* Call info overlay */}
              <div className={`absolute top-0 left-0 right-0 p-4 transition-opacity duration-300 ${showControls ? 'opacity-100' : 'opacity-0'}`}>
                <div className="flex justify-between items-center">
                  <div>
                    <h3 className="text-lg font-medium text-white">{doctor?.name || 'Dr. Johnson'}</h3>
                    <p className="text-sm text-white text-opacity-70">{formatTime(callDuration)}</p>
                  </div>
                  <div className="flex items-center">
                    <div className="px-3 py-1 rounded-full bg-black bg-opacity-30 text-white text-sm mr-2">
                      HD
                    </div>
                    <div className="w-8 h-8 rounded-full bg-black bg-opacity-30 flex items-center justify-center">
                      <MoreVertical size={16} className="text-white" />
                    </div>
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>

        {/* User video (local) */}
        <div className={`absolute bottom-24 right-4 w-32 h-48 rounded-xl overflow-hidden transition-opacity duration-300 ${showControls ? 'opacity-100' : 'opacity-80'}`}
             style={{ 
               boxShadow: '0 8px 32px rgba(0, 0, 0, 0.4)',
               transform: showControls ? 'scale(1)' : 'scale(0.9)',
               transformOrigin: 'bottom right'
             }}>
          {/* Local video element */}
          <video
            ref={localVideoRef}
            autoPlay
            playsInline
            muted={true} // Always mute local video to prevent feedback
            className={`w-full h-full object-cover ${isVideoOff ? 'hidden' : ''}`}
          />

          {/* Background when video is off */}
          <div className={`absolute inset-0 dark-mode-transition ${isVideoOff ? '' : 'hidden'}`} 
               style={{ background: isVideoOff ? videoOffBg : videoOnBg }}>
          </div>

          {/* User placeholder when video is off */}
          {isVideoOff && (
            <div className="absolute inset-0 flex items-center justify-center">
              <div className="w-12 h-12 rounded-full bg-white bg-opacity-10 flex items-center justify-center">
                <Users size={18} className="text-white" />
              </div>
            </div>
          )}

          {/* Muted indicator */}
          {isMuted && (
            <div className="absolute top-2 right-2">
              <div className="w-6 h-6 rounded-full bg-black bg-opacity-50 flex items-center justify-center">
                <MicOff size={12} className="text-white" />
              </div>
            </div>
          )}
        </div>

        {/* Call controls */}
        <div className={`absolute bottom-0 left-0 right-0 p-6 transition-all duration-500 ${showControls ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-16'}`}>
          <div className="flex justify-center items-center space-x-4 mb-2">
            <button 
              className="w-12 h-12 rounded-full flex items-center justify-center transition-all duration-300"
              style={{ 
                backgroundColor: isMuted ? colors.danger : 'rgba(255, 255, 255, 0.2)',
                transform: isMuted ? 'scale(1.1)' : 'scale(1)'
              }}
              onClick={() => setIsMuted(!isMuted)}
            >
              {isMuted ? (
                <MicOff size={20} className="text-white" />
              ) : (
                <Mic size={20} className="text-white" />
              )}
            </button>

            <button 
              className="w-16 h-16 rounded-full flex items-center justify-center transition-all duration-300"
              style={{ 
                backgroundColor: colors.danger,
                boxShadow: '0 0 20px rgba(220, 38, 38, 0.5)'
              }}
              onClick={handleEndCall}
            >
              <Phone size={24} className="text-white transform rotate-135" />
            </button>

            <button 
              className="w-12 h-12 rounded-full flex items-center justify-center transition-all duration-300"
              style={{ 
                backgroundColor: isVideoOff ? colors.danger : 'rgba(255, 255, 255, 0.2)',
                transform: isVideoOff ? 'scale(1.1)' : 'scale(1)'
              }}
              onClick={() => setIsVideoOff(!isVideoOff)}
            >
              {isVideoOff ? (
                <VideoOff size={20} className="text-white" />
              ) : (
                <Video size={20} className="text-white" />
              )}
            </button>
          </div>

          <div className="flex justify-center items-center space-x-6">
            <button 
              className="w-10 h-10 rounded-full flex items-center justify-center transition-all duration-300"
              style={{ backgroundColor: 'rgba(255, 255, 255, 0.15)' }}
              onClick={() => setIsSpeakerOff(!isSpeakerOff)}
            >
              {isSpeakerOff ? (
                <Volume size={18} className="text-white" />
              ) : (
                <Volume2 size={18} className="text-white" />
              )}
            </button>

            <button 
              className="w-10 h-10 rounded-full flex items-center justify-center transition-all duration-300"
              style={{ backgroundColor: 'rgba(255, 255, 255, 0.15)' }}
            >
              <MessageSquare size={18} className="text-white" />
            </button>
          </div>
        </div>
      </div>

      {/* Custom CSS for animations */}
      <style jsx>{`
        @keyframes pulse {
          0%, 100% { opacity: 0.5; transform: scale(1); }
          50% { opacity: 1; transform: scale(1.2); }
        }
      `}</style>
    </div>
  );
};

export default VideoCallScreen;
