// Fixed VideoCallScreen.jsx to ensure video displays on both sides
import React, { useState, useEffect, useRef } from 'react';
import { useTheme } from '../../ThemeContext';
import webRTCService from '../../services/WebRTCService';
import VideoDisplay from './VideoDisplay';
import CallControls from './CallControls';
import ConnectionStatus from './ConnectionStatus'; 
import CallHeader from './CallHeader';
import ConnectingState from './ConnectingState';
import EndedState from './EndedState';

const VideoCallScreen = ({ isOpen, onClose, colors, doctor }) => {
  const { isDarkMode } = useTheme();

  // State variables
  const [callStatus, setCallStatus] = useState('connecting'); // connecting, active, ended
  const [isMuted, setIsMuted] = useState(false);
  const [isVideoOff, setIsVideoOff] = useState(false);
  const [isSpeakerOff, setIsSpeakerOff] = useState(false);
  const [callDuration, setCallDuration] = useState(0);
  const [showControls, setShowControls] = useState(true);
  const [connectionQuality, setConnectionQuality] = useState('good'); // good, poor, unstable
  const [reconnectAttempt, setReconnectAttempt] = useState(0);
  const [errorMessage, setErrorMessage] = useState('');
  const [audioMonitorCleanup, setAudioMonitorCleanup] = useState(null);
  const [forceDisplay, setForceDisplay] = useState(false);

  // Refs for video elements
  const localVideoRef = useRef(null);
  const remoteVideoRef = useRef(null);
  const lastMoveTimeRef = useRef(0);
  const callDurationTimer = useRef(null);
  const peerConnectionRef = useRef(null);
  const hasMedia = useRef(false);
  const stateTransitionAttempt = useRef(0);

  // Function to update call status with logging
  const updateCallStatus = (newStatus) => {
    console.log(`[VideoCallScreen] Updating call status: ${callStatus} -> ${newStatus}`);
    setCallStatus(newStatus);

    // If transitioning to active, also set forceDisplay
    if (newStatus === 'active') {
      setForceDisplay(true);
      console.log('[VideoCallScreen] Force display set to true');

      // Start call timer if needed
      if (!callDurationTimer.current) {
        console.log('[VideoCallScreen] Starting call duration timer');
        callDurationTimer.current = setInterval(() => {
          setCallDuration(prev => prev + 1);
        }, 1000);
      }
    }
  };

  // Function to safely toggle video state
  const toggleVideo = (videoOff) => {
    console.log('[VideoCallScreen] Toggle video:', videoOff);

    if (webRTCService && webRTCService.mediaManager.hasLocalStream()) {
      console.log('[VideoCallScreen] Toggling video through webRTCService');
      webRTCService.toggleVideo(videoOff);
      setIsVideoOff(videoOff);
    } else {
      console.log('[VideoCallScreen] Setting video state directly');
      setIsVideoOff(videoOff);
    }
  };

  // CRITICAL EFFECT: Check WebRTC service state directly
  useEffect(() => {
    if (!isOpen) return;

    console.log('[VideoCallScreen] Starting WebRTC state monitor');

    const stateMonitor = setInterval(() => {
      // Check WebRTC service state directly
      const serviceActive = webRTCService.isCallActive();
      const serviceConnected = webRTCService.isCallConnected();
      const hasRemoteStream = webRTCService.connectionManager?.remoteStream;

      // Log remote video tracks
      const remoteTracks = remoteVideoRef.current?.srcObject?.getTracks() || [];
      const videoTrackCount = remoteTracks.filter(t => t.kind === 'video').length;

      console.log(`[VideoCallScreen] State check - UI: ${callStatus}, Service: ${serviceActive}, Remote video tracks: ${videoTrackCount}`);

      // Force transition to active if needed
      if ((serviceActive || serviceConnected || hasRemoteStream || remoteTracks.length > 0) && callStatus !== 'active') {
        stateTransitionAttempt.current += 1;
        console.log(`[VideoCallScreen] !!! FORCING ACTIVE STATE !!! (attempt ${stateTransitionAttempt.current})`);
        updateCallStatus('active');

        // Directly call the WebRTC service to update its state too
        if (typeof webRTCService.updateCallState === 'function') {
          webRTCService.updateCallState('active');
        }
      }
    }, 500); // Check every 500ms

    return () => clearInterval(stateMonitor);
  }, [isOpen, callStatus]);

  // Additional direct check for remote stream
  useEffect(() => {
    if (!isOpen || !remoteVideoRef.current) return;

    const streamChecker = setInterval(() => {
      if (remoteVideoRef.current && remoteVideoRef.current.srcObject) {
        const tracks = remoteVideoRef.current.srcObject.getTracks();
        console.log(`[VideoCallScreen] Remote video has ${tracks.length} tracks`);

        if (tracks.length > 0) {
          // Always update to active if we have tracks
          console.log('[VideoCallScreen] Remote tracks detected, ensuring active state');
          updateCallStatus('active');
          hasMedia.current = true;

          // Try to play the video manually if needed
          if (remoteVideoRef.current.paused) {
            remoteVideoRef.current.play().catch(e => 
              console.log('[VideoCallScreen] Manual play failed:', e)
            );
          }
        }
      }
    }, 700);

    return () => clearInterval(streamChecker);
  }, [isOpen, remoteVideoRef.current]);

  // Initialize or clean up WebRTC when component mounts/unmounts
  useEffect(() => {
    if (isOpen && doctor) {
      console.log('[VideoCallScreen] Opening for doctor:', doctor.name);

      // Set up WebRTC callbacks
      const callbacks = {
        onLocalStream: (stream) => {
          console.log('[VideoCallScreen] Got local stream with tracks:', 
            stream.getTracks().map(t => `${t.kind}:${t.enabled}`).join(','));

          if (localVideoRef.current) {
            localVideoRef.current.srcObject = stream;
            console.log('[VideoCallScreen] Set local video source');
          }
        },

        onRemoteStream: (stream) => {
          console.log('[VideoCallScreen] Got remote stream with tracks:', 
            stream.getTracks().map(t => `${t.kind}:${t.enabled}`).join(','));

          if (remoteVideoRef.current) {
            remoteVideoRef.current.srcObject = stream;
            remoteVideoRef.current.muted = isSpeakerOff;
            console.log('[VideoCallScreen] Set remote video source');

            // IMPORTANT: Force active state and ensure video is displayed
            updateCallStatus('active');
            hasMedia.current = true;
            setForceDisplay(true);

            // Try to play the video immediately
            remoteVideoRef.current.play().catch(e => 
              console.log('[VideoCallScreen] Initial play failed:', e)
            );
          }

          const cleanup = monitorAudioLevel(stream);
          if (cleanup) setAudioMonitorCleanup(cleanup);
        },

        onCallStarted: () => {
          console.log('[VideoCallScreen] onCallStarted callback fired');
          updateCallStatus('active');
          setReconnectAttempt(0);
          setErrorMessage('');

          // Store peer connection reference
          if (webRTCService.connectionManager?.peerConnection) {
            peerConnectionRef.current = webRTCService.connectionManager.peerConnection;
          }
        },

        onCallEnded: () => {
          console.log('[VideoCallScreen] onCallEnded callback fired');
          updateCallStatus('ended');

          if (callDurationTimer.current) {
            clearInterval(callDurationTimer.current);
            callDurationTimer.current = null;
          }

          setTimeout(() => {
            onClose();
          }, 2000);
        },

        onCallConnecting: () => {
          console.log('[VideoCallScreen] onCallConnecting callback fired');
          // Only update if not already active (don't go backwards)
          if (callStatus !== 'active') {
            updateCallStatus('connecting');
          }
        },

        onICEConnectionStateChange: (state) => {
          console.log('[VideoCallScreen] ICE connection state changed:', state);

          if (state === 'checking') {
            setConnectionQuality('unstable');
          } else if (state === 'connected' || state === 'completed') {
            setConnectionQuality('good');

            // IMPORTANT: Force call to active state on ICE connected
            console.log('[VideoCallScreen] ICE connected/completed, forcing active state');
            updateCallStatus('active');

            // Double-check after a short delay
            setTimeout(() => {
              updateCallStatus('active');
            }, 500);
          } else if (state === 'disconnected') {
            setConnectionQuality('poor');
            setReconnectAttempt(prev => prev + 1);
          } else if (state === 'failed') {
            setConnectionQuality('unstable');
            setReconnectAttempt(prev => prev + 1);

            // Try reconnection
            if (webRTCService && typeof webRTCService.attemptReconnection === 'function') {
              webRTCService.attemptReconnection(true);
            }
          }
        }
      };

      // IMPORTANT: Listen for call state changes with enhanced reliability
      const callStateListener = (state) => {
        console.log('[VideoCallScreen] callStateChanged event received:', state);

        // Never go backwards from active to connecting
        if (state === 'active' || (state === 'connecting' && callStatus !== 'active')) {
          updateCallStatus(state);
        } else if (state === 'ended') {
          updateCallStatus('ended');
        }
      };

      const unsubscribeCallState = webRTCService.on('callStateChanged', callStateListener);

      // Listen for errors
      const unsubscribeError = webRTCService.on('error', (error) => {
        console.log('[VideoCallScreen] WebRTC error:', error);
        setErrorMessage(error.message || 'Call error');

        if (error.fatal) {
          updateCallStatus('ended');
        }
      });

      webRTCService.updateCallbacks(callbacks);

      // Get media access if needed
      if (!webRTCService.mediaManager.hasLocalStream()) {
        console.log('[VideoCallScreen] Requesting media access');
        webRTCService.getLocalMedia(true, true).catch(error => {
          console.error('[VideoCallScreen] Media access error:', error);
          setErrorMessage(`Camera/mic access failed: ${error.message}`);
        });
      }

      // IMPORTANT: Force check WebRTC service state
      console.log('[VideoCallScreen] Initial state check:', webRTCService.callState);
      if (webRTCService.callState === 'active') {
        console.log('[VideoCallScreen] WebRTC service reports active on mount, syncing UI');
        updateCallStatus('active');
      }

      return () => {
        console.log('[VideoCallScreen] Cleaning up resources');

        unsubscribeCallState();
        unsubscribeError();

        if (callDurationTimer.current) {
          clearInterval(callDurationTimer.current);
          callDurationTimer.current = null;
        }

        if (audioMonitorCleanup) {
          audioMonitorCleanup();
        }

        if (localVideoRef.current?.srcObject) {
          const stream = localVideoRef.current.srcObject;
          stream.getTracks().forEach(track => {
            track.stop();
          });
          localVideoRef.current.srcObject = null;
        }

        if (remoteVideoRef.current?.srcObject) {
          remoteVideoRef.current.srcObject = null;
        }

        // Always end call on cleanup
        webRTCService.endCall(true);
      };
    }
  }, [isOpen, doctor, onClose, isSpeakerOff]);

  // Monitor WebRTC stats for connection quality
  useEffect(() => {
    if (peerConnectionRef.current && callStatus === 'active') {
      const monitorConnectionQuality = () => {
        const statsInterval = setInterval(async () => {
          try {
            const stats = await peerConnectionRef.current.getStats();
            let packetLoss = 0;
            let jitter = 0;
            let roundTripTime = 0;
            let statsCount = 0;

            stats.forEach(report => {
              if (report.type === 'inbound-rtp' && report.kind === 'video') {
                if (report.packetsLost && report.packetsReceived) {
                  packetLoss = report.packetsLost / (report.packetsLost + report.packetsReceived);
                }
                if (report.jitter) {
                  jitter = report.jitter;
                }
                statsCount++;
              }
              if (report.type === 'remote-inbound-rtp') {
                if (report.roundTripTime) {
                  roundTripTime = report.roundTripTime;
                }
                statsCount++;
              }
            });

            if (statsCount > 0) {
              if (packetLoss > 0.1 || jitter > 50 || roundTripTime > 500) {
                setConnectionQuality('poor');
              } else if (packetLoss > 0.05 || jitter > 30 || roundTripTime > 300) {
                setConnectionQuality('unstable');
              } else {
                setConnectionQuality('good');
              }
            }
          } catch (error) {
            console.error('Error getting connection stats:', error);
          }
        }, 2000);

        return () => clearInterval(statsInterval);
      };

      const cleanup = monitorConnectionQuality();
      return cleanup;
    }
  }, [peerConnectionRef.current, callStatus]);

  // Update remote video muted state when isSpeakerOff changes
  useEffect(() => {
    if (remoteVideoRef.current) {
      remoteVideoRef.current.muted = isSpeakerOff;
    }
  }, [isSpeakerOff]);

  // Function to monitor audio levels for connection quality checks
  const monitorAudioLevel = (stream) => {
    if (!window.AudioContext && !window.webkitAudioContext) {
      return null;
    }

    try {
      const AudioContext = window.AudioContext || window.webkitAudioContext;
      const audioContext = new AudioContext();
      const analyser = audioContext.createAnalyser();
      const microphone = audioContext.createMediaStreamSource(stream);
      const javascriptNode = audioContext.createScriptProcessor(2048, 1, 1);

      microphone.connect(analyser);
      analyser.connect(javascriptNode);
      javascriptNode.connect(audioContext.destination);

      let lowAudioCounter = 0;
      const LOW_AUDIO_THRESHOLD = 10;

      javascriptNode.onaudioprocess = () => {
        const array = new Uint8Array(analyser.frequencyBinCount);
        analyser.getByteFrequencyData(array);
        const values = array.reduce((a, b) => a + b, 0) / array.length;

        if (values < 5 && callStatus === 'active' && connectionQuality !== 'poor') {
          lowAudioCounter++;
          if (lowAudioCounter >= LOW_AUDIO_THRESHOLD && callDuration > 5) {
            setConnectionQuality(prevQuality => 
              prevQuality === 'unstable' ? 'poor' : prevQuality);
          }
        } else {
          lowAudioCounter = 0;
        }
      };

      return () => {
        try {
          javascriptNode.disconnect();
          analyser.disconnect();
          microphone.disconnect();
          if (audioContext.state !== 'closed') {
            audioContext.close();
          }
        } catch (error) {
          console.error('Error cleaning up audio monitoring:', error);
        }
      };
    } catch (error) {
      console.error('Error setting up audio monitoring:', error);
      return null;
    }
  };

  // Update WebRTC when audio mute state changes
  useEffect(() => {
    if (webRTCService) {
      webRTCService.toggleAudio(isMuted);
    }
  }, [isMuted]);

  // Update WebRTC when video state changes
  useEffect(() => {
    if (webRTCService && webRTCService.mediaManager.hasLocalStream()) {
      webRTCService.toggleVideo(isVideoOff);
    }
  }, [isVideoOff]);

  // Handle key press to end call
  useEffect(() => {
    const handleKeyDown = (e) => {
      if (e.key === 'Escape' && isOpen) {
        handleEndCall();
      }
    };

    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, [isOpen]);

  // Mouse movement handler to show/hide controls
  useEffect(() => {
    let timer;

    const handleMouseMove = () => {
      const now = Date.now();
      if (callStatus === 'active' && now - lastMoveTimeRef.current > 150) {
        lastMoveTimeRef.current = now;

        if (!showControls) {
          setShowControls(true);
        }

        clearTimeout(timer);
        timer = setTimeout(() => {
          setShowControls(false);
        }, 5000);
      }
    };

    window.addEventListener('mousemove', handleMouseMove);
    return () => {
      window.removeEventListener('mousemove', handleMouseMove);
      clearTimeout(timer);
    };
  }, [callStatus, showControls]);

  // Format seconds to MM:SS
  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  // Handle ending call
  const handleEndCall = () => {
    console.log('[VideoCallScreen] User ending call');
    webRTCService.endCall(true);
  };

  // FORCE ACTIVE on mount
  useEffect(() => {
    if (!isOpen) return;

    // Force check WebRTC state after a delay
    const initialTimeouts = [200, 800, 1500, 3000].map(delay => 
      setTimeout(() => {
        const isServiceActive = webRTCService?.isCallActive();
        console.log(`[VideoCallScreen] Delayed check (${delay}ms): WebRTC active: ${isServiceActive}, UI: ${callStatus}`);

        if (isServiceActive && callStatus !== 'active') {
          console.log(`[VideoCallScreen] Forcing active state after ${delay}ms delay`);
          updateCallStatus('active');
        }

        // IMPORTANT: Check for remote video
        const hasVideoTracks = remoteVideoRef.current?.srcObject?.getVideoTracks().length > 0;
        if (hasVideoTracks) {
          console.log(`[VideoCallScreen] Remote video has video tracks at ${delay}ms, forcing active`);
          updateCallStatus('active');
        }

        // If we have either audio or video tracks, activate immediately
        const hasTracks = remoteVideoRef.current?.srcObject?.getTracks().length > 0;
        if (hasTracks) {
          console.log(`[VideoCallScreen] Remote has ${remoteVideoRef.current.srcObject.getTracks().length} tracks at ${delay}ms`);
          updateCallStatus('active');
        }
      }, delay)
    );

    return () => initialTimeouts.forEach(clearTimeout);
  }, [isOpen]);

  if (!isOpen) return null;

  // Always show video if call status is active
  const shouldShowVideo = true;

  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center"
         style={{ backgroundColor: 'rgba(0,0,0,0.85)', backdropFilter: 'blur(10px)' }}>
      <div className="w-full h-full max-w-md relative overflow-hidden">

        {callStatus === 'ended' ? (
          <EndedState 
            doctor={doctor}
            errorMessage={errorMessage} 
            callDuration={callDuration} 
            formatTime={formatTime}
            colors={colors}
            isDarkMode={isDarkMode}
          />
        ) : (
          <VideoDisplay
            remoteVideoRef={remoteVideoRef}
            localVideoRef={localVideoRef}
            isSpeakerOff={isSpeakerOff}
            isVideoOff={isVideoOff}
            doctor={doctor}
            connectionQuality={connectionQuality}
            reconnectAttempt={reconnectAttempt}
            showControls={showControls}
            isDarkMode={isDarkMode}
            colors={colors}
            callStatus={callStatus}
          >
            <CallHeader 
              doctor={doctor}
              callDuration={callDuration}
              formatTime={formatTime}
              showControls={showControls}
            />

            <ConnectionStatus
              connectionQuality={connectionQuality}
              showControls={showControls}
            />

            {/* Connecting state overlay - Only show when status is connecting */}
            {callStatus === 'connecting' && (
              <div className="absolute inset-0 flex items-center justify-center z-20 bg-black bg-opacity-70">
                <ConnectingState doctor={doctor} colors={colors} />
              </div>
            )}
          </VideoDisplay>
        )}

        <CallControls
          isMuted={isMuted}
          setIsMuted={setIsMuted}
          isVideoOff={isVideoOff}
          setIsVideoOff={toggleVideo} 
          isSpeakerOff={isSpeakerOff}
          setIsSpeakerOff={setIsSpeakerOff}
          handleEndCall={handleEndCall}
          showControls={showControls}
          colors={colors}
          callStatus={callStatus}
        />
      </div>

      <style jsx>{`
        @keyframes pulse {
          0%, 100% { opacity: 0.5; transform: scale(1); }
          50% { opacity: 1; transform: scale(1.2); }
        }
      `}</style>
    </div>
  );
};

export default VideoCallScreen;
