import React, { useState } from 'react';
import { 
  ArrowLeft, PlusCircle, X, Bell, 
  Eye, Clock, Pill, Loader
} from "lucide-react";
import { useTheme } from '../ThemeContext';

// Component for adding a new medication with dark mode support
const AddMedicationForm = ({ setView, addMedication, isProcessing, colors }) => {
  const { isDarkMode } = useTheme();

  // Form state
  const [formData, setFormData] = useState({
    name: '',
    dosage: '',
    form: '',
    purpose: '',
    instructions: '',
    schedule: [
      { time: "08:00", status: "upcoming", day: "today" }
    ],
    reminders: true,
    sideEffects: [],
    category: 'general',
    refillRemaining: 30,
    adherenceRate: 100,
    missedDoses: 0,
    refillDate: formatDate(new Date(Date.now() + 30 * 24 * 60 * 60 * 1000)), // Default to 30 days from now
    prescribedBy: "",
    startDate: formatDate(new Date()),
    interactions: [],
    history: []
  });

  // Format date to YYYY-MM-DD for database
  function formatDate(date) {
    const year = date.getFullYear();
    const month = String(date.getMonth() + 1).padStart(2, '0');
    const day = String(date.getDate()).padStart(2, '0');
    return `${year}-${month}-${day}`;
  }

  // Format date for display
  function formatDateForDisplay(dateString) {
    const date = new Date(dateString);
    const options = { month: 'long', day: 'numeric', year: 'numeric' };
    return date.toLocaleDateString('en-US', options);
  }

  // Update form fields
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  // Add a new time to the schedule
  const addScheduleTime = () => {
    setFormData(prev => ({
      ...prev,
      schedule: [...prev.schedule, { time: "12:00", status: "upcoming", day: "today" }]
    }));
  };

  // Remove a time from the schedule
  const removeScheduleTime = (index) => {
    setFormData(prev => ({
      ...prev,
      schedule: prev.schedule.filter((_, idx) => idx !== index)
    }));
  };

  // Update a specific schedule time
  const updateScheduleTime = (index, time) => {
    setFormData(prev => {
      const newSchedule = [...prev.schedule];
      newSchedule[index] = { ...newSchedule[index], time };
      return { ...prev, schedule: newSchedule };
    });
  };

  // Add a side effect
  const addSideEffect = () => {
    const effect = prompt("Enter a potential side effect:");
    if (effect && effect.trim()) {
      setFormData(prev => ({
        ...prev,
        sideEffects: [...prev.sideEffects, effect.trim()]
      }));
    }
  };

  // Remove a side effect
  const removeSideEffect = (index) => {
    setFormData(prev => ({
      ...prev,
      sideEffects: prev.sideEffects.filter((_, idx) => idx !== index)
    }));
  };

  // Add an interaction
  const addInteraction = () => {
    const medication = prompt("Enter medication that interacts:");
    if (medication && medication.trim()) {
      const severity = prompt("Enter severity (high, moderate, low):", "moderate");
      const description = prompt("Enter description of the interaction:");

      if (description && description.trim()) {
        setFormData(prev => ({
          ...prev,
          interactions: [...prev.interactions, {
            medication: medication.trim(),
            severity: severity ? severity.trim().toLowerCase() : 'moderate',
            description: description.trim()
          }]
        }));
      }
    }
  };

  // Remove an interaction
  const removeInteraction = (index) => {
    setFormData(prev => ({
      ...prev,
      interactions: prev.interactions.filter((_, idx) => idx !== index)
    }));
  };

  // Toggle reminders on/off
  const toggleReminders = () => {
    setFormData(prev => ({ ...prev, reminders: !prev.reminders }));
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    // Basic validation
    if (!formData.name || !formData.dosage || !formData.form) {
      alert('Please fill out all required fields');
      return;
    }

    if (formData.schedule.length === 0) {
      alert('Please add at least one schedule time');
      return;
    }

    // Format the times in AM/PM format for consistency
    const formattedSchedule = formData.schedule.map(item => {
      const [hours, minutes] = item.time.split(':');
      const hour = parseInt(hours);
      const ampm = hour >= 12 ? 'PM' : 'AM';
      const formattedHour = hour % 12 || 12;
      return {
        ...item,
        time: `${formattedHour}:${minutes} ${ampm}`
      };
    });

    // Add the new medication with formatted schedule
    const success = await addMedication({
      ...formData,
      schedule: formattedSchedule
    });

    if (success) {
      setView('main');
    }
  };

  // Dark mode specific styles
  const cardBg = isDarkMode ? colors.cardBg : 'white';
  const inputBg = isDarkMode ? colors.darkBg : 'white';
  const inputBorder = isDarkMode ? `${colors.primary}30` : 'rgba(219, 234, 254, 1)';
  const inputText = isDarkMode ? colors.textPrimary : 'inherit';
  const hoverBgColor = isDarkMode ? colors.primary + '20' : 'rgba(239, 246, 255, 1)';
  const buttonHoverBg = isDarkMode ? colors.primary + '30' : 'rgba(219, 234, 254, 0.7)';

  return (
    <>
      <header className="flex items-center mb-8">
        <button 
          className="w-10 h-10 rounded-full flex items-center justify-center mr-4 hover:bg-blue-50 transition-colors duration-300 dark-mode-transition"
          onClick={() => setView('main')}
          style={{ '&:hover': { backgroundColor: hoverBgColor } }}
          disabled={isProcessing}
        >
          <ArrowLeft size={24} style={{ color: colors.textPrimary }} />
        </button>
        <div>
          <h1 className="text-2xl font-bold dark-mode-transition" style={{ color: colors.textPrimary }}>Add Medication</h1>
          <p className="text-sm dark-mode-transition" style={{ color: colors.textSecondary }}>Because who can remember everything they take?</p>
        </div>
      </header>

      <div className="mb-8 p-6 rounded-2xl shadow-lg border dark-mode-transition"
           style={{ 
             backgroundColor: cardBg, 
             borderColor: inputBorder,
             boxShadow: `0 10px 15px -3px ${colors.primary}20` 
           }}>
        <form className="space-y-6" onSubmit={handleSubmit}>
          {/* Medication name */}
          <div>
            <label className="block text-sm font-medium mb-2 dark-mode-transition" style={{ color: colors.textPrimary }}>
              Medication Name
            </label>
            <input 
              type="text"
              name="name"
              value={formData.name}
              onChange={handleChange}
              className="w-full p-3 rounded-xl border focus:outline-none focus:ring-2 focus:ring-blue-400 transition-all duration-300 dark-mode-transition"
              style={{ 
                backgroundColor: inputBg, 
                borderColor: inputBorder,
                color: inputText
              }}
              placeholder="That pill whose name you always forget"
              required
              disabled={isProcessing}
            />
          </div>

          {/* Dosage and Form */}
          <div className="grid grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium mb-2 dark-mode-transition" style={{ color: colors.textPrimary }}>
                Dosage
              </label>
              <input 
                type="text"
                name="dosage"
                value={formData.dosage}
                onChange={handleChange}
                className="w-full p-3 rounded-xl border focus:outline-none focus:ring-2 focus:ring-blue-400 transition-all duration-300 dark-mode-transition"
                style={{ 
                  backgroundColor: inputBg, 
                  borderColor: inputBorder,
                  color: inputText
                }}
                placeholder="e.g., 10mg"
                required
                disabled={isProcessing}
              />
            </div>
            <div>
              <label className="block text-sm font-medium mb-2 dark-mode-transition" style={{ color: colors.textPrimary }}>
                Form
              </label>
              <select 
                name="form"
                value={formData.form}
                onChange={handleChange}
                className="w-full p-3 rounded-xl border focus:outline-none focus:ring-2 focus:ring-blue-400 transition-all duration-300 dark-mode-transition"
                style={{ 
                  backgroundColor: inputBg, 
                  borderColor: inputBorder,
                  color: inputText
                }}
                required
                disabled={isProcessing}
              >
                <option value="">Select form</option>
                <option value="tablet">Tablet</option>
                <option value="capsule">Capsule</option>
                <option value="liquid">Liquid</option>
                <option value="injection">Injection</option>
                <option value="patch">Patch</option>
                <option value="inhaler">Inhaler</option>
              </select>
            </div>
          </div>

          {/* Category and Purpose */}
          <div className="grid grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium mb-2 dark-mode-transition" style={{ color: colors.textPrimary }}>
                Category
              </label>
              <select
                name="category"
                value={formData.category}
                onChange={handleChange}
                className="w-full p-3 rounded-xl border focus:outline-none focus:ring-2 focus:ring-blue-400 transition-all duration-300 dark-mode-transition"
                style={{ 
                  backgroundColor: inputBg, 
                  borderColor: inputBorder,
                  color: inputText
                }}
                required
                disabled={isProcessing}
              >
                <option value="general">General</option>
                <option value="cardiovascular">Cardiovascular</option>
                <option value="metabolic">Metabolic</option>
                <option value="pain">Pain</option>
                <option value="antibiotic">Antibiotic</option>
                <option value="neurological">Neurological</option>
                <option value="respiratory">Respiratory</option>
                <option value="gastrointestinal">Gastrointestinal</option>
                <option value="vitamin">Vitamin/Supplement</option>
                <option value="other">Other</option>
              </select>
            </div>
            <div>
              <label className="block text-sm font-medium mb-2 dark-mode-transition" style={{ color: colors.textPrimary }}>
                Purpose
              </label>
              <input 
                type="text"
                name="purpose"
                value={formData.purpose}
                onChange={handleChange}
                className="w-full p-3 rounded-xl border focus:outline-none focus:ring-2 focus:ring-blue-400 transition-all duration-300 dark-mode-transition"
                style={{ 
                  backgroundColor: inputBg, 
                  borderColor: inputBorder,
                  color: inputText
                }}
                placeholder="What it's used for"
                required
                disabled={isProcessing}
              />
            </div>
          </div>

          {/* Schedule */}
          <div>
            <label className="block text-sm font-medium mb-2 dark-mode-transition" style={{ color: colors.textPrimary }}>
              Schedule
            </label>
            <div className="space-y-3">
              {formData.schedule.map((scheduleItem, index) => (
                <div 
                  key={index} 
                  className="flex items-center justify-between p-3 rounded-xl border dark-mode-transition"
                  style={{ 
                    backgroundColor: inputBg, 
                    borderColor: inputBorder
                  }}
                >
                  <div className="flex items-center">
                    <div className="w-8 h-8 rounded-full flex items-center justify-center mr-2 dark-mode-transition" 
                         style={{ backgroundColor: `${colors.primary}15`, color: colors.primary }}>
                      <Clock size={16} />
                    </div>
                    <input 
                      type="time"
                      className="p-1 border-0 focus:outline-none bg-transparent dark-mode-transition"
                      style={{ color: inputText }}
                      value={scheduleItem.time}
                      onChange={(e) => updateScheduleTime(index, e.target.value)}
                      required
                      disabled={isProcessing}
                    />
                  </div>
                  <button 
                    type="button" 
                    className="w-6 h-6 rounded-full flex items-center justify-center dark-mode-transition"
                    style={{ backgroundColor: `${colors.danger}15`, color: colors.danger }}
                    onClick={() => formData.schedule.length > 1 && removeScheduleTime(index)}
                    disabled={isProcessing}
                  >
                    <X size={14} />
                  </button>
                </div>
              ))}
              <button 
                type="button"
                className="flex items-center justify-center w-full p-3 rounded-xl border border-dashed transition-all duration-300 text-sm dark-mode-transition"
                style={{ 
                  color: colors.primary,
                  borderColor: isDarkMode ? `${colors.primary}40` : 'rgba(191, 219, 254, 1)',
                  '&:hover': { borderColor: isDarkMode ? colors.primary : 'rgba(96, 165, 250, 1)' }
                }}
                onClick={addScheduleTime}
                disabled={isProcessing}
              >
                <PlusCircle size={16} className="mr-2" />
                Add another time
              </button>
            </div>
          </div>

          {/* Instructions */}
          <div>
            <label className="block text-sm font-medium mb-2 dark-mode-transition" style={{ color: colors.textPrimary }}>
              Instructions
            </label>
            <textarea 
              name="instructions"
              value={formData.instructions}
              onChange={handleChange}
              className="w-full p-3 rounded-xl border focus:outline-none focus:ring-2 focus:ring-blue-400 transition-all duration-300 dark-mode-transition"
              style={{ 
                backgroundColor: inputBg, 
                borderColor: inputBorder,
                color: inputText
              }}
              placeholder="Special instructions (e.g., take with food, avoid with alcohol)"
              rows="3"
              required
              disabled={isProcessing}
            ></textarea>
          </div>

          {/* Dates and Refill */}
          <div className="grid grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium mb-2 dark-mode-transition" style={{ color: colors.textPrimary }}>
                Start Date
              </label>
              <input 
                type="date"
                name="startDate"
                value={formData.startDate}
                onChange={handleChange}
                className="w-full p-3 rounded-xl border focus:outline-none focus:ring-2 focus:ring-blue-400 transition-all duration-300 dark-mode-transition"
                style={{ 
                  backgroundColor: inputBg, 
                  borderColor: inputBorder,
                  color: inputText
                }}
                disabled={isProcessing}
              />
            </div>
            <div>
              <label className="block text-sm font-medium mb-2 dark-mode-transition" style={{ color: colors.textPrimary }}>
                Refill Date
              </label>
              <input 
                type="date"
                name="refillDate"
                value={formData.refillDate}
                onChange={handleChange}
                className="w-full p-3 rounded-xl border focus:outline-none focus:ring-2 focus:ring-blue-400 transition-all duration-300 dark-mode-transition"
                style={{ 
                  backgroundColor: inputBg, 
                  borderColor: inputBorder,
                  color: inputText
                }}
                disabled={isProcessing}
              />
            </div>
          </div>

          <div>
            <label className="block text-sm font-medium mb-2 dark-mode-transition" style={{ color: colors.textPrimary }}>
              Refills Remaining
            </label>
            <input 
              type="number"
              name="refillRemaining"
              value={formData.refillRemaining}
              onChange={handleChange}
              min="0"
              className="w-full p-3 rounded-xl border focus:outline-none focus:ring-2 focus:ring-blue-400 transition-all duration-300 dark-mode-transition"
              style={{ 
                backgroundColor: inputBg, 
                borderColor: inputBorder,
                color: inputText
              }}
              disabled={isProcessing}
            />
          </div>

          {/* Prescriber */}
          <div>
            <label className="block text-sm font-medium mb-2 dark-mode-transition" style={{ color: colors.textPrimary }}>
              Prescribed By
            </label>
            <input 
              type="text"
              name="prescribedBy"
              value={formData.prescribedBy}
              onChange={handleChange}
              className="w-full p-3 rounded-xl border focus:outline-none focus:ring-2 focus:ring-blue-400 transition-all duration-300 dark-mode-transition"
              style={{ 
                backgroundColor: inputBg, 
                borderColor: inputBorder,
                color: inputText
              }}
              placeholder="Your doctor's name"
              disabled={isProcessing}
            />
          </div>

          {/* Side Effects */}
          <div>
            <div className="flex items-center justify-between mb-2">
              <label className="text-sm font-medium dark-mode-transition" style={{ color: colors.textPrimary }}>
                Side Effects
              </label>
              <button 
                type="button"
                className="text-xs px-2 py-1 rounded-lg transition-all duration-300 dark-mode-transition"
                style={{ 
                  backgroundColor: `${colors.primary}15`,
                  color: colors.primary
                }}
                onClick={addSideEffect}
                disabled={isProcessing}
              >
                Add Effect
              </button>
            </div>

            {formData.sideEffects.length > 0 ? (
              <div className="flex flex-wrap gap-2 p-3 rounded-xl border dark-mode-transition"
                   style={{ 
                     backgroundColor: inputBg, 
                     borderColor: inputBorder
                   }}>
                {formData.sideEffects.map((effect, idx) => (
                  <div 
                    key={idx}
                    className="flex items-center px-3 py-1 rounded-full text-xs dark-mode-transition"
                    style={{ 
                      backgroundColor: `${colors.textSecondary}15`,
                      color: colors.textSecondary
                    }}
                  >
                    {effect}
                    <button 
                      type="button"
                      className="ml-1 w-4 h-4 rounded-full flex items-center justify-center"
                      onClick={() => removeSideEffect(idx)}
                      disabled={isProcessing}
                    >
                      <X size={10} />
                    </button>
                  </div>
                ))}
              </div>
            ) : (
              <div className="p-3 rounded-xl border flex items-center dark-mode-transition"
                   style={{ 
                     backgroundColor: inputBg, 
                     borderColor: inputBorder
                   }}>
                <p className="text-xs italic dark-mode-transition" style={{ color: colors.textSecondary }}>
                  No side effects added
                </p>
              </div>
            )}
          </div>

          {/* Interactions */}
          <div>
            <div className="flex items-center justify-between mb-2">
              <label className="text-sm font-medium dark-mode-transition" style={{ color: colors.textPrimary }}>
                Interactions
              </label>
              <button 
                type="button"
                className="text-xs px-2 py-1 rounded-lg transition-all duration-300 dark-mode-transition"
                style={{ 
                  backgroundColor: `${colors.primary}15`,
                  color: colors.primary
                }}
                onClick={addInteraction}
                disabled={isProcessing}
              >
                Add Interaction
              </button>
            </div>

            {formData.interactions.length > 0 ? (
              <div className="space-y-2 p-3 rounded-xl border dark-mode-transition"
                   style={{ 
                     backgroundColor: inputBg, 
                     borderColor: inputBorder
                   }}>
                {formData.interactions.map((interaction, idx) => (
                  <div 
                    key={idx}
                    className="flex items-center justify-between px-3 py-2 rounded-lg text-xs dark-mode-transition"
                    style={{ 
                      backgroundColor: interaction.severity === 'high' ? `${colors.danger}15` : 
                                       interaction.severity === 'moderate' ? `${colors.warning}15` : 
                                       `${colors.success}15`,
                      color: interaction.severity === 'high' ? colors.danger : 
                             interaction.severity === 'moderate' ? colors.warning : 
                             colors.success
                    }}
                  >
                    <div>
                      <span className="font-medium">{interaction.medication}</span> - 
                      <span className="ml-1">{interaction.description}</span>
                    </div>
                    <button 
                      type="button"
                      className="ml-1 w-5 h-5 rounded-full flex items-center justify-center"
                      onClick={() => removeInteraction(idx)}
                      disabled={isProcessing}
                    >
                      <X size={12} />
                    </button>
                  </div>
                ))}
              </div>
            ) : (
              <div className="p-3 rounded-xl border flex items-center dark-mode-transition"
                   style={{ 
                     backgroundColor: inputBg, 
                     borderColor: inputBorder
                   }}>
                <p className="text-xs italic dark-mode-transition" style={{ color: colors.textSecondary }}>
                  No interactions added
                </p>
              </div>
            )}
          </div>

          {/* Reminders */}
          <div className="flex items-center justify-between p-3 rounded-xl hover:bg-blue-50 transition-colors duration-300 dark-mode-transition"
               style={{ '&:hover': { backgroundColor: hoverBgColor } }}>
            <div className="flex items-center">
              <div className="w-8 h-8 rounded-full flex items-center justify-center mr-3 dark-mode-transition" 
                   style={{ 
                     backgroundColor: `${colors.primary}20`,
                     color: colors.primary
                   }}>
                <Bell size={18} />
              </div>
              <span className="text-sm font-medium dark-mode-transition" style={{ color: colors.textPrimary }}>Reminders</span>
            </div>
            <button 
              type="button"
              className="w-10 h-5 rounded-full relative transition-all duration-300 dark-mode-transition"
              style={{ 
                backgroundColor: formData.reminders ? colors.primary : isDarkMode ? colors.darkBg : '#E5E7EB',
                boxShadow: formData.reminders ? `0 0 10px ${colors.primary}40` : 'none'
              }}
              onClick={toggleReminders}
              disabled={isProcessing}
            >
              <span 
                className="absolute top-0.5 w-4 h-4 rounded-full bg-white transform transition-transform duration-300"
                style={{ 
                  left: formData.reminders ? 'auto' : '0.125rem',
                  right: formData.reminders ? '0.125rem' : 'auto'
                }}
              ></span>
            </button>
          </div>

          {/* Submit button */}
          <button 
            type="submit"
            className="w-full py-4 rounded-xl text-white font-medium transition-all duration-500 hover:shadow-xl transform hover:-translate-y-1 flex items-center justify-center"
            style={{ 
              background: `linear-gradient(to right, ${colors.primary}, ${colors.accent})`,
              boxShadow: `0 10px 15px -3px ${colors.primary}40`
            }}
            disabled={isProcessing}
          >
            {isProcessing ? (
              <>
                <Loader size={20} className="animate-spin mr-2" />
                Saving Medication...
              </>
            ) : (
              'Save Medication'
            )}
          </button>
        </form>
      </div>

      {/* Custom CSS for spinner */}
      <style jsx>{`
        @keyframes spin {
          from { transform: rotate(0deg); }
          to { transform: rotate(360deg); }
        }
        .animate-spin {
          animation: spin 1s linear infinite;
        }
      `}</style>
    </>
  );
};

export default AddMedicationForm;
