import io from 'socket.io-client';

/**
 * Service that handles WebRTC connections for video calling
 */
class WebRTCService {
  constructor() {
    // Connection objects
    this.socket = null;
    this.peerConnection = null;

    // Media streams
    this.localStream = null;
    this.remoteStream = null;

    // User identifiers
    this.userId = null;
    this.targetUserId = null;

    // Call state
    this.isCallInitiator = false;
    this.pendingOffer = null;
    this.isCallActive = false; // Track active calls
    this.isCallConnected = false; // Track established connections

    // Connection stability enhancements
    this.reconnectionAttempts = 0;
    this.maxReconnectionAttempts = 3;
    this.disconnectionTimer = null;
    this.lastOffer = null;

    // Callback functions
    this.onLocalStream = null;
    this.onRemoteStream = null;
    this.onCallStarted = null;
    this.onCallEnded = null;
    this.onCallConnecting = null;
    this.onIncomingCall = null;
    this.onICEConnectionStateChange = null;
    this.onUserNotOnline = null;

    // Initialization state
    this.isInitialized = false;
  }

  /**
   * Log messages with timestamp and store in localStorage
   */
  log(message, data = null) {
    const timestamp = new Date().toISOString().split('T')[1].substring(0, 8);
    const prefix = `[WebRTC ${timestamp}]`;

    if (data) {
      console.log(prefix, message, data);
    } else {
      console.log(prefix, message);
    }

    // Store logs in localStorage for debugging across page refreshes
    const logs = JSON.parse(localStorage.getItem('webrtc_logs') || '[]');
    logs.push({
      timestamp: new Date().toISOString(),
      message,
      data: data ? JSON.stringify(data) : null
    });

    // Keep only the last 100 logs
    if (logs.length > 100) {
      logs.shift();
    }

    localStorage.setItem('webrtc_logs', JSON.stringify(logs));
  }

  /**
   * Initialize the WebRTC service with user information and callbacks
   * @param {string} userId - The current user's ID
   * @param {object} callbacks - Callback functions to handle events
   * @param {string} serverUrl - The URL of the signaling server (optional)
   */
  initialize(userId, callbacks = {}, serverUrl = null) {
    this.log('Initializing WebRTC service for user:', userId);
    this.userId = userId;

    // Set up callbacks
    this.updateCallbacks(callbacks);

    // Connect to the signaling server if not already connected
    if (!this.socket || !this.socket.connected) {
      // Use provided URL, fallback to production URL, then localhost as last resort
      const signalingServer = serverUrl || 
                             (process.env.REACT_APP_SIGNALING_SERVER || 
                              'http://localhost:3001');

      this.log('Connecting to signaling server:', signalingServer);
      this.socket = io(signalingServer);

      // Set up event listeners for signaling messages
      this.setupSocketListeners();
    }

    // Register user ID with the signaling server
    if (this.socket) {
      this.socket.emit('register', userId);
    }

    this.isInitialized = true;
  }

  /**
   * Update callback functions
   * @param {object} callbacks - Callback functions to update
   */
  updateCallbacks(callbacks = {}) {
    // Only update provided callbacks, keep existing ones otherwise
    if (callbacks.onLocalStream !== undefined) 
      this.onLocalStream = callbacks.onLocalStream;
    if (callbacks.onRemoteStream !== undefined) 
      this.onRemoteStream = callbacks.onRemoteStream;
    if (callbacks.onCallStarted !== undefined) 
      this.onCallStarted = callbacks.onCallStarted;
    if (callbacks.onCallEnded !== undefined) 
      this.onCallEnded = callbacks.onCallEnded;
    if (callbacks.onCallConnecting !== undefined) 
      this.onCallConnecting = callbacks.onCallConnecting;
    if (callbacks.onIncomingCall !== undefined) 
      this.onIncomingCall = callbacks.onIncomingCall;
    if (callbacks.onICEConnectionStateChange !== undefined) 
      this.onICEConnectionStateChange = callbacks.onICEConnectionStateChange;
    if (callbacks.onUserNotOnline !== undefined)
      this.onUserNotOnline = callbacks.onUserNotOnline;

    // Initialize default callback functions if not already set
    this.onLocalStream = this.onLocalStream || (() => {});
    this.onRemoteStream = this.onRemoteStream || (() => {});
    this.onCallStarted = this.onCallStarted || (() => {});
    this.onCallEnded = this.onCallEnded || (() => {});
    this.onCallConnecting = this.onCallConnecting || (() => {});
    this.onIncomingCall = this.onIncomingCall || (() => {});
    this.onICEConnectionStateChange = this.onICEConnectionStateChange || (() => {});
    this.onUserNotOnline = this.onUserNotOnline || (() => {});
  }

  /**
   * Set up all socket event listeners for signaling
   */
  setupSocketListeners() {
    // Handle incoming calls
    this.socket.on('incoming-call', async ({ callerUserId, offerSDP }) => {
      this.log('Incoming call from:', callerUserId);

      // If there's already an active call, ignore this one
      if (this.isCallActive) {
        this.log('Already in a call, ignoring incoming call');
        return;
      }

      this.targetUserId = callerUserId;
      this.isCallInitiator = false;

      // Store the offer to use when call is accepted
      this.pendingOffer = offerSDP;

      // Notify the application of the incoming call
      this.onIncomingCall(callerUserId);
    });

    // Handle when the other person answers our call
    this.socket.on('call-answered', async ({ answerSDP }) => {
      this.log('Call was answered, setting remote description');
      try {
        if (this.peerConnection) {
          await this.peerConnection.setRemoteDescription(
            new RTCSessionDescription(answerSDP)
          );
        }
      } catch (error) {
        this.log('Error setting remote description:', error.message);
        console.error('Error setting remote description:', error);
      }
    });

    // Handle ICE candidates from the other peer
    this.socket.on('ice-candidate', async ({ candidate }) => {
      try {
        if (this.peerConnection) {
          await this.peerConnection.addIceCandidate(
            new RTCIceCandidate(candidate)
          );
          this.log('Added ICE candidate');
        }
      } catch (error) {
        this.log('Error adding ICE candidate:', error.message);
        console.error('Error adding ICE candidate:', error);
      }
    });

    // Handle the other person ending the call
    this.socket.on('call-ended', () => {
      this.log('Call ended remotely');
      this.endCall(false); // Don't notify the other person since they ended it
    });

    // Handle call failure
    this.socket.on('call-failed', ({ message, targetUserId }) => {
      this.log('Call failed:', message);

      // Handle user not online case
      if (message === 'User is not online') {
        if (this.onUserNotOnline) {
          this.onUserNotOnline(targetUserId);
        } else {
          this.onCallEnded();
        }
      } else {
        this.onCallEnded();
      }

      this.resetCallState();
    });

    // Handle user online status
    this.socket.on('user-online-status', ({ userId, isOnline }) => {
      this.log('User online status received:', { userId, isOnline });
      // This event will be handled directly by the requestor
    });
  }

  /**
   * Check if a user is online
   * @param {string} userId - ID of the user to check
   * @returns {Promise<boolean>} - Whether the user is online
   */
  checkUserOnline(userId) {
    return new Promise((resolve) => {
      if (!this.socket || !this.socket.connected) {
        this.log('Socket not connected, user is offline');
        resolve(false);
        return;
      }

      this.log('Checking if user is online:', userId);
      this.socket.emit('check-user-online', { userId });

      // Set a timeout
      const timeout = setTimeout(() => {
        this.log('User online check timed out for:', userId);
        resolve(false);
      }, 3000);

      // Listen for the response
      this.socket.once('user-online-status', ({ userId: responseUserId, isOnline }) => {
        if (responseUserId === userId) {
          clearTimeout(timeout);
          this.log('User online status for', userId, ':', isOnline);
          resolve(isOnline);
        }
      });
    });
  }

  /**
   * Initiate a call to another user
   * @param {string} targetUserId - ID of the user to call
   */
  async makeCall(targetUserId) {
    try {
      // Check if the user is online first
      const isOnline = await this.checkUserOnline(targetUserId);

      if (!isOnline) {
        this.log('Cannot make call - user is not online:', targetUserId);

        if (this.onUserNotOnline) {
          this.onUserNotOnline(targetUserId);
        } else {
          this.onCallEnded();
        }

        return;
      }

      // If there's already an active call, end it first
      if (this.isCallActive) {
        this.log('Ending current call before starting a new one');
        this.endCall(true);
      }

      this.log('Making call to user:', targetUserId);
      this.targetUserId = targetUserId;
      this.isCallInitiator = true;
      this.isCallActive = true; // Mark call as active
      this.onCallConnecting();

      // Make sure we have camera and microphone access
      if (!this.localStream) {
        await this.getLocalMedia();
      }

      // Create peer connection
      this.initializePeerConnection();

      // Create an offer (SDP)
      this.log('Creating offer...');
      const offer = await this.peerConnection.createOffer();

      // Store offer for potential reconnection
      this.lastOffer = offer;

      // Set the offer as our local description
      await this.peerConnection.setLocalDescription(offer);

      // Send the offer to the other user via signaling server
      this.socket.emit('call-user', {
        targetUserId: this.targetUserId,
        offerSDP: offer,
        callerUserId: this.userId
      });

      this.log('Call offer sent');
    } catch (error) {
      this.log('Error making call:', error.message);
      console.error('Error making call:', error);
      this.endCall(true);
    }
  }

  /**
   * Accept an incoming call
   */
  async acceptCall() {
    try {
      this.log('Accepting incoming call from:', this.targetUserId);
      this.isCallActive = true; // Mark call as active

      // Make sure we have camera and microphone access
      if (!this.localStream) {
        await this.getLocalMedia();
      }

      // Create peer connection
      this.initializePeerConnection();

      // Set the remote description from the stored offer
      this.log('Setting remote description from offer');
      await this.peerConnection.setRemoteDescription(
        new RTCSessionDescription(this.pendingOffer)
      );

      // Create an answer
      this.log('Creating answer...');
      const answer = await this.peerConnection.createAnswer();

      // Set the answer as our local description
      await this.peerConnection.setLocalDescription(answer);

      // Send the answer to the caller
      this.socket.emit('call-accepted', {
        targetUserId: this.targetUserId,
        answerSDP: answer
      });

      this.log('Call accepted, answer sent');
      this.onCallConnecting();
    } catch (error) {
      this.log('Error accepting call:', error.message);
      console.error('Error accepting call:', error);
      this.endCall(true);
    }
  }

  /**
   * End an ongoing call
   * @param {boolean} notifyPeer - Whether to send end-call signal to the other user
   */
  endCall(notifyPeer = true) {
    this.log('Ending call, notify peer:', notifyPeer);

    // Notify the other peer if needed
    if (notifyPeer && this.socket && this.targetUserId) {
      this.socket.emit('end-call', { targetUserId: this.targetUserId });
    }

    // Clear any reconnection timers
    if (this.disconnectionTimer) {
      clearTimeout(this.disconnectionTimer);
      this.disconnectionTimer = null;
    }

    // Close peer connection
    if (this.peerConnection) {
      this.peerConnection.close();
      this.peerConnection = null;
    }

    // Stop all media tracks
    if (this.localStream) {
      this.localStream.getTracks().forEach(track => {
        this.log('Stopping track:', track.kind);
        track.stop();
      });
      this.localStream = null;
    }

    // Mark call as inactive
    this.isCallActive = false;

    // Trigger callback
    this.onCallEnded();

    // Reset state
    this.resetCallState();
  }

  /**
   * Reset call state variables
   */
  resetCallState() {
    this.log('Resetting call state');
    this.remoteStream = null;
    this.targetUserId = null;
    this.isCallInitiator = false;
    this.pendingOffer = null;
    this.isCallActive = false;
    this.isCallConnected = false;
    this.reconnectionAttempts = 0;

    if (this.disconnectionTimer) {
      clearTimeout(this.disconnectionTimer);
      this.disconnectionTimer = null;
    }

    this.lastOffer = null;
  }

  /**
   * Initialize the WebRTC peer connection
   */
  initializePeerConnection() {
    // Enhanced ICE servers configuration with multiple STUN servers and free TURN servers
    const configuration = {
      iceServers: [
        // Multiple STUN servers for better connectivity
        { urls: 'stun:stun.l.google.com:19302' },
        { urls: 'stun:stun1.l.google.com:19302' },
        { urls: 'stun:stun2.l.google.com:19302' },
        { urls: 'stun:stun3.l.google.com:19302' },
        { urls: 'stun:stun4.l.google.com:19302' },
        // Free TURN server (replace with your own in production)
        {
          urls: 'turn:openrelay.metered.ca:80',
          username: 'openrelayproject',
          credential: 'openrelayproject'
        },
        {
          urls: 'turn:openrelay.metered.ca:443',
          username: 'openrelayproject',
          credential: 'openrelayproject'
        },
        // If environment variables exist, add them too
        ...(process.env.REACT_APP_TURN_SERVER ? [{
          urls: process.env.REACT_APP_TURN_SERVER,
          username: process.env.REACT_APP_TURN_USERNAME || '',
          credential: process.env.REACT_APP_TURN_CREDENTIAL || ''
        }] : [])
      ],
      iceCandidatePoolSize: 10,
      // The following helps with connection stability
      sdpSemantics: 'unified-plan'
    };

    this.log('Initializing peer connection with configuration:', configuration);

    // Create the RTCPeerConnection
    this.peerConnection = new RTCPeerConnection(configuration);

    // Add local media tracks to the connection if we have them
    if (this.localStream) {
      this.localStream.getTracks().forEach(track => {
        this.log('Adding local track to peer connection:', track.kind);
        this.peerConnection.addTrack(track, this.localStream);
      });
    }

    // Set up event handler for receiving remote media
    this.peerConnection.ontrack = (event) => {
      this.log('Received remote track:', event.track.kind);

      // Create remote stream if it doesn't exist
      if (!this.remoteStream) {
        this.remoteStream = new MediaStream();
        this.onRemoteStream(this.remoteStream);
      }

      // Add all tracks from the remote stream
      event.streams[0].getTracks().forEach(track => {
        this.log('Adding remote track to remote stream:', track.kind);
        this.remoteStream.addTrack(track);
      });
    };

    // Handle ICE candidates generated by our side
    this.peerConnection.onicecandidate = (event) => {
      if (event.candidate) {
        const candidateStr = event.candidate.candidate || '';
        const candidateType = candidateStr.split(' ')[7] || 'unknown'; // Extract type (host, srflx, relay)
        this.log('Generated ICE candidate of type:', candidateType);

        // Send our ICE candidate to the other peer
        this.socket.emit('ice-candidate', {
          targetUserId: this.targetUserId,
          candidate: event.candidate
        });
      }
    };

    // Additional monitoring for ICE gathering state
    this.peerConnection.onicegatheringstatechange = () => {
      this.log('ICE gathering state changed:', this.peerConnection.iceGatheringState);
    };

    // Monitor signaling state
    this.peerConnection.onsignalingstatechange = () => {
      this.log('Signaling state changed:', this.peerConnection.signalingState);
    };

    // Monitor connection state
    this.peerConnection.onconnectionstatechange = () => {
      this.log('Connection state changed:', this.peerConnection.connectionState);
    };

    // Improved ICE connection state handling with reconnection attempts
    this.peerConnection.oniceconnectionstatechange = () => {
      const state = this.peerConnection.iceConnectionState;
      this.log('ICE connection state changed to:', state);

      this.onICEConnectionStateChange(state);

      // Handle connection establishment
      if (state === 'connected' || state === 'completed') {
        this.log('Call connected successfully!');
        if (!this.isCallConnected) {
          this.isCallConnected = true;
          this.onCallStarted();

          // Reset reconnection attempts
          this.reconnectionAttempts = 0;
        }
      } 
      // Only handle connection failures after multiple attempts
      else if (state === 'failed') {
        this.log('Connection failed');

        // Attempt to restart ICE if possible before ending the call
        if (this.reconnectionAttempts < this.maxReconnectionAttempts) {
          this.reconnectionAttempts++;
          this.log(`Trying to reconnect (attempt ${this.reconnectionAttempts}/${this.maxReconnectionAttempts})...`);

          try {
            // Try to restart ICE connection
            this.peerConnection.restartIce();

            // If we have the original offer, we could also try to renegotiate
            if (this.isCallInitiator && this.lastOffer) {
              this.log('Attempting to renegotiate connection...');
              setTimeout(async () => {
                try {
                  const offer = await this.peerConnection.createOffer({ iceRestart: true });
                  await this.peerConnection.setLocalDescription(offer);

                  this.socket.emit('call-user', {
                    targetUserId: this.targetUserId,
                    offerSDP: offer,
                    callerUserId: this.userId
                  });

                  this.lastOffer = offer;
                } catch (error) {
                  this.log('Error during ICE restart negotiation:', error.message);
                  console.error('Error during ICE restart negotiation:', error);
                }
              }, 1000);
            }
          } catch (error) {
            this.log('Error attempting to restart ICE:', error.message);
            console.error('Error attempting to restart ICE:', error);
          }
        } else {
          this.log('Max reconnection attempts reached, ending call');
          this.endCall(true);
        }
      }
      // For disconnected state, wait a bit before taking action
      else if (state === 'disconnected') {
        this.log('Connection temporarily disconnected');

        // Don't end the call immediately, add a delay to allow for reconnection
        if (!this.disconnectionTimer) {
          this.disconnectionTimer = setTimeout(() => {
            // After timeout, check if state is still disconnected
            if (this.peerConnection && 
                this.peerConnection.iceConnectionState === 'disconnected') {
              this.log('Connection remained disconnected for too long');
              this.endCall(true);
            }
            this.disconnectionTimer = null;
          }, 10000); // Wait 10 seconds before ending the call
        }
      }
      // Reset the disconnection timer if we move out of the disconnected state
      else if (this.disconnectionTimer && state !== 'disconnected') {
        clearTimeout(this.disconnectionTimer);
        this.disconnectionTimer = null;
      }
    };
  }

  /**
   * Get access to local camera and microphone
   * @param {boolean|object} videoConstraints - Constraints for video
   * @param {boolean|object} audioConstraints - Constraints for audio
   * @returns {Promise<MediaStream>} - Local media stream
   */
  async getLocalMedia(videoConstraints = true, audioConstraints = true) {
    try {
      this.log('Requesting access to local media with constraints:', {
        video: videoConstraints,
        audio: audioConstraints
      });

      // Request access to camera and microphone
      const stream = await navigator.mediaDevices.getUserMedia({
        video: videoConstraints,
        audio: audioConstraints
      });

      this.log('Access to local media granted');

      this.localStream = stream;
      this.onLocalStream(stream);
      return stream;
    } catch (error) {
      this.log('Error accessing media devices:', error.message);
      console.error('Error accessing media devices:', error);
      throw error;
    }
  }

  /**
   * Toggle audio mute state
   * @param {boolean} muted - Whether to mute audio
   */
  toggleAudio(muted) {
    if (this.localStream) {
      this.localStream.getAudioTracks().forEach(track => {
        this.log(muted ? 'Muting audio' : 'Unmuting audio');
        track.enabled = !muted;
      });
    }
  }

  /**
   * Toggle video on/off
   * @param {boolean} videoOff - Whether to turn off video
   */
  toggleVideo(videoOff) {
    if (this.localStream) {
      this.localStream.getVideoTracks().forEach(track => {
        this.log(videoOff ? 'Turning off camera' : 'Turning on camera');
        track.enabled = !videoOff;
      });
    }
  }

  /**
   * Disconnect from signaling server and clean up resources
   */
  disconnect() {
    this.log('Disconnecting WebRTC service');

    // End any active call
    this.endCall(true);

    // Disconnect from signaling server
    if (this.socket) {
      this.socket.disconnect();
      this.socket = null;
    }

    // Reset initialization status
    this.isInitialized = false;

    // Clear user ID
    this.userId = null;
  }
}

// Create a singleton instance
const webRTCService = new WebRTCService();
export default webRTCService;
